INSERT INTO `question` VALUES ('1272', 'Nêu các yếu tố của môi trường marketing?', null, null, '5', '0', '278', '463', '1551707351', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1273', null, null, null, '5', '1272', '278', '463', '1551707351', null, null, null, '<p><strong>Các nhân tố của môi trường marketing</strong></p>\r\n<p>-        Môi trường marketing <a class=\"cloze\" href=\"#mce_temp_url#\">vĩ mô</a></p>\r\n<p>+ Nhân khẩu học</p>\r\n<p>+Chính trị</p>\r\n<p>+Pháp luật</p>\r\n<p>+Tự nhiên</p>\r\n<p>+Khoa học-công nghệ</p>\r\n<p>+ Văn hóa</p>\r\n<p>+Xã hội</p>\r\n<p>+Kinh tế</p>\r\n<p>-        Môi trường<a class=\"cloze\" href=\"#mce_temp_url#\"> vi mô</a></p>\r\n<p>+ Trung gian marketing</p>\r\n<p>+Các nhà cung cấp</p>\r\n<p>+Đối thủ cạnh tranh</p>\r\n<p>+Doanh nghiệp</p>\r\n<p>+Khách hàng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1274', 'Nêu khái niệm môi trường marketing vi mô?', null, null, '5', '0', '278', '463', '1551707563', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1275', null, null, null, '5', '1274', '278', '463', '1551707563', null, null, null, '<p>-        Là những lực lượng có quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">trực tiếp </a>với các hoạt đông marketing của doanh nghiệp và đồng thời hoạt động marketing của doanh nghiệp có thể tác động ngược lại mang tính cá biệt và cục bộ, có thể chia thành hai nhóm</p>\r\n<p>+ Môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">nội bộ</a>: Các yếu tố trong doanh nghiệp có ảnh hưởng khả năng vận hành marketing hữu hiệu</p>\r\n<p>+ Môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">ngành</a>: nhà cung cấp, trung gian marketing, khách hàng, đối thủ cạnh tranh và công chúng.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1276', null, null, null, '5', '1274', '278', '463', '1551707563', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1277', 'Các yếu tố thuộc môi trường marketing vi mô ? Trình bày một trong các yếu tố thuộc môi trường?', null, null, '5', '0', '278', '463', '1551708093', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1278', null, null, null, '5', '1277', '278', '463', '1551708093', null, null, null, '<ol>\r\n<li><span style=\"font-size: 18px; color: #000000;\"><strong>Nhà cung cấp</strong></span></li>\r\n</ol>\r\n<p>-        Những chủ thể đưa ra những yếu tố <a class=\"cloze\" href=\"#mce_temp_url#\">đầu vào</a> cho doanh nghiệp, có ảnh hưởng đến hoạt động marketing của doanh nghiệp.</p>\r\n<p>-        Các yếu tố đầu vào của hoạt động marketing của doanh nghiệp</p>\r\n<p>+ Nguyên, nhiên, vật liệu</p>\r\n<p>+ Thành phẩm và bán thành phẩm</p>\r\n<p>+Máy móc, thiết bị</p>\r\n<p>+Quy trình công nghệ</p>\r\n<p>+Lao động</p>\r\n<p>+Thông tin</p>\r\n<p><strong><span style=\"font-size: 18px;\">   2. Trung gian marketing</span></strong></p>\r\n<p>-        Những chủ thể giúp <a class=\"cloze\" href=\"#mce_temp_url#\">kết nối</a> doanh nghiệp với khách hàng, có ảnh hưởng đến hoạt động marketing của doanh nghiệp</p>\r\n<p>+ Trung gian <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối</a>: vận chuyển, phân phối hàng hóa tới khách hàng. Trung gian phân phối có thể là các đại lý, bán buôn,..</p>\r\n<p>+Trung gian <a class=\"cloze\" href=\"#mce_temp_url#\">tài chính:</a> đảm bảo nguồn cung về tài chính. Có thể kể đến như các ngân hàng, các tố chức tín dụng..</p>\r\n<p>+Trung gian <a class=\"cloze\" href=\"#mce_temp_url#\">dịch vụ</a>: Cung cấp các dịch vụ đi kèm với các hoạt đông marketing</p>\r\n<p><span style=\"font-size: 18px; color: #000000;\"><strong>3.Đối thủ cạnh tranh</strong></span></p>\r\n<p>-        Khái niệm: Những chủ thể cùng hướng tới một đối tượng<a class=\"cloze\" href=\"#mce_temp_url#\"> khách hàng</a> mà doanh nghiệp đang hướng tới</p>\r\n<p>-        Bốn góc độ/dạng đối thủ cạnh tranh</p>\r\n<p>+Dạng 1: Cạnh tranh ngân sách: Là  những đối thủ cạnh tranh thể hiện khát vọng người tiêu dùng, muốn thỏa mãn các dạng nhu cầu cụ thể, mong muốn khác nhau trên cơ sở cùng một lượng <a class=\"cloze\" href=\"#mce_temp_url#\">thu nhập</a>.</p>\r\n<p>+Dạng 2: Cạnh tranh nhu cầu: Đối thủ cạnh tranh là những  hàng hóa khác nhau cùng thỏa mãn nhu cầu; Cạnh tranh về các sản phẩm khác nhau cùng <a class=\"cloze\" href=\"#mce_temp_url#\">công dụng</a></p>\r\n<p>+Dạng 3: Cạnh tranh sản phẩm: Cạnh tranh giữa các kiểu <a class=\"cloze\" href=\"#mce_temp_url#\">hàng hóa</a> khác nhau trong cùng ngành hàng</p>\r\n<p>+Dạng 4: Cạnh tranh thương hiệu: là sự cạnh tranh của các doanh nghiệp sản xuất sản phẩm có thể thay thế nhau nhưng có<a class=\"cloze\" href=\"#mce_temp_url#\"> nhãn hiệu </a>khác nhau.</p>\r\n<p><span style=\"font-size: 18px;\"><strong>4. Khách hàng</strong></span></p>\r\n<p>-        Những chủ thể <a class=\"cloze\" href=\"#mce_temp_url#\">mua</a> sản phẩm và dịch vụ của doanh nghiệp</p>\r\n<p>+Khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a>: Là một người hoặc một nhóm người đang hoặc sẽ mua và sử dụng sản phẩm, dịch vụ phục vụ cho mục đích cá nhân của hok.</p>\r\n<p>+Khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a>: Bao gồm các cá nhân và tổ chức mua sắm sản phẩm và dịch vụ vì mục đích sản xuát hàng hóa hay dịch vụ khác để bán, cho thuê hay cung ứng cho người khác để kiếm lợi</p>\r\n<p><strong><span style=\"font-size: 18px;\">5. Công chúng</span></strong></p>\r\n<p>-        Những chủ thể có <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ</a> nhất đinh với doanh nghiệp, tạo ra sự tác động đủ lớn đến hoạt động marketing của doanh nghiệp</p>\r\n<p>+ Công chúng tài chính</p>\r\n<p>+ Công chúng truyền thông đại chúng:báo đài…</p>\r\n<p>+ Công chúng công quyền</p>\r\n<p>+ Công chúng hoạt động xã hội</p>\r\n<p>+Công chúng nội bộ</p>\r\n<p>+Công chúng địa phương</p>\r\n<p><strong><span style=\"font-size: 18px;\">6.Nhóm môi trường nội bộ</span></strong></p>\r\n<p>-        Các yếu tố <a class=\"cloze\" href=\"#mce_temp_url#\">bên trong</a> doanh nghiệp có liên quan trực tiếp đến điều hành và quản trị marketing như nguồn tài chính, nguồn lực R&amp;D, văn hóa doanh nghiệp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1279', 'Nêu khái niệm môi trường marketing vĩ mô?', null, null, '5', '0', '279', '463', '1551708488', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1280', null, null, null, '5', '1279', '279', '463', '1551708488', null, null, null, '<ul>\r\n<li>Môi trường marketing vĩ mô: bao hàm các lực lượng <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội</a> rộng lớn đang ảnh hưởng đến toàn cục môi trường vi mô, nội bộ doanh nghiệp và tạo ra <a class=\"cloze\" href=\"#mce_temp_url#\">thời cơ</a> cũng như thách thức cũng như mối đe dọa với doanh nghiệp</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1281', 'Nêu các yếu tố thuộc môi trường marketing vĩ mô? Trình bày có ví dụ minh họa một trong các yếu thuộc môi trường marketing vĩ mô (nhân khẩu học, môi trường kinh tế vĩ mô, môi trường khoa học kĩ thuật)', null, null, '5', '0', '279', '463', '1551708847', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1282', null, null, null, '5', '1281', '279', '463', '1551708847', null, null, null, '<p> </p>\r\n<ul>\r\n<li>Các yếu tố cơ bản</li>\r\n</ul>\r\n<p> </p>\r\n<p>-        Chính trị-pháp luật</p>\r\n<p> </p>\r\n<p>-        Nhân khẩu học</p>\r\n<p> </p>\r\n<p>-        Điều kiện tự nhiên</p>\r\n<p> </p>\r\n<p>-        Khoa học- công nghệ</p>\r\n<p> </p>\r\n<p>-        Kinh tế vĩ mô</p>\r\n<p> </p>\r\n<p>-        Văn hóa-xã hội</p>\r\n<p> </p>\r\n<p><span style=\"font-size: 14px;\"><strong>1.<a class=\"cloze\" href=\"#mce_temp_url#\">Nhân khẩu họ</a>c</strong></span></p>\r\n<p>-        <em><strong><span style=\"text-decoration: underline;\">Khái niệm</span></strong></em>: Nhân khẩu học là<a class=\"cloze\" href=\"#mce_temp_url#\"> khoa học</a> tự nhiên nghiên cứu về vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">dân số</a> dựa trên các khía cạnh như qui mô dân số, mật độ dân số,giới tính, chủng tộc, nghề nghiệp và các chỉ tiêu thống kê khác.</p>\r\n<p>-    <span style=\"text-decoration: underline;\"><em><strong>    Một số yếu tố cơ bản</strong></em></span>:</p>\r\n<p>+ Quy mô, mật độ và tốc độ gia tăng dân số</p>\r\n<p>+Cơ cấu dân số, tình trạng hôn nhân và gia đình</p>\r\n<p>+Tốc độ đô thị hóa</p>\r\n<p>+ Các chỉ tiêu khác</p>\r\n<p>+Tỉ lệ giới tính</p>\r\n<p>Ví dụ: Tại những nơi đông dân cư, mật độ lớnèNhu cầu lớn. Thể hiện tại thành phố Hà Nội đông dân cư, tập trung các trung tâm thương mại. Và ngược lại.</p>\r\n<p><span style=\"font-size: 14px;\"><strong>2. <a class=\"cloze\" href=\"#mce_temp_url#\">Chính trị-pháp luật</a></strong></span></p>\r\n<p><span style=\"text-decoration: underline;\"><em><strong>Các yếu tố ảnh hưởng:</strong></em></span></p>\r\n<p>+ Thể chế chính trị</p>\r\n<p>+ Các bộ luật, luật, các văn bản dưới luật</p>\r\n<p>+Các luật chủ yếu ảnh hưởng đến marketing như luật quảng cáo…</p>\r\n<p>+ Hệ thống các chính sách</p>\r\n<p><span style=\"font-size: 14px;\"><strong>3. <a class=\"cloze\" href=\"#mce_temp_url#\">Điều kiện tự nhiên</a></strong></span></p>\r\n<p>-        Điều kiện tự nhiên, vị trí địa lý, khí hậu</p>\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\">Tài nguyên </a>thiên nhiên</p>\r\n<p>-        Ô nhiễm môi trường và sự cạn kiệt tài nguyên thiên nhiên</p>\r\n<p>-        Sự <a class=\"cloze\" href=\"#mce_temp_url#\">can thiệp </a>của chính phủ trong việc quản lý tài nguyên thiên nhiên</p>\r\n<p>Ví dụ: Đối với bảo quản thực phẩm đặc biệt là nông sản thì yếu tố thời tiết có ý nghĩa đặc biệt quan trọng</p>\r\n<p><span style=\"font-size: 14px;\"><strong>4.<a class=\"cloze\" href=\"#mce_temp_url#\"> Khoa học kĩ thuật và công nghệ</a></strong></span></p>\r\n<p>-        Tình hình nghiên cứu cơ bản và ứng dụng</p>\r\n<p>-        Trình độ công nghệ sản xuất kinh doanh</p>\r\n<p>-        Bảo hộ và ứng dụng công nghệ</p>\r\n<p>-        Hệ thống tiêu chuẩn hóa và phân loại</p>\r\n<p>Ví dụ: Một số tiêu chuẩn như ISO, Vietgap</p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\"><span style=\"font-size: 14px;\"><strong>5. Kinh tế vĩ mô</strong></span></a></p>\r\n<p>-        Tạo ra những cơ hội và thách thức cho doanh nghiệp</p>\r\n<p>-        Gồm các yếu tiis kinh tế ảnh hưởng đến khả năng và mô hình tiêu thụ của người tiêu dùng</p>\r\n<p>-        Tình hình phát triển kinh tế tổng quát</p>\r\n<p>-        Các chỉ số kinh tế: Chính sách tài khóa, đầu tư công,chính sách tiền tệ….</p>\r\n<p>-        Ảnh hưởng của các khối hiệp ước kinh tế..</p>\r\n<p>Ví dụ: Việt Nam gia nhập các khối hiệp ước kinh tế như WTO,.. èGiúp các doanh nghiệp mở rộng thị trường, tiếp xúc với vốn đầu tư nước ngoài nhưng mặt khác cũng tạo ra khó khan như sự cạnh tranh giữa các sản phẩm trong nước và sản phẩm nước ngoài trên chính thị trường Việt Nam.</p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">6. Văn hóa-xã hội</a></p>\r\n<p>-        Bao gồm những định chế và lực lượng đang tác động đến giá trị căn bản, nhận thức, thị hiếu cùng phương cách thay thế, xử sự của xã hội</p>\r\n<p>+ Nền văn hóa</p>\r\n<p>+Phân tầm văn hóa</p>\r\n<p>+Hội nhập và biến đối văn hóa</p>\r\n<p>+ Tầng lớp xã hội</p>\r\n<p>+ Phương thức xử sự,tập tính thói quen tiêu dùng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1283', 'Nêu khái niệm nghiên cứu marketing?', null, null, '5', '0', '281', '463', '1551709085', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1284', null, null, null, '5', '1283', '281', '463', '1551709085', null, null, null, '<p>Nghiên cứu marketing là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">thu thập </a>có hệ thống,<strong>ghi chép</strong>, <strong>phân loại</strong>, <strong>phân tích</strong> và<strong> thông đạt</strong> có mục tiêu các tài liệu có liên quan đến thái độ ứng xử, nhu cầu, quan điểm.. của các cá nhâ, tổ chức tiêu dùng trong mối quan hệ với các hoạt động hàng ngày.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1285', 'Vai trò của nghiên cứu marketing?', null, null, '5', '0', '281', '463', '1551709151', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1286', null, null, null, '5', '1285', '281', '463', '1551709151', null, null, null, '<p>-        Cung cấp <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> cho người ra quyết định</p>\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Giảm</a> rủ ro trong kinh doanh</p>\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Tìm kiếm</a> cơ hội mới. thị trường mới</p>\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Cung cấp</a> thông tin cho hoạch định chiến lược</p>\r\n<p>-        Là tiền đề<a class=\"cloze\" href=\"#mce_temp_url#\"> hỗ trợ </a>quảng cáo</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1287', 'Trình bày có các nguồn dữ liệu  trong nghiên cứu marketing?', null, null, '5', '0', '281', '463', '1551709346', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1288', null, null, null, '5', '1287', '281', '463', '1551709346', null, null, null, '<p>Nguồn dữ kiêu trong nghiên cứu marketing gồm <span style=\"text-decoration: underline;\"><em><strong>dữ liệu</strong></em></span> <span style=\"text-decoration: underline;\"><em><strong>sơ cấp và dữ liệu thứ cấp</strong></em></span>:</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Dữ liệu<a class=\"cloze\" href=\"#mce_temp_url#\"> thứ cấp</a></span></em></strong></p>\r\n<p>+ Bao gồm các thông tin hiện đã có, sẵn sang ở một chỗ nào đó và đã được tập hợp cho một mục đích nghiên cứu.</p>\r\n<p>+Nguồn thu thập dữ liệu<a class=\"cloze\" href=\"#mce_temp_url#\"> thứ cấp</a>:</p>\r\n<p>      Thông tin <a class=\"cloze\" href=\"#mce_temp_url#\">trong</a> công ty: Các tài liệu ghi chép, văn bản thuộc hệ thống thông tin nội bộ của công ty.</p>\r\n<p>     THông tin <a class=\"cloze\" href=\"#mce_temp_url#\">ngoài</a> công ty: Sách báo, tạp chí chuyên ngành,thông tin từ các hiệp hội thương mại..</p>\r\n<p>+Ưu điểm: Thu thập nhanh, chi phí thấp</p>\r\n<p>+Nhược điểm: Độ chính xác, độ phù hợp với nghiên cứu hiện tại không cao; thông tin có thể đã lỗi thời, không còn phù hợp</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Dữ liệu <a class=\"cloze\" href=\"#mce_temp_url#\">sơ cấp</a></span></em></strong></p>\r\n<p>+ Là hệ dữ liệu bao gồm những thông nguyên gốc mà người làm nghiên cứu phải tự khảo cứu hiện trường để phục vụ cho mục tiêu nghiên cứu đặt ra.</p>\r\n<p>+ Nguồn thu thập”</p>\r\n<p>                    <a class=\"cloze\" href=\"#mce_temp_url#\">Quan sát</a>: Quan sát bằng các phương tiện máy móc và quan sát cá nhân trực tiếp</p>\r\n<p>                   <a class=\"cloze\" href=\"#mce_temp_url#\"> Quan trắ</a>c: Điều tra qua thư,điện thoại, điểu tra cá nhân trực tiếp, trao đổi chuyên đề,tọa đàm..</p>\r\n<p>                +Ưu điểm: Thông tin mới, phù hợp với thực tiễn nghiên cứu marketing</p>\r\n<p>                +Nhược điểm: Thời gian thu thập lâu,chi phí lớn.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1289', 'Nêu khái niệm hệ thống thông tin marketing của doanh nghiệp?', null, null, '5', '0', '281', '463', '1551709512', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1290', null, null, null, '5', '1289', '281', '463', '1551709512', null, null, null, '<p>MIS là cấu trúc bao gồn con người,thiết bị, và các <a class=\"cloze\" href=\"#mce_temp_url#\">quy trình</a> <strong>thu thập, </strong><a class=\"cloze\" href=\"#mce_temp_url#\">phân loại</a>, phân tích, lượng giá và phân phối các thông tin chính xác, kịp thời, thích hợp cho người quản trị quyết định marketing nhằm tăng cường <a class=\"cloze\" href=\"#mce_temp_url#\">hiệu quả</a> hoạch hóa,thực hiện và kiểm tra marketing.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1291', null, null, null, '5', '1289', '281', '463', '1551709512', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1292', 'cấu trúc hệ thống thông tin marketing của doanh nghiệp?', null, null, '5', '0', '281', '463', '1551710119', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1293', null, null, null, '5', '1292', '281', '463', '1551710119', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Hệ <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> <a class=\"cloze\" href=\"#mce_temp_url#\">nội bộ</a></span></em></strong>: Tập hợp các quy trình và nguồn cung cấp thông tin cơ bản bên trong</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Hệ <a class=\"cloze\" href=\"#mce_temp_url#\">điều tra</a> marketing:</span></em></strong> Tập hợp các quy trình và nguồn lực được sử dụng để có dữ liệu đang diễn biến hằng ngày</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Hệ <a class=\"cloze\" href=\"#mce_temp_url#\">nghiên cứu</a> marketing:</span></em></strong> Thu thập, xử lý thông tin về một vấn đề công ty đang đối diện</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Hệ <a class=\"cloze\" href=\"#mce_temp_url#\">phân tích</a> marketing:</span></em></strong> Các kĩ thuật thống kê và phân tích mô hình để điều hành marketing hiệu quả.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1294', null, null, null, '5', '1292', '281', '463', '1551710119', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1295', 'Trình bày các loại hình nghiên cứu marketing? (Nghiên cứu cạnh tranh,nghiên cứu khách hàng và người tiêu thụ)', null, null, '5', '0', '281', '463', '1551710266', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1296', null, null, null, '5', '1295', '281', '463', '1551710266', null, null, null, '<p><strong>Các loại hình nghiên cứu marketing</strong></p>\r\n<p>-        Đặc trưng và đo lường khái quát thị trường</p>\r\n<p>-        Khách hàng và người tiêu thụ</p>\r\n<p>-        Phân đoạn thị trường mục tiêu</p>\r\n<p>-        Marketing sản phẩm</p>\r\n<p>-        Marketing xúc tiến</p>\r\n<p>-        Marketing phân phối và phân tích sức bán</p>\r\n<p>-        Marketing giá kinh doanh</p>\r\n<p>-        Cạnh tranh</p>\r\n<p>-        Nghiên cứu dự báo</p>\r\n<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Nghiên cứu<a class=\"cloze\" href=\"#mce_temp_url#\"> cạnh tranh:</a></span></em></strong></li>\r\n</ol>\r\n<p>-        Mục tiêu: Giúp nhận dạng được các điểm  <a class=\"cloze\" href=\"#mce_temp_url#\">mạnh- yếu</a> của các chiến lược marketing của đối thủ trên thị trường</p>\r\n<p>-        Nội dung:</p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Nhận dạng</a> đối thủ: Đặc điểm của đổi thủ cạnh tranh; chiến lược của đối thủ cạnh tranh; mục tiêu hoạt động của chiến lược cạnh tranh..</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Nghiên cứu</a> trên nhiều mặt: Giá cả, sản xuất công nghệ, chất lượng, quảng cáo, dịch vụ thương mại…</p>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Nghiên cứu khách hàng và người tiêu dùng</span></em></strong></li>\r\n</ol>\r\n<p>-        Mục tiêu: Đánh giá đặc điểm, nhu cầu của khách hàng làm cơ sở xác lập mối quan hệ với khách hàng</p>\r\n<p>-        Nội dung:</p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Xác định </a>thông số khái quát và phân loại tập khách hàng tiềm năng</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Nghiên cứu</a> tập tính hiện thực của tập khách hàng( Suy nghĩ,động cơ mua,…)</p>\r\n<p>+ Nghiên cứu<a class=\"cloze\" href=\"#mce_temp_url#\"> tâm lý</a> khách hàng theo dấu hiệu phân loại đặc trưng tính cách.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1297', 'Vẽ sơ đồ quy trình phương pháp luận nghiên cứu marketing của doanh nghiệp?', null, null, '5', '0', '282', '463', '1551711088', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1298', null, null, null, '5', '1297', '282', '463', '1551711088', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1315', 'Nêu khái niệm hành vi khách hàng?', null, null, '5', '0', '284', '467', '1551712529', '1551712544', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1316', null, null, null, '5', '1315', '284', '467', '1551712529', '1551712544', null, null, '<p>Khái niệm:</p>\r\n<ul>\r\n<li>Là các <a class=\"clozetip\" title=\"trạng thái của con người\" href=\"#\">phản ứng/ứng xử</a> trong quá trình hình thành nhu cầu mua sản phẩm, dịch vụ, nhận biêt, đánh giá lựa chọn mua,sử dụng trong số những nhãn hiệu thế vị đang được chào bán trên thị trường nhằm thỏa mãn những nhu cầu của người tiêu dùng và tổ chức</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1318', 'Tại sao phải nghiên cứu hành vi khách hàng?', null, null, '5', '0', '284', '467', '1551712682', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1319', null, null, null, '5', '1318', '284', '467', '1551712682', null, null, null, '<p>Tại sao phải nghiên cứu hành vi khách hàng:</p>\r\n<ul>\r\n<li>Với vai trò là người mua:</li>\r\n</ul>\r\n<p>-         Hiểu được quyết định tiêu dùng: Cái gì? Tại sao? Như thế nào?</p>\r\n<p>-         Nhận ra các yếu tố thúc đẩy bản thân lựa chọn mua sản phẩm</p>\r\n<ul>\r\n<li>Với vai trò là người làm marketing:</li>\r\n</ul>\r\n<p>-         Tại sao cá nhân ra quyết định tiêu dùng</p>\r\n<p>-         Họ quyết định như thế nào?</p>\r\n<p>Nghiên cứu về hành vi mua giúp các nhà làm marketing ra <a class=\"clozetip\" title=\"Hành động\" href=\"#\">quyết định</a> mang tính chiến lược và có lợi thế cạnh tranh</p>\r\n<ul>\r\n<li>Các lý do khác</li>\r\n</ul>\r\n<p>-         Người tiêu dùng hành động đôi khi không theo các học thuyết kinh tế đã chỉ ra</p>\r\n<p>-         Qui mô thị trường người tiêu dùng ngày càng mở rộng</p>\r\n<p>-         Sở thích có thể thay đổi và đa dạng hóa</p>\r\n<p>-         Hành vi mua khó dự báo</p>\r\n<p>-         Người tiêu dùng có một số điểm chung nhưng không phải tất cả</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1321', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của </span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex? \\[A = \\sqrt[3]{{2.{{(2,98)}^3} - 3.{{(4,01)}^2} + 2}}\\] \" alt=\" \\ [A = \\ sqrt [3] {{2. {{(2.98)} ^ 3} - 3. {{(4,01)} ^ 2} + 2}} \\] \" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">1,89</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1322', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?B = {({\\rm{arctan x)}}^3}.\\sqrt {4,01} \" alt=\"B = {({\\ rm {arctan x)}} ^ 3}. \\ Sqrt {4,01} \" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">1.007</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1323', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của </span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?C = \\sin 29^\\circ .\\tan 46^\\circ \" alt=\"C = \\ sin 29 ^ \\ Circ. \\ Tan 46 ^ \\ Circ \" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">0,52</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1324', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của </span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?D = \\sqrt {{4^{2,99}} + \\ln (1,02)} \" alt=\"D = \\ sqrt {{4 ^ {2,99}} + \\ ln (1,02)} \" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">7.946</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1325', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của </span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?E = \\ln {\\rm{[}}{(0,02)^3} + {(0,99)^3}{\\rm{]}}\" alt=\"E = \\ ln {\\ rm {[}} {(0,02) ^ 3} + {(0,99) ^ 3} {\\ rm {]}}\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">-0,03</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1326', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?F = \\sqrt {5.{e^{0,02}} + {y^2}} \" alt=\"F = \\ sqrt {5. {e ^ {0,02}} + {y ^ 2}} \" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">3.036</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1327', null, null, null, '5', '1320', '286', '450', '1551712760', '1551724806', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Kết quả của</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?G = {(0,97)^{0,22}}\" alt=\"G = {(0,97) ^ {0,22}}\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là </span></span><a class=\"clozetip\" title=\"0,22 = 0,99 * 2/9 \" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">0,993</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1328', 'Trình bày có ví dụ minh họa một trong các nội dung của kế hoạch nghiên cứu của doanh nghiệp (Lựa chọn nguồn dữ liệu;Xác định công cụ nghiên cứu, Lựa chọn phương pháp nghiên cứu)', null, null, '5', '0', '282', '463', '1551713948', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1329', null, null, null, '5', '1328', '282', '463', '1551713948', null, null, null, '<p><strong>Nội dung cơ bản của nội dung kế hoạch nghiên cứu marketing</strong></p>\r\n<p>-        Lựa chọn và phát triển nguồn dữ liệu</p>\r\n<p>-        Lựa chọn phương pháp tiếp cận nghiên cứu</p>\r\n<p>-        Xác định công cụ nghiên cúu</p>\r\n<p>-        Xác lập dự án chọn mẫu nghiên cứu</p>\r\n<p>-        Lựa chọn phương pháp nghiên cứu</p>\r\n<p>-        Xác định cách thức nghiên cứu</p>\r\n<p>-        Xác định thời gian nghiên cứu</p>\r\n<p>-        Xác định chi phí nghiên cứu</p>\r\n<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Lựa chọn phát triển nguồn dữ liệu (Đã trình bày)</span></em></strong></li>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Lựa chọn <a class=\"cloze\" href=\"#mce_temp_url#\">phương pháp</a> tiếp cận dữ liệu nghiên cứu</span></em></strong></li>\r\n</ol>\r\n<p>-        Với dữ liệu thứ cấp, có thể tiếp cận từ nhiều nguồn có sẵn</p>\r\n<p>-        Với dữ liệu sơ cấp</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\"> Quan sát</a>: Cách thức thu thập dữ liệu sống đông qua quan sát trực tiếp các đối tượng</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Quan trắc</a>: Sử dụng các bảng hỏi chuẩn bị sẵn để phát cho các đối tượng trả lời (Đối tượng có tính đại diện tổng thể)</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Thử nghiệm:</a> Doanh nghiệp lựa chọn những nhóm người tương tự nhau đưa ra các kích thích khác nhau với từng nhóm kiểm soát các yếu tố không liên quan, kiểm tra phản ứng giữa các nhóm khách hàng để xác định sự khác biệt.</p>\r\n<ol>\r\n<li><strong><em>3.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác đinh <a class=\"cloze\" href=\"#mce_temp_url#\">công cụ</a> nghiên cứu</span></em></strong></li>\r\n</ol>\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\">Bảng câu hỏ</a>i:</p>\r\n<p>+ Khái niệm: là dang liệt kê các câu hỏi liên quan tới vấn đề cần nghiên cứu để người trả lời điền các thông tin vào. Là công cụ phổ biến và thông dụng trong nghiên cứu marketing</p>\r\n<p>+ Các dạng câu hỏi: Câu hỏi kết đóng; câu hỏi kết mở</p>\r\n<p>+Cấu trúc bảng câu hỏi: Cấu trúc cần hợp lý, có sự liên kết giữa các phần</p>\r\n<p>+Yêu cầu xây dựng bảng câu hỏi: Phải đảm bản thu được lượng thông tin, thẩm dịnh kĩ càng để góp phần vào mục tiêu nghiên cứu</p>\r\n<p>+Từ ngữ sử dụng trong bảng hỏi: Ngôn từ đơn giản, dễ hiểu, phổ biến, trực tiếp, chính xác</p>\r\n<p>-        Các <a class=\"cloze\" href=\"#mce_temp_url#\">thiết bị</a> hỗ trợ nghiên cứu: Thiết bị đếm, camera…</p>\r\n<ol>\r\n<li><strong><em>4.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác lập <a class=\"cloze\" href=\"#mce_temp_url#\">dự án </a>chọn mẫu nghiên cứu:</span></em></strong></li>\r\n</ol>\r\n<p>-        Khái niệm mẫu: Là <a class=\"cloze\" href=\"#mce_temp_url#\">tập hợp</a> con hoặc một số phần tử của một tổng thể, là mộ số lượng nhất định phần tử được lựa chọn từ một tổng thể theo một nguyên tắc xác định.</p>\r\n<p>-        Yêu cầu chọn mẫu:</p>\r\n<p>+ Độ chính xác</p>\r\n<p>+ Phù hợp với điều kiện nguồn lực</p>\r\n<p>+Phù hợp với thời gian</p>\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\">Phương pháp</a> chọn mẫu : Mẫu xác suất và mẫu phi xác suất</p>\r\n<p>-        Quy trình chọn mẫu :</p>\r\n<p>B1: Xác định<a class=\"cloze\" href=\"#mce_temp_url#\"> tổng thể</a> nghiên cứu và đơn vị chọn mẫu nghiên cứu</p>\r\n<p>B2: Lựa chọn khung lấy mẫu</p>\r\n<p>B3: Lựa chọn <a class=\"cloze\" href=\"#mce_temp_url#\">phương pháp</a> lấy mẫu đơn vị</p>\r\n<p>B4: Quyết định về <a class=\"cloze\" href=\"#mce_temp_url#\">quy mô </a>mẫu hay cỡ mẫu</p>\r\n<p>B5:<a class=\"cloze\" href=\"#mce_temp_url#\"> Danh sách </a>các thành viên thực tế của mẫu</p>\r\n<p>B6: <a class=\"cloze\" href=\"#mce_temp_url#\">Kiểm tra </a>quá trình chọn mẫu</p>\r\n<ol>\r\n<li><strong><em>5.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Lựa chọn phương pháp <a class=\"cloze\" href=\"#mce_temp_url#\">giao tiếp</a></span></em></strong></li>\r\n</ol>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">-        Thư</a></p>\r\n<p>-        Điện thoại</p>\r\n<p>-        Phỏng vấn trực tiếp</p>\r\n<p>-        Online</p>\r\n<ol>\r\n<li><strong><em>6.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">cách thức</a> nghiên cứu</span></em></strong></li>\r\n</ol>\r\n<p>-        Tự nghiên cứu</p>\r\n<p>-        Thuê nghiên cứu</p>\r\n<p>-        Phối hợp</p>\r\n<ol>\r\n<li><strong><em>7.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">thời gian</a> nghiên cứu</span></em></strong></li>\r\n</ol>\r\n<p>-        Ngắn hạn: Trong vòng 1 năm</p>\r\n<p>-        Trung hạn: 1-2 năm</p>\r\n<p>-        Dài hạn: từ 3 năm trở lên</p>\r\n<ol>\r\n<li><strong><em>8.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">chi phí </a>nghiên cứu</span></em></strong></li>\r\n</ol>\r\n<p>-        Chi phí thường xuyên</p>\r\n<p>-        Chi phí phát sinh</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1330', 'Vẽ sơ đồ quá trình quyết định mua của khách hàng-người tiêu dùng?', null, null, '5', '0', '294', '463', '1551714144', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1331', null, null, null, '5', '1330', '294', '463', '1551714144', null, null, null, '<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1332', 'Trình bày có ví dụ minh họa một trong các giai đoạn của quá trình quyết định mua của khách hàng.(nhu cầu được nhận thức, tìm kiếm thông tin)', null, null, '5', '0', '294', '463', '1551714678', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1333', null, null, null, '5', '1332', '294', '463', '1551714678', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">B1: Nhận thức <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a></span></em></strong></p>\r\n<p>+ Tác động <a class=\"cloze\" href=\"#mce_temp_url#\">bên trong</a>, bên ngoài==&gt;Người tiêu dùng nhận thức nhu cầu</p>\r\n<p>+Ứng xử: Tìm hiểu hoàn cảnh thúc đẩy nhận thức người tiêu dùng</p>\r\n<p>             -Vấn đề phát sinh là gì</p>\r\n<p>             -Điều gì làm cho nó xuất hiện</p>\r\n<p>             - Hướng khách hàng đến sản phẩm như thế nào?</p>\r\n<p>             ==&gt;Hoạt động marketing thích hợp</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B2: Tìm kiếm <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a></span></em></strong></p>\r\n<p>+ Nguồn <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a>: Gia đình, bạn bè..</p>\r\n<p>+ Nguồn <a class=\"cloze\" href=\"#mce_temp_url#\">thương mại</a>: Quảng cáo, người bán hàng</p>\r\n<p>+ Nguồn <a class=\"cloze\" href=\"#mce_temp_url#\">công chúng</a>: Công luận, các tổ chức bảo vệ người tiêu dùng,..</p>\r\n<p>+Nguồn <a class=\"cloze\" href=\"#mce_temp_url#\">thử nghiệm</a>: Kiểm định, sử dụng sản phẩm</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B3: Đánh giá <a class=\"cloze\" href=\"#mce_temp_url#\">thế vị</a></span></em></strong></p>\r\n<p>Quy trình đánh giá thế vị lựa chọn sản phẩm</p>\r\n<ol>\r\n<li>Phân tích <a class=\"cloze\" href=\"#mce_temp_url#\">động cơ </a>về sản phẩm</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xác định</a> ý niệm- tham số hành vi đối với sản phẩm</li>\r\n<li>Đánh giá <a class=\"cloze\" href=\"#mce_temp_url#\">niềm tin</a> đối với từng ý niệm/từng điểm nhãn hiệu theo thang điểm 10</li>\r\n<li>Phân tích <a class=\"cloze\" href=\"#mce_temp_url#\">độ quan trọng</a> và xác định hệ số<a class=\"cloze\" href=\"#mce_temp_url#\"> đương lượng</a> của từng tham số tới hành vi</li>\r\n<li>Xác định giá trị hành vi <a class=\"cloze\" href=\"#mce_temp_url#\">kì vọng</a> chọn nhãn hiệu sản phẩm</li>\r\n</ol>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B4: Quyết định mua</span></em></strong></p>\r\n<p>+ CÁc nhân tố tình huống không tham dự</p>\r\n<p>             -Thái độ của người khác</p>\r\n<p>             - Các nhân tố khác</p>\r\n<p>+Các  quyết định</p>\r\n<p>             -Quyết định nhãn hiệu</p>\r\n<p>             -Quyết định người bán</p>\r\n<p>             - Quyết định số lượng,thời gian mua</p>\r\n<p>+Ứng xử: Hạn chế những tác động tiêu cực đến quyết định mua của khách hàngèhoạt động marketing phù hợp</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B5: Cảm nhận sau mua:</span></em></strong></p>\r\n<p>+Hài lòng</p>\r\n<p>+Bất mãn</p>\r\n<p>+Ứng xử: Tạo cảm giác hài lòng ngay cả khia khách hàng không mua hàngèhoạt động marketing phù hợp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1334', 'Nêu khái niệm thị trường theo quan điểm của marketing?', null, null, '5', '0', '294', '463', '1551714910', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1335', null, null, null, '5', '1334', '294', '463', '1551714910', null, null, null, '<p>-        Là tập hợp<a class=\"cloze\" href=\"#mce_temp_url#\"> khách hàng </a>và những người cung ứng hiện thực, tiềm năng có <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> về sản phẩm mà doanh nghiệp có dự án kinh doanh trong mối quan hệ với các nhân tố môi trường marketing và tập người bán- đối thủ cạnh tranh của nó.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1336', 'Trình bày các yếu tố xác định thị trường của doanh nghiệp? Cho biết ý nghĩa của các yếu tố này với hoạt động marketing của doanh nghiệp.', null, null, '5', '0', '294', '463', '1551715040', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1337', null, null, null, '5', '1336', '294', '463', '1551715040', null, null, null, '<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Mua</a> cái gì? (Xác định đối tượng mua)</p>\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Tại sao</a> mua? ( Xác định mục đích mua)</p>\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Ai</a> mua? ( Xác định khách hàng?)</p>\r\n<p>-        Mua <a class=\"cloze\" href=\"#mce_temp_url#\">như thế nào</a>? (Xác định cách thức mua)</p>\r\n<p>-        Mua<a class=\"cloze\" href=\"#mce_temp_url#\"> bao nhiêu</a>? (Xác định số lượng mua)</p>\r\n<p>-        Mua <a class=\"cloze\" href=\"#mce_temp_url#\">ở đâu?</a> (Xác định địa điểm mua)</p>\r\n<p>==&gt;Các yếu tố xác định thị trường có ý nghĩa quan trọng trong việc <a class=\"cloze\" href=\"#mce_temp_url#\">hỗ trợ</a> hoạt động nghiên cứu marketing , hoạch đinh <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> của doanh nghiệp.</p>\r\n<p>Kết quả của các yếu tố xác định thị trường của doanh nghiệp giúp doanh nghiệp tính toán, dự báo tiềm năng thị trường từ đó đưa ra các quyết định marketing phù hợp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1338', 'Nêu các loại thị trường căn cứ vào các yếu tố đầu vào, đầu ra của doanh nghiệp?', null, null, '5', '0', '294', '463', '1551715125', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1339', null, null, null, '5', '1338', '294', '463', '1551715125', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Thị trường dưới</span></em></strong></p>\r\n<p>+ Với công ty <a class=\"cloze\" href=\"#mce_temp_url#\">sản xuất</a>: thị trường vật tư, nguyên vật liệu</p>\r\n<p>+Với công ty <a class=\"cloze\" href=\"#mce_temp_url#\">thương mại:</a> Các sản phẩm hoàn chỉnh</p>\r\n<p>          ==&gt;Người làm marketing cần tuân thủ 6 tiêu chuẩn lựa chọn nhà cung ứng</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">lao động:</a></span></em></strong> Các doanh nghiệp được tự do tuyển chọn lao động phù hợp với nhu cầu kinh doanh.</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">tiền tệ </a>và vốn:</span></em></strong> LÀ nơi các doanh nghiệp có thể trao đổi vốn hoạt động</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Thị trường trên:</span></em></strong> Nơi tiêu thụ sản phẩm</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1341', 'Vẽ mô hình các yếu tố ảnh hưởng đến hành vi mua của khách hàng người tiêu dùng?', null, null, '5', '0', '296', '467', '1551749652', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1342', null, null, null, '5', '1341', '296', '467', '1551749652', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1343', 'Phân tích ảnh hưởng của yếu tố văn hóa?', null, null, '5', '0', '296', '467', '1551749823', '1551942402', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1344', null, null, null, '5', '1343', '296', '467', '1551749823', '1551942402', null, null, '<ul>\r\n<li>Nền văn hóa:  Văn hóa là một hệ thống giá trị, niềm tin, truyền thống, các chuẩn mực hành vi được hình thành, phát triển, thừa kế qua các thế hệ. Văn hóa được hấp thụ ngay trong cuộc sống gia đình, sau đó là trường học và xã hội. Nền văn hóa là yếu tố quyết định cơ bản nhất trong những mong muốn và hành vi của NTD. Những người có nền văn hóa khác nhau sẽ có những hành vi mua hàng khác nhau</li>\r\n<li>Nhánh văn hóa: là một bộ phận cấu thành nhỏ hơn của nền văn hóa. Các nhánh văn hóa khác nhau có lối sống riêng, cách tiêu dùng riêng. Nhóm tôn giáo là một nhánh văn hóa. Các nhánh văn hóa khác nhau sẽ tạo thành những phân đoạn thị trường khác nhau.</li>\r\n<li>Giai tầng xã hội: Giai tầng xã hội là nhóm người tương đối ổn định trong xã hội được xắp xếp theo thứ bậc, đẳng cấp,..Các doanh nghiệp cần quan tâm đến hành vi tiêu dùng trong các giai tầng, đặc biệt là đối với các hàng hoá có tính dễ phô trương như quần áo, giày dép, xe cộ, nhà cửa, hoạt động vui chơi, giả trí... Hiểu rõ hành vi tiêu dùng của các giai tầng, doanh nghiệp sẽ có cơ sở để thực hiện phương châm \"Bán những thứ mà khách hàng cần\". Những người cùng chung trong một giai tầng thì thường có hành vi tiêu dùng giống nhau.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1345', 'Phân tích ảnh hưởng của nhóm yếu tố xã hội?', null, null, '5', '0', '296', '467', '1551749846', '1551942494', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1346', null, null, null, '5', '1345', '296', '467', '1551749846', '1551942494', null, null, '<ul>\r\n<li>Nhóm tham khảo: là nhóm ảnh hưởng trực tiếp hoặc gián tiếp đến thái độ, hành vi của con người</li>\r\n</ul>\r\n<p><em>+ Nhóm tham khảo đầu tiên</em> : gia đình, người thân, bạn bè, đồng nghiệp</p>\r\n<p>       +   <em>Nhóm tham khảo thứ hai</em> gồm các tổ chức hiệp hội như: Tổ chức tôn giáo, Hiệp hội ngành nghề, Công đoàn, Đoàn thể, Các câu lạc bộ.</p>\r\n<p>       +  <em>Nhóm ngưỡng mộ</em> là nhóm mà cá nhân có mong muốn gia nhập, trở thành viên (các ngôi sao...)</p>\r\n<p>       + <em>Nhóm tẩy chay</em> là nhóm mà cá nhân không chấp nhận hành vi của nó. Do vậy, các cá nhân không tiêu dùng như các thành viên của nhóm mà họ tẩy chay.</p>\r\n<ul>\r\n<li>Nhóm gia đình: có ảnh hưởng mạnh đến hành vi mua của cá nhân, đặc biệt trong điều kiện</li>\r\n<li>Nhóm vai trò và địa vị xã hội: Mỗi vai trò đều gắn với một địa vị. Người ta lựa chọn những sản phẩm thể hiện được vai trò và địa vị của mình trong xã hội.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1347', 'Tác động của khách hàng đối với Marketing?', null, null, '5', '0', '297', '467', '1551750042', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1348', null, null, null, '5', '1347', '297', '467', '1551750042', null, null, null, '<ul>\r\n<li>Marketing là thỏa mãn <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a>\r\n<ul>\r\n<li>Hiểu được HVM khách hàng rất quan trọng</li>\r\n<li>Tri thức về HVM khách hàng được sử dụng là căn cứ được phối hợp vào chương trình Marketing</li>\r\n</ul>\r\n</li>\r\n<li>Sử dụng ở các bước KHH marketing:\r\n<ul>\r\n<li>Xác định định hướng kinh doanh: Sứ mạng</li>\r\n<li>Xem xét môi trường- Phân tích thời cơ- đe dọa</li>\r\n<li>Xây dựng mục đích và mục tiêu</li>\r\n<li>Phân tích hố sơ sản phẩm hiện tại</li>\r\n<li>Phát triển 1 kế hoạch kinh doanh mới: Phân đoạn, lựa chọn, định vị, phát triển Mkt-mix</li>\r\n<li>Xây dựng kế hoạch và thực hiện</li>\r\n<li>Đo lường kết quả thực hiện</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1349', 'Tác động của Marketing đến hành vi mua khách hàng?', null, null, '5', '0', '297', '467', '1551750073', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1350', null, null, null, '5', '1349', '297', '467', '1551750073', null, null, null, '<ul>\r\n<li>Định hướng tiêu dùng( Thông qua sản phẩm)</li>\r\n<li>Xúc tiến thương mại tác động đến suy nghĩ và nhận thức và đánh giá của NTD</li>\r\n</ul>\r\n<p>-&gt; Thay đối hành vi mua, thay đổi cách đánh giá</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1351', 'Nêu khái niệm nhận thức?', null, null, '5', '0', '299', '467', '1551750397', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1352', null, null, null, '5', '1351', '299', '467', '1551750397', null, null, null, '<p>    Nhận thức cá nhân là tiến trình qua đó con người lựa chọntổ chức và thông đạt, những thông tin hội nhập để tạo thành một hình ảnh có ý nghĩa về thế giới. Cũng có thể hiểu nhận thức là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">kích thích </a>được <a class=\"cloze\" href=\"#mce_temp_url#\">lựa chọn</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">sắp xếp</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">giải thích</a></p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1353', 'Mô hình “ Quá trình nhận thức tổng quá”?', null, null, '5', '0', '299', '467', '1551750424', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1354', null, null, null, '5', '1353', '299', '467', '1551750424', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1355', 'Trình bày Các bước của quá trình nhận thức?', null, null, '5', '0', '299', '467', '1551750642', '1551750654', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1356', null, null, null, '5', '1355', '299', '467', '1551750642', '1551750654', null, null, '<ul>\r\n<li>Kích thích Có 2 nhóm kích thích:</li>\r\n</ul>\r\n<p>-         Kích thích từ môi trường bao gồm các yếu tố về kinh tế, chính trị, văn hóa, xã hội,…</p>\r\n<p>-         Kích thích Marketing từ doanh nghiệp bao gồm 4P Sản phẩm, giá, xúc tiến, phân phối</p>\r\n<p>-&gt;Con người chỉ nhận được một một số kích thích từ môi trường</p>\r\n<ul>\r\n<li>Cơ quan thụ cảm: Các kích thích tác động vào các giác quan thụ cảm sẽ tạo ra cảm giác.</li>\r\n</ul>\r\n<p>-         Cảm giác là những <a class=\"cloze\" href=\"#mce_temp_url#\">đáp ứng </a>trực tiếp của các cơ quan thần kinh thụ cảm ( mắt, mũi, tai, lưỡi, ngón tay) với các kích thích cơ bản như ánh sáng, màu sắc, âm thanh.</p>\r\n<p>-         Cảm giác là mộ quá trình nhận thức cảm tính nhằm phản ánh từng thuộc tính riêng lẻ của sự vật, hiện tượng khi nó trực tiếp tác động đến giác quan con người</p>\r\n<p>-         Vai trò của cảm giác</p>\r\n<p>+ Mường tượng quá khứ</p>\r\n<p>+ Mường tượng thích thú</p>\r\n<p>-         Quan trọng với loại hình tiêu thụ thỏa mãn nhu cầu tinh thần</p>\r\n<p>-         Dữ liệu từ thông tin cảm giác- xác định cách thức đáp ứng với sản phẩm của NTD</p>\r\n<p>-         Đặc điểm</p>\r\n<p>+ Cảm giác là một quá trình tâm lí : có nảy sinh, mở đầu, kết thúc</p>\r\n<p>+Cảm giác chỉ có được khi có sự tác động trực tiếp của các sự vật, hiện tượng</p>\r\n<p>+Cảm giác phản ánh yếu tố bên ngoài của sự vật hiện tượng</p>\r\n<p>+ Cảm giác chỉ phản ánh cái riêng lẻ về sự vật, hiện tượng</p>\r\n<p>-         Bao gồm</p>\r\n<ul>\r\n<li>Thị giác-nhìn : kích cỡ, màu sắc, kiểu dáng( mang đến 85% thông tin) Mắt có thể phân biệt được ½ triệu màu sắc</li>\r\n<li>Khứu giác-mùi: Hương thơm ( 10% thông tin)</li>\r\n<li>Thính giác-âm thanh: nhạc, giai điệu</li>\r\n<li>Xúc giác-bề mặt: thô, nhẵn, mềm, cứng</li>\r\n<li>Vị giác- khẩu vị: Mặn, ngọt</li>\r\n</ul>\r\n<p>+ Ngưỡng cảm giác của người tiêu dùng</p>\r\n<ul>\r\n<li>Ngưỡng tuyệt đối: là mức độ tối thiểu và tối đa của các kích thích để người ta có thể nhận dạng ở một kênh cảm giác</li>\r\n<li>Ngưỡng khác biệt: Là khả năng của hệ thống cảm giác nhận dạng những thay đổi/ khác biệt giữa hai kích thích cùng loại</li>\r\n</ul>\r\n<p>-         Chú ý: Định hướng các cơ quan thụ cảm vào một số kích thích. NTD chú ý tới một số ít hơn các kích thích nhận được</p>\r\n<p>Chia làm 2 loại: chú ý có chủ định và chú ý chú ý không chủ định</p>\r\n<ul>\r\n<li>Chú ý có chủ định: Laf sự tri giác có mục đích, đối tượng</li>\r\n</ul>\r\n<p>+ Chủ động</p>\r\n<p>+ Gây căng thẳng nếu kéo dài</p>\r\n<ul>\r\n<li>Chú ý không chủ định: tri giác không có mục đích</li>\r\n</ul>\r\n<p>+ Thụ động</p>\r\n<p>+ Không gây căng thẳng</p>\r\n<p>+ Là loại chú ý chủ yếu</p>\r\n<p>-         Giải thích: Là quá trình quy các ý nghĩa cho các kích thích đáng chú ý</p>\r\n<p>   + Đặc trưng của giải thích:</p>\r\n<ul>\r\n<li>Ý nghĩa được gán khác biệt giữa các cá nhân phụ thuộc nhận thức của từng cá nhân và cá nhân mong đợi</li>\r\n<li>Ý nghĩa phụ thuộc vào giản đồ: giản đồ là tập hợp có sắp xếp các tin tưởng và cảm giác của một cá nhân. Giản đồ quyết định các đánh giá vật thể và phụ thuộc vào văn hóa của cá nhân người tiêu dùng</li>\r\n<li>Ý nghĩa được gán khác nhau do mơ hồ kích thích diễn ra ( Khi một kích thích không được nhận thức rõ ràng hoặc có nhiều ý nghĩa khác nhau)=&gt; duwjaa vào mong muốn và lợi ích của bản thân để gán ý nghĩa</li>\r\n</ul>\r\n<p>ð Vận dụng: Tạo mơ hồ để thu hút tranh luận</p>\r\n<p>-         Đáp ứng: Là động thái của người tiêu dùng để đáp ứng lại với các kích thích</p>\r\n<p>Khi mua NTD phụ thuộc họ đáp ứng với các KT Marketing và cách họ giải thích các kích thích</p>\r\n<p>-         Nhận thức: Nhận thức là sự thừa nhận của NTD- bóp méo kích thích</p>\r\n<p>ð họ nhận thức theo ý mình ( biến kích thích theo ý mình)</p>\r\n<ul>\r\n<li> Quá trình nhận thức đi từ bộ não cá nhân nên nhiều lúc khách hàng nhận thúc không chính xác</li>\r\n<li>Nghiên cứu nhận thức là vấn đề con người bổ xung/ loại bỏ khỏi cảm giác khi gán ý nghĩa cho cảm giác</li>\r\n</ul>\r\n<p>+ Bổ sung: thêm vào các kích thích họ mong muốn</p>\r\n<p>+ Loại trừ: loại bỏ các kích thích không mong muốn</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1357', 'Nêu khái niệm giải thích?', null, null, '5', '0', '300', '467', '1551750866', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1358', null, null, null, '5', '1357', '300', '467', '1551750866', null, null, null, '<p>Là quá trình<a class=\"cloze\" href=\"#mce_temp_url#\"> quy </a>các <a class=\"cloze\" href=\"#mce_temp_url#\">ý nghĩa</a> cho các kích thích đáng chú ý</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1359', 'Các đặc trưng của giải thích?', null, null, '5', '0', '300', '467', '1551750922', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1360', null, null, null, '5', '1359', '300', '467', '1551750922', null, null, null, '<p>+ Đặc trưng của giải thích:</p>\r\n<ul>\r\n<li>Ý nghĩa được<a class=\"cloze\" href=\"#mce_temp_url#\"> gán khác biệt</a> giữa các cá nhân phụ thuộc nhận thức của từng cá nhân và cá nhân mong đợi</li>\r\n<li>Ý nghĩa phụ thuộc vào<a class=\"cloze\" href=\"#mce_temp_url#\"> giản đồ</a>: giản đồ là tập hợp có sắp xếp các tin tưởng và cảm giác của một cá nhân. Giản đồ quyết định các đánh giá vật thể và phụ thuộc vào văn hóa của cá nhân người tiêu dùng</li>\r\n<li>Ý nghĩa được gán khác nhau do <a class=\"cloze\" href=\"#mce_temp_url#\">mơ hồ kích thích diễn ra</a> ( Khi một kích thích không được nhận thức rõ ràng hoặc có nhiều ý nghĩa khác nhau)=&gt; duwjaa vào mong muốn và lợi ích của bản thân để gán ý nghĩa</li>\r\n</ul>\r\n<p>-&gt; Vận dụng: Tạo mơ hồ để thu hút tranh luận</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1361', 'Các yếu tố cấu thành “hệ thống cảm giác”? Phân tích các yếu tố cấu thành hệ thống cảm giác?', null, null, '5', '0', '301', '467', '1551751270', '1551751337', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1362', null, null, null, '5', '1361', '301', '467', '1551751270', '1551751337', null, null, '<p>-         Bao gồm</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thị giác</a>-nhìn : kích cỡ, màu sắc, kiểu dáng( mang đến 85% thông tin) Mắt có thể phân biệt được ½ triệu màu sắc</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Khứu giác</a>-mùi: Hương thơm ( 10% thông tin)</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thính giác</a>-âm thanh: nhạc, giai điệu</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xúc giác</a>-bề mặt: thô, nhẵn, mềm, cứng</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Vị giác</a>- khẩu vị: Mặn, ngọt</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1363', 'Nêu khái niệm động cơ?', null, null, '5', '0', '302', '467', '1551751387', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1364', null, null, null, '5', '1363', '302', '467', '1551751387', null, null, null, '<p>Nêu khái niệm động cơ:</p>\r\n<ul>\r\n<li>Là những <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> bên trong mỗi cá nhân, nó thúc đẩy họ hành động</li>\r\n<li>Là những quá trình vó vai trò là <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên nhân</a> của hành động</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1365', 'Nguyên nhân xuất hiện động cơ?', null, null, '5', '0', '302', '467', '1551751448', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1366', null, null, null, '5', '1365', '302', '467', '1551751448', null, null, null, '<p>Nguyên nhân xuất hiện động cơ:</p>\r\n<ul>\r\n<li>Động cơ xuất hiện do <a class=\"cloze\" href=\"#mce_temp_url#\">căng thẳng</a>: nhu cầu chưa được thỏa mãn hay nhu cầu được gọi mởèCố gắng giảm căng thẳng</li>\r\n<li>Người tiêu dùng sẽ nỗ lực có ý thức/không có ý thức<a class=\"cloze\" href=\"#mce_temp_url#\"> giảm bớt căng thẳng</a> qua:</li>\r\n</ul>\r\n<p>-         Các hành vi mà họ dự đoán sẽ đán ứng nhu cầu- giảm áp lực</p>\r\n<p>-         Mục tiêu lựa chọn và các mô hình hành động của họ là kết quả cỉa học tập và suy nghĩ cá nhân.</p>\r\n<p>-         Sản phẩm với lợi ích mong muốn-giảm bớt căng thẳng</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1367', 'Trình bày quá trình động cơ thúc đẩy?', null, null, '5', '0', '302', '467', '1551751497', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1368', null, null, null, '5', '1367', '302', '467', '1551751497', null, null, null, '<p><em><span style=\"text-decoration: underline;\">Quá trình động cơ thúc đẩy</span></em>: Nhận thức nhu cầu – Căng thẳng – Sức mạnh động cơ- Hướng động cơ- Hành vi- Mong muốn- Mục tiêu- Giảm bớt căng thẳng</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhận thức nhu cầu</a>: Bao gồm nhu cầu vật chất và nhu cầu về tinh thần</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Căng thẳng</a>: sự khách biệt giữa thực tế và ước muốn. Độ lớn của sự khác biệt xác định mức độ khẩn cấp cảm thấy phải giảm căng thẳng</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mong muốn</a>: Là biểu hiện của nhu cầu. Khi mà NTD đã nhận thức được nhu cầu của mình. Là cách NTD chọn để thỏa mãn nhu cầu, phụ thuộc vào kinh nghiệm, văn hóa và giáo dục</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sức mạnh động cơ</a>:Là mức độ động lực (mức độ khơi gợi) tác động vào người tiêu dùng. Chính là hoạt động các nhà làm marketing tác động đến người tiêu dùng: sản phẩm, giá, xúc tiến hay phân phối</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Hướng động cơ</a>: phương cách mà người tiêu dùng sử dụng để giảm căng thẳng đang bị thúc ép</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mục tiêu</a>: Đáp ứng nhu cầu là trạng thái ước muốn</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Giảm bớt căng thẳng</a></li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1369', 'Phân tích “ sức mạnh động cơ”?', null, null, '5', '0', '302', '467', '1551751589', '1551751603', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1370', null, null, null, '5', '1369', '302', '467', '1551751589', '1551751603', null, null, '<p><em><span style=\"text-decoration: underline;\">Sức mạnh động cơ:</span></em></p>\r\n<ul>\r\n<li>Khái niệm: Sức mạnh động cơ là <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ </a>động lực tác động vào NTD</li>\r\n<li>Đặc điểm</li>\r\n</ul>\r\n<p>+ Sức mạnh động cơ phụ thuộc: Nhu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">sinh học</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">học được</a></p>\r\n<p>+ Học thuyết sức mạnh động cơ: <a class=\"cloze\" href=\"#mce_temp_url#\">Động lực</a>( Như cầu sinh học) và <a class=\"cloze\" href=\"#mce_temp_url#\">mong đợi</a>( Khích lệ từ ngoài)</p>\r\n<p>+ Mức độ thu hút và lôi cuốn NTD của sức mạnh động cơ: Là mức ssooj quan trọng và quan tâm mà cá nhân nhận thức được gợi ra bởi một hoặc nhiều kích thích trong một tình huống nhất định</p>\r\n<p>          Các loại thu hút/ lôi cuốn: Thu hút trong mua sắm, Thu hút trong đáp ứng thông điệp, Thu hút trong cái tôi</p>\r\n<ul>\r\n<li>Vận dụng</li>\r\n</ul>\r\n<p>+ Phân đoạn theo mức độ thu hút. Mức độ thu hút thay đổi giữa các nền văn hóa</p>\r\n<p>+ Chiến lược gia tăng mức thu hút: Nâng cao động lực xem xét thông tin của NTD</p>\r\n<p>+ Khi thiết kế giao tiếp thuyết phục:</p>\r\n<p>   &gt; Lôi cuốn về nhu cầu tinh thần của NTD</p>\r\n<p>   &gt; Sử dụng các kích thích mới</p>\r\n<p>   &gt; Sử dụng các kích thích nổi bật</p>\r\n<p>   &gt; Sứ dụng nhân vật có danh tiếng</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1372', 'Nêu khái niệm  của “sức mạnh động cơ”?', null, null, '5', '0', '303', '467', '1551751662', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1373', null, null, null, '5', '1372', '303', '467', '1551751662', null, null, null, '<p>Khái niệm: Sức mạnh động cơ là <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ</a> động lực tác động vào NTD</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1374', 'Đặc điểm của “sức mạnh động cơ”?', null, null, '5', '0', '303', '467', '1551751731', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1375', null, null, null, '5', '1374', '303', '467', '1551751731', null, null, null, '<ul>\r\n<li>Đặc điểm</li>\r\n</ul>\r\n<p>+ Sức mạnh động cơ phụ thuộc: Nhu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">sinh học</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">học được</a></p>\r\n<p>+ Học thuyết sức mạnh động cơ: <a class=\"cloze\" href=\"#mce_temp_url#\">Động lực</a>( Như cầu sinh học) và <a class=\"cloze\" href=\"#mce_temp_url#\">mong đợi</a>( Khích lệ từ ngoài)</p>\r\n<p>+ Mức độ thu hút và lôi cuốn NTD của sức mạnh động cơ: Là mức ssooj quan trọng và quan tâm mà cá nhân nhận thức được gợi ra bởi một hoặc nhiều kích thích trong một tình huống nhất định</p>\r\n<p>          Các loại thu hút/ lôi cuốn: Thu hút trong mua sắm, Thu hút trong đáp ứng thông điệp, Thu hút trong cái tôi</p>\r\n<ul>\r\n<li>Vận dụng</li>\r\n</ul>\r\n<p>+ Phân đoạn theo mức độ thu hút. Mức độ thu hút thay đổi giữa các nền văn hóa</p>\r\n<p>+ Chiến lược gia tăng mức thu hút: Nâng cao động lực xem xét thông tin của NTD</p>\r\n<p>+ Khi thiết kế giao tiếp thuyết phục:</p>\r\n<p>   &gt; Lôi cuốn về nhu cầu tinh thần của NTD</p>\r\n<p>   &gt; Sử dụng các kích thích mới</p>\r\n<p>   &gt; Sử dụng các kích thích nổi bật</p>\r\n<p>   &gt; Sứ dụng nhân vật có danh tiếng</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1376', 'Nêu hướng động cơ ?', null, null, '5', '0', '304', '467', '1551751817', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1377', null, null, null, '5', '1376', '304', '467', '1551751817', null, null, null, '<p>Hướng động cơ:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Phương cách</a> mà người tiêu dùng <a class=\"cloze\" href=\"#mce_temp_url#\">sử dụng</a> để <a class=\"cloze\" href=\"#mce_temp_url#\">giảm </a>căng thẳng đang bị thúc ép.</li>\r\n</ul>\r\n<p>-&gt;Người làm marketing <a class=\"cloze\" href=\"#mce_temp_url#\">thuyết phục</a> sản phẩm của họ là cơ hội tốt nhất giúp người tiêu dùng đạt mục tiêu</p>\r\n<ul>\r\n<li>Phân biệt nhu cầu và mong muốn:</li>\r\n</ul>\r\n<p>-         Hình thái tiêu thụ dùng để thỏa mãn nhu cầu là mong muốn</p>\r\n<p>-         Mong muốn phụ thuộc: Lịch sử cá nhân, kinh nghiệm và môi trường văn hóa</p>\r\n<p>-         Gây ảnh hưởng về cách thỏa mãn nhu cầu người tiêu dùng</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1378', 'Các loại xung đột động cơ?', null, null, '5', '0', '304', '467', '1551751968', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1379', null, null, null, '5', '1378', '304', '467', '1551751968', null, null, null, '<ul>\r\n<li>Các loại xung đột động cơ”</li>\r\n</ul>\r\n<p>-         Xung đột <a class=\"cloze\" href=\"#mce_temp_url#\">tiến tới-né tránh</a></p>\r\n<p>+ sản phẩm muốn có hiệu quả tiêu cực</p>\r\n<p>+Cảm thấy tội lỗi-phô trương</p>\r\n<p>+Vận dụng trong hàng cao cấp, xa xỉ</p>\r\n<p>-         Xung đột <a class=\"cloze\" href=\"#mce_temp_url#\">né tránh-né tránh</a></p>\r\n<p>+ “Tránh vỏ dưa gặp vở dừa”</p>\r\n<p>+Gặp hai lựa chọn đề không mong muốn</p>\r\n<p>-&gt;Người làm marketing nhấn mạnh lợi ích của sản phẩm</p>\r\n<p>-         Xung đột <a class=\"cloze\" href=\"#mce_temp_url#\">tiến tới-tiến tới</a></p>\r\n<p>+ Hai giải pháp đều mong muốn “con rô cũng tiếc, con riếc cũng muốn”</p>\r\n<p>+Xung đột diễn ra khi phải lựa chọn một trong hai giải pháp</p>\r\n<p>+Bất hòa nhận thức: Giả thuyết con người có nhu cầu sắp đặt thứ tự và đảm bato nhất quá trong cuộc sống và trạng thái căng thẳng xuất hiện khi những tin tưởng một hành vi mâu thuẫn với nhau.</p>\r\n<p>+ Giảm bất hòa loại trừ căng thẳng</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1380', 'Khái niệm học tập?', null, null, '5', '0', '306', '467', '1551752280', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1381', null, null, null, '5', '1380', '306', '467', '1551752280', null, null, null, '<p>Sự <span style=\"color: #000000;\"><a class=\"cloze\" href=\"#mce_temp_url#\"><span style=\"color: #000000;\">thay đổi</span></a></span> không ngừng trong hành vi của cong người xảy ra như kết quả của <a class=\"cloze\" href=\"#mce_temp_url#\">kinh nghiệm</a></p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1382', 'Nguyên tắc học tập?', null, null, '5', '0', '306', '467', '1551752312', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1383', null, null, null, '5', '1382', '306', '467', '1551752312', null, null, null, '<ul>\r\n<li>Có thể học tập<a class=\"cloze\" href=\"#mce_temp_url#\"> trực tiếp</a> hoặc dán tiếp</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Ngẫu nhiên</a> hoặc có chủ đích</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1384', 'Kể tên các học thuyết học tập?', null, null, '5', '0', '306', '467', '1551752345', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1385', null, null, null, '5', '1384', '306', '467', '1551752345', null, null, null, '<p>Các học thuyết học tập</p>\r\n<ul>\r\n<li>Học thuyết học tập <a class=\"cloze\" href=\"#mce_temp_url#\">điều kiện</a></li>\r\n<li>Học thuyết học tập <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động</a></li>\r\n<li>Học thuyết học tập <a class=\"cloze\" href=\"#mce_temp_url#\">nhận thức</a></li>\r\n<li>Học thuyết học tập <a class=\"cloze\" href=\"#mce_temp_url#\">hành vi</a></li>\r\n<li>Học thuyết học tập <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội</a></li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1386', 'Thuyết học tập điều kiện cổ điển?', null, null, '5', '0', '306', '467', '1551752437', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1387', null, null, null, '5', '1386', '306', '467', '1551752437', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Thuyết học tập điều kiện</span></em>: Được phát triển từ lý thuyết hình thành nên phản xạ có điều kiện</p>\r\n<ul>\r\n<li>Một kích thích tạo ra một đáp ứng được gắn với một kích thích khác úc đầu tự nó không tạo ra được phản ứng</li>\r\n<li>Kích thích không điều kiện+ Kích thích có điều kiện= Đáp ứng có điều kiện</li>\r\n<li>Điều kiện của học thuyết:\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\"> Sự lặp lại</a>: 2 loại Điều kiện phải gắn vowsinhau rất nhiều lần, sức mạnh liên kết giữa các kích thích =&gt; <a class=\"cloze\" href=\"#mce_temp_url#\">Phản ứng</a>. Ví dụ quảng cáo xuất hiện vào mấy giờ, bao nhiêu lần trong 1 tháng</li>\r\n<li>Tổng quát hóa kích thích: Là khuynh hương của kích thích <a class=\"cloze\" href=\"#mce_temp_url#\">không điều kiện</a> trở thành kích thích <a class=\"cloze\" href=\"#mce_temp_url#\">có điều kiện</a>. Ví dụ: Nước uống có ga+ Nhãn hiệu coca-cola=&gt; chọn mua Coca-Cola</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>-&gt;Vận dụng: Lặp lại, tạo ra liên tưởng sản phẩm. củng cố tiêu thụ</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1388', 'Học thuyết học tập hoạt động?', null, null, '5', '0', '306', '467', '1551752534', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1389', null, null, null, '5', '1388', '306', '467', '1551752534', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Học thuyết học tập hoạt động</span></em></p>\r\n<ul>\r\n<li>B.F.Skinner: Cá nhân học thực hiện các hành vi tạo ra các kết quả <a class=\"cloze\" href=\"#mce_temp_url#\">tích cực</a> và né tránh các hành vi tạo ra kết quả <a class=\"cloze\" href=\"#mce_temp_url#\">tiêu cực</a>- Phức tạp hơn so với học tập điều kiện cổ điển</li>\r\n<li>Hành vi được học tập trong một thời gian theo quá trình định dạng hành vi: là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">củng cố </a>từng bước đến với hành vi mong đợi</li>\r\n<li>Cách thức học tập: Mối liên kết được củng cố( tích cực, tiêu cực), làm suy yếu mối liên kết( trừng phạt)</li>\r\n<li>Các chương trình củng cố: Khoảng- cố định, khoảng- biến đôi, hệ số- cố định, hệ số- biến đổi</li>\r\n<li>Cách thức học tập:\r\n<ul>\r\n<li>Củng cố tích cực: Phần thưởng củng cố <a class=\"cloze\" href=\"#mce_temp_url#\">phản ứng</a> và sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">học</a> được hành vi thích hợp</li>\r\n<li> Củng cố tiêu cực: Học thực hiện các hành vi<a class=\"cloze\" href=\"#mce_temp_url#\"> tránh</a> được điều không mong muốn/ không vui. Nếu các kết quả tích cực của hành vi không nhận được nữa- không có phần thưởng =&gt; Làm suy yếu mối liên kết =&gt; Trừng phạt( Theo sau phản ứng là những sự kiện không vui, học không lặp lại phản ứng), loại trừ</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>-&gt; Vận dụng: Phần thưởng đảm bảo được trả và cách trao phần thưởng đảm bảo được trả và cách trao phần thưởng đúng cách</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1390', 'Học thuyết học tập xã hội( quan sát)?', null, null, '5', '0', '306', '467', '1551752599', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1391', null, null, null, '5', '1390', '306', '467', '1551752599', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Học thuyết học tập xã hội( quan sát)</span></em></p>\r\n<ul>\r\n<li>Khi họ <a class=\"cloze\" href=\"#mce_temp_url#\">nhìn</a> hành động của người khác và <a class=\"cloze\" href=\"#mce_temp_url#\">nhận ra</a> những sự <a class=\"cloze\" href=\"#mce_temp_url#\">củng cố </a>mà họ nhận được từ hành vi của bản thân</li>\r\n<li>Là quy trình phức tạp:\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lưu giữ</a> quan sát trong trí nhớ khi họ trao đổi tri thức, sau đó dùng nó để <a class=\"cloze\" href=\"#mce_temp_url#\">định hướng</a> hành vi</li>\r\n<li>Là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">bắt trước</a>: Tái tạo lặp lại hành vi mong đợi</li>\r\n<li>Điều kiện để HT diễn ra: chú ý, ghi nhớ, thực tập, củng cố</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>-&gt;Vận dụng: Sử dụng người bảo trợ thì phụ thuộc vào mức độ hấp dẫn xã hội</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1392', 'Nêu khái niệm quá trình ghi nhớ?', null, null, '5', '0', '309', '467', '1551752682', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1393', null, null, null, '5', '1392', '309', '467', '1551752682', null, null, null, '<p>Khái niệm “quá trình ghi nhớ”:</p>\r\n<ul>\r\n<li>Là quá trình<a class=\"cloze\" href=\"#mce_temp_url#\"> thu nhận </a>thông tin và<a class=\"cloze\" href=\"#mce_temp_url#\"> lưu giữ </a>nó theo thời gian để nó <a class=\"cloze\" href=\"#mce_temp_url#\">có sẵn</a> khi cần</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1394', 'Vẽ mô hình quá trình ghi nhớ.?', null, null, '5', '0', '309', '467', '1551752719', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1395', null, null, null, '5', '1394', '309', '467', '1551752719', null, null, null, '<p><span style=\"font-size: 13.0pt; mso-bidi-font-size: 11.0pt; line-height: 130%; font-family: \'Times New Roman\',serif; mso-fareast-font-family: Calibri; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA; mso-no-proof: yes;\"><br /><!--[endif]--></span></p>\r\n<p> </p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1396', 'Khái niệm thái độ?', null, null, '5', '0', '307', '467', '1551752842', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1397', null, null, null, '5', '1396', '307', '467', '1551752842', null, null, null, '<p>Khái niệm thái độ:</p>\r\n<ul>\r\n<li>Thái độ là những <a class=\"cloze\" href=\"#mce_temp_url#\">đánh giá</a> có tính <a class=\"cloze\" href=\"#mce_temp_url#\">lâu dài</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">khái quát</a> về con người (bao gồm của bản thân), các vật thể hoặc các vấn đề.</li>\r\n<li>Thái độ cá nhân người tiêu dùng về một đối tượng là tổng hợp các quan điểm, long tin, thói quen, kinh nghiệm, mong muốn và phản ứng của cá nhân với đối tương đó.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1398', 'Đặc điểm của thái độ?', null, null, '5', '0', '307', '467', '1551752893', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1399', null, null, null, '5', '1398', '307', '467', '1551752893', null, null, null, '<p>Đặc điểm của thái độ:</p>\r\n<ul>\r\n<li>Thái độ bao giờ cũng hướng tới một <a class=\"cloze\" href=\"#mce_temp_url#\">đối tượng</a> cụ thể (một nhãn hiệu, cửa hàng..)</li>\r\n<li>Thái  độ cho phép cá nhân <a class=\"cloze\" href=\"#mce_temp_url#\">ứng xử</a> tương đối ổn định với những gì quen thuộc</li>\r\n<li>Thái độ có thể <a class=\"cloze\" href=\"#mce_temp_url#\">ổn định</a> hoặc thay đổi</li>\r\n<li>Ảnh hưởng:  cách thức người tiêu dùng cảm nhận về đối tượng của thái độ. Thái độ bị ảnh hưởng bởi nhận thức về lợi ích của đối tượng</li>\r\n<li>Lòng tin: Những suy nghĩ của người tiêu dùng về đối tượng, thái độ, độ tin cậy</li>\r\n<li>Hành vi: Người tiêu dùng đáp trả đối tượng thái độ (mua hay không)</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1400', 'Kể tên các mô hình nghiên cứu thái độ?', null, null, '5', '0', '307', '467', '1551752949', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1401', null, null, null, '5', '1400', '307', '467', '1551752949', null, null, null, '<ul>\r\n<li>Mô hình thái độ đa thuộc tính của Fishbein</li>\r\n<li>Mô hình điểm lý tưởng</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1402', 'Mô hình thái độ đa thuộc tính?', null, null, '5', '0', '307', '467', '1551753059', '1551753088', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1403', null, null, null, '5', '1402', '307', '467', '1551753059', '1551753088', null, null, '<p>Mô hình thái độ đa thuộc tính của Fishbein</p>\r\n<ul>\r\n<li>Martin Fishbein đưa ra công thức: A<sub>0</sub>=∑ B<sub>i</sub>E<sub>i</sub> với i=1-n</li>\r\n</ul>\r\n<p>trong đó</p>\r\n<p>   A<sub>0</sub>: Thái độ về đối tượng</p>\r\n<p>   B<sub>i</sub>: Mức độ của niềm tin rằng đối tượng có thuộc tính thứ i</p>\r\n<p>   E<sub>i</sub>: Đánh giá về thuộc tính thứ i</p>\r\n<p>   n: Số lượng các thuộc tính nổi trội</p>\r\n<ul>\r\n<li><sub>·          </sub>Mô hình giả định: Thái độ với một đối tượng dựa trên <a class=\"cloze\" href=\"#mce_temp_url#\">tổng thể</a> những <a class=\"cloze\" href=\"#mce_temp_url#\">niềm tin</a> về <a class=\"cloze\" href=\"#mce_temp_url#\">các thuộc tính</a> được đánh giá của đối tượng đó</li>\r\n<li><sub>·          </sub>Yêu cầu:\r\n<ul>\r\n<li><sub>Ø    </sub>Tìm hiểu những thuộc tính nổi trội theo quan điểm của TTMT</li>\r\n<li><sub>Ø    </sub>Chọn thang đo lường Bi( Bi- Thang đo 7 mức độ từ rất phù hợp đến rất không phù hợp) và Ei( Ei- Đánh giá từ rất tốt đến rất kém cũng 7 mức độ) thích hợp</li>\r\n<li><sub>Ø    </sub>Chọn mẫu điều tra, kết quả tính trung bình cho 2 thông số. Kết quả tổng đánh giá đối với các nhãn hiệu lựa chọn sẽ quyết định thái độ kì vong của KH</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1404', 'Mô hình điểm lí tưởng?', null, null, '5', '0', '307', '467', '1551753160', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1405', null, null, null, '5', '1404', '307', '467', '1551753160', null, null, null, '<p>Mô hình điểm lý tưởng:</p>\r\n<ul>\r\n<li><sub>·          </sub>Công thức tính Ao=∑Wi|Li-Xi| với i=1-n</li>\r\n<li><sub>·          </sub>Trong đó:\r\n<ul>\r\n<li><sub>Ø    </sub>Ao: Thái độ với nhãn hiệu</li>\r\n<li><sub>Ø    </sub>Wi: Tầm quan trọng của thuộc tính thức i</li>\r\n<li><sub>Ø    </sub>Li: Tác dụng lý tưởng của thuộc tính thứ i</li>\r\n<li><sub>Ø    </sub>Xi: Niềm tin về tác dụng thực tế của thuộc tính thứ I của nhãn hiệu- so sánh sản phẩm thực tế và sản phẩm lý tưởng</li>\r\n<li><sub>Ø    </sub>n: Số lượng các thuộc tính nổi trội</li>\r\n</ul>\r\n</li>\r\n<li><sub>·          </sub>NTD chỉ rõ niềm tin <a class=\"cloze\" href=\"#mce_temp_url#\">nhãn hiệu</a> được đặt vào <a class=\"cloze\" href=\"#mce_temp_url#\">vị trí</a> nào trên các thang đo lường thể hiện<a class=\"cloze\" href=\"#mce_temp_url#\"> cấp độ</a> của những thuộc tính nổi trội</li>\r\n<li><sub>·          </sub>NTD chỉ rõ một <a class=\"cloze\" href=\"#mce_temp_url#\">nhãn hiệu lý tưởng </a>sẽ ở vị trí nào trên thang đo lường các thuộc tính đó</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1406', 'Nêu các thành tố cấu thành” Cấu trúc thái độ”?', null, null, '5', '0', '308', '467', '1551753233', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1407', null, null, null, '5', '1406', '308', '467', '1551753233', null, null, null, '<p>Các thành tố cấu thành “cấu trúc thái độ”:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhận thức</a>: Niềm tin và kiến thức</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cảm nhận</a>: Cảm xúc đối với một sự vật, hiện tượng đó chính là một thành phần của thái độ</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Ý định hành động</a>: Thể hiện khuynh hướng hành đôngk đối với đối tượng</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1408', 'Trình bày các chiến thuật giao tiếp thay đổi thái độ?', null, null, '5', '0', '308', '467', '1551753330', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1409', null, null, null, '5', '1408', '308', '467', '1551753330', null, null, null, '<p>Các chiến thuật giao tiếp thay đổi thái độ</p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Người bảo trợ</a> như các nguồn phát giao tiếp: người lớn, trẻ em, người nổi tiếng</p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Cấu trúc</a> thông điệp: Câu truyện, tình huống</p>\r\n<ul>\r\n<li>Nhấn mạnh <a class=\"cloze\" href=\"#mce_temp_url#\">hậu quả</a> tiêu cực: mũ bảo hiểm</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">So sánh</a> trực tiếp đối thủ: omo và tide</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Trình bày</a> 1 câu truyện hứng thú: Nokia</li>\r\n</ul>\r\n<p>-         Phương tiện truyền tải thông tin: tivi, đài, quảng cáo in ấn, truyền hình, web</p>\r\n<p>-         Đặc điểm của người nhận tin mục tiêu?</p>\r\n<p>-         Thông điệp khác biệt hóa nhãn hiệu</p>\r\n<ul>\r\n<li>Gửi thông điệp:\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Hình ảnh</a>: Gửi thông điệp có tính kinh tế cao, khi gây ảnh hưởng tới tâm trạng người nhận có tính thẩm mĩ phải liên quan tới phần lời hoặc hình ảnh</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lời</a>: Hiệu quả hơn trong giao tiếp thông tin, phản ứng khác với kích thích nhìn thấy, khía cạnh hiện thực của sản phẩm</li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lặp lại</a>: Cần để học tập diễn ra, tránh quen thuộc</li>\r\n<li>Xây sựng cấu trức <a class=\"cloze\" href=\"#mce_temp_url#\">lập luận</a>: lập luận một hoặc hai phía: lập luận ủng hộ</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Loại thu hút</a> của thông điệp\r\n<ul>\r\n<li>Cảm tính- lí tính</li>\r\n<li>Hài hước- lo sợ</li>\r\n<li>Hình thái trình bày: Vở kịch, bài giảng</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1410', 'Khái niệm \"quan niệm bản thân\"?', null, null, '5', '0', '311', '467', '1551753672', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1411', null, null, null, '5', '1410', '311', '467', '1551753672', null, null, null, '<p>    Khái niệm: Là <a class=\"cloze\" href=\"#mce_temp_url#\">thái độ</a> của cá nhân đối với bản thân họ</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1412', 'Các thành tố cấu thành “quan niệm về bản thân” ?Trình bày có ví dụ minh họa các thành tố “ quan niệm bản thân”?', null, null, '5', '0', '311', '467', '1551753758', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1413', null, null, null, '5', '1412', '311', '467', '1551753758', null, null, null, '<p>-         Thành tố:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nội dụng</a>: Hấp dẫn vẻ mặt và năng lực trí óc</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tính chất</a>: Tích cực hay tiêu cực</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cường độ</a>: Mạnh-yếu</li>\r\n<li>Mức độ <a class=\"cloze\" href=\"#mce_temp_url#\">ổn định</a> theo thời gian: Ngắn ngủi- lâu dài</li>\r\n<li>Độ <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a>- <a class=\"cloze\" href=\"#mce_temp_url#\">tương thích</a> với thực tế bản thân đến mức độ nào</li>\r\n</ul>\r\n<p>-         Kết luận:</p>\r\n<ul>\r\n<li>NTD với vai trò cá nhân có thể ảnh hưởng trực tiếp hoặc gián tiếp đến công ty</li>\r\n<li>Thái độ là yếu tố có thể thay đổi được</li>\r\n<li>Các công ty phải hiểu được nhu cầu, nắm bắt được động cơ của NTD để có hoạt động Marketing hiệu quả</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1414', 'Khái niệm cá tính?', null, null, '5', '0', '312', '467', '1551753809', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1415', null, null, null, '5', '1414', '312', '467', '1551753809', null, null, null, '<p>Khái niệm cá tính</p>\r\n<ul>\r\n<li>Là các đặc điểm <a class=\"cloze\" href=\"#mce_temp_url#\">tâm lý riêng</a> có của cá nhân cáo ảnh hưởng<a class=\"cloze\" href=\"#mce_temp_url#\"> nhất quán</a> tới cách con người phản ứng với môi trường của người đó.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1416', 'Đặc điểm của cá tính?', null, null, '5', '0', '312', '467', '1551753857', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1417', null, null, null, '5', '1416', '312', '467', '1551753857', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Đặc điểm của cá tính</span></em>:</p>\r\n<ul>\r\n<li>Thể hiện<a class=\"cloze\" href=\"#mce_temp_url#\"> sự khác biệt</a> giữa các cá nhân, không có hai người giống nhau hoàn toàn về cá tính\r\n<ul>\r\n<li>Có thể giống nhau ở 1 đặc điểm cá tính</li>\r\n<li>Để phân đoạn hay nhóm gộp trên một hoặc hai dấu hiệu</li>\r\n</ul>\r\n</li>\r\n<li>Cá tính <a class=\"cloze\" href=\"#mce_temp_url#\">nhất quán</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">bền vững</a>\r\n<ul>\r\n<li>Không nên thay đổi cá tính mà thu hút các dấu hiệu liên quan tới khách hàng mục tiêu</li>\r\n<li>Mặc dù bền vững nhưng nhân tố tâm lý, xã hội, văn hóa và môi trường làm biến thiện những hành vi này</li>\r\n<li>Cá tính chỉ là một trong các nhân tố ảnh hưởng tới hành vi</li>\r\n</ul>\r\n</li>\r\n<li>Cá tính có thể <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổi</a>: Trong một số tình huống xảy ra thay đổi- sự kiện cuộc đời hoặc quá trình trưởng thành</li>\r\n<li>Cá tính có vai trò quan trọng xác định<a class=\"cloze\" href=\"#mce_temp_url#\"> hành vi</a></li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1418', 'Trình bày  học thuyết Freud?', null, null, '5', '0', '312', '467', '1551754041', '1551754100', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1419', null, null, null, '5', '1418', '312', '467', '1551754041', '1551754100', null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Học thuyết Freud</span></em></p>\r\n<p>Quan điểm của Freud: Cá tính con người gồm 3 hệ thống </p>\r\n<ul>\r\n<li>   Xung lực bản nằng id:</li>\r\n</ul>\r\n<p>     +  Thỏa mãn ngay để có cảm giác thích thú</p>\r\n<p>      + Ích kỉ và có thể không hợp lí</p>\r\n<p>      + Định hướng năng lực tâm lí đến hđ không cần biết đến hậu quả</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cái tôi</a>:</li>\r\n</ul>\r\n<p>      + Hệ thống trung gian giữa id và superego</p>\r\n<p>      + Trong tài theo nguyên tắc hiện thực thỏa mãn id theo phương</p>\r\n<p>         thức xã hội chấp nhận</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cái siêu tôi</a></li>\r\n</ul>\r\n<p>      + Đối tượng của id là ý thức của con người</p>\r\n<p>      + Nội hóa các quy định xã hội</p>\r\n<p>      + Chống lại sự t/n ích kỉ</p>\r\n<ul>\r\n<li>Đặc điểm của học thuyết của Freud\r\n<ul>\r\n<li>Cá tính được hình thành theo các gia đình khác nhau của thời thơ ấu: miệng, hậu môn, cơ quan sinh dục, GĐ âm I, dậy thì. VD: GD miệng không thỏa mãn đầy đủ- trưởng thành</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Không ý thức</a> được các động cơ của họ- Cần tìm hiểu các động lực bên trong thúc đẩy HvNTD</li>\r\n<li>Cho rằng vẻ <a class=\"cloze\" href=\"#mce_temp_url#\">bên ngoài</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">hình tượng</a>- phản ánh cá tính</li>\r\n<li>Cá tính của sản phẩm- sử dụng sản phẩm tăng lòng tự tin cá nhân…</li>\r\n<li>Cá tính của nhãn hiệu: X-men</li>\r\n<li>Cá tính gắn với màu sắc</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1422', 'Trình bày học thuyết Freud mới?', null, null, '5', '0', '312', '467', '1551754173', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1423', null, null, null, '5', '1422', '312', '467', '1551754173', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Học thuyết Freud mới</span></em>:</p>\r\n<ul>\r\n<li>Các quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội</a>- <a class=\"cloze\" href=\"#mce_temp_url#\">cơ sở</a> trong hình thành và phát triển cá tính</li>\r\n<li>Hảy Stack Sullivan: Con người không ngừng cố gắng xây dựng các mối quan hệ quan trọng và có ý nghĩa với người khác, họ nỗ lực giảm căng thẳng( lo lắng)</li>\r\n<li>Karen Homey: Tác động của quan hệ con cái- cha mẹ, nhất là <a class=\"cloze\" href=\"#mce_temp_url#\">khát vọng cá nhân</a> muốn chế ngự được cảm giác bất an</li>\r\n<li>Chia thành 3 nhóm:\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tuân thủ</a>: dịch chuyển hướng tới người khác</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xông xáo</a>: dịch chuyển đối lập với người khác</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Độc lập</a>: dịch chuyển khỏi ảnh hưởng của người khác</li>\r\n</ul>\r\n</li>\r\n<li>Đặc điểm: Đề cao tầm quan trọng của môi trường như là nhân tố để xác định cá tính</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1424', 'Trình bày học thuyết dấu hiệu?', null, null, '5', '0', '312', '467', '1551754227', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1425', null, null, null, '5', '1424', '312', '467', '1551754227', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">HỌc thuyết dấu hiệu</span></em>:<a class=\"cloze\" href=\"#mce_temp_url#\"> Định lượng</a> các dấu hiệu nhận dạng cá nhân như hướng nội hay hướng ngoại</p>\r\n<ul>\r\n<li>Các dấu hiệu có liên quan tới hành vi\r\n<ul>\r\n<li>Đổi mới, vật chất, ý thức về bản thân, nhu cầu nhận thức</li>\r\n<li>Thành tựu: Cố gắng cao nhất hoàn thành nhiệm vụ với thành tích cao nhất, làm việc tốt hơn người khác, thành công và có quyền lực được công nhận</li>\r\n<li>Trật tự: Công việc rõ ràng và được sắp xếp, lưu trữ, sắp đặt mọi thứ để nó vận hành thông suốt</li>\r\n<li>Các vấn đề với các học thuyết dấu hiệu</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>+ Dùng <a class=\"cloze\" href=\"#mce_temp_url#\">phân đoạn</a> thị trường</p>\r\n<p>+ Người tiêu dùng mua một số sản phẩm là sự mở rộng tính cách của họ- tạo ra đặc điểm nhãn hiệu</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1426', 'Các đặc điểm và sản phẩm có đặc trưng giới tính?', null, null, '5', '0', '313', '467', '1551754312', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1427', null, null, null, '5', '1426', '313', '467', '1551754312', null, null, null, '<p>-         Các đặc điểm và sản phẩm có đặc trưng giới tính</p>\r\n<ul>\r\n<li>Đặc điểm: Phụ thuộc vào <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa</a></li>\r\n<li>Dao díp( Đàn ông)/ giày cao gót( nữ giới)</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1428', 'Phân tích vai trò nữ giới?', null, null, '5', '0', '313', '467', '1551754348', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1429', null, null, null, '5', '1428', '313', '467', '1551754348', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Vai trò của nữ giới</span></em></p>\r\n<ul>\r\n<li>Chiếm 50% dân số, giữ vai trò đăng biệt trong mua hàng. họ mua hàng mềm, đóng gói</li>\r\n<li>Vai trò truyền thống: Gia đình, họ hàng</li>\r\n<li>Tiến hóa vai trò: Thay đổi nhanh chóng: cách nhìn của nam giới, bản thân nữ giới và sản phẩm họ mua</li>\r\n<li>Họ chọn hàng hóa rất kĩ càng và yêu cầu khắt khe với nhà sản xuất</li>\r\n<li>Họ rất chú ý đến thực dụng, lợi ích cụ thể của hàng hóa</li>\r\n<li>Chú ý đến tính tiện lợi và sáng tạo của hàng hóa trong đời sống</li>\r\n<li>Thường chú trọng đến cái tôi bản thân và lòng tự trọng cao</li>\r\n<li>Chú trọng đến tình cảm và hình thức bề ngoài của sản phẩm</li>\r\n<li>Họ có khả năng tác động nhanh rộng và mạnh đến người khác</li>\r\n<li>Phân đoạn thị trường nữ giới:\r\n<ul>\r\n<li>Người nội trợ</li>\r\n<li>Đi làm nhưng tự nguyện với lối sống vì gia đình</li>\r\n<li>Định hướng sự nghiệp- Đánh giá cao thành công nghề nghiệp và đạt được các chức vụ</li>\r\n<li>Đơn thuần là công việc- làm vì cần đến tiền</li>\r\n<li>Họ cần độc lập và tự do</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1430', 'Phân tích vai trò nam giới?', null, null, '5', '0', '313', '467', '1551754385', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1431', null, null, null, '5', '1430', '313', '467', '1551754385', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Vai trò của nam giới</span></em>:</p>\r\n<ul>\r\n<li>Truyền thống là cứng rắn, năng nổ, vạm vỡ</li>\r\n<li>Tiến hóa vai trò:\r\n<ul>\r\n<li>Trắc ẩn hơn và quan hệ bạn bè thân mất hơn với người khác</li>\r\n<li>Đưa hình ảnh người đàn ông ở góc độ xúc cảm</li>\r\n<li>Họ ngày càng thu hút hơn vào vai trò là người cha trong gia đình</li>\r\n</ul>\r\n</li>\r\n<li>Các sản phẩm thường mua là các sản phẩm công nghệ, phần cứng</li>\r\n<li>Thường chú trọng đến lí trí và về tính năng sản phẩm</li>\r\n<li>Ít cân nhắc về hình thức bề ngoài khi mua sản phẩm</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1432', 'Nêu khái niệm lối sống?', null, null, '5', '0', '314', '467', '1551754424', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1433', null, null, null, '5', '1432', '314', '467', '1551754424', null, null, null, '<p>Khái niệm:</p>\r\n<ul>\r\n<li>Là những <a class=\"cloze\" href=\"#mce_temp_url#\">giá trị</a> hay <a class=\"cloze\" href=\"#mce_temp_url#\">sở thích</a> được chia sẻ, đặc biệt là những cái được <a class=\"cloze\" href=\"#mce_temp_url#\">phản ánh</a> trong các mô hình tiêu thụ.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1434', 'Mô hình nghiên cứu lối sống AIOs', null, null, '5', '0', '314', '467', '1551754533', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1435', null, null, null, '5', '1434', '314', '467', '1551754533', null, null, null, '<p>-         Phân loại lối sống theo 3 nhóm biến số:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Hoạt động</a>: phân bổ thời gian như thế nào\r\n<ul>\r\n<li>Công việc</li>\r\n<li>Sở thích</li>\r\n<li>Sự kiện xã hội</li>\r\n<li>Giải trí</li>\r\n<li>Mua sắm</li>\r\n<li>Thể thao</li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mối quan tâm</a>: Cái j thu hút và quan trọng\r\n<ul>\r\n<li>Gia đình</li>\r\n<li>Nhà cửa</li>\r\n<li>Công việc</li>\r\n<li>Mốt</li>\r\n<li>Thực phẩm</li>\r\n<li>Phương tiện</li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quan điểm</a>: từ quan niệm và quan niệm về thế giới như thế nào?\r\n<ul>\r\n<li>Bản thân</li>\r\n<li>Vấn đề xã hội</li>\r\n<li>Chính trị</li>\r\n<li>Giáo dục</li>\r\n<li>Văn hóa</li>\r\n<li>Tương lai</li>\r\n</ul>\r\n</li>\r\n<li>Các thông số <a class=\"cloze\" href=\"#mce_temp_url#\">nhân khẩu học</a>\r\n<ul>\r\n<li>Tuổi tác</li>\r\n<li>Học vấn</li>\r\n<li>Thu nhập</li>\r\n<li>Nghề nghiệp</li>\r\n<li>Quy mô gia đình</li>\r\n<li>Gia đoạn</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>-&gt; Phân tích tâm lí xác định <a class=\"cloze\" href=\"#mce_temp_url#\">phân đoạn</a> có đa số người quan tâm đến sản phẩm</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1436', 'Mô hình nghiên cứu lỗi sống VALS2', null, null, '5', '0', '314', '467', '1551754631', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1437', null, null, null, '5', '1436', '314', '467', '1551754631', null, null, null, '<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Nguyên tắc</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Địa vị</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Nguồn lực sẵn có</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"200\">\r\n<p> </p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Hiện thực</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Đáp ứng</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Giành được</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Kinh nghiệm</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Tin tưởng</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Nỗ lực</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Chế tạo</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"200\">\r\n<p> </p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p>Đấu tranh</p>\r\n</td>\r\n<td valign=\"top\" width=\"200\">\r\n<p> </p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>-         Người hiện thực: NTD thành công với rất nhiều nguồn lực. Người giàu mua săm xa hoa</p>\r\n<p>-         Người đáp ứng:</p>\r\n<ul>\r\n<li>Thỏa mãn và thoải mái, có suy tính</li>\r\n<li>Thực hiện mang tính thực tế và giá trị</li>\r\n</ul>\r\n<p>-         Người giành được: Định hướng nghề nghiệp, thích dự báo đối với rủi ro</p>\r\n<p>-         Người kinh nghiêm: Bốc đồng, trẻ, thích thú với các tình huống rủi ro cao và khác thường</p>\r\n<p>-         Người tin tưởng: Rất nguyên tắc và thích các nhãn hiệu đã được khẳng định</p>\r\n<p>-         Người nỗ lực</p>\r\n<ul>\r\n<li>Giống người thành đạt nhưng có nguồn lực ít hơn</li>\r\n<li>Rất quan tâm đến sự công nhận của người khác</li>\r\n</ul>\r\n<p>-         Người chế tạo</p>\r\n<ul>\r\n<li>Định hướng hành động tập trung vào tự đáp ứng- tự làm</li>\r\n</ul>\r\n<p>-         Người đấu tranh: Thấp nhất trong xã hội, muốn đáp ứng nhu cầu trước mắt</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1438', 'Vận dụng các mô hình vào Marketing?', null, null, '5', '0', '314', '467', '1551754730', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1439', null, null, null, '5', '1438', '314', '467', '1551754730', null, null, null, '<p>-         Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường mục tiêu</a>: Mô tả kỹ hơn về phân đoạn bên cạnh các đặc điểm nhân khẩu học</p>\r\n<p>-         Hình thành <a class=\"cloze\" href=\"#mce_temp_url#\">cách nhìn mới</a> về thị trường: Xuất phát từ NTD chứ không phải từ suy nghĩ người làm MKt</p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Định vị</a> sản phẩm: Tập chung vào đặc điểm sản phẩm phù hợp với lối sống cá nhân</p>\r\n<p>-         Thể hiện tốt hơn các đặc điểm của sản phẩm</p>\r\n<ul>\r\n<li>Đầu vào của sáng tạo- Phải đưa thông tin về sản phẩm trong giao tiếp</li>\r\n<li>Tỏ ra thông tin có sức thuyết phục hơn- khi biết hình tượng tâm lý của khách hàng</li>\r\n</ul>\r\n<p>-         Phát triển chiến lược bao quát</p>\r\n<ul>\r\n<li>Đánh giá <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ phù hợp</a> với lối sống NTd nhằm:\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhận dạng</a> các cơ hội sản phẩm mới, <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> chiến lược phương tiện</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tạo ra </a>môi trường thích hợp và hài hòa với các mô hình tiêu thụ của Ntd</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>-         MKT các vấn đề xã hội và chính trị</p>\r\n<ul>\r\n<li>Phân đoạn theo lối sống là công cụ quan trọng trong các chiến dịch về xã hội và chính trị\\</li>\r\n<li>Tìm kiếm điểm chung giữa các Kh có các HV tiêu cực</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1440', 'Khái niệm văn hóa?', null, null, '5', '0', '317', '467', '1551755073', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1441', null, null, null, '5', '1440', '317', '467', '1551755073', null, null, null, '<ul>\r\n<li>Là các phương thức khác nhau của <a class=\"cloze\" href=\"#mce_temp_url#\">lối sống</a> và những<a class=\"cloze\" href=\"#mce_temp_url#\"> mô hình tổng thể</a> của một nhóm người về lối sống của hộ. Các cá nhân học tập những mô hình và phương thức trên qua cộng đồng và được chia sẻ giữa các thành viên trong cùng xã hội.</li>\r\n<li>Được <a class=\"cloze\" href=\"#mce_temp_url#\">đúc kết</a> trong cộng đồng và được<a class=\"cloze\" href=\"#mce_temp_url#\"> truyền</a> từ đời này sang đời khác</li>\r\n<li>Con người <a class=\"cloze\" href=\"#mce_temp_url#\">hấp thu</a> trong quá trình lớn lên và mặc dù sau này sống trong một nền văn hóa khác vẫn chịu ảnh hưởng của nền văn hóa sinh trưởng</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1442', 'Nhánh văn hóa?', null, null, '5', '0', '317', '467', '1551755106', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1443', null, null, null, '5', '1442', '317', '467', '1551755106', null, null, null, '<ul>\r\n<li>Là <a class=\"cloze\" href=\"#mce_temp_url#\">nhóm văn hóa riêng</a> biệt tồn tại như một <a class=\"cloze\" href=\"#mce_temp_url#\">phân đoạn thị trường</a> rõ rangf trong một xã hội rộng lớn  và phức tạp hơn.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1444', 'Khái niệm nhóm tham khảo?', null, null, '5', '0', '317', '467', '1551755163', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1445', null, null, null, '5', '1444', '317', '467', '1551755163', null, null, null, '<p>Nhóm tham khảo:</p>\r\n<ul>\r\n<li>Là các cá nhân (nhóm) có thực( tưởng tượng) có <a class=\"cloze\" href=\"#mce_temp_url#\">ảnh hưởng lớn</a> đến những<a class=\"cloze\" href=\"#mce_temp_url#\"> đánh giá</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">nguyện vọng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">hành vi</a> của từng cá nhân.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1446', 'Khái niệm tầng lớp xã hội?', null, null, '5', '0', '317', '467', '1551755215', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1447', null, null, null, '5', '1446', '317', '467', '1551755215', null, null, null, '<p>Tầng lớp xã hội:</p>\r\n<ul>\r\n<li>Là các <a class=\"cloze\" href=\"#mce_temp_url#\">lớp người</a> khác nha do kết quả <a class=\"cloze\" href=\"#mce_temp_url#\">phân chia</a> tương đối đồng nhất và ổn định trong xã hội, được <a class=\"cloze\" href=\"#mce_temp_url#\">sắp xếp</a>  theo một thứ bậc, những thành viên trong từng thứ bậc cùng <a class=\"cloze\" href=\"#mce_temp_url#\">chia sẻ </a>những <a class=\"cloze\" href=\"#mce_temp_url#\">giá trị lợi ích</a> và cách <a class=\"cloze\" href=\"#mce_temp_url#\">ứng xử</a> giống nhau.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1448', 'Khái niệm gia đình?', null, null, '5', '0', '317', '467', '1551755257', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1449', null, null, null, '5', '1448', '317', '467', '1551755257', null, null, null, '<p>Gia đình:</p>\r\n<ul>\r\n<li>Bao gồm một nhóm các thành viên có <a class=\"cloze\" href=\"#mce_temp_url#\">mối liên hệ</a> nhất định được xã hội và pháp luật thừa nhạn, cùng chung sống một cách <a class=\"cloze\" href=\"#mce_temp_url#\">lâu dài và ổn định</a>; có hoạt động<a class=\"cloze\" href=\"#mce_temp_url#\"> kinh tế</a> chung bằng cách khai thác tài sản sở hữu chung.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1450', 'Đặc trưng của văn hóa?', null, null, '5', '0', '318', '467', '1551755318', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1451', null, null, null, '5', '1450', '318', '467', '1551755318', null, null, null, '<p>-         Đặc trưng của văn hóa:</p>\r\n<ul>\r\n<li>Văn hóa được sáng tạo ra</li>\r\n<li>Văn hóa là bàn tay vô hình ảnh hưởng tự động đến hành vi của con người trong xã hội</li>\r\n<li>Văn hóa gắn với môi trường xã hội nhất định</li>\r\n<li>Văn hóa là quá trình học hỏi, lĩnh hội của các thành viên trong xã hội</li>\r\n<li>Van hóa được chia sẻ</li>\r\n<li>Văn hóa có tính lâu bền và thích nghi</li>\r\n<li>Văn hóa có tính giao hưu và tiếp diễn</li>\r\n</ul>\r\n<p>-&gt; Ảnh hưởng đến hành vi mua của NTD</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1452', 'Các ảnh hưởng của văn hóa đến hành vi NTD?', null, null, '5', '0', '318', '467', '1551755402', '1551755416', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1453', null, null, null, '5', '1452', '318', '467', '1551755402', '1551755416', null, null, '<p>-&gt;Văn hóa ảnh hưởng tới:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Ngôn ngữ</a> bằng lời và ngôn ngữ cử chỉ: Là bộ phận quan trọng nhất của văn hóa. Sự khác biệt về ngôn ngữ được biểu hiện trong giao tiếp, đàm phán kinh doanh.</li>\r\n</ul>\r\n<p> VD Bánh kitkat được yêu thích tại nhật bản không chỉ bởi vì hương vị mà trong tiếng nhật kitket là may mắn</p>\r\n<ul>\r\n<li>Ý nghĩa của các <a class=\"cloze\" href=\"#mce_temp_url#\">biểu tượng</a>: Hình số, con vật, màu sắc,.. mang ý nghĩa, quan điểm, tình cảm, thái độ riêng gắn với một nền văn hóa nhất định , gắn với phong tục tập quán giá trị niềm tin… thường tồn tại lâu dài trong lich sử ảnh hưởng đến hành vi người tiêu dùng.</li>\r\n</ul>\r\n<p>VD: Người Ấn Độ người dân thờ thần bò =&gt; không ăn thịt bò</p>\r\n<ul>\r\n<li>Các <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống</a> phong tục, tập quán là các quy định về cách ứng xử của các thành viên của một xã hội gắn với nền văn hóa nhất định, nó thường được hình thành từ rất lâu trong quá khứ và được truyền lại qua các thế hệ</li>\r\n</ul>\r\n<p>VD ngày tết truyền thống của việt nam</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Đồ ăn thức uống</a></li>\r\n<li>Điều <a class=\"cloze\" href=\"#mce_temp_url#\">cấm kị</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">cấm đoán</a></li>\r\n<li>Các <a class=\"cloze\" href=\"#mce_temp_url#\">cách ứng xử </a>trong các hoàn cảnh khác nhau</li>\r\n<li>Các <a class=\"cloze\" href=\"#mce_temp_url#\">giá trị</a>: Là những niềm tin được nâng đỡ, xác định các vấn đề con người ước ao:</li>\r\n</ul>\r\n<p>      + Mỗi dân tộc có 1 hệ thống giá trị riêng</p>\r\n<p>      + Các giá trị vawn hóa ảnh hưởng đến hành vi NTD</p>\r\n<ul>\r\n<li>Theo Rokeach: Một giá trị là một niềm tin cơ bản liên quan đến cách thức họ hoạt động để đạt mục tiêu. có 2 loại giá trị cơ bản:</li>\r\n</ul>\r\n<p>     + Giá trị trung gian</p>\r\n<p>     + Giá trị cuối cùng</p>\r\n<ul>\r\n<li>Chuẩn mực văn hóa: Biên giới mà văn hóa đặt ra cho các hành vi, nó là quy tắc hoặc chỉ dẫn hoặc ngăn cản hành vi nhất định</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1454', 'Nêu cơ sở phân loại văn hóa?', null, null, '5', '0', '319', '467', '1551755434', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1455', null, null, null, '5', '1454', '319', '467', '1551755434', null, null, null, '<ul>\r\n<li>Ngắn hạn hay dài hạn</li>\r\n<li>Nam tính hay nữ tính</li>\r\n<li>Nhóm hay cá nhân</li>\r\n<li>Văn cảnh cao hay thấp</li>\r\n<li>Quyền lực cao hay thấp</li>\r\n<li>Né tránh rủi ro cao hay thấp</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1456', 'Phân tích nội dung nhánh văn hóa Dân Tộc?', null, null, '5', '0', '320', '467', '1551755567', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1457', null, null, null, '5', '1456', '320', '467', '1551755567', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Nhánh văn hóa- Dân Tộc</span></em></p>\r\n<ul>\r\n<li>Các <a class=\"cloze\" href=\"#mce_temp_url#\">quốc gia</a> khác nhau tạo thành <a class=\"cloze\" href=\"#mce_temp_url#\">nhánh văn hóa</a> khác nhau</li>\r\n<li>Việt nam &amp; Trung quốc\r\n<ul>\r\n<li>Người cao tuổi có quyền lực, con cái nghe lời</li>\r\n<li>Trách nhiệm con cái với cha mẹ, gắn bó với họ hàng</li>\r\n<li>Mỗi gia đình nên có 1 con trai</li>\r\n<li>Thích xem vô tuyến</li>\r\n<li>Coi mua sắm là nhàn nhã và thực hiện quan hệ xã hội</li>\r\n<li>Mua sắm theo gia đình nhiều hơn</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1458', 'Phân tích nội dung nhánh văn hóa lứa tuổi?', null, null, '5', '0', '320', '467', '1551755663', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1459', null, null, null, '5', '1458', '320', '467', '1551755663', null, null, null, '<ul>\r\n<li>Người trẻ tuổi 19-25\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mạnh dạn</a>, phóng khoánh, tiêu dùng mạnh</li>\r\n<li>Trình độ giáo dục cao hơn nên <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu cao hơn</a></li>\r\n<li>Muốn có được mọi thứ và được <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập</a></li>\r\n<li>Muốn được <a class=\"cloze\" href=\"#mce_temp_url#\">thử thách </a>nhưng ở mức độ không quá cao</li>\r\n<li>Muốn có những cái mới nhưng không quá mạo hiểm</li>\r\n<li>Họ tiêu dùng: Quần áo, giày dép, băng đĩa nhạc, phương tiện, đồ thể thao, tặng phẩm, sách truyện</li>\r\n<li>Ảnh hưởng quyết định mua sắm của cha mẹ</li>\r\n</ul>\r\n</li>\r\n<li>Người già- cao tuổi\r\n<ul>\r\n<li>Vai trò: Nhóm quan trọng nhưng đôi khi bị nhà làm marketing bỏ qua</li>\r\n<li>Đặc điểm: Họ thường ít muốn thử các<a class=\"cloze\" href=\"#mce_temp_url#\"> vật phẩm mới</a>, Cho rằng bản thân bị hạn chế về sức khỏe , tích cực rèn luyện thể lực, nhiều thời gian và tự do hơn</li>\r\n<li>Cửa hàng bán: Xe lăn, thiết bị cảm biến đo nồng độ Cholestron Robot phục vụ người cao tuổi, cầu thang thoải cho người già và lối đi cho xe lắn</li>\r\n<li>Mua hàng theo thói quen, Khó hình thành nhu cầu mới, cần sự tôn trọng và coi trọng sự giúp đỡ</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1460', 'Nêu và phân tích giai tầng thượng lưu?', null, null, '5', '0', '321', '467', '1551755755', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1461', null, null, null, '5', '1460', '321', '467', '1551755755', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Giai tầng thượng lưu</span></em>:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lớp trên</a>: Các chính trị gia: Gia đình xuất chúng cổ xưa, giai tầng nhà nước, nghề cao quý,.. Là NTD bảo thủ, mua ít sản phẩm, sử dụng nhiều dịch vụ</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lớp dưới</a>: Người mới giàu. là chuyên gia giỏi, người sáng lập ngành kinh doanh mới, bác sĩ, luật sư, Thu nhập rất cao. HVM khiến nhiều người khác để ý, mua đồ xa xỉ, làm từ thiện, nhóm tham khảo tốt cho giai tầng dưới họ</li>\r\n</ul>\r\n<p>-&gt; Họ am hiểu mua, khoảng lựa chọn rộng trong mua</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1462', 'Nêu và phân tích giai tầng trung lưu?', null, null, '5', '0', '321', '467', '1551755805', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1463', null, null, null, '5', '1462', '321', '467', '1551755805', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Giai tầng trung lưu</span></em>:</p>\r\n<ul>\r\n<li>Lớp trên: Bác sĩ, kỹ sư, doanh nhân có trình độ giáo dục tốt nghieejp trung học và cao hơn. Động cơ phấn đấu cho sự thành công, đấu tranh cho sự hấp dẫn trí tuệ. Mua nhiều nhất thể hiện sự thành công, ganh đua với các giai tầng phía trên</li>\r\n<li>Trung lưu: Là người đứng đầu trình độ trung bình, người lao động, động cơ đấu tranh cho cuộc sống và thu nhập. Quan tâm gia đình và học tập của con cái. Mua sắm đề cao sự chấp nhận của xã hội. Quan tâm đến nhà cửa</li>\r\n<li>Lớp dưới: Lớn nhất về quy mô, Người lao động nghèo, định hướng gia đình, tôn trọng truyền thông, giới hạn tầm nhìn, HVM chủ yếu là an toàn, tiết kiệm, sự trợ giúp</li>\r\n</ul>\r\n<p>-&gt; Họ<a class=\"cloze\" href=\"#mce_temp_url#\"> tỉnh táo</a> trong mua sắm, không quá quan tâm đến <a class=\"cloze\" href=\"#mce_temp_url#\">triết giá</a></p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1464', 'Nêu và phân tích giai tầng hạ lưu?', null, null, '5', '0', '321', '467', '1551755857', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1465', null, null, null, '5', '1464', '321', '467', '1551755857', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Giai tầng hạ lưu</span></em></p>\r\n<ul>\r\n<li>Lớp trên: Người l<a class=\"cloze\" href=\"#mce_temp_url#\">ao động nghèo</a>, có việc là không thường xuyên</li>\r\n<li>Lớp dưới: Sống dưới<a class=\"cloze\" href=\"#mce_temp_url#\"> mức nghèo</a>, hoạt động phi pháp, sống do phúc lợi xã hội</li>\r\n</ul>\r\n<p>-&gt; Hành vi mua: Chủ yếu hàng thực phẩm, tin tín dụng, rủi ro mua sắm</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1466', 'Phân loại sự ảnh hưởng của nhóm tham khảo?', null, null, '5', '0', '322', '467', '1551755914', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1467', null, null, null, '5', '1466', '322', '467', '1551755914', null, null, null, '<p>Phân loại sự ảnh hưởng của nhóm tham khảo:</p>\r\n<ol>\r\n<li>Theo<a class=\"cloze\" href=\"#mce_temp_url#\"> tính chất</a> của<a class=\"cloze\" href=\"#mce_temp_url#\"> tổ chức</a></li>\r\n</ol>\r\n<p>-         Nhóm chính thức</p>\r\n<p>-         Nhóm không chính thức</p>\r\n<ol>\r\n<li>Phân loại theo<a class=\"cloze\" href=\"#mce_temp_url#\"> thành viên</a>:</li>\r\n</ol>\r\n<ul>\r\n<li>Nhóm tích cực/Nhóm tiêu cưc</li>\r\n<li>Nhóm tham khảo nhận dạng:</li>\r\n</ul>\r\n<p>-Tương tự</p>\r\n<p>-Dùng người bình thường</p>\r\n<ul>\r\n<li>Nhóm tham khảo khát khao</li>\r\n</ul>\r\n<p>-         Gồm những hình mẫu được lý tưởng hóa nhu các doanh nhân thành đạt, vận động viên, ca sĩ…</p>\r\n<p>-         Hướng dẫn qua sản phẩm được những người thuộc hình mẫu lý thưởng sử dụngètác động đến khẩu vị và sở thích</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1468', 'Phân tích hình thức tác động của nhóm tham khảo?', null, null, '5', '0', '322', '467', '1551755952', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1469', null, null, null, '5', '1468', '322', '467', '1551755952', null, null, null, '<p>Hình thức tác động của nhóm tham khảo:</p>\r\n<p>* <a class=\"cloze\" href=\"#mce_temp_url#\">Thông tin</a>: về sản phẩm, nhãn hiệu từ các hiệp hội, bạn bè, người thân…</p>\r\n<p>* <a class=\"cloze\" href=\"#mce_temp_url#\">Thực tế</a>: Chọn mua ảnh hưởng, sở thích của các đồng nghiệp, gia đình…</p>\r\n<p>* Biểu lộ <a class=\"cloze\" href=\"#mce_temp_url#\">giá tr</a>ị: Nâng cao hình ảnh bản thân – giống người sử dụng sản phẩm trong quảng cáo…</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1470', 'Nêu các “ Quan điểm ra quyết định mua của người tiêu dùng”?', null, null, '5', '0', '324', '467', '1551756189', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1471', null, null, null, '5', '1470', '324', '467', '1551756189', null, null, null, '<p>Quan điểm ra quyết định mua của người tiêu dùng:</p>\r\n<ul>\r\n<li>Theo kiểu <a class=\"cloze\" href=\"#mce_temp_url#\">lý tính</a>: Nhận dạng được sản phẩm tốt nhất</li>\r\n<li>Theo kiểu<a class=\"cloze\" href=\"#mce_temp_url#\"> thụ động</a>: hoàn toàn tuân thủ lợi ích bản thân</li>\r\n<li>Theo kiểu <a class=\"cloze\" href=\"#mce_temp_url#\">nhận thức</a>: chủ động tìm kiếm thông tin vầ sản phẩm, cửa hàng</li>\r\n<li>Theo<a class=\"cloze\" href=\"#mce_temp_url#\"> cảm tính</a>: Dựa vào cảm giác</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1472', 'Các giai đoạn của mô hình đơn giản ra quyết định hành vi mua khách hàng?', null, null, '5', '0', '324', '467', '1551756229', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1473', null, null, null, '5', '1472', '324', '467', '1551756229', null, null, null, '<p>Các giai đoạn của mô hình đơn giản</p>\r\n<ul>\r\n<li>Đầu vào-ảnh hưởng từ<a class=\"cloze\" href=\"#mce_temp_url#\"> bên ngoài</a></li>\r\n</ul>\r\n<p>-         Nỗ lực marketing</p>\r\n<p>-         Môi trường văn hóa-xã hội</p>\r\n<ul>\r\n<li>Quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">ra quyết định</a></li>\r\n</ul>\r\n<p>-         Nhận thức nhu cầu</p>\r\n<p>-         Tìm kiếm thông tin trước khi mua</p>\r\n<p>-         Đánh giá các phương án</p>\r\n<ul>\r\n<li>Đầu ra- <a class=\"cloze\" href=\"#mce_temp_url#\">hành vi</a> sau quyết định mua</li>\r\n</ul>\r\n<p>-         Mua thử nghiệm/mua lặp lại</p>\r\n<p>-         Đánh giá sau mua</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1474', 'Phân tích “các loại hình ra quyết định mua” của khách hàng người tiêu dùng?', null, null, '5', '0', '324', '467', '1551756374', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1475', null, null, null, '5', '1474', '324', '467', '1551756374', null, null, null, '<p>Phân tích” Các loại hình ra quyết định mua”:</p>\r\n<p> *<a class=\"cloze\" href=\"#mce_temp_url#\"> Đáp ứng</a> theo thói quen: mua thực phẩm, tạp phẩm, hóa phẩm,…</p>\r\n<p>*Giải quyết vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">có giới hạn</a>: linh phụ kiện, đồ gia dụng/vật dụng đơn giản,…</p>\r\n<p>*Giải quyết vấn <a class=\"cloze\" href=\"#mce_temp_url#\">dề mở rộng</a>: mua điện thoại thông minh, xe máy, laptop,…</p>\r\n<p>*(lần lượt theo thứ tự) Mua theo thói quen – Giải quyết vấn đề có giới hạn – Giải quyết vấn đề mở rộng.</p>\r\n<p>•           Chi phí <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a> – <a class=\"cloze\" href=\"#mce_temp_url#\">trung bình</a> – <a class=\"cloze\" href=\"#mce_temp_url#\">cao</a></p>\r\n<p>•           Mua <a class=\"cloze\" href=\"#mce_temp_url#\">thường xuyên</a> – <a class=\"cloze\" href=\"#mce_temp_url#\">thỉnh thoảng</a> – <a class=\"cloze\" href=\"#mce_temp_url#\">hiếm khi</a></p>\r\n<p>•           Độ thu hút <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a> – <a class=\"cloze\" href=\"#mce_temp_url#\">trung bình</a> – <a class=\"cloze\" href=\"#mce_temp_url#\">cao</a></p>\r\n<p>•           Lớp SP/nhãn hiệu <a class=\"cloze\" href=\"#mce_temp_url#\">quen thuộc</a> – Mua sản phẩm giới hạn nguồn lực – Mua mới/giá trị cao.</p>\r\n<p>•           Ít <a class=\"cloze\" href=\"#mce_temp_url#\">cân nhắc</a>, ít <a class=\"cloze\" href=\"#mce_temp_url#\">tìm hiểu</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">đầu tư</a> thời gian mua – Có cân nhắc – Cân nhắc kỹ lưỡng.</p>\r\n<p>•        <a class=\"cloze\" href=\"#mce_temp_url#\">   Động cơ</a>: Không động lực rõ ràng – Mạo hiểm/sức thu hút thấp – Rủi ro/sức thu hút cao.</p>\r\n<p>•           Thu thập <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a>: Không chủ động tìm kiếm – Tìm kiếm ít – Tìm kiếm mở rộng.</p>\r\n<p>•           Đánh giá <a class=\"cloze\" href=\"#mce_temp_url#\">thế vị</a>: Tự động chọn, khôg ý thức – Niềm tin yếu, dựa theo định hướng chung – Niềm tin mạnh mẽ, thận trọng.</p>\r\n<p>•         <a class=\"cloze\" href=\"#mce_temp_url#\">  Mua sắm</a>: Tiết kiệm nguồn lực – Hạn chế thời gian mua, tự phục vụ - Tìm kiếm nhiều, giao tiếp rộng, thường xuyên.</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1476', 'Phân tích các bước ra quyết định mua của người tiêu dùng?', null, null, '5', '0', '328', '467', '1551756501', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1477', null, null, null, '5', '1476', '328', '467', '1551756502', null, null, null, '<p><em><span style=\"text-decoration: underline;\">B1: <a class=\"cloze\" href=\"#mce_temp_url#\">Nhận thức nhu cầu</a></span></em>: Xuất phát từ những vấn đề nảy sinh trong cuộc sốngg. Vì thế, trong đời sống hàng ngày, khi những vấn đề nảy sinh, người tiêu dùng tự nhận thức đươc nhu cầu mà mình mong muốn thỏa mãn nhu cầu đó.</p>\r\n<p><em><span style=\"text-decoration: underline;\">B2: <a class=\"cloze\" href=\"#mce_temp_url#\">Tìm kiếm</a> thông tin về</span></em>:  tìm kiếm thông tin cần thiết để quyết định hợp lý; tìm kiếm bên ngoài, bên trong</p>\r\n<p>- Loại tìm kiếm: định hướng, “ngẫu nhiên”</p>\r\n<p>- Nguồn tìm kiếm: thương mại, cá nhân,…</p>\r\n<p>- Nhân tố xác định tìm kiếm thôn tin dựa theo</p>\r\n<p>            + Kinh nghiệm đã có</p>\r\n<p>            + Rủi ro nhận thức: tiền bạc, xã hội, tâm lý…</p>\r\n<p>            + Nhân tố sản phẩm: độ bền, phong cách,…</p>\r\n<p><em><span style=\"text-decoration: underline;\">B3: Đánh giá các <a class=\"cloze\" href=\"#mce_temp_url#\">phương án</a></span></em></p>\r\n<p>-           Nhận dạng các phương án</p>\r\n<p>-           Phân loại sản phẩm để đánh giá</p>\r\n<p><em><span style=\"text-decoration: underline;\">B4: Lựa chọn giữa các <a class=\"cloze\" href=\"#mce_temp_url#\">phương án thế vị</a></span></em></p>\r\n<p>-           Các tiêu chí đánh giá</p>\r\n<p>-           Các quy luật ra quyết định không bù trừ</p>\r\n<p>-           Quy luật bù trừ - cho những khiếm khuyết</p>\r\n<p><em><span style=\"text-decoration: underline;\">B5: <a class=\"cloze\" href=\"#mce_temp_url#\">Khám phá</a>: đường tắt của trí óc trong đánh giá</span></em></p>\r\n<p>-           Mực giá: quan hệ - chât lượng</p>\r\n<p>-           Tên nhãn hiệu – uy tín và nổi tiếng</p>\r\n<p>-           Nguồn gốc sản xuất (địa phương/quốc gia nổi tiếng)</p>\r\n<p>-           Các tin tưởng của thị trường (SP mới/Doanh số cao)</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1478', 'Nêu chức năng của gia đình?', null, null, '5', '0', '325', '467', '1551756533', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1479', null, null, null, '5', '1478', '325', '467', '1551756533', null, null, null, '<p>Chức năng của gia đình:</p>\r\n<ul>\r\n<li>Đảm bảo<a class=\"cloze\" href=\"#mce_temp_url#\"> kinh tế</a> ổn định</li>\r\n<li>Cổ vũ về mặt <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần</a>: đối phó với các vấn đề cá nhân và xã hội</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lối sống</a> của gia đình.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1480', 'Phân tích “các vai trò trong trung tâm mua gia đình”?', null, null, '5', '0', '325', '467', '1551756560', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1481', null, null, null, '5', '1480', '325', '467', '1551756560', null, null, null, '<p>Phân tích các vai trò của trung tâm mua trong gia đình</p>\r\n<p>- Người khởi xướng</p>\r\n<p>- Người gác cổng</p>\r\n<p>- Người ảnh hưởng</p>\r\n<p>- Người mua</p>\r\n<p>- Người chuẩn bị                                                                                                    </p>\r\n<p>- Người sử dụng</p>\r\n<p>- Người bảo trì</p>\r\n<p>- Người loại bỏ</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1482', 'Nêu đặc điểm của hộ gia đình', null, null, '5', '0', '326', '467', '1551756620', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1483', null, null, null, '5', '1482', '326', '467', '1551756620', null, null, null, '<p>Đặc điểm của hộ gia đình:</p>\r\n<ul>\r\n<li>Gia đình trên thị trường: Là <a class=\"cloze\" href=\"#mce_temp_url#\">tế bào</a> của xã hội-&gt;Ảnh hưởng đến hành vi <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a>.</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhu càu và chi tiêu</a> của hộ gia đình thay đổi theo: Số người, tuổi tác, phụ nữ có đi làm hay không..</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\"><span style=\"color: #000000;\">Chu kì sống</span></a> của gia đình -&gt;thay đổi chi tiêu</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1484', 'Các giai đoạn sống trong chu kì sống của hộ gia đình?', null, null, '5', '0', '326', '467', '1551756730', '1551756867', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1485', null, null, null, '5', '1484', '326', '467', '1551756730', '1551756867', null, null, '<ol>\r\n<li>Giai đoạn <a class=\"cloze\" href=\"#mce_temp_url#\">độc thân</a></li>\r\n</ol>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mâu thuẫn</a> khả năng tài chính thấp và nhu cầu giải trí cao, mua sắm..</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Ít kinh nghiệm</a> mua sắm, bốc đồng, ngẫu hứng</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Phụ thuộc</a> vào người xung quanh</li>\r\n<li>Cuối gia đoạn này là mua sắm để chuẩn bị cho gia đoạn gia đình trẻ…</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1486', null, null, null, '5', '1484', '326', '467', '1551756730', '1551756867', null, null, '<p>2.Vợ chồng mới cưới</p>\r\n<ul>\r\n<li>Khả năng <a class=\"cloze\" href=\"#mce_temp_url#\">tài chính</a> tương đối cao</li>\r\n<li>Mua sắm với <a class=\"cloze\" href=\"#mce_temp_url#\">tần suất lớn</a></li>\r\n<li>Hàng hóa<a class=\"cloze\" href=\"#mce_temp_url#\"> lâu bền</a></li>\r\n<li>Sử dụng các dich vụ mới, giải trí,..chịu ảnh hưởng nhiều của quảng cáo.</li>\r\n<li>Cuối giai đoạn có khuynh hướng <a class=\"cloze\" href=\"#mce_temp_url#\">tiết kiệm</a> cho gia đình mới</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1487', null, null, null, '5', '1484', '326', '467', '1551756801', '1551756867', null, null, '<p>3.Tổ ấm đầy đủ 1(khoảng 30 tuổi, có con dưới 6 tuổi)</p>\r\n<ul>\r\n<li>Thu nhập <a class=\"cloze\" href=\"#mce_temp_url#\">giảm dấn</a></li>\r\n<li>Kế hoạch chi tiêu tập trung cho trẻ</li>\r\n<li>ảnh hưởng của quảng cáo</li>\r\n<li>Mua đề cao kinh nghiệm tiêu dùng</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1488', null, null, null, '5', '1484', '326', '467', '1551756801', '1551756867', null, null, '<p>4.Tổ ấm đầy đủ 2</p>\r\n<ul>\r\n<li>Thu nhập<a class=\"cloze\" href=\"#mce_temp_url#\"> ổn định hơn</a></li>\r\n<li>Chi tiêu cho con</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Ảnh hưởng</a> của con đến quyết định của bố mẹ</li>\r\n<li>Ít chịu ảnh hưởng của <a class=\"cloze\" href=\"#mce_temp_url#\">quàng cáo</a></li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1489', null, null, null, '5', '1484', '326', '467', '1551756867', null, null, null, '<p>5.Tổ ấm đầy đủ 3</p>\r\n<ul>\r\n<li>Các con <a class=\"cloze\" href=\"#mce_temp_url#\">đã trưởng thành</a> nhưng <a class=\"cloze\" href=\"#mce_temp_url#\">phụ thuộc </a>tài chính</li>\r\n<li>Tài chính ổn định</li>\r\n<li>Chi tiêu cho lâu bền, gia đình</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1490', null, null, null, '5', '1484', '326', '467', '1551756867', null, null, null, '<p>6.Tổ ấm trống vắng 1</p>\r\n<ul>\r\n<li>Con cái tự lập</li>\r\n<li>Tài chính ổn định</li>\r\n<li>Chăm lo đến nhà của, giải trí, sức khỏe,..</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1491', null, null, null, '5', '1484', '326', '467', '1551756867', null, null, null, '<p>7.Tổ ấm trống vắng 2</p>\r\n<ul>\r\n<li>Một hoặc hai người nghỉ hưu</li>\r\n<li>Con cái có thể không ở chung</li>\r\n<li>Chi tiêu cho dịch vụ y tế, sức khỏe</li>\r\n<li>Cần có sự quan tâm của con cái và xã hội</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1492', null, null, null, '5', '1484', '326', '467', '1551756867', null, null, null, '<p>8.Người gia cô độc</p>\r\n<ul>\r\n<li>Nhu cầu về tình cảm, sức khỏe cao..</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1493', 'Phân tích xung đột trong quyết định mua của hộ gia đình?', null, null, '5', '0', '327', '467', '1551756924', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1494', null, null, null, '5', '1493', '327', '467', '1551756924', null, null, null, '<p>- Có sự khác biệt -&gt; <a class=\"cloze\" href=\"#mce_temp_url#\">Tiềm ẩn xung đột</a></p>\r\n<p>- Các nguyên nhân xung đột: không đáp ứng <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a>/<a class=\"cloze\" href=\"#mce_temp_url#\">sở thích</a> của các thành viên.</p>\r\n<p>            + Mức độ tham gia</p>\r\n<p>            + Mức độ sản phẩm được sử dụng hay thỏa mãn nhu cầu</p>\r\n<p>            +Trách nhiệm với mua, duy trì và chi trả</p>\r\n<p>            + Mức độ người này gây ảnh hưởng tới người khác trong gia đình.</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1495', 'Phân tích đặc điểm nhu cầu?', null, null, '5', '0', '330', '467', '1551757185', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1496', null, null, null, '5', '1495', '330', '467', '1551757185', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Đặc điểm nhu cầu</span></em></p>\r\n<ul>\r\n<li>Nhu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">phái sinh</a>: Cầu phụ thuộc và cầu của Khách hàng cá nhân</li>\r\n<li>Nhu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">dao động</a> ở <a class=\"cloze\" href=\"#mce_temp_url#\">biên độ cao</a> hơn, <a class=\"cloze\" href=\"#mce_temp_url#\">thất thường</a>:Mua nhiều =&gt; một thay đổi nhỏ về nhu cầu dãn đến 1 sự thay đổi lớn về sản phẩm</li>\r\n<li>Nhu cầu có <a class=\"cloze\" href=\"#mce_temp_url#\">tính liên kết</a>:Nhu cầu về các  sản phẩm và dịch vụ  phụ thuộc lẫn nhau</li>\r\n<li>Nhu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">ít co giãn </a>so với giá</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1497', 'Phân tích đặc điểm mục đích mua?', null, null, '5', '0', '330', '467', '1551757232', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1498', null, null, null, '5', '1497', '330', '467', '1551757232', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Đặc điểm mục đích mua</span></em></p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sản xuất</a> sản phẩm và dịch vụ khác</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Bán lại</a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sử dụng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tiêu dùng</a> cho tổ chức của họ trong quá trình hoạt động và vận hành</li>\r\n<li>Xu hướng chuyển dần sang<a class=\"cloze\" href=\"#mce_temp_url#\"> thuê mua</a> tài chính, tín dụng</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1499', 'Phân tích đặc điểm khách hàng?', null, null, '5', '0', '330', '467', '1551757255', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1500', null, null, null, '5', '1499', '330', '467', '1551757256', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Đặc điểm khách hàng</span></em></p>\r\n<ul>\r\n<li>Có số lượng KH ít hơn nhưng quy mô KH lớn hơn</li>\r\n<li>Tập chung về địa lí</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1501', 'Phân tích đặc điểm về sản phẩm, dịch vụ?', null, null, '5', '0', '330', '467', '1551757313', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1502', null, null, null, '5', '1501', '330', '467', '1551757313', null, null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Đặc điểm sản phẩm, dịch vụ</span></em>:</p>\r\n<ul>\r\n<li>Về bản chất, có tính <a class=\"cloze\" href=\"#mce_temp_url#\">kỹ thuật cao</a> và mua trên cơ sở các yêu cầu kỹ thuật chi tiết</li>\r\n<li>Thường là <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên vật liêu thô </a>và bán thành <a class=\"cloze\" href=\"#mce_temp_url#\">thành phẩm</a></li>\r\n<li>Chú trọng tới <a class=\"cloze\" href=\"#mce_temp_url#\">giao hàng </a>đúng hạn, hỗ trợ kỹ thuật, và dịch vụ sau bán</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1503', 'Phân tích đặc điểm quá trình mua?', null, null, '5', '0', '330', '467', '1551757348', '1551757404', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1504', null, null, null, '5', '1503', '330', '467', '1551757348', '1551757404', null, null, '<p>-         <em><span style=\"text-decoration: underline;\">Đặc điểm của quá trình mua</span></em></p>\r\n<ul>\r\n<li>Các ĐDMH đã được <a class=\"cloze\" href=\"#mce_temp_url#\">huấn luyện </a>chuyên nghiệp</li>\r\n<li>có nhiều <a class=\"cloze\" href=\"#mce_temp_url#\">kỹ thuật mua hàng</a> cụ thể</li>\r\n<li>Suốt sự nghiệp người mua học hỏi cách thức <a class=\"cloze\" href=\"#mce_temp_url#\">mua hàng</a> tốt hơn</li>\r\n<li>KH và người cung cấp thân thiện, gần gũi</li>\r\n<li>Mua sắm qua <a class=\"cloze\" href=\"#mce_temp_url#\">mạng</a> thường là phổ biến</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhiều người</a> tham gia vào quá trình mua, và có nhiều người<a class=\"cloze\" href=\"#mce_temp_url#\"> ảnh hưởng</a> tới quyết định mua</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('1505', 'Câu này thử nghiệm lỗi bị che mẹo', null, null, '1', '0', '332', '6', '1551764650', '1551772493', 'Có bao giờ lia xa', null, '<p><img alt=\"\\frac{{\\sqrt 9 }}{{10}} - {x^2} = {y^4}\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{\\sqrt 9 }}{{10}} - {x^2} = {y^4}\" /></p>\r\n\r\n<p><br />\r\nthử xem kiểu gì</p>\r\nMột tỉ thứ muốn làm.\r\nNgắt dòng ra kiểu gì.', '<p><img alt=\"\\frac{8}{9} = {x^2} + {y^7} - {9^{10}}\" src=\"http://latex.codecogs.com/gif.latex?\\frac{8}{9} = {x^2} + {y^7} - {9^{10}}\" /></p>', '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('1509', '<p><img alt=\"\\mathop {\\lim }\\limits_{\\delta x \\to 0} \\frac{{{x^2} + {y^4}}}{{x + {r^5}}}\" src=\"http://latex.codecogs.com/gif.latex?\\mathop {\\lim }\\limits_{\\delta x \\to 0} \\frac{{{x^2} + {y^4}}}{{x + {r^5}}}\" /></p>', null, null, '2', '0', '332', '6', '1551765478', '1551765498', null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('1515', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768518', null, null, '<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> Tích phân tổng quát của</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\" alt=\"\\ frac {{dy}} {{d {\\ rm {x}}}} = \\ frac {{x. {e ^ x}}} {{y \\ sqrt {1 + {y ^ 2}}}}\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\sqrt {{{(1 + {y^2})}^3}}  = \\_\\_.{e^x} - \\_\\_.{e^x} + C\" alt=\"\\ sqrt {{{(1 + {y ^ 2})} ^ 3}} = \\ _ \\ _. {e ^ x} - \\ _ \\ _. {e ^ x} + C\" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">3, 3</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1516', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768518', null, null, '<p>Nghiệm tổng quát của</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = 2 + 2y + x + xy\" alt=\"\\ frac {{dy}} {{d {\\ rm {x}}}} = 2 + 2y + x + xy\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?y =  - 1;y = C.{e^{\\frac{{{x^2}}}{2} + \\_\\_\\_}} - 1\" alt=\"y = - 1; y = C. {e ^ {\\ frac {{{x ^ 2}}} {2} + \\ _ \\ _ \\ _}} - 1\" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">2 lần</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1517', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768518', null, null, '<p>Tích phân tổng quát của</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = 1 + {y^2}\" alt=\"\\ frac {{dy}} {{d {\\ rm {x}}}} = 1 + {y ^ 2}\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\[{\\mathop{\\rm arctany}\\nolimits}  = \\_\\_\\_ + C\\]\" alt=\"\\ [{\\ mathop {\\ rm arctany} \\ nolimits} = \\ _ \\ _ \\ _ + C \\]\" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">x</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1518', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768518', null, null, '<p>Nghiệm tổng quát của</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?y\' = {e^{x + y}} + {e^{x - y}}\" alt=\"y \'= {e ^ {x + y}} + {e ^ {x - y}}\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\[\\arctan \\_\\_{\\_^y} = \\_\\_{\\_^x} + C\\] \" alt=\"\\ [\\ arctan \\ _ \\ _ {\\ _ ^ y} = \\ _ \\ _ {\\ _ ^ x} + C \\] \" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">e</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1519', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768518', null, null, '<p>Nghiệm tổng quát của <img src=\"http://latex.codecogs.com/gif.latex?y\' = 4{{\\rm{x}}^3}y\" alt=\"y \'= 4 {{\\ rm {x}} ^ 3} y\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?y = 0;y = C.{e^{\\_\\_\\_}}\" alt=\"y = 0; y = C. {e ^ {\\ _ \\ _ \\ _}}\" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">x ^ 4</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1520', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768518', null, null, '<p>Tích phân tổng quát của</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\tan y{\\rm{dx}} - x\\ln {\\rm{xd}}y = 0\" alt=\"\\ tan y {\\ rm {dx}} - x \\ ln {\\ rm {xd}} y = 0\" /></p>\r\n<p>là</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\_\\_\\_) = C\" alt=\"\\ frac {1} {{{{\\ sin} ^ 2} y}} + \\ ln (\\ _ \\ _ \\ _) = C\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">. </span></span><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">lnx</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1521', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768519', null, null, '<p>Tích phân tổng quát của</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{{4 + {y^2}}}{{\\sqrt {{x^2} + 4{\\rm{x}} + 13} }} = \\frac{{3y + 2}}{{x + 1}}y\'\" alt=\"\\ frac {{4 + {y ^ 2}}} {{\\ sqrt {{x ^ 2} + 4 {\\ rm {x}} + 13}}} = \\ frac {{3y + 2}} {{x + 1}} y \'\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\_\\_\\_\\ln ({y^2} + 4) + \\arctan \\_\\_\\_ = \\sqrt {{{(x + 2)}^2} + 9}  - \\ln \\left| {x + 2 + \\sqrt {{{(x + 2)}^2} + 9} } \\right| + C\" alt=\"\\ _ \\ _ \\ _ \\ ln ({y ^ 2} + 4) + \\ arctan \\ _ \\ _ \\ _ = \\ sqrt {{{(x + 2)} ^ 2} + 9} - \\ ln \\ left |  {x + 2 + \\ sqrt {{{(x + 2)} ^ 2} + 9}} \\ phải |  + C\" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">3/2, y / 2</span></span></a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1522', null, null, null, '5', '1514', '335', '450', '1551768257', '1551768519', null, null, '<p>Tích phân tổng quát của</p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{{x{\\rm{dx}}}}{{{x^2} + 1}} + (y + 1)dy = 0\" alt=\"\\ frac {{x {\\ rm {dx}}}} {{{x ^ 2} + 1}} + (y + 1) dy = 0\" /></p>\r\n<p><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">là</span></span></p>\r\n<p><img src=\"http://latex.codecogs.com/gif.latex?\\frac{{{y^2}}}{{\\_\\_\\_}} + y = \\frac{{ - 1}}{{\\_\\_\\_}}\\ln ({x^2} + 1) + C\" alt=\"\\ frac {{{y ^ 2}}} {{\\ _ \\ _ \\ _}} + y = \\ frac {{- 1}} {{\\ _ \\ _ \\ _}} \\ ln ({x ^ 2} + 1) + C\" /></p>\r\n<p><a class=\"clozetip\" title=\"\" href=\"#\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">2,2</span></span></a></p>', null, '51', null, null, null, null, null);
