INSERT INTO `question` VALUES ('1623', null, null, null, '5', '1622', '333', '450', '1551788350', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = x\\sqrt y  - {x^2} - y + 6{\\rm{x + 3}}\" src=\"http://latex.codecogs.com/gif.latex?z = x\\sqrt y  - {x^2} - y + 6{\\rm{x + 3}}\" /></p>  là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">5,(4;4)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1624', null, null, null, '5', '1622', '333', '450', '1551788350', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = 1 + 6{\\rm{x - }}{{\\rm{x}}^2} - xy - {y^2}\" src=\"http://latex.codecogs.com/gif.latex?z = 1 + 6{\\rm{x - }}{{\\rm{x}}^2} - xy - {y^2}\" /></p>    là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">13,(4;-2)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1625', null, null, null, '5', '1622', '333', '450', '1551788350', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = (5x + 7y - 25).{e^{ - {x^2} - xy - {y^2}}}\" src=\"http://latex.codecogs.com/gif.latex?z = (5x + 7y - 25).{e^{ - {x^2} - xy - {y^2}}}\" /></p>   là<p><img alt=\"{f_{c{\\rm{d}}}} = {3^{ - 13}}\" src=\"http://latex.codecogs.com/gif.latex?{f_{c{\\rm{d}}}} = {3^{ - 13}}\" /></p>   tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">(1;3)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1626', null, null, null, '5', '1622', '333', '450', '1551788350', null, '', null, ' Giá trị cực tiểu của:<p><img alt=\"z = ({x^2} + y).\\sqrt {{e^y}} \" src=\"http://latex.codecogs.com/gif.latex?z = ({x^2} + y).\\sqrt {{e^y}} \" /></p> là<p><img alt=\"{f_{ct}} = \\frac{{ - 2}}{e}\" src=\"http://latex.codecogs.com/gif.latex?{f_{ct}} = \\frac{{ - 2}}{e}\" /></p> tại điểm    <a class=\"clozetip\" title=\"\" href=\"#\">(0;2)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1627', null, null, null, '5', '1622', '333', '450', '1551788350', null, '', null, ' Giá trị cực tiểu của:<p><img alt=\"z = 2{{\\rm{x}}^2} + 3{y^2} - {e^{ - ({x^2} + {y^2})}}\" src=\"http://latex.codecogs.com/gif.latex?z = 2{{\\rm{x}}^2} + 3{y^2} - {e^{ - ({x^2} + {y^2})}}\" /></p>    là___tại điểm    <a class=\"clozetip\" title=\"\" href=\"#\">-1,(0;0)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1628', null, null, null, '5', '1622', '333', '450', '1551788350', null, '', null, ' Giá trị cực tiểu của:<p><img alt=\"z = {x^2} + 2{\\rm{x}}y + 2{y^2} + x - y + 1\" src=\"http://latex.codecogs.com/gif.latex?z = {x^2} + 2{\\rm{x}}y + 2{y^2} + x - y + 1\" /></p>    là<p><img alt=\"{f_{ct}} = \\frac{{ - 1}}{4}\" src=\"http://latex.codecogs.com/gif.latex?{f_{ct}} = \\frac{{ - 1}}{4}\" /></p> tại điểm    <a class=\"clozetip\" title=\"\" href=\"#\">(-3/2;1)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1630', null, null, null, '5', '1629', '334', '450', '1551788957', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = 3{{\\rm{x}}^2} + 5{\\rm{x}}y;x + y = 16\" src=\"http://latex.codecogs.com/gif.latex?z = 3{{\\rm{x}}^2} + 5{\\rm{x}}y;x + y = 16\" /></p>  là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">88,(20;-4)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1631', null, null, null, '5', '1629', '334', '450', '1551788957', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = xy;\\frac{{{x^2}}}{8} + \\frac{{{y^2}}}{2} = 1\" src=\"http://latex.codecogs.com/gif.latex?z = xy;\\frac{{{x^2}}}{8} + \\frac{{{y^2}}}{2} = 1\" /></p>    là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">2,(2;1)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1632', null, null, null, '5', '1629', '334', '450', '1551788957', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = {e^{xy}};x + y = 1\" src=\"http://latex.codecogs.com/gif.latex?z = {e^{xy}};x + y = 1\" /></p>    là<p><img alt=\"{f_{c{\\rm{d}}}} = {e^{\\frac{1}{4}}}\" src=\"http://latex.codecogs.com/gif.latex?{f_{c{\\rm{d}}}} = {e^{\\frac{1}{4}}}\" /></p>  tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">(3/2;-1/2)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1633', null, null, null, '5', '1629', '334', '450', '1551788957', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = {x^2} + 2{y^2};{x^2} - 2{\\rm{x}} + 2{y^2} + 4y = 0\" src=\"http://latex.codecogs.com/gif.latex?z = {x^2} + 2{y^2};{x^2} - 2{\\rm{x}} + 2{y^2} + 4y = 0\" /></p>   là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">12,(2;-2)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1634', null, null, null, '5', '1629', '334', '450', '1551788957', null, '', null, ' Giá trị cực tiểu của:<p><img alt=\"z = 6 - 4{\\rm{x}} - 3y;{x^2} + {y^2} = 1\" src=\"http://latex.codecogs.com/gif.latex?z = 6 - 4{\\rm{x}} - 3y;{x^2} + {y^2} = 1\" /></p>    là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">1,(4/5;3/5)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1635', 'Trình bày tiêu chuẩn lựa chọn nhà cung ứng của doanh nghiệp?', null, null, '5', '0', '294', '463', '1551791310', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1636', null, null, null, '5', '1635', '294', '463', '1551791310', null, null, null, '<p>-        Giá và điều kiện<a class=\"cloze\" href=\"#mce_temp_url#\"> thanh toán</a></p>\r\n<p>-        Chất lượng và tính<a class=\"cloze\" href=\"#mce_temp_url#\"> khả ứng </a>của mặt hàng</p>\r\n<p>-        Thời hạn và tính <a class=\"cloze\" href=\"#mce_temp_url#\">kịp thời</a> của việc giao hàng</p>\r\n<p>-        Dịch vụ của người bán</p>\r\n<p>-        Khả năng <a class=\"cloze\" href=\"#mce_temp_url#\">thích nghi </a>của người bán</p>\r\n<p>-        Khối lượng và tính<a class=\"cloze\" href=\"#mce_temp_url#\"> liên tục</a> của việc cung ứng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
