INSERT INTO `question` VALUES ('1639', 'Trình bày có ví dụ bậc thị trường hiện hữu của doanh nghiệp?', null, null, '5', '0', '294', '463', '1551791396', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1640', null, null, null, '5', '1639', '294', '463', '1551791396', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Dung lượng thị trường của một sản phẩm</span></em></strong></p>\r\n<p>Sử dụng nạp khối lượng sản phẩm tối đa được mua bởi toàn bộ thị trường ở mức giá xác định trong một thời kì xác định. Điều đó có nghĩa là ở tình trạng đó, nhu cầu thị trường được bão hòa</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">tiềm năng</a> của doanh nghiệp:</span></em></strong> Là một tập xác định người tiêu dùng có biểu hiện một vài mức quan tâm tới một sự chào hàng nhất định nào đó của doanh nghiệp</p>\r\n<p><em>+ Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">khả hiệu lực</a>:</em> Gồm những người mong muốn mua sản phẩm của doanh nghiệp. đủ khả năng thanh toán và điều kiện mua sản phẩm</p>\r\n<p><em>+ Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">hiện hữu</a>:</em> Gồm những người có nhu cầu mua, có khả năng thanh toán, đủ điều kiện mua, có khả năng thanh toán và có thể tiếp cận được những chào hàng của doanh nghiệp</p>\r\n<p>+<em>Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">được cung</a>:</em> Là một phần thị trường hiện hữu mà công ty quyết định theo đuổi thỏa mãn</p>\r\n<p><em>+ Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">hiện hữu</a>:</em> Là tập khách hàng đã, đang mua sản phẩm của doanh nghiệp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1641', 'Nêu vai trò trong mua sắm  của khách hàng người tiêu dung?', null, null, '5', '0', '294', '463', '1551791460', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1642', null, null, null, '5', '1641', '294', '463', '1551791460', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Các vai trò trong mua sắm</span></em></strong></p>\r\n<p>+ <em>Người <a class=\"cloze\" href=\"#mce_temp_url#\">khởi xướng</a>:</em> là người đầu tiên gợi ý mua một sản phẩm hay dịch vụ</p>\r\n<p><em>+Người <a class=\"cloze\" href=\"#mce_temp_url#\">ảnh hưởng</a>:</em> là người có quan điểm, lời khuyên có sức nặng trong việc quyết định sau cùng.</p>\r\n<p><em>+Người <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định</a>:</em> là người quyết định bất cứ thành phần nào trong quyết định mua sắm: khi nào mua? Mua ở đâu? Mua như thế nào?</p>\r\n<p><em>+Người <a class=\"cloze\" href=\"#mce_temp_url#\">mua:</a></em> là người thực hiện mua</p>\r\n<p><em>+Người <a class=\"cloze\" href=\"#mce_temp_url#\">sử dụng</a>:</em> là người sử dụng, tiêu dùng sản phẩm, dịch vụ</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1643', 'Các loại hành vi mua của khách hàng người tiêu dùng?', null, null, '5', '0', '294', '463', '1551791861', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1644', null, null, null, '5', '1643', '294', '463', '1551791861', null, null, null, '<ol>\r\n<li><em>1.     </em><em>Hành vi mua <a class=\"cloze\" href=\"#mce_temp_url#\">phức tạp</a></em></li>\r\n</ol>\r\n<p>+ Người tiêu dùng tham gia vào việc mua khá nhiều, <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt</a> giữa các nhãn hiệu cảm nhận được lớn</p>\r\n<p>+mua những hàng hóa <a class=\"cloze\" href=\"#mce_temp_url#\">đắt tiền</a>, có tính chất may rủ, lâu mới mua</p>\r\n<p>Ví dụ: nhà, oto</p>\r\n<ol>\r\n<li><em>2.     </em><em>Hành vi mua định hướng <a class=\"cloze\" href=\"#mce_temp_url#\">đa dạng hóa</a></em></li>\r\n</ol>\r\n<p>+Mức độ cân nhắc khi mua hàng <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a> nhưng Người tiêu dùng thấy sự <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt </a>nhiều giữa các nhãn hiệu</p>\r\n<p>+Người tiêu dùng thường xuyên <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổi</a> nhãn hiệu thực chất không phải vì nhãn hiệu hiện tại không thỏa mãn họ mà họ muốn thử nhiều nhãn hiệu khác nhau.</p>\r\n<p>Ví dụ: Bánh kẹo</p>\r\n<ol>\r\n<li><em>3.     </em><em>Hành vi làm<a class=\"cloze\" href=\"#mce_temp_url#\"> giảm trái nghịch</a></em></li>\r\n</ol>\r\n<p>+ Mức độ cân nhắc khi mua hàng <a class=\"cloze\" href=\"#mce_temp_url#\">lớn</a> nhưng Người tiêu dùng <a class=\"cloze\" href=\"#mce_temp_url#\">không </a>thấy sự khách biệt nhiều giữa các nhãn hiệu</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Giá cả</a> và sự tiện lời của phân phối đóng vai trò quyết định trong lựa chọn  của Người tiêu dùng</p>\r\n<p>Ví dụ: Gửi tiền ở ngân hàng.</p>\r\n<ol>\r\n<li><em>4.     </em><em>Hành vi mua theo <a class=\"cloze\" href=\"#mce_temp_url#\">thói quen</a></em></li>\r\n</ol>\r\n<p>+ Mức độ cân nhắc khi mua hàng <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a> và nhận thức về sự khác biệt giữa các nhãn hiệu cũng <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a></p>\r\n<p>+ Người tiêu dùng có xu hướng mua theo<a class=\"cloze\" href=\"#mce_temp_url#\"> thói quen</a>, sự tiện lợi, giá cả</p>\r\n<p>Ví dụ: Xăng dầu..</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
