INSERT INTO `question` VALUES ('1665', 'Trình bày nội dung giai đoạn cảm nhận sau mua và cho biết các ứng xử marketing  của giai đoạn này? (Đã trình bày)', null, null, '5', '0', '345', '463', '1551792220', null, null, null, null, null, '58', null, null, null, null, null);
