INSERT INTO `question` VALUES ('1671', 'Nêu khái niệm hành vi ứng xử khách hàng.- người tiêu dùng?', null, null, '5', '0', '345', '463', '1551792412', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1672', null, null, null, '5', '1671', '345', '463', '1551792412', null, null, null, '<p>-        Là thuộc tính tư duy <a class=\"cloze\" href=\"#mce_temp_url#\">tâm lý</a> hỗn hợp để chỉ một tổ chức ổn định của những <a class=\"cloze\" href=\"#mce_temp_url#\">tin tưởng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">thiên hướng</a> đối với một khách thể/một diễn biến và là tiền đề trực tiếp để cá nhân nào đo tiếp nhận và đáp ứng có ưu tiên đối với khách thể/diễn biến đó</p>\r\n<p>-        Các thành tố cấu thành: Tri thức-tình cảm-thiên hướng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1673', 'Trình bày có ví dụ minh họa sự ảnh hưởng của một trong các nhóm nhân tố ảnh hưởng đến hành vi ứng xử của khách hàng-người tiêu dùng? (Tham khảo nội dung tóm tắt chương)', null, null, '5', '0', '345', '463', '1551792460', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1674', null, null, null, '5', '1673', '345', '463', '1551792460', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1675', 'Nêu khái niệm phân đoạn thị trường?', null, null, '5', '0', '347', '463', '1551792598', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1676', null, null, null, '5', '1675', '347', '463', '1551792598', null, null, null, '<p>-        Là <a class=\"cloze\" href=\"#mce_temp_url#\">“quá trình chi tiết</a>” của marketing mục tiêu nhằm phân định thị trường tổng thể thành những cấu trúc <a class=\"cloze\" href=\"#mce_temp_url#\">nhỏ hơn</a>(đoạn, khúc mảng, lát cắt) có thông số, đặc tính và đường nét hành vi <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt</a> nhau nhưng trong nội bộ một đoạn lại <a class=\"cloze\" href=\"#mce_temp_url#\">thống nhất</a> với nhau mà doanh nghiệp có thể vận dụng marketing-mix hữu hiệu.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1677', 'Các tiêu thức phân đoạn thị trường và các yêu cầu phân đoạn thị trường?', null, null, '5', '0', '347', '463', '1551792655', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1678', null, null, null, '5', '1677', '347', '463', '1551792655', null, null, null, '<p><strong>Các nhóm tiêu thức phân đoạn thị trường</strong></p>\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Điạ dư</a>: Khí hậu, điều kiện tự nhiên</p>\r\n<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Dân số-xã hội</a>: Tuổi; giới tính;qui mô gia đình; thu nhập; tôn giáo</p>\r\n<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Phác đồ tâm lý</a>: Tầng lớp xã hội; cách sống; đặc tính nhân cách</p>\r\n<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Hành vi ứng xử</a>: sự hiểu biết, ý niệm</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1679', 'Các yêu cầu phân đoạn thị trường?', null, null, '5', '0', '347', '463', '1551792747', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1680', null, null, null, '5', '1679', '347', '463', '1551792747', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Tính <a class=\"cloze\" href=\"#mce_temp_url#\">xác đáng</a></span></em></strong>:</p>\r\n<p>+ Nhận dạng rõ rang</p>\r\n<p>+Qui mô đủ lớn</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Tính <a class=\"cloze\" href=\"#mce_temp_url#\">khả thi</a></span></em></strong></p>\r\n<p>+Ổn định trong thời gian đủ dài</p>\r\n<p>+Thực thi đồng bộ marketing-mix</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Tính <a class=\"cloze\" href=\"#mce_temp_url#\">khả hữu hiệu:</a></span></em></strong></p>\r\n<p>+Chi phí có tiềm năng bù đắp và sinh lời</p>\r\n<p>+ Tận dụng tối ưu công suất kinh doanh, thị phần</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1681', 'Nêu khái niệm  marketing-mix?', null, null, '5', '0', '347', '463', '1551792838', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1682', null, null, null, '5', '1681', '347', '463', '1551792838', null, null, null, '<p>-        Marketing-mix là<a class=\"cloze\" href=\"#mce_temp_url#\"> phối thức </a>định hướng các <a class=\"cloze\" href=\"#mce_temp_url#\">biến số</a> marketing có thể kiểm soát được sử dụng một cách liên hoàn đồng bộ nhằm theo đuổi một sức bán và lời nhuận dự kiến trong một<a class=\"cloze\" href=\"#mce_temp_url#\"> thị trường </a>trọng điểm</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1683', 'Cấu trúc marketing-mix?', null, null, '5', '0', '347', '463', '1551792901', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1684', null, null, null, '5', '1683', '347', '463', '1551792901', null, null, null, '<ol>\r\n<li><strong><em>1.     </em></strong><a class=\"cloze\" href=\"#mce_temp_url#\"><strong><em><span style=\"text-decoration: underline;\">Sản phẩm:</span></em></strong></a></li>\r\n</ol>\r\n<p>-        Chất lượng<a class=\"cloze\" href=\"#mce_temp_url#\"> sản phẩm</a></p>\r\n<p>-        Tính nổi trội</p>\r\n<p>-        Nhãn hiệu</p>\r\n<p>-        Bao gói</p>\r\n<p>-        Dịch vụ bảo hành</p>\r\n<p>-        Mức độ đa dạng</p>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><a class=\"cloze\" href=\"#mce_temp_url#\"><strong><em><span style=\"text-decoration: underline;\">Giá</span></em></strong></a></li>\r\n</ol>\r\n<p>-        Chiết khấu</p>\r\n<p>-        Giá niêm yếu</p>\r\n<p>-        Hạn thanh toán</p>\r\n<p>-        Điều kiện tín dụng</p>\r\n<ol>\r\n<li><strong><em>3.   <a class=\"cloze\" href=\"#mce_temp_url#\">  </a></em></strong><a class=\"cloze\" href=\"#mce_temp_url#\"><strong><em><span style=\"text-decoration: underline;\">Phân phối</span></em></strong></a></li>\r\n</ol>\r\n<p>-        Các kênh phân phối</p>\r\n<p>-        Định vị</p>\r\n<p>-        Vận chuyển</p>\r\n<p>-        Công nghệ bán</p>\r\n<ol>\r\n<li><strong><em>4.     </em></strong><a class=\"cloze\" href=\"#mce_temp_url#\"><strong><em><span style=\"text-decoration: underline;\">Xúc tiến</span></em></strong></a></li>\r\n</ol>\r\n<p>-        Quảng cáo</p>\r\n<p>-        Marketing trực tiếp</p>\r\n<p>-        Bán hàng cá nhân</p>\r\n<p>-        Pr</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1685', 'Nêu khái niệm marekting mục tiêu? T', null, null, '5', '0', '348', '463', '1551793120', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1686', null, null, null, '5', '1685', '348', '463', '1551793120', null, null, null, '<p>-        Marketing mục tiêu là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">phân chia</a> thị trường thành nhiều phân đoạn khác nhau (Có những đặc điểm và đường nét thái độ mua khác nhau) <a class=\"cloze\" href=\"#mce_temp_url#\">lựa chọn</a> một hoặc nhiều phân đoạn thị trường và <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng </a>chương trình marketing tương ứng cho từng phân đoạn đó.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1687', null, null, null, '5', '1685', '348', '463', '1551793120', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1688', 'rình bày có ví dụ minh họa nội dung “ Lựa chọn thị trường mục tiêu của doanh nghiệp?', null, null, '5', '0', '348', '463', '1551793170', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1689', null, null, null, '5', '1688', '348', '463', '1551793170', null, null, null, '<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Qui mô </a>và sức tăng trưởng</p>\r\n<p>-        Sức <a class=\"cloze\" href=\"#mce_temp_url#\">hấp dẫn</a> về cấu trúc thị trường</p>\r\n<p>-        Mục tiêu và <a class=\"cloze\" href=\"#mce_temp_url#\">nguồn lực</a> của doanh nghiệp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1690', 'Nêu các bước của marketing mục tiêu?', null, null, '5', '0', '348', '463', '1551793348', '1551793668', null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1691', null, null, null, '5', '1690', '348', '463', '1551793348', '1551793668', null, null, '<div class=\"O1\">±<a class=\"cloze\" href=\"#mce_temp_url#\">Phân đoạn</a> thị trường</div>\r\n<div class=\"O1\">\r\n<p>           - Xác định các <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc </a>phân đoạn</p>\r\n<p>            - <a class=\"cloze\" href=\"#mce_temp_url#\">Nhận dạng</a> đặc điểm từng đoạn</p>\r\n</div>\r\n<div class=\"O1\">±<a class=\"cloze\" href=\"#mce_temp_url#\">Lựa chọn</a> thị trường mục tiêu</div>\r\n<div class=\"O1\">\r\n<p>             -Đo lường sự hấp dẫn của các đoạn</p>\r\n<p>               -Lựa chọn các đoạn mục tiêu</p>\r\n</div>\r\n<div class=\"O1\">±<a class=\"cloze\" href=\"#mce_temp_url#\">Định vị </a>trên thị trường mục tiêu</div>\r\n<div class=\"O1\">\r\n<p>               -<a class=\"cloze\" href=\"#mce_temp_url#\">Hoạch định</a> vị thế sản phẩm theo đoạn</p>\r\n<p>               -<a class=\"cloze\" href=\"#mce_temp_url#\">Phát triển</a> marketing-mix ở mỗi đoạn</p>\r\n</div>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1692', 'Trình bày có ví dụ minh họa nội dung “Định vị thị trường”của doanh nghiệp?', null, null, '5', '0', '348', '463', '1551793495', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1693', null, null, null, '5', '1692', '348', '463', '1551793495', null, null, null, '<p>Định vị: là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng </a>và thông đạt những giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">đặc trưng</a> của thương hiệu/SP vào tâm trí của các khách hàng mục tiêu</p>\r\n<p>- Dị biệt hóa <a class=\"cloze\" href=\"#mce_temp_url#\">hình ảnh</a></p>\r\n<p>- Dị biệt hóa <a class=\"cloze\" href=\"#mce_temp_url#\">nhân sự</a></p>\r\n<p>- Dị biệt hóa <a class=\"cloze\" href=\"#mce_temp_url#\">dịch vụ</a></p>\r\n<p>- Dị biêt hóa <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1694', 'Nêu khái niệm chiến lược chung marketing?', null, null, '5', '0', '348', '463', '1551793772', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1695', null, null, null, '5', '1694', '348', '463', '1551793772', null, null, null, '<p>-        Chiến lược marketing bao gồm các <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc</a>, nguyên lý rộng lớn mà các nhà quản trị marketing mong muốn nhờ có nó để đạt được <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> marketing của mình trên <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường</a> mục tiêu.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1696', 'Trình bày nội dung cơ bản của một chiến lược chung marketing?', null, null, '5', '0', '348', '463', '1551793826', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1697', null, null, null, '5', '1696', '348', '463', '1551793826', null, null, null, '<p>-      <span style=\"text-decoration: underline;\"><strong>  Xác định thị trường mục tiêu</strong></span></p>\r\n<p>-        <span style=\"text-decoration: underline;\"><strong>Mục tiêu chiến lược chung marketing</strong></span></p>\r\n<p>+ Lợi nhuận</p>\r\n<p>+ Thế lực(Địa vị)</p>\r\n<p>+An toàn</p>\r\n<p>+ Xã hội</p>\r\n<p>-      <span style=\"text-decoration: underline;\"><strong>  Phát triển marketing mix</strong></span></p>\r\n<p>-       <span style=\"text-decoration: underline;\"><strong> Nguồn lực marketing</strong></span></p>\r\n<p>+ Nhân sự,</p>\r\n<p>+ Tài chính</p>\r\n<p>+Công nghệ</p>\r\n<p>+ Tài sản vô hình</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1698', 'Thiết lập quy trình soạn thảo chiến lược chung marketing?Trình bày có ví dụ minh họa một trong các nội dung của quy trình soạn thảo chiến lược chung marketing?(Phân tích tình thế chiến lược marketing;xác định mục tiêu marketing)', null, null, '5', '0', '348', '463', '1551794047', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1699', null, null, null, '5', '1698', '348', '463', '1551794047', null, null, null, '<p><strong>Thiết lập quy trình soạn thảo chiến lược chung marketing</strong></p>\r\n<p>-        Phân tích tình thế chiến lược chung marketing</p>\r\n<p>-        Xác định mục tiêu chiến lược chung marketing</p>\r\n<p>-        Hoạch định chiến lược chung mlt</p>\r\n<p>-        Quyết định chiến lược chung marketing tối ưu</p>\r\n<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Phân tích các tình thế chiến lược chung marketing</span></em></strong></li>\r\n</ol>\r\n<p>-        <strong><em>Các <a class=\"cloze\" href=\"#mce_temp_url#\">dạng</a> tình thế chiến lược chung marketing</em></strong></p>\r\n<p>+ Tình thế thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">hiện tại</a></p>\r\n<p>+ Tình thế <a class=\"cloze\" href=\"#mce_temp_url#\">nhãn hiệu </a>sản phẩm</p>\r\n<p>+ Tình thế <a class=\"cloze\" href=\"#mce_temp_url#\">cạnh tranh</a></p>\r\n<p>+ Tình thế <a class=\"cloze\" href=\"#mce_temp_url#\">phân phố</a>i-bán hàng</p>\r\n<p>+Tình thế môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">vĩ mô</a></p>\r\n<p>-       <strong> Một số kĩ thuật để phân tích tình thế: SWOT, TOWS,BCG</strong></p>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định mục tiêu chiến lược chung marketing</span></em></strong></li>\r\n</ol>\r\n<p>-       <strong> <em>Các định <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> chiến lược chung marketing</em></strong></p>\r\n<p>+  Định lượng</p>\r\n<p>+ Định tính</p>\r\n<p><strong>-        <em>Yêu cầu:</em></strong></p>\r\n<p>+ Không quá tham vọng</p>\r\n<p>+ có quan hệ chặt chẽ với nhau</p>\r\n<p>+ Có thứ bậc</p>\r\n<p>+ Có tính khả thi và đủ thách thức</p>\r\n<ol>\r\n<li><strong><em>3.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Hoạch định chiến lược chung marketing</span></em></strong></li>\r\n</ol>\r\n<p>-  <strong>      Xác định các <a class=\"cloze\" href=\"#mce_temp_url#\">loại hình</a> chiến lược</strong></p>\r\n<p><em>+ Marketing <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> phân biệt</em> :Một chiến lược chung cho toàn bộ thị trường</p>\r\n<p><em>+ marketing<a class=\"cloze\" href=\"#mce_temp_url#\"> có</a> phân biệt</em>: Nhiều chiến lược marketing vào nhiều đoạn thị trường</p>\r\n<p><em>+ marketing <a class=\"cloze\" href=\"#mce_temp_url#\">tập trung</a></em>: Một chiến lược marketing khai thác một phân đoạn thị trường duy nhất</p>\r\n<ol>\r\n<li><strong><em>4.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Quyết định chiến lược marketing tối ưu</span></em></strong></li>\r\n</ol>\r\n<p>-        <strong>Các nguyên tắc lựa chọn <a class=\"cloze\" href=\"#mce_temp_url#\">phương án</a> chiến lược marketing</strong></p>\r\n<p>        +  Đánh giá về chất lượng: Sự thích ứng; Sự liên kết; Ưu thế từng phần</p>\r\n<p>-        Đánh giá có tính lượng hóa mục tiêu : Thông qua các con số phản ánh kết quả chiến lược dự kiến trên từng phân đoạn thị trường riêng biệt</p>\r\n<p>-        Quyết định phương án chiến lược marketing và chuẩn bị các kiều kiện triển khai chiến lược.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1700', ' Câu 1: Đối tượng nghiên cứu và ý nghĩa học tập môn Đường lối cách mạng của Đảng cộng sản Việt Nam?', null, null, '2', '0', '350', '464', '1551794479', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('1701', 'Nêu khái niệm sản phẩm ?', null, null, '5', '0', '351', '463', '1551795629', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1702', null, null, null, '5', '1701', '351', '463', '1551795629', null, null, null, '<p>-        Khái niệm sản phẩm</p>\r\n<p>Sản phẩm (theo quan điểm marketing) là <a class=\"cloze\" href=\"#mce_temp_url#\">bất kì</a> thứ gì được <a class=\"cloze\" href=\"#mce_temp_url#\">cung ứng</a> chào hàng cho một thị trường để tạo ra sự chú ý, đạt tới việc mua và tiêu dùng nhằm <a class=\"cloze\" href=\"#mce_temp_url#\">thỏa mãn</a> một nhu cầu hoặc một mong muốn nào đó</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1703', 'Sơ đồ cấu trúc 3 lớp của sản phẩm?', null, null, '5', '0', '351', '463', '1551795819', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1704', null, null, null, '5', '1703', '351', '463', '1551795819', null, null, null, '<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1705', 'Nêu các chính sách sản phẩm cơ bản của doanh nghiệp', null, null, '5', '0', '351', '463', '1551795907', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1706', null, null, null, '5', '1705', '351', '463', '1551795907', null, null, null, '<p>-        Chính sách sản phẩm bao gồm <a class=\"cloze\" href=\"#mce_temp_url#\">tổ hợp</a> những định hướng, <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc</a>, chỉ dẫn cho việc đưa ra quyết định <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý </a>sản phẩm của doanh nghiệp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1708', null, null, null, '5', '1707', '351', '463', '1551795907', null, null, null, '<p>-        Chính sách sản phẩm bao gồm <a class=\"cloze\" href=\"#mce_temp_url#\">tổ hợp</a> những định hướng, <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc</a>, chỉ dẫn cho việc đưa ra quyết định <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý </a>sản phẩm của doanh nghiệp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1709', 'Nêu khái niệm chu kì sống của sản phẩm và vẽ sơ đồ chu kì sống của sản phẩm?', null, null, '5', '0', '351', '463', '1551796009', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1710', null, null, null, '5', '1709', '351', '463', '1551796009', null, null, null, '<p>-        Khái niệm chu kì sống của sản phẩm: <a class=\"cloze\" href=\"#mce_temp_url#\">Mô tả </a>động thái của việc tiêu thụ một hàng hóa từ thời điểm <a class=\"cloze\" href=\"#mce_temp_url#\">xuất hiện</a> của nó trên thị trường tới khi <a class=\"cloze\" href=\"#mce_temp_url#\">không bán</a> được chúng nữa (Rút lui khỏi thị trường)</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1711', 'rình bày đặc điểm cơ bản của một trong các giai đoạn của chu kì sống của sản phẩm?( Triển khai, tăng trưởng, bão hòa)?', null, null, '5', '0', '351', '463', '1551796272', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1712', null, null, null, '5', '1711', '351', '463', '1551796272', null, null, null, '<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Triển khai:</a> Thời gian chuẩn bị kĩ lưỡng</span></em></strong></li>\r\n</ol>\r\n<p>-    <strong>    <em>Đặc trưng</em></strong>:</p>\r\n<p>+ Thị trường thẩm định sản phẩm</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\"> Chi phí</a> marketing cao</p>\r\n<p>+Doanh số bán thấpèlợi nhuận thấp</p>\r\n<p>+ Người tiêu dùng <a class=\"cloze\" href=\"#mce_temp_url#\">chưa biêt </a>đến sản phẩm</p>\r\n<p>+ Ít đối thủ cạnh tranh</p>\r\n<p>-      <strong>  <em>Nguyên tắc ứng xử marketing:</em></strong></p>\r\n<p>+ Thông tin, giới thiệu, tăng cường quảng cáo</p>\r\n<p>+Kích thích tiêu dùng</p>\r\n<p>+ Tổ chức hệ thống tiêu thụ</p>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Tăng trưởng</a>: Chi phí marketing giảm</span></em></strong></li>\r\n</ol>\r\n<p>-<strong>        <em>Đặc trưng: </em></strong></p>\r\n<p>+ Tốc độ tăng doanh số <a class=\"cloze\" href=\"#mce_temp_url#\">nhanh</a></p>\r\n<p>+ Phạm vi tiếp cận thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">rộng</a></p>\r\n<p>+Lợi nhuận <a class=\"cloze\" href=\"#mce_temp_url#\">tăng</a></p>\r\n<p>+ Số lượng khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">mở rộng</a></p>\r\n<p><strong>-        <em>Ứng xử marketing:</em></strong></p>\r\n<ul>\r\n<li>Cải tiến, hoàn thiện, <a class=\"cloze\" href=\"#mce_temp_url#\">nâng cao</a> chất lượng sản phẩm</li>\r\n<li>Sử dụng những kênh phân phối mới</li>\r\n<li>Thâm nhập thị trường</li>\r\n</ul>\r\n<p> </p>\r\n<ol>\r\n<li><strong><em>3.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Giai đoạn<a class=\"cloze\" href=\"#mce_temp_url#\"> bão hòa:</a></span></em></strong></li>\r\n</ol>\r\n<p>- <strong>       Đặc trưng:</strong></p>\r\n<ul>\r\n<li>Thời cơ tiêu thụ, doanh số <a class=\"cloze\" href=\"#mce_temp_url#\">cực đại</a>;</li>\r\n<li>Cạnh tranh<a class=\"cloze\" href=\"#mce_temp_url#\"> gay gắt</a>;</li>\r\n<li>Sự co giãn của khá cầu<a class=\"cloze\" href=\"#mce_temp_url#\"> lớn</a></li>\r\n</ul>\r\n<ol>\r\n<li><strong><em>4.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Giai đoạn <a class=\"cloze\" href=\"#mce_temp_url#\">suy thoái:</a></span></em></strong></li>\r\n</ol>\r\n<p>- <strong>       Đặc trưng</strong></p>\r\n<ul>\r\n<li>Doanh số <strong>giảm</strong></li>\r\n<li>Giá <strong>giảm</strong>,</li>\r\n<li>Yêu cầu của người mua khắt khe hơn</li>\r\n</ul>\r\n<p><strong>-        Nguyên nhân:</strong></p>\r\n<p>Sự tiến bộ của KHKT, thị hiếu thay đổi, cạnh tranh gay gắt → sản xuất thừa</p>\r\n<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1713', 'Nêu khái niệm sản phẩm mới theo quan điểm marketing?', null, null, '5', '0', '352', '463', '1551796478', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1714', null, null, null, '5', '1713', '352', '463', '1551796478', null, null, null, '<p>-        Là những sự <a class=\"cloze\" href=\"#mce_temp_url#\">tiến bộ</a> về khoa học- kĩ thuật, về kinh tế và được người tiêu dùng cho là <a class=\"cloze\" href=\"#mce_temp_url#\">mới </a>và đáp ứng được nhu cầu ngày càng tăng của thị trường</p>\r\n<p>+  Sản phẩm mới <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tác</a></p>\r\n<p>+ Sản phẩm mới <a class=\"cloze\" href=\"#mce_temp_url#\">cải tiến</a></p>\r\n<p>+ Sản phẩm mới <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên mẫu</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1715', 'Thiết kế quy trình phái triển sản phẩm mới?', null, null, '5', '0', '352', '463', '1551796544', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1716', null, null, null, '5', '1715', '352', '463', '1551796544', null, null, null, '<p>-        B1:<a class=\"cloze\" href=\"#mce_temp_url#\"> Hình thành</a> ý tưởng</p>\r\n<p>-        B2:<a class=\"cloze\" href=\"#mce_temp_url#\"> Sàng lọc</a> ý tưởng</p>\r\n<p>-        B3: <a class=\"cloze\" href=\"#mce_temp_url#\">Soạn thảo</a> dự án và thẩm định</p>\r\n<p>-        B4: Soạn thảo <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> marketing cho sản phẩm mới</p>\r\n<p>-        B5:<a class=\"cloze\" href=\"#mce_temp_url#\"> Phân tích</a> khả năng sản xuất và tiêu thụ</p>\r\n<p>-        B6: <a class=\"cloze\" href=\"#mce_temp_url#\">Thiết kế</a> sản phẩm mới</p>\r\n<p>-        B7: <a class=\"cloze\" href=\"#mce_temp_url#\">Thử nghiệm</a> sản phẩm mới trên thị trường (Bán thử)</p>\r\n<p>-        B8: <a class=\"cloze\" href=\"#mce_temp_url#\">Sản xuất</a> hàng loạt và tung ra thị trường (thương mại hóa sản phẩm)</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1717', 'Nêu khái niệm sản phẩm, tuyến sản phẩm?', null, null, '5', '0', '352', '463', '1551796613', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1718', null, null, null, '5', '1717', '352', '463', '1551796613', null, null, null, '<p><strong>Khái niệm tuyết sản phẩm</strong>: Là <a class=\"cloze\" href=\"#mce_temp_url#\">nhóm</a> các sản phẩm có quan hệ chặt chẽ với nhau, do đó thực hiện một chức năng <a class=\"cloze\" href=\"#mce_temp_url#\">tương tự</a> hoặc được bán cho cùng một nhóm <a class=\"cloze\" href=\"#mce_temp_url#\">người tiêu dùng</a> hoặc qua cùng một <a class=\"cloze\" href=\"#mce_temp_url#\">kênh</a> hoặc khung giá nhất định.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1719', 'Nêu nội dung của chính sách sản phẩm?', null, null, '5', '0', '352', '463', '1551796747', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1720', null, null, null, '5', '1719', '352', '463', '1551796747', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Nội dung của chính sách sản phẩm:</span></em></strong></p>\r\n<p><strong>+  Mục tiêu</strong></p>\r\n<p>               <a class=\"cloze\" href=\"#mce_temp_url#\">-Kéo dà</a>i chu kì sống của sản phẩm</p>\r\n<p>               -<a class=\"cloze\" href=\"#mce_temp_url#\">Nâng ca</a>o khả năng bán</p>\r\n<p>               - Đảm bảo điều kiện sinh lời</p>\r\n<p>               - Đảm bảo nâng cao uy tín, sức cạnh tranh</p>\r\n<p>+ Vai trò:</p>\r\n<p>               -Nền tảng, xương sống cho chiến lược marketing</p>\r\n<p>               -Giúp<a class=\"cloze\" href=\"#mce_temp_url#\"> thực hiện</a> mục tiêu của chiến lược marketing</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1721', 'Trình bày có ví dụ nội dung “ Cơ cấu và chủng loại sản phẩm;Nhãn hiệu và bao gói sản phẩm; Chính sách dịch vụ hỗ trợ”?', null, null, '5', '0', '352', '463', '1551796915', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1722', null, null, null, '5', '1721', '352', '463', '1551796915', null, null, null, '<p>-        <strong><em><span style=\"text-decoration: underline;\">Chính sách cơ cấu và chủng loại sản phẩm, chất lượng sản phẩm</span></em></strong></p>\r\n<p><strong>+ <em>Cơ cấu chủng loại</em></strong></p>\r\n<p>    <a class=\"cloze\" href=\"#mce_temp_url#\"> Thiết lập</a> cơ cấu chủng loại: Xác định chiều rộng, chiều sâu, mặt hàng chủ đạo, mặt hàng bổ sung, mức độ liên kết</p>\r\n<p>    <a class=\"cloze\" href=\"#mce_temp_url#\"> Hạn chế </a>chủng loại: Loại bỏ sản phẩm không hiệu quả==&gt;chuyên môn hóa</p>\r\n<p>   <a class=\"cloze\" href=\"#mce_temp_url#\">  Biến thể </a>chủng loại: Thay đổi thể thức thỏa mãn nhu cầu</p>\r\n<p><strong><em>+ Chất lượng sản phẩm:</em></strong></p>\r\n<p>     Biến đổi các đặc tính chất lượng sản phẩm theo hướng <a class=\"cloze\" href=\"#mce_temp_url#\">tiếp cận</a> và thích ứng với<a class=\"cloze\" href=\"#mce_temp_url#\"> nhu cầu</a> thị trường</p>\r\n<ul>\r\n<li>Tạo ưu thế:Đặc trưng, truyền thống chất lượng</li>\r\n<li>Mở rộng giải chất lượng sản phẩm</li>\r\n</ul>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Chính sách nhãn hiệu, bao gói</span></em></strong>:</p>\r\n<p><strong><em>+ Chính sách nhãn hiệu<span style=\"text-decoration: underline;\">:</span></em></strong></p>\r\n<ul>\r\n<li>Có gắn nhãn hiệu hay không</li>\r\n<li>Đầu tư cho nhãn hiệu</li>\r\n<li>Đặt tên cho nhãn hiệu</li>\r\n<li>Chiến lược nhãn hiệu</li>\r\n<li>Tái định vị nhãn hiệu</li>\r\n</ul>\r\n<p><strong><em>+ Chính sách bao gói</em></strong></p>\r\n<ul>\r\n<li>Quyết định về kiểu dáng,kích thước, chất lượgn</li>\r\n<li>Quyết đinh về thông tin trên bao bì</li>\r\n<li>Các quyết định về các yếu tố khác</li>\r\n</ul>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Chính sách dịch vụ hỗ trợ:</span></em></strong></p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Xác định</a> các yếu tố dịch vụ mà khách hàng yêu cầu và<a class=\"cloze\" href=\"#mce_temp_url#\"> khả năng</a> đáp ứng của doanh nghiệp</p>\r\n<p>+ Mức <a class=\"cloze\" href=\"#mce_temp_url#\">chất lượng</a> dịch vụ mà doanh nghiệp cung ứng cho khách hàng so với <a class=\"cloze\" href=\"#mce_temp_url#\">đối thủ</a> cạnh tranh</p>\r\n<p>+Quyết định <a class=\"cloze\" href=\"#mce_temp_url#\">chi phí</a> mà khách hàng phải trả</p>\r\n<p>+ Hình thức cung cấp dịch vụ</p>\r\n<p> </p>\r\n<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1723', 'Khái niệm giá kinh doanh theo quan điểm marketing?', null, null, '5', '0', '355', '463', '1551797027', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1724', null, null, null, '5', '1723', '355', '463', '1551797027', null, null, null, '<p>-        Số tiền phải<a class=\"cloze\" href=\"#mce_temp_url#\"> trả</a> cho hàng hóa hoặc dịch vụ hay <a class=\"cloze\" href=\"#mce_temp_url#\">tổng giá</a> trị lợi ích của việc<a class=\"cloze\" href=\"#mce_temp_url#\"> sử dụng</a> hàng hóa/dịch vụ đó mà khách hàng quy đổi được.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1725', 'Nêu các bước trong quy trình xác lập giá kinh doanh? Trình bày nội dung một trong các bước xác lập giá kinh doanh ( Tính toán và phân tích chi phí)', null, null, '5', '0', '355', '463', '1551797497', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1726', null, null, null, '5', '1725', '355', '463', '1551797497', null, null, null, '<p>Các bước trong xác lập giá kinh doanh:</p>\r\n<p><strong><em><span>B1: Xác định mục tiêu định giá:</span></em></strong></p>\r\n<p><strong><em><span> <strong><em><span>B2: Phân tích nhu cầu</span></em></strong>:</span></em></strong></p>\r\n<p><strong><em><span> <strong><em><span>B3: Tính toán và phân tích chi phí</span></em></strong></span></em></strong></p>\r\n<p><strong><em><span><strong><em><span> <strong><em><span>B4: Phân tích giá và chào hàng của đối thủ</span></em></strong></span></em></strong></span></em></strong></p>\r\n<p><strong><em><span><strong><em><span><strong><em><span><strong><em><span>B5: Lựa chọn phương pháp định giá</span></em></strong></span></em></strong></span></em></strong></span></em></strong></p>\r\n<p><strong><em><span><strong><em><span><strong><em><span><strong><em><span><strong><em><span>B6: Lựa chọn mức giá cuối cùng</span></em></strong></span></em></strong></span></em></strong></span></em></strong></span></em></strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B1: Xác định mục tiêu định giá:</span></em></strong></p>\r\n<p><strong><em>+ Căn cứ:</em></strong></p>\r\n<p>               -<a class=\"cloze\" href=\"#mce_temp_url#\">Mục tiêu</a> của doanh nghiệp</p>\r\n<p>               -<a class=\"cloze\" href=\"#mce_temp_url#\">Thích ứng</a> với thị trường mục tiêu và dịch vụ</p>\r\n<p>               -Chiến lược marketing mix</p>\r\n<p><strong>+<em>Các mục tiêu thường gặp</em>:</strong></p>\r\n<p>          -Tồn tại</p>\r\n<p>          -<a class=\"cloze\" href=\"#mce_temp_url#\">Tối đa</a> hóa lợi nhuận, thị phần</p>\r\n<p>          -<a class=\"cloze\" href=\"#mce_temp_url#\">Dẫn đạo </a>thị trường về chất lượng</p>\r\n<p>          -Mục tiêu khác</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B2: Phân tích nhu cầu</span></em></strong>:</p>\r\n<p><strong><em>+ Tính chất thị trường:</em></strong></p>\r\n<p>               -Cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">thuần tuý</a></p>\r\n<p>               -Cạnh tranh độc quyền</p>\r\n<p>               -Cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">nhóm </a>độc quyền</p>\r\n<p><strong><em>+ Nhận thức của người tiêu dùng</em></strong></p>\r\n<p>               -Về giá và giá trị</p>\r\n<p>               -Ảnh hưởng của <a class=\"cloze\" href=\"#mce_temp_url#\">nhận thứ</a>c đến quyết định mua</p>\r\n<p>               -Về các biến khác của marketing mix</p>\r\n<p><strong><em>+ Mối quan hệ giữa giá và nhu cầu</em>:</strong></p>\r\n<p>               -Mối quan hệ giữa nhu cầu với giá: Sản phẩm thông thường và uy tín</p>\r\n<p>               -Biểu hiện: <a class=\"cloze\" href=\"#mce_temp_url#\">Độ đàn hồi</a> của cầu so với giá</p>\r\n<p><strong><em>+Các yếu tố khác:</em></strong></p>\r\n<p>               -Điều kiện kinh tế</p>\r\n<p>               -Người bán lại</p>\r\n<p>               -Chính phủ</p>\r\n<p><strong>+ Xác định quy mô nhu cầu</strong>:<a class=\"cloze\" href=\"#mce_temp_url#\"> Số lượng </a>người mua tiềm năng, mức t<a class=\"cloze\" href=\"#mce_temp_url#\">iêu thụ</a> mong muốn, sức mua của người tiêu dùng, giá dự kiến…</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B3: Tính toán và phân tích chi phí</span></em></strong></p>\r\n<p><strong><em>+ Tính toán chi phí:</em></strong> Chi phí cố định và chi phí biến đổi</p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\"><em>+Phân tích hòa vốn</em></a>:</p>\r\n<p> </p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B4: Phân tích giá và chào hàng của đối thủ:</span></em></strong></p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Mức giá</a> và phản ứng của đối thủ cạnh tranh</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Tương qua</a>n giá-chất lượng sản phẩm của đôi thủ cạnh tranh</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B5: Lựa chọn phương pháp định giá</span></em></strong></p>\r\n<p><em>+ Dựa trên chi phí:</em></p>\r\n<p><em>+Dựa trên người mua: </em></p>\r\n<p>      Khách hàng=&gt;giá trị=&gt;Giá bán=&gt;Chi phí=&gt;Sản phẩm</p>\r\n<p>       Nhận thức của người mua về giá trị</p>\r\n<p>        Giá tương thích-giá trị cảm nhận được từ marketing-mix của doanh nghiệp</p>\r\n<p>   <span style=\"text-decoration: underline;\"><em>Các công việc cần thiết để tiến hành</em></span>:</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xây dựng</a> khái niệm sản phẩm cho thị trường mục tiêu với chất lượng, giá cả dự kiến cụ thể.</li>\r\n<li>Dự kiến <a class=\"cloze\" href=\"#mce_temp_url#\">khối lượng</a> bán mong muốn theo <a class=\"cloze\" href=\"#mce_temp_url#\">mức giá</a> bán dự kiến</li>\r\n<li>Dự kiến <a class=\"cloze\" href=\"#mce_temp_url#\">công suất </a>của nhà máy, vốn và chi phí sản xuất</li>\r\n<li>Xác định<a class=\"cloze\" href=\"#mce_temp_url#\"> lợi nhuận</a> theo mức giá dự kiến</li>\r\n</ul>\r\n<p><strong><em>+ Dựa trên cạnh tranh:</em></strong></p>\r\n<p>               <span style=\"text-decoration: underline;\">Tỉ lệ hiện thời:</span> Đặt giá <a class=\"cloze\" href=\"#mce_temp_url#\">ngang bằng</a> với giá sản phẩm cạnh tranh, định giá cao hơn giá sản phẩm cạnh tranh; định giá <a class=\"cloze\" href=\"#mce_temp_url#\">thấp hơn</a> giá sản phẩm cạnh tranh.</p>\r\n<p>               <span style=\"text-decoration: underline;\">Định giá đấu thầu bí mật-</span>Định giá thấp hơn: Các doanh nghiệp tham gia đấu thầu định giá trên cơ sở <a class=\"cloze\" href=\"#mce_temp_url#\">dự đoán</a> mức giá đấu thầu của đối thủ cạnh tranh. Doanh nghiệp muốn thắng thầu phải <a class=\"cloze\" href=\"#mce_temp_url#\">chấp nhận</a> mức giá thấp hơn so với đối thủ cạnh tranh.</p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">B6: Lựa chọn mức giá cuối cùng:</span></em></strong></p>\r\n<p><em>Dựa trên</em>: Lợi nhuận; nhu cầu; định giá tâm lý…</p>\r\n<p><em>Thích ứng mức giá</em></p>\r\n<p>     <span style=\"text-decoration: underline;\">Định giá <a class=\"cloze\" href=\"#mce_temp_url#\">địa lý</a>:</span> Phân biệt khách hàng ở các khu vực và quốc gia khác nhau</p>\r\n<p>Hình thức chi trả: tiền mặt, trao đổi</p>\r\n<p>     <span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Chiết khấu</a> và trợ cấp</span>: Giảm giá tiền mặt, số lượng</p>\r\n<p>     <span style=\"text-decoration: underline;\">Định giá <a class=\"cloze\" href=\"#mce_temp_url#\">phân biệt</a>:</span> Địa điểm, thời gian..</p>', null, '58', null, null, null, null, null);
