INSERT INTO `question` VALUES ('1752', null, null, null, '5', '1751', '333', '450', '1551840813', null, '', null, ' Giá trị cực tiểu của:<p><img alt=\"z = {x^2} + 2{\\rm{x}}y + 2{y^2} + x - y + 1\" src=\"http://latex.codecogs.com/gif.latex?z = {x^2} + 2{\\rm{x}}y + 2{y^2} + x - y + 1\" /></p>    là<p><img alt=\"{f_{ct}} = \\frac{{ - 1}}{4}\" src=\"http://latex.codecogs.com/gif.latex?{f_{ct}} = \\frac{{ - 1}}{4}\" /></p> tại điểm    <a class=\"clozetip\" title=\"\" href=\"#\">(-3/2;1)</a>', null, '51', null, null, null, null, null);
