INSERT INTO `question` VALUES ('1758', null, null, null, '5', '1757', '334', '450', '1551840836', null, '', null, ' Giá trị cực đại của:<p><img alt=\"z = {e^{xy}};x + y = 1\" src=\"http://latex.codecogs.com/gif.latex?z = {e^{xy}};x + y = 1\" /></p>    là<p><img alt=\"{f_{c{\\rm{d}}}} = {e^{\\frac{1}{4}}}\" src=\"http://latex.codecogs.com/gif.latex?{f_{c{\\rm{d}}}} = {e^{\\frac{1}{4}}}\" /></p>  tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">(3/2;-1/2)</a>', null, '51', null, null, null, null, null);
