INSERT INTO `question` VALUES ('1762', null, null, null, '5', '1761', '334', '450', '1551840837', null, '', null, ' Giá trị cực tiểu của:<p><img alt=\"z = 6 - 4{\\rm{x}} - 3y;{x^2} + {y^2} = 1\" src=\"http://latex.codecogs.com/gif.latex?z = 6 - 4{\\rm{x}} - 3y;{x^2} + {y^2} = 1\" /></p>    là___ tại điểm ___   <a class=\"clozetip\" title=\"\" href=\"#\">1,(4/5;3/5)</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1764', null, null, null, '5', '1763', '335', '450', '1551840866', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\" /></p> là<p><img alt=\"\\sqrt {{{(1 + {y^2})}^3}}  = \\_\\_.{e^x} - \\_\\_.{e^x} + C\" src=\"http://latex.codecogs.com/gif.latex?\\sqrt {{{(1 + {y^2})}^3}}  = \\_\\_.{e^x} - \\_\\_.{e^x} + C\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">3x,3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1766', null, null, null, '5', '1765', '335', '450', '1551840866', null, '', null, ' nghiệm tổng quát của:<p><img alt=\"\\frac{{dy}}{{d{\\rm{x}}}} = 2 + 2y + x + xy\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = 2 + 2y + x + xy\" /></p>    là<p><img alt=\"y =  - 1;y = C.{e^{\\frac{{{x^2}}}{2} + \\_\\_\\_}} - 1\" src=\"http://latex.codecogs.com/gif.latex?y =  - 1;y = C.{e^{\\frac{{{x^2}}}{2} + \\_\\_\\_}} - 1\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">2x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1768', null, null, null, '5', '1767', '335', '450', '1551840866', null, '', null, 'Tích phân tổng quát của:<p><img alt=\"\\frac{{dy}}{{d{\\rm{x}}}} = 1 + {y^2}\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = 1 + {y^2}\" /></p> là  <p><img alt=\"\\[{\\mathop{\\rm arctany}\\nolimits}  = \\_\\_\\_ + C\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[{\\mathop{\\rm arctany}\\nolimits}  = \\_\\_\\_ + C\\]\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1770', null, null, null, '5', '1769', '335', '450', '1551840866', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' = {e^{x + y}} + {e^{x - y}}\" src=\"http://latex.codecogs.com/gif.latex?y\' = {e^{x + y}} + {e^{x - y}}\" /></p>   là<p><img alt=\"\\[\\arctan \\_\\_{\\_^y} = \\_\\_{\\_^x} + C\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[\\arctan \\_\\_{\\_^y} = \\_\\_{\\_^x} + C\\]\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">e</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1772', null, null, null, '5', '1771', '335', '450', '1551840866', null, '', null, ' nghiệm tổng quát của:<p><img alt=\"y\' = 4{{\\rm{x}}^3}y\" src=\"http://latex.codecogs.com/gif.latex?y\' = 4{{\\rm{x}}^3}y\" /></p>   là<p><img alt=\"y = 0;y = C.{e^{\\_\\_\\_}}\" src=\"http://latex.codecogs.com/gif.latex?y = 0;y = C.{e^{\\_\\_\\_}}\" /></p>   <a class=\"clozetip\" title=\"\" href=\"#\">x^4</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1774', null, null, null, '5', '1773', '335', '450', '1551840866', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\tan y{\\rm{dx}} - x\\ln {\\rm{xd}}y = 0\" src=\"http://latex.codecogs.com/gif.latex?\\tan y{\\rm{dx}} - x\\ln {\\rm{xd}}y = 0\" /></p>  <p><img alt=\"\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\ln {\\rm{x}}) = C\" src=\"http://latex.codecogs.com/gif.latex?\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\ln {\\rm{x}}) = C\" /></p>  là<p><img alt=\"\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\_\\_\\_) = C\" src=\"http://latex.codecogs.com/gif.latex?\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\_\\_\\_) = C\" /></p>.  <a class=\"clozetip\" title=\"\" href=\"#\">lnx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1776', null, null, null, '5', '1775', '335', '450', '1551840866', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\frac{{4 + {y^2}}}{{\\sqrt {{x^2} + 4{\\rm{x}} + 13} }} = \\frac{{3y + 2}}{{x + 1}}y\'\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{4 + {y^2}}}{{\\sqrt {{x^2} + 4{\\rm{x}} + 13} }} = \\frac{{3y + 2}}{{x + 1}}y\'\" /></p> là<p><img alt=\"\\_\\_\\_\\ln ({y^2} + 4) + \\arctan \\_\\_\\_ = \\sqrt {{{(x + 2)}^2} + 9}  - \\ln \\left| {x + 2 + \\sqrt {{{(x + 2)}^2} + 9} } \\right| + C\" src=\"http://latex.codecogs.com/gif.latex?\\_\\_\\_\\ln ({y^2} + 4) + \\arctan \\_\\_\\_ = \\sqrt {{{(x + 2)}^2} + 9}  - \\ln \\left| {x + 2 + \\sqrt {{{(x + 2)}^2} + 9} } \\right| + C\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">3/2,y/2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1778', null, null, null, '5', '1777', '335', '450', '1551840866', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\frac{{x{\\rm{dx}}}}{{{x^2} + 1}} + (y + 1)dy = 0\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{x{\\rm{dx}}}}{{{x^2} + 1}} + (y + 1)dy = 0\" /></p> là<p><img alt=\"\\frac{{{y^2}}}{{\\_\\_\\_}} + y = \\frac{{ - 1}}{{\\_\\_\\_}}\\ln ({x^2} + 1) + C\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{{y^2}}}{{\\_\\_\\_}} + y = \\frac{{ - 1}}{{\\_\\_\\_}}\\ln ({x^2} + 1) + C\" /></p>   <a class=\"clozetip\" title=\"\" href=\"#\">2,2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1780', null, null, null, '5', '1779', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\left( {{x^2} + 2{\\rm{x}}y - {y^2}} \\right)d{\\rm{x}} + \\left( {{y^2} + 2{\\rm{x}}y - {x^2}} \\right)dy = 0\" src=\"http://latex.codecogs.com/gif.latex?\\left( {{x^2} + 2{\\rm{x}}y - {y^2}} \\right)d{\\rm{x}} + \\left( {{y^2} + 2{\\rm{x}}y - {x^2}} \\right)dy = 0\" /></p>   là<p><img alt=\"\\ln \\left[ {\\left( {\\_\\_\\_} \\right)\\left( {{x^2} + 4{\\rm{x}}y + {y^2}} \\right)} \\right] = C\" src=\"http://latex.codecogs.com/gif.latex?\\ln \\left[ {\\left( {\\_\\_\\_} \\right)\\left( {{x^2} + 4{\\rm{x}}y + {y^2}} \\right)} \\right] = C\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">x-y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1782', null, null, null, '5', '1781', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' = \\frac{{2{\\rm{x}}y}}{{{x^2} - {y^2}}}\" src=\"http://latex.codecogs.com/gif.latex?y\' = \\frac{{2{\\rm{x}}y}}{{{x^2} - {y^2}}}\" /></p>    là<p><img alt=\"y = C.\\left( {{x^{\\_\\_\\_}} + {y^{\\_\\_\\_}}} \\right)\" src=\"http://latex.codecogs.com/gif.latex?y = C.\\left( {{x^{\\_\\_\\_}} + {y^{\\_\\_\\_}}} \\right)\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">2,2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1784', null, null, null, '5', '1783', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' = \" src=\"http://latex.codecogs.com/gif.latex?y\' = \" /></p>y\' = \\frac{y}{x} + \\sin \\frac{y}{x}<p><img alt=\"    là\" src=\"http://latex.codecogs.com/gif.latex?    là\" /></p>\\[\\tan \\_\\_\\_ = C{\\rm{x}}\\]$  <a class=\"clozetip\" title=\"\" href=\"#\">y/2x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1786', null, null, null, '5', '1785', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\\left( {x - y} \\right)d{\\rm{x}} - {x^2}y = 0\" src=\"http://latex.codecogs.com/gif.latex?y\\left( {x - y} \\right)d{\\rm{x}} - {x^2}y = 0\" /></p>  là<p><img alt=\"\\[y = \\frac{{\\_\\_\\_}}{{\\ln \\left| {\\_\\_\\_} \\right| + C}}\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[y = \\frac{{\\_\\_\\_}}{{\\ln \\left| {\\_\\_\\_} \\right| + C}}\\]\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">-x,x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1788', null, null, null, '5', '1787', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\left( {{x^2} - 3{y^2}} \\right)d{\\rm{x}} + 2{\\rm{x}}y{\\rm{d}}y = 0\" src=\"http://latex.codecogs.com/gif.latex?\\left( {{x^2} - 3{y^2}} \\right)d{\\rm{x}} + 2{\\rm{x}}y{\\rm{d}}y = 0\" /></p>  là<p><img alt=\"y = C\\_\\_\\_ + \\_\\_\\_\" src=\"http://latex.codecogs.com/gif.latex?y = C\\_\\_\\_ + \\_\\_\\_\" /></p>   <a class=\"clozetip\" title=\"\" href=\"#\">x^3,x^2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1790', null, null, null, '5', '1789', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"xy\' + x\\tan \\frac{y}{x} - y = 0\" src=\"http://latex.codecogs.com/gif.latex?xy\' + x\\tan \\frac{y}{x} - y = 0\" /></p>   là<p><img alt=\"{\\sin ^2}\\_\\_\\_ = \\frac{1}{{\\ln \\left| x \\right| - C}}\" src=\"http://latex.codecogs.com/gif.latex?{\\sin ^2}\\_\\_\\_ = \\frac{1}{{\\ln \\left| x \\right| - C}}\" /></p>    <a class=\"clozetip\" title=\"\" href=\"#\">y/x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1792', null, null, null, '5', '1791', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\left( {{x^2}{y^2} - 1} \\right)dy + 2{\\rm{x}}{y^3}d{\\rm{x}} = 0\" src=\"http://latex.codecogs.com/gif.latex?\\left( {{x^2}{y^2} - 1} \\right)dy + 2{\\rm{x}}{y^3}d{\\rm{x}} = 0\" /></p> là<p><img alt=\"Cy = 1 + {(\\_\\_\\_)^2}\" src=\"http://latex.codecogs.com/gif.latex?Cy = 1 + {(\\_\\_\\_)^2}\" /></p>     <a class=\"clozetip\" title=\"đặt  y=1/z thế vào phương trình đã cho rồi đặt   z=ux\" href=\"#\">xy</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1794', null, null, null, '5', '1793', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\left( {x - y + 4} \\right)dy + \\left( {x + y - 2} \\right)d{\\rm{x}} = 0\" src=\"http://latex.codecogs.com/gif.latex?\\left( {x - y + 4} \\right)dy + \\left( {x + y - 2} \\right)d{\\rm{x}} = 0\" /></p>  là<p><img alt=\"{x^2} + 2{\\rm{x}}y - {y^2} - \\_\\_\\_ + \\_\\_\\_ + C = 0\" src=\"http://latex.codecogs.com/gif.latex?{x^2} + 2{\\rm{x}}y - {y^2} - \\_\\_\\_ + \\_\\_\\_ + C = 0\" /></p>      <a class=\"clozetip\" title=\"\" href=\"#\">4x,8y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1796', null, null, null, '5', '1795', '359', '450', '1551841046', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{{x^2} + {y^2}}}{{2{\\rm{x}}y}}\" src=\"http://latex.codecogs.com/gif.latex?\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{{x^2} + {y^2}}}{{2{\\rm{x}}y}}\" /></p>    là<p><img alt=\"{y^2} = {x^2} - C\\_\\_\\_\" src=\"http://latex.codecogs.com/gif.latex?{y^2} = {x^2} - C\\_\\_\\_\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1798', null, null, null, '5', '1797', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\[xy\' - y = {x^2}\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[xy\' - y = {x^2}\\]\" /></p> là<p><img alt=\"y = C{{\\rm{x}}^3} + \\_\\_\\_\" src=\"http://latex.codecogs.com/gif.latex?y = C{{\\rm{x}}^3} + \\_\\_\\_\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">1/3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1800', null, null, null, '5', '1799', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' - \\frac{y}{x} = {x^2}\" src=\"http://latex.codecogs.com/gif.latex?y\' - \\frac{y}{x} = {x^2}\" /></p>  là<p><img alt=\"y = \\frac{{{x^3}}}{{\\_\\_\\_}} + C{\\rm{x}}\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{{x^3}}}{{\\_\\_\\_}} + C{\\rm{x}}\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1802', null, null, null, '5', '1801', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' - y\\cot {\\rm{x}} = 2{\\rm{x}}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" src=\"http://latex.codecogs.com/gif.latex?y\' - y\\cot {\\rm{x}} = 2{\\rm{x}}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" /></p> là<p><img alt=\"y = {x^2}\\_\\_\\_ + C\\_\\_\\_\" src=\"http://latex.codecogs.com/gif.latex?y = {x^2}\\_\\_\\_ + C\\_\\_\\_\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">sinx,sinx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1804', null, null, null, '5', '1803', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' - \\frac{y}{x} = x{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" src=\"http://latex.codecogs.com/gif.latex?y\' - \\frac{y}{x} = x{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" /></p> là<p><img alt=\"y =  - x\\_\\_\\_{\\rm{ + Cx}}\" src=\"http://latex.codecogs.com/gif.latex?y =  - x\\_\\_\\_{\\rm{ + Cx}}\" /></p>   <a class=\"clozetip\" title=\"\" href=\"#\">cosx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1806', null, null, null, '5', '1805', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' + 2y = 2.{e^x}\" src=\"http://latex.codecogs.com/gif.latex?y\' + 2y = 2.{e^x}\" /></p>  là<p><img alt=\"y = \\_\\_\\_{e^x}\" src=\"http://latex.codecogs.com/gif.latex?y = \\_\\_\\_{e^x}\" /></p>    <a class=\"clozetip\" title=\"\" href=\"#\">2/3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1808', null, null, null, '5', '1807', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"xy\' + y\\ln y = y\" src=\"http://latex.codecogs.com/gif.latex?xy\' + y\\ln y = y\" /></p>   là<p><img alt=\"y = {e^{1 + \\frac{C}{{\\_\\_\\_}}}}\" src=\"http://latex.codecogs.com/gif.latex?y = {e^{1 + \\frac{C}{{\\_\\_\\_}}}}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1810', null, null, null, '5', '1809', '360', '450', '1551841098', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' + y\\tan x = c{\\rm{o}}{{\\rm{s}}^2}x\" src=\"http://latex.codecogs.com/gif.latex?y\' + y\\tan x = c{\\rm{o}}{{\\rm{s}}^2}x\" /></p>    là<p><img alt=\"y = \\sin {\\rm{x\\_\\_\\_ + }}C.\\_\\_\\_\" src=\"http://latex.codecogs.com/gif.latex?y = \\sin {\\rm{x\\_\\_\\_ + }}C.\\_\\_\\_\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">cosx,cosx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1812', null, null, null, '5', '1811', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' + \\frac{y}{x} = x\\sqrt y\" src=\"http://latex.codecogs.com/gif.latex?y\' + \\frac{y}{x} = x\\sqrt y\" /></p>  là<p><img alt=\"y = {\\left( {\\frac{{{x^2}}}{{\\_\\_\\_}} + \\frac{C}{{\\sqrt x }}} \\right)^2}\" src=\"http://latex.codecogs.com/gif.latex?y = {\\left( {\\frac{{{x^2}}}{{\\_\\_\\_}} + \\frac{C}{{\\sqrt x }}} \\right)^2}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">5</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1814', null, null, null, '5', '1813', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' + \\frac{y}{x} = x{y^2}.\\left( {\\frac{{{e^x}}}{{{e^x} + 1}}} \\right)\" src=\"http://latex.codecogs.com/gif.latex?y\' + \\frac{y}{x} = x{y^2}.\\left( {\\frac{{{e^x}}}{{{e^x} + 1}}} \\right)\" /></p>   là<p><img alt=\"y = \\frac{1}{{C - \\_\\_\\_\\ln (1 + {e^x})}}\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{1}{{C - \\_\\_\\_\\ln (1 + {e^x})}}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1816', null, null, null, '5', '1815', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"{x^2}y\' = y\\left( {x + y} \\right)\" src=\"http://latex.codecogs.com/gif.latex?{x^2}y\' = y\\left( {x + y} \\right)\" /></p>   là<p><img alt=\"y = \\frac{{\\_\\_\\_}}{{C - \\ln \\left| x \\right|}}\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{\\_\\_\\_}}{{C - \\ln \\left| x \\right|}}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1818', null, null, null, '5', '1817', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y{\\rm{dx}} + 2{\\rm{xd}}y = \\frac{{2y\\sqrt x }}{{{\\rm{co}}{{\\rm{s}}^2}y}}dy;y(0) = \\pi \" src=\"http://latex.codecogs.com/gif.latex?y{\\rm{dx}} + 2{\\rm{xd}}y = \\frac{{2y\\sqrt x }}{{{\\rm{co}}{{\\rm{s}}^2}y}}dy;y(0) = \\pi \" /></p>   là<p><img alt=\"\\[x = {(y\\_\\_\\_ - {\\left( {\\_\\_\\_} \\right)^2})^2}\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[x = {(y\\_\\_\\_ - {\\left( {\\_\\_\\_} \\right)^2})^2}\\]\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">tany,tany</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1820', null, null, null, '5', '1819', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"xy{\\rm{d}}y = \\left( {{y^2} + x} \\right)d{\\rm{x}}\" src=\"http://latex.codecogs.com/gif.latex?xy{\\rm{d}}y = \\left( {{y^2} + x} \\right)d{\\rm{x}}\" /></p>    là<p><img alt=\"{y^2} = C{{\\rm{x}}^2} - \\frac{{\\_\\_\\_}}{{\\_\\_\\_x}}\" src=\"http://latex.codecogs.com/gif.latex?{y^2} = C{{\\rm{x}}^2} - \\frac{{\\_\\_\\_}}{{\\_\\_\\_x}}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">2,3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1822', null, null, null, '5', '1821', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"xy\' - 2{{\\rm{x}}^2}\\sqrt y  = 4y\" src=\"http://latex.codecogs.com/gif.latex?xy\' - 2{{\\rm{x}}^2}\\sqrt y  = 4y\" /></p>   là <p><img alt=\"y = \\_\\_\\_{(C - \\ln \\left| x \\right|)^2}\" src=\"http://latex.codecogs.com/gif.latex?y = \\_\\_\\_{(C - \\ln \\left| x \\right|)^2}\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">x^4</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1824', null, null, null, '5', '1823', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"2{{\\rm{x}}^2}y\' = {y^2}\\left( {2{\\rm{x}}y - y} \\right)\" src=\"http://latex.codecogs.com/gif.latex?2{{\\rm{x}}^2}y\' = {y^2}\\left( {2{\\rm{x}}y - y} \\right)\" /></p>    là <p><img alt=\"{y^2} = x{\\mathop{\\rm lnC}\\nolimits} \\_\\_{\\_^2}\" src=\"http://latex.codecogs.com/gif.latex?{y^2} = x{\\mathop{\\rm lnC}\\nolimits} \\_\\_{\\_^2}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1826', null, null, null, '5', '1825', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\' - xy =  - x{y^3}\" src=\"http://latex.codecogs.com/gif.latex?y\' - xy =  - x{y^3}\" /></p>    là <p><img alt=\"{y^2} = \\frac{{{e^{\\_\\_\\_}}}}{{{e^{\\_\\_\\_}} + C}}\" src=\"http://latex.codecogs.com/gif.latex?{y^2} = \\frac{{{e^{\\_\\_\\_}}}}{{{e^{\\_\\_\\_}} + C}}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">x^2,x^2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1828', null, null, null, '5', '1827', '361', '450', '1551841121', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"xy\' + y = {y^2}\\ln {\\rm{x}}\" src=\"http://latex.codecogs.com/gif.latex?xy\' + y = {y^2}\\ln {\\rm{x}}\" /></p>     là <p><img alt=\"y = \\frac{1}{{C{\\rm{x}} + \\_\\_\\_ + 1}}\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{1}{{C{\\rm{x}} + \\_\\_\\_ + 1}}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">lnx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1830', null, null, null, '5', '1829', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = {\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = {\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" /></p>   là<p><img alt=\"y = C{\\rm{x}} + \\_\\_\\_ + D\" src=\"http://latex.codecogs.com/gif.latex?y = C{\\rm{x}} + \\_\\_\\_ + D\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">sinx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1832', null, null, null, '5', '1831', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = {x^2}\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = {x^2}\" /></p>    là<p><img alt=\"y = \\frac{{{x^4}}}{{\\_\\_\\_}} + C{\\rm{x}} + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{{x^4}}}{{\\_\\_\\_}} + C{\\rm{x}} + D\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">12</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1834', null, null, null, '5', '1833', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = {e^x} + x + 1\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = {e^x} + x + 1\" /></p>     là<p><img alt=\"y = {e^x} + \\frac{{{x^3}}}{{\\_\\_\\_}} + \\frac{{{x^2}}}{{\\_\\_\\_}} + C{\\rm{x}} + D\" src=\"http://latex.codecogs.com/gif.latex?y = {e^x} + \\frac{{{x^3}}}{{\\_\\_\\_}} + \\frac{{{x^2}}}{{\\_\\_\\_}} + C{\\rm{x}} + D\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">6,2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1836', null, null, null, '5', '1835', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = \\frac{{3{{\\rm{x}}^2} - 6}}{{{x^2} + 1}}\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = \\frac{{3{{\\rm{x}}^2} - 6}}{{{x^2} + 1}}\" /></p>     là<p><img alt=\"\\[y = \\_\\_\\_{x^2}{\\rm{ + Cx  - 9}}\\arctan x + \\_\\_\\_\\ln \\left( {{x^2} + 1} \\right) + D\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[y = \\_\\_\\_{x^2}{\\rm{ + Cx  - 9}}\\arctan x + \\_\\_\\_\\ln \\left( {{x^2} + 1} \\right) + D\\]\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">3/2,9/2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1838', null, null, null, '5', '1837', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = {e^x}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = {e^x}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\" /></p>      là<p><img alt=\"y = C{\\rm{x}} - \\frac{{{e^x}{\\rm{\\_\\_\\_}}}}{2}\" src=\"http://latex.codecogs.com/gif.latex?y = C{\\rm{x}} - \\frac{{{e^x}{\\rm{\\_\\_\\_}}}}{2}\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">cosx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1840', null, null, null, '5', '1839', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = {\\sin ^2}x.{\\cos ^3}x\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = {\\sin ^2}x.{\\cos ^3}x\" /></p>     là<p><img alt=\"y = \\frac{{{{\\cos }^5}x}}{{\\_\\_\\_}} + \\frac{{{{\\cos }^3}x}}{9} - \\frac{{{{\\cos }^2}x}}{5} - \\_\\_\\_c{\\rm{osx}} + C{\\rm{x}} + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{{{\\cos }^5}x}}{{\\_\\_\\_}} + \\frac{{{{\\cos }^3}x}}{9} - \\frac{{{{\\cos }^2}x}}{5} - \\_\\_\\_c{\\rm{osx}} + C{\\rm{x}} + D\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">25,2/5</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1842', null, null, null, '5', '1841', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' + x{\\log _2}x = 0\" src=\"http://latex.codecogs.com/gif.latex?y\'\' + x{\\log _2}x = 0\" /></p>      là<p><img alt=\"y = \\frac{{5{{\\rm{x}}^3}}}{{\\_\\_\\_\\ln 2}} - \\frac{{{x^3}{{\\log }_2}x}}{{\\_\\_\\_}} + C{\\rm{x}} + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{5{{\\rm{x}}^3}}}{{\\_\\_\\_\\ln 2}} - \\frac{{{x^3}{{\\log }_2}x}}{{\\_\\_\\_}} + C{\\rm{x}} + D\" /></p> <a class=\"clozetip\" title=\"\" href=\"#\">36,6</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1844', null, null, null, '5', '1843', '362', '450', '1551841150', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = x{\\sin ^2}x\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = x{\\sin ^2}x\" /></p>      là<p><img alt=\"y = \\frac{{{x^3}}}{{\\_\\_\\_}} + C{\\rm{x}} + \\frac{{x.\\cos {\\rm{x}}}}{4} - \\frac{{\\sin 2{\\rm{x}}}}{{\\_\\_\\_}} + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{{x^3}}}{{\\_\\_\\_}} + C{\\rm{x}} + \\frac{{x.\\cos {\\rm{x}}}}{4} - \\frac{{\\sin 2{\\rm{x}}}}{{\\_\\_\\_}} + D\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">12,16</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1846', null, null, null, '5', '1845', '363', '450', '1551841175', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"\\[2{\\rm{x}}y\'y\'\' = {\\left( {y\'} \\right)^2} - 1\\]\" src=\"http://latex.codecogs.com/gif.latex?\\[2{\\rm{x}}y\'y\'\' = {\\left( {y\'} \\right)^2} - 1\\]\" /></p>   là<p><img alt=\"y = \\frac{2}{{\\_\\_\\_C}}\\sqrt {C{\\rm{x + 1}}}  + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{2}{{\\_\\_\\_C}}\\sqrt {C{\\rm{x + 1}}}  + D\" /></p>  <a class=\"clozetip\" title=\"\" href=\"#\">3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1848', null, null, null, '5', '1847', '363', '450', '1551841175', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = x - \\frac{{y\'}}{x}\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = x - \\frac{{y\'}}{x}\" /></p>   là<p><img alt=\"y = \\frac{{{x^3}}}{{\\_\\_\\_}} + C\\ln \\left| x \\right| + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{{{x^3}}}{{\\_\\_\\_}} + C\\ln \\left| x \\right| + D\" /></p>   <a class=\"clozetip\" title=\"\" href=\"#\">9</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1850', null, null, null, '5', '1849', '363', '450', '1551841175', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' - \\frac{{y\'}}{{2{\\rm{x}} + 1}} = 0\" src=\"http://latex.codecogs.com/gif.latex?y\'\' - \\frac{{y\'}}{{2{\\rm{x}} + 1}} = 0\" /></p>   là<p><img alt=\"y = \\frac{1}{3}C\\sqrt {{{\\left( {2{\\rm{x}} + 1} \\right)}^{\\_\\_\\_}}}  + D\" src=\"http://latex.codecogs.com/gif.latex?y = \\frac{1}{3}C\\sqrt {{{\\left( {2{\\rm{x}} + 1} \\right)}^{\\_\\_\\_}}}  + D\" /></p>    <a class=\"clozetip\" title=\"\" href=\"#\">3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('1852', null, null, null, '5', '1851', '363', '450', '1551841175', null, '', null, ' Tích phân tổng quát của:<p><img alt=\"y\'\' = y\' - {\\left( {y\'} \\right)^2}\" src=\"http://latex.codecogs.com/gif.latex?y\'\' = y\' - {\\left( {y\'} \\right)^2}\" /></p>     là<p><img alt=\"y - \\ln \\left| {1 - C.\\_\\_\\_} \\right| = x + D\" src=\"http://latex.codecogs.com/gif.latex?y - \\ln \\left| {1 - C.\\_\\_\\_} \\right| = x + D\" /></p><a class=\"clozetip\" title=\"\" href=\"#\">e^y</a>', null, '51', null, null, null, null, null);
