INSERT INTO `question` VALUES ('1933', 'Trình bày các mục tiêu định giá của doanh nghiệp?', null, null, '5', '0', '371', '463', '1551877346', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1934', null, null, null, '5', '1933', '371', '463', '1551877346', null, null, null, '<p>-        Mục tiêu tồn tại</p>\r\n<p>-        Mục tiêu tối đa hóa <a class=\"cloze\" href=\"#mce_temp_url#\">lợi nhuận</a></p>\r\n<p>-        Mục tiêu gia tăng <a class=\"cloze\" href=\"#mce_temp_url#\">số lượng </a>bán</p>\r\n<p>-        Mục tiêu<a class=\"cloze\" href=\"#mce_temp_url#\"> dẫn đầu </a>về chất lượng sản phẩm</p>\r\n<p>-        Mục tiêu giữ được ổn định tránh được những phản ứng bất lợi</p>\r\n<p>-        Các mục tiêu khác: Ngăn chặn đối thủ ra nhập thị trường..</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1947', 'Nêu các nội dung của  chính sách định giá? Trình bày có ví dụ minh họa một trong các chính sách giá của doanh nghiệp?', null, null, '5', '0', '371', '463', '1551879677', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1948', null, null, null, '5', '1947', '371', '463', '1551879677', null, null, null, '<p><strong><em>Các chính sách định giá của doanh nghiệp</em></strong></p>\r\n<p><strong><em><strong><em>          </em></strong><strong><em><span>Chính sách giá cho sản phẩm mới</span></em></strong></em></strong></p>\r\n<p><strong>           Chính sách giá cho SP hỗn hợp</strong></p>\r\n<p><strong>           Chính sách giá phân hóa</strong></p>\r\n<p> </p>\r\n<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Chính sách giá cho <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm mới</a></span></em></strong></li>\r\n</ol>\r\n<p>-<em>        Định giá hớt váng sữa:</em></p>\r\n<p>+ Doanh nghiệp đặt giá bán cho sản phẩm của mình ở mức <a class=\"cloze\" href=\"#mce_temp_url#\">cao nhất</a> có thể cho nhũng đoạn thị trường người mua sẵn sang chấp nhận sản phẩm mới.</p>\r\n<p>+Điều kiện áp dụng:</p>\r\n<p>               Dựa vào lượng cầu <a class=\"cloze\" href=\"#mce_temp_url#\">lớn</a></p>\r\n<p>               Chi phí sản xuất <a class=\"cloze\" href=\"#mce_temp_url#\">không </a>phụ thuộc vào quy mô</p>\r\n<p>               Hạn chế được cạnh tranh</p>\r\n<p><em>               Tạo lập hình ảnh, định vị</em></p>\r\n<p>-        <em>Định giá thâm nhập</em>:</p>\r\n<p>+ Doanh nghiệp ấn định mức giá bán sản phẩm mới của họ <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a> nhằm theo đuổi mục tiêu “Giành được thị phần lớn và lợi nhuận dài hạn</p>\r\n<p>+Điều kiện áp dụng:</p>\r\n<p>     Thị trường<a class=\"cloze\" href=\"#mce_temp_url#\"> nhạy cảm</a> với giá</p>\r\n<p>     Chi phí sản xuất và pp <a class=\"cloze\" href=\"#mce_temp_url#\">giảm </a>theo qui mô</p>\r\n<p>     Giá hạ không thu hút thêm đối thủ cạnh tranh tiềm ẩn</p>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Định giá <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm hỗn hợp</a></span></em></strong></li>\r\n</ol>\r\n<p>-        Định giá cho <a class=\"cloze\" href=\"#mce_temp_url#\">tuyến</a> sản phẩm/nhãn hiệu: Hình thành khung giá chung cho các sản phẩm trong<a class=\"cloze\" href=\"#mce_temp_url#\"> tuyến.</a></p>\r\n<p>-        Định giá sản phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">phụ</a> thêm</p>\r\n<p>-        Định giá cho sản phẩm kèm theo bắt buộc</p>\r\n<p>-        Định giá sản phẩm phụ thuộc</p>\r\n<p>-        Định giá <a class=\"cloze\" href=\"#mce_temp_url#\">gói</a> sản phẩm</p>\r\n<ol>\r\n<li><strong><em>3.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Chính sách giá <a class=\"cloze\" href=\"#mce_temp_url#\">phân hóa</a></span></em></strong></li>\r\n</ol>\r\n<p>-        Giá <a class=\"cloze\" href=\"#mce_temp_url#\">chiết khấu </a>và phụ giá: chiết khấu cho đơn hàng số lượng lớn; chiết khấu giá thương mại; chiết khấu thời vụ; chiết khấu thanh toán</p>\r\n<p>-        Giá <a class=\"cloze\" href=\"#mce_temp_url#\">tâm lý</a>: người bán cân nhắc đến yếu tố tâm lý của giá cả</p>\r\n<p>-        Giá <a class=\"cloze\" href=\"#mce_temp_url#\">khuyến mại</a>: Điều chỉnh giá tạm thời thực hiện trong một thời gian nhất định,định giá cho dịp đặc biệt, giá trả góp..</p>\r\n<p>-        Giá theo vùng <a class=\"cloze\" href=\"#mce_temp_url#\">địa lý</a>: áp dụng cho từng khu vực</p>\r\n<p>-        Giá <a class=\"cloze\" href=\"#mce_temp_url#\">phân biệt</a>: theo không gian, thời gian</p>\r\n<p>-        Giá theo giá trị</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1951', 'Nêu khái niệm kênh phân phối ?', null, null, '5', '0', '373', '463', '1551880006', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1952', null, null, null, '5', '1951', '373', '463', '1551880006', null, null, null, '<ul>\r\n<li>Kênh phân phối là sự<a class=\"cloze\" href=\"#mce_temp_url#\"> kết hợp</a> hài hòa giữa người<a class=\"cloze\" href=\"#mce_temp_url#\"> sản xuât</a> với người <a class=\"cloze\" href=\"#mce_temp_url#\">trung gian</a> để tổ chức<a class=\"cloze\" href=\"#mce_temp_url#\"> vận chuyển</a> hàng hóa hợp lý nhất nhằm thảo mãn tối đa như cầu của khách hàng cuối cùng.</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1953', 'Vẽ mô hình kênh phân phối hàng tiêu dùng của công ty doanh nghiệp?', null, null, '5', '0', '373', '463', '1551880035', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1954', null, null, null, '5', '1953', '373', '463', '1551880035', null, null, null, '<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1957', 'rình bày kênh phân phối  không cấp/hai cấp ở công ty kinh doanh?', null, null, '5', '0', '373', '463', '1551880155', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1958', null, null, null, '5', '1957', '373', '463', '1551880155', null, null, null, '<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Kênh phân phối <a class=\"cloze\" href=\"#mce_temp_url#\">cấp không</a> (trực tiếp)</span></em></strong></li>\r\n</ol>\r\n<p>-        Chỉ người sản xuất và người tiêu dùng. Sử dụng cho hàng có tính thương phẩm đặc biệt, chậm luân chuyển, hàng hóa của người sản xuất nhỏ.</p>\r\n<p>-        Ưu điểm: Tốc độ lưu thông hàng hóa nhanh, người sản xuất chủ đạo, có lợi nhuận cao.</p>\r\n<p>-        Nhược điểm: Kém chuyên môn hóa,phân tán nguồn lực</p>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Kênh<a class=\"cloze\" href=\"#mce_temp_url#\"> trực tuyến </a>( Kênh 1 cấp) </span></em></strong></li>\r\n</ol>\r\n<p>-        Bán lẻ đảm nhận bán buôn</p>\r\n<p>-        Ưu điểm: Tăng cường chuyên môn hóa nhưng vẫn phát huy ưu thế loại hình trực tiếp, ổn định hơn</p>\r\n<p>-        Nhược điểm: Chưa phát huy triệt để phân công lao động xã hội; hạn chế chất lượng vận động vật lý</p>\r\n<ol>\r\n<li><strong><em>3.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Kênh phân phối <a class=\"cloze\" href=\"#mce_temp_url#\">hai cấp</a></span></em></strong></li>\r\n</ol>\r\n<p>-        Đây là kênh <a class=\"cloze\" href=\"#mce_temp_url#\">phổ biến</a> nhất</p>\r\n<p>-        Ưu điểm: Chặt chẽ; vòng quay vốn nhanh; chuyên môn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">sâu</a>; khả năng thỏa mãn thị trường lớn; thích hợp với sản xuất lớn</p>\r\n<p>-        Nhược điểm: Rủ ro lớn; vận hành <a class=\"cloze\" href=\"#mce_temp_url#\">phức tạp</a>, chi phí lớn</p>\r\n<ol>\r\n<li><strong><em>4.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Kênh dài mức kênh 3</span></em></strong></li>\r\n</ol>\r\n<p>-        Sử dụng cho mặt hàng mới nhưng khó khan trong quảng cáo; thị trường biến động lớn</p>\r\n<p>-        Ưu điểm và nhược điểm tương tự kênh phân phối 2 cấp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1959', 'Nêu khái niệm phân phối trong marketing?', null, null, '5', '0', '374', '463', '1551880685', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1960', null, null, null, '5', '1959', '374', '463', '1551880685', null, null, null, '<p>-        Phân phối trong marketing là những <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định</a> đưa hàng hóa và kênh phân phối theo<a class=\"cloze\" href=\"#mce_temp_url#\"> một hệ thống</a> tổ chức và công nghệ điều hòa, cân đối hàng hóa để tiếp cận và khai thác hợp lý nhất nhu cầu của một thị trường để đưa hàng hóa từ<a class=\"cloze\" href=\"#mce_temp_url#\"> sản xuất</a> đến <a class=\"cloze\" href=\"#mce_temp_url#\">khách hàng</a> cuối cùng nhanh nhất và nhằm đạt lợi nhuận tối đa</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\">  </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1963', 'Trình bày có ví dụ minh họa các nhân tố ảnh hưởng đến chính sách phân phối của doanh nghiệp?', null, null, '5', '0', '374', '463', '1551881213', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1964', null, null, null, '5', '1963', '374', '463', '1551881213', null, null, null, '<p><img title=\"Những tác động từ bên ngoài\" src=\"/images/buttons/info.gif\" alt=\"\" align=\"baseline\" border=\"0\" /> Sản phẩm;</p>\r\n<p> <a class=\"clozetip\" title=\"Đối tượng doanh nghiệp hướng tới\" href=\"#\">Khách hàng;</a></p>\r\n<p> Doanh nghiệp;</p>\r\n<p> Trung gian;</p>\r\n<p>INSERT INTO `question` VALUES ('1967', 'Trình bày nội dung cơ bản của chính sách phân phối?', null, null, '5', '0', '374', '463', '1551881318', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1968', null, null, null, '5', '1967', '374', '463', '1551881318', null, null, null, '<ul>\r\n<li><strong><em><span style=\"text-decoration: underline;\">Mục tiêu của chính sách phân phối:</span></em></strong></li>\r\n</ul>\r\n<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Mức độ</a> bao phủ thị trường</p>\r\n<p>-        Lợi nhuận,<a class=\"cloze\" href=\"#mce_temp_url#\">thị phần</a> và doanh số trên các đoạn thị trường</p>\r\n<p>-        Mức độ kiểm soát thị trường và chất lượng hoạt động marketing</p>\r\n<p>-        Tiết kiệm <a class=\"cloze\" href=\"#mce_temp_url#\">chi phí </a>phân phối,đặc biệt là vận chuyển</p>\r\n<ul>\r\n<li><strong><em><span style=\"text-decoration: underline;\">Một số chính sách phân phối</span></em></strong>:</li>\r\n</ul>\r\n<p>-   <em>     Chính sách <a class=\"cloze\" href=\"#mce_temp_url#\">kênh</a> phân phôi</em></p>\r\n<p>+  Thiết kế kênh phân phối</p>\r\n<p>+Quản lý kênh phân phối</p>\r\n<p>-        <em>Chính sách <a class=\"cloze\" href=\"#mce_temp_url#\">trung gian</a></em></p>\r\n<p>+ Phân phối không hạn chế</p>\r\n<p>+Phân phối chọn lọc</p>\r\n<p>+Phân phối qua <a class=\"cloze\" href=\"#mce_temp_url#\">đại lý</a> độc quyền</p>\r\n<p>-        <em>Chính sách liên kết</em></p>\r\n<p>+ Hệ thống liên kết ngang/dọc</p>\r\n<p>+Hệ thống đơn kênh/đa kênh</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1969', 'Nêu khái niệm chính sách phân phối?', null, null, '5', '0', '374', '463', '1551881413', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1970', null, null, null, '5', '1969', '374', '463', '1551881413', null, null, null, '<p>-        Bao gồm một <a class=\"cloze\" href=\"#mce_temp_url#\">tổ hợp </a>các định hướng, nguyên tắc,biện pháp và quy tắc hoạt động được các tổ chức marketing chấp nhận và tôn trọng thực hiện đối với việc <a class=\"cloze\" href=\"#mce_temp_url#\">lựa chọn</a> kênh phân phối,<a class=\"cloze\" href=\"#mce_temp_url#\"> phân công</a> nhiệm vụ marketing giữa các chủ thể khác nhau và các<a class=\"cloze\" href=\"#mce_temp_url#\"> ứng xử </a>cơ bản được chấp nhận theo định hướng thỏa mãn nhu cầu thị trường.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1977', 'Trình bày nội dung của một trong các chính sách phân phối?(Phân phối đại trà; phân phối chọn lọc)', null, null, '5', '0', '374', '463', '1551881808', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1978', null, null, null, '5', '1977', '374', '463', '1551881808', null, null, null, '<ol>\r\n<li><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Chính sách phân phối <a class=\"cloze\" href=\"#mce_temp_url#\">đại trà</a></span></em></strong></li>\r\n</ol>\r\n<ul>\r\n<li>Mục đích: Phân phối r<a class=\"cloze\" href=\"#mce_temp_url#\">ộng rãi</a> sản phẩm, đảm bảo hàng hóa định hướng tối đa tới người tiêu dùng.</li>\r\n<li>Khả năng áp dụng: Đối với hàng hóa <a class=\"cloze\" href=\"#mce_temp_url#\">thông dụng</a> như thuốc lá, xà phòng</li>\r\n</ul>\r\n<p>=&gt;do <a class=\"cloze\" href=\"#mce_temp_url#\">đặc tính</a> của sản phẩm , nhu cầu và kì vong của người tiêu dùng</p>\r\n<ul>\r\n<li>Yêu cầu: Sản phẩm tiện lợi dự trữ, bảo quản, trưng bày, chào hàng rộng rãi</li>\r\n<li>Hạn chế:</li>\r\n</ul>\r\n<p>-        Do các nhân tố chi phí, các yêu cầu của kế toán và ứng xử mua hàng của khách hàng</p>\r\n<p>-        Quyền kiểm soát <a class=\"cloze\" href=\"#mce_temp_url#\">kém</a></p>\r\n<p>-        Các dịch vụ, quà tặng kèm đến khách hàng bị <a class=\"cloze\" href=\"#mce_temp_url#\">hạn chế</a></p>\r\n<ul>\r\n<li>Ưu điểm: Hàng hóa phân bố<a class=\"cloze\" href=\"#mce_temp_url#\"> rộng rãi</a></li>\r\n</ul>\r\n<ol>\r\n<li><strong><em>2.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Phân phối <a class=\"cloze\" href=\"#mce_temp_url#\">chọn lọc</a></span></em></strong></li>\r\n</ol>\r\n<ul>\r\n<li>Đặc điểm:</li>\r\n</ul>\r\n<p>-        Sử dụng <a class=\"cloze\" href=\"#mce_temp_url#\">một số </a>trung gian</p>\r\n<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Hạn chế</a> địa điểm tiêu thụ trong mỗi kl</p>\r\n<ul>\r\n<li>Khả năng đáp ứng: Khi số nhà trung gian ít hơn số người sẵn sang bán hàng</li>\r\n</ul>\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\">Tập trung </a>vừa phải cho mỗi điểm bán</p>\r\n<p>-        Triển khái quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">tốt đẹp</a> giữa các thành viên</p>\r\n<p>-        Chiếm lĩnh thị trường rộng, kiểm soát<a class=\"cloze\" href=\"#mce_temp_url#\"> chặt chẽ</a></p>\r\n<p>-        Chi phí <a class=\"cloze\" href=\"#mce_temp_url#\">thấp</a> hơn so với phân phối rộng rãi</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1979', 'Nêu khái niệm xúc tiến bán?', null, null, '5', '0', '376', '463', '1551882158', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1980', null, null, null, '5', '1979', '376', '463', '1551882158', null, null, null, '<ul>\r\n<li>Là các hoạt động<a class=\"cloze\" href=\"#mce_temp_url#\"> khích lệ</a> ngắn hạn cho người tiêu dùng và các trung gian nhằm <a class=\"cloze\" href=\"#mce_temp_url#\">thu hút</a>/kích thích quá trình mua/bán hàng hóa và dịch vụ.</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1981', 'Trình bày có ví dụ minh họa một trong các công cụ xúc tiến với người tiêu dùng?', null, null, '5', '0', '376', '463', '1551882954', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1982', null, null, null, '5', '1981', '376', '463', '1551882954', null, null, null, '<p><span style=\"text-decoration: underline;\"><strong>Các công cụ xúc tiến</strong></span></p>\r\n<ul>\r\n<li><strong>Quảng cáo</strong></li>\r\n<li><strong>Marketing trực tiếp</strong></li>\r\n<li><strong>Xúc tiến bán</strong></li>\r\n<li><strong>Bán hàng trực tiếp cá nhân</strong></li>\r\n<li><strong>Quan hệ công chúng</strong></li>\r\n</ul>\r\n<p><strong>1. <a class=\"cloze\" href=\"#mce_temp_url#\">Quảng cáo</a></strong></p>\r\n<ul>\r\n<li>Khái niệm: <em>hình thức trình bày phi cá nhân, phải trả tiền nhằm giới thiệu các ý tưởng, hàng hoá hay dịch vụ được một người bảo trợ chỉ định.</em></li>\r\n<li> Vai trò\r\n<ul>\r\n<li>Thông tin</li>\r\n<li>Thuyết phục</li>\r\n<li>Nhắc nhở</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p><strong>2.<a class=\"cloze\" href=\"#mce_temp_url#\">Xúc tiến bán</a></strong>:</p>\r\n<ul>\r\n<li>Khái niệm: Khích lệ <a class=\"cloze\" href=\"#mce_temp_url#\">ngắn hạn</a> cho người tiêu dùng và các trung gian nhằm thu hút/kích thích quá trình mua/bán hang hóa và dịch vụ.</li>\r\n<li>Đặc điểm\r\n<ul>\r\n<li>Truyền thông mang tính rung động nhất thời, tính hấp dẫn và chứa đựng thông tin, thúc đẩy và khích lệ hành động mua/bán.</li>\r\n</ul>\r\n</li>\r\n<li>Phương tiện xúc tiến bán\r\n<ul>\r\n<li>Xúc tiến bán cho trung gian và lực lượng bán</li>\r\n<li>Xúc tiến bán cho người tiêu dùng</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p><strong>3. <a class=\"cloze\" href=\"#mce_temp_url#\">Quan hệ công chúng</a></strong></p>\r\n<p>-  Khái niệm:<a class=\"cloze\" href=\"#mce_temp_url#\"> xây dựng </a>mối quan hệ tốt đẹp với các dạng công chúng khác nhau bằng cách làm cho nhiều người biết và có thiện cảm với mình, xây dựng một hình ảnh tốt đẹp và xử lý hay kiểm soát những đàm tiếu/câu chuyện bất lợi với mình</p>\r\n<p>- Vai trò:</p>\r\n<p>          Thuyết phục</p>\r\n<p>           Đề cao/cải thiện hình ảnh</p>\r\n<p>          Thông tin – tuyên truyền</p>\r\n<p><strong>4. <a class=\"cloze\" href=\"#mce_temp_url#\">Marketing trực tiếp</a></strong></p>\r\n<p> - Khái niệm: kết nối với các phân đoạn mục tiêu hoặc khách hàng cá nhân thông qua tương tác một một hoặc tương tác đối ngẫu</p>\r\n<p>-  Vai trò</p>\r\n<p>            Thông tin đến những nhóm<a class=\"cloze\" href=\"#mce_temp_url#\"> mục tiêu</a></p>\r\n<p>            Nhắc nhở</p>\r\n<p>-  <a class=\"cloze\" href=\"#mce_temp_url#\">Phương tiện </a>marketing trực tiếp</p>\r\n<p>            Catalog</p>\r\n<p>           Tờ rơi</p>\r\n<p>            Thư – fax</p>\r\n<p>            Qua bưu điện</p>\r\n<p>            Thư điện tử</p>\r\n<p>             Điện thoại</p>\r\n<p>            Ecommerce</p>\r\n<p><strong> </strong></p>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1983', 'Nêu khái niệm quảng cáo? Trình bày có ví dụ minh họa các yêu cầu cơ bản với hoạt động quàng cáo của doanh nghiệp?', null, null, '5', '0', '376', '463', '1551883490', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1984', null, null, null, '5', '1983', '376', '463', '1551883490', null, null, null, '<p><strong>1. Đảm bảo hàm lượng <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> cao</strong>: Vì giá trị cho quảng cáo rất nhiều chi phi phí nên phải có hàm lượng thông tin cao để khách hàng có thể hiểu biết đầy đủ về sản phẩm</p>\r\n<p>Ví dụ: Quảng cáo sữa của Vinamilk trong vòng khoảng 1 phút truyền tải được thông tin về sữa sạch 3 không của công ty.</p>\r\n<p> </p>\r\n<p>2. Đảm bảo <a class=\"cloze\" href=\"#mce_temp_url#\">hợp lý</a>: Hợp lý về ngôn từ, phương tiện truyền thông, cấu trúc truyền thông.</p>\r\n<p>       Ví dụ: Quảng cáo của Vinamilk sử dụng trên nhiều phương tiện thông tin đại chúng như truyền hình, mạng xã hội… thông qua giai điệu bắt tai của bài hát.</p>\r\n<p><strong>3. Đảm bảo tính <a class=\"cloze\" href=\"#mce_temp_url#\">pháp lý</a></strong>: Quảng cáo phải đảm bảo  tính pháp lý về mặt nội dung,hình thức, thông điệp..</p>\r\n<p>          Ví dụ: Quảng cáo củ Vinamilk đảm bảo không vi phạm pháp luật về các phương diện</p>\r\n<p><strong>4. Đảm bảo tính <a class=\"cloze\" href=\"#mce_temp_url#\">nghệ thuật</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">khoa học</a></strong></p>\r\n<p>           Ví dụ: Tính nghệ thuật trong quảng cáo sữa của Vinamilk là sử dụng các giai điệu bắt tai của âm nhạc, kết hợp vói nội dung khoa học, thuyết phục người tiêu dùng về sản phẩm sữa sạch của mình.</p>\r\n<p><strong>5. Đảm bảo<a class=\"cloze\" href=\"#mce_temp_url#\"> đồng bộ</a>, đa dạng hóa:</strong></p>\r\n<p>            Ví dụ: Tất cả các quảng cáo của Vinamilk đều nhấn mạnh vào tính sạch, an toàn của sản phẩm, hàm lượng dinh dưỡng đảm bảo</p>\r\n<p><strong>6.Đảm bảo phù hợp với <a class=\"cloze\" href=\"#mce_temp_url#\">kinh phí</a></strong>: Quảng cáo trên các phương tiện truyền thông phải ngắn ngọn, xúc tích tránh gây lãng phí.</p>\r\n<p><strong>7.Đảm bảo tính <a class=\"cloze\" href=\"#mce_temp_url#\">hiệu quả</a></strong>: Đảm bảo khả năng tiếp cận thông tin của người tiêu dùng.</p>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1985', 'Trình bày sự khác biệt giữa công cụ quảng cáo và công chúng trong xúc tiến xúc tiến.', null, null, '5', '0', '376', '463', '1551883962', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1986', null, null, null, '5', '1985', '376', '463', '1551883962', null, null, null, '<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p><strong>Quảng cáo</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p><strong>Quan hệ công chúng</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<ul>\r\n<li>Là truyền thông <a class=\"cloze\" href=\"#mce_temp_url#\">một</a> chiều</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tự mình</a> nói về mình</li>\r\n<li>Chi phí <a class=\"cloze\" href=\"#mce_temp_url#\">lớn</a></li>\r\n<li>Hiệu quả không cao, mang tính nâng cao nhận biết của khách hàng về sản phẩm</li>\r\n<li>Là sự <a class=\"cloze\" href=\"#mce_temp_url#\">kết hợp</a> giữa âm thanh, hình ảnh và ngôn ngữ</li>\r\n<li>Đánh giá: Doanh số,<a class=\"cloze\" href=\"#mce_temp_url#\"> lợi nhuận</a>, thị phần của doanh nghiệp</li>\r\n</ul>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<ul>\r\n<li><strong> </strong>Truyền thông <a class=\"cloze\" href=\"#mce_temp_url#\">hai</a> chiều</li>\r\n<li>Để người khác nói về mình</li>\r\n<li>Chi phí thấp hơn</li>\r\n<li>Hiệu quả cao</li>\r\n</ul>\r\n<ul>\r\n<li>Là những câu chuyện hay bài viết lý thú về sản phẩm hay công ty</li>\r\n<li>Đánh giá:<a class=\"cloze\" href=\"#mce_temp_url#\"> Tần suất</a> xuất hiện của thông tin trên các phương tiện đại chúng, thái độ của khách hàng với sản phẩm, lợi nhuận</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1991', 'Nêu các phương pháp xác lập ngân sách xúc tiến?', null, null, '5', '0', '377', '463', '1551884228', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1992', null, null, null, '5', '1991', '377', '463', '1551884228', null, null, null, '<p><strong>Nêu các phương pháp xác lập ngân sách xúc tiến:</strong></p>\r\n<ul>\r\n<li>Phương pháp tỉ lệ <a class=\"cloze\" href=\"#mce_temp_url#\">phần trăm</a> trên <a class=\"cloze\" href=\"#mce_temp_url#\">doanh số</a></li>\r\n<li>Phương pháp cân bằng<a class=\"cloze\" href=\"#mce_temp_url#\"> cạnh tranh</a></li>\r\n<li>Phương pháp lập ngân sách theo <a class=\"cloze\" href=\"#mce_temp_url#\">khả năng</a></li>\r\n<li>Phương pháp lập ngân sách theo<a class=\"cloze\" href=\"#mce_temp_url#\"> mục tiêu </a>và nhiệm vụ thực hiện</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1995', 'Khái niệm xúc tiến thương mại?', null, null, '5', '0', '377', '463', '1551884814', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1996', null, null, null, '5', '1995', '377', '463', '1551884814', null, null, null, '<ul>\r\n<li>Xúc tiến là hoạt động <a class=\"cloze\" href=\"#mce_temp_url#\">marketing đặc biệt</a> và có chủ đích được định hướng vào việc <a class=\"cloze\" href=\"#mce_temp_url#\">chào hàng</a>, chiêu khách và xác lập quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">thuận lợi </a>nhất giữa <a class=\"cloze\" href=\"#mce_temp_url#\">doanh nghiệp</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> bạn hàng</a> của nó với <a class=\"cloze\" href=\"#mce_temp_url#\">tập khách hàng tiềm năng</a> trọng điểm nhằm phối thuộc, triển khai năng động  chiến lược và chương trình marketing mix đã lựa chọn của doanh nghiệp</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1997', 'Vẽ mô hình xúc tiến thương mại?', null, null, '5', '0', '377', '463', '1551884865', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('1998', null, null, null, '5', '1997', '377', '463', '1551884865', null, null, null, '<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('2001', 'Trình bày có ví dụ minh họa nội dung xác lập thông điệp xúc tiến?', null, null, '5', '0', '377', '463', '1551886332', null, null, null, null, null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('2002', null, null, null, '5', '2001', '377', '463', '1551886332', null, null, null, '<p><strong><em> </em></strong></p>\r\n<ol>\r\n<li><strong><em>1.    </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác nhận <a class=\"cloze\" href=\"#mce_temp_url#\">đối tượng</a> nhận tin</span></em></strong></li>\r\n</ol>\r\n<p>-        Khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">tiềm năng</a></p>\r\n<p>-        Khách hàng hiện tại</p>\r\n<p>-        Khách hàng cá nhân, <a class=\"cloze\" href=\"#mce_temp_url#\">công chúng</a></p>\r\n<p>=&gt;Với mỗi đối tượng cụ thể cần xác định nói gì? Nói như thế nào? Nói bao giờ? Nói ở đâu? Ai nói?</p>\r\n<ol>\r\n<li><strong><em>2.    </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định<a class=\"cloze\" href=\"#mce_temp_url#\"> mục tiêu </a>xúc tiến thương mại</span></em></strong></li>\r\n</ol>\r\n<p>-        Căn cứ xác định mục tiêu:</p>\r\n<p>       + Mục tiêu marketing:  Các nhân tố bên trong và bên ngoài; mục tiêu chung của donh nghiẹp</p>\r\n<p>        + Mục tiêu xúc tiến:  mục tiêu quản cáo, mục tiêu quan hệ công chúng...</p>\r\n<p>-        Các mục tiêu cơ bản:</p>\r\n<p>+ Thông tin</p>\r\n<p>+THuyết phục</p>\r\n<p>+nhắc nhở</p>\r\n<ol>\r\n<li><strong><em>3.    </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">ngân sác</a>h xúc tiến thương mại</span></em></strong></li>\r\n</ol>\r\n<p>-        Căn cứ:</p>\r\n<p>+ mục tiêu và nhiệm vụ</p>\r\n<p>+ Tùy theo khả năng</p>\r\n<p>+Tỉ lệ phần trăm  tổng doanh số bán</p>\r\n<p>+cân bằng cạnh tranh</p>\r\n<p>+ Ngân sách cố định</p>\r\n<ol>\r\n<li><strong><em>4.    </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác lập <a class=\"cloze\" href=\"#mce_temp_url#\">thông điệ</a>p, kênh <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thông</a></span></em></strong></li>\r\n</ol>\r\n<p>-       <em> Xác lập thông điệp truyền thôn</em>g</p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Nội dung</a> cấu trúc: Lý tính;cảm tính;đạo đức</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Cấu trúc </a>truyền thông</p>\r\n<p>+Hình thức truyền thông</p>\r\n<p>==&gt;Thông điệp truyền thông nhằm đạt được sự chú ý, giữ được sự yêu thích, khuấy động ham muốn, thu được hành động….(mô hình ADIA)</p>\r\n<p>-  <em>      Xác lập kênh truyền thông</em></p>\r\n<p>+ Kênh <a class=\"cloze\" href=\"#mce_temp_url#\">trực tiếp</a>: Giao tiếp cá nhân, truyền miệng, tin đồn</p>\r\n<p>+Kênh<a class=\"cloze\" href=\"#mce_temp_url#\"> gián tiếp</a>: Phương tiện (in ấn,bảng hiệu,…; môi trường tiếp xúc, sự kiện</p>\r\n<ol>\r\n<li><strong><em>5.    </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác lập<a class=\"cloze\" href=\"#mce_temp_url#\"> phối thức</a> xúc tiến</span></em></strong></li>\r\n</ol>\r\n<p>-        Các công cụ: Quảng cáo, bán hàng cá nhân,...</p>\r\n<ol>\r\n<li><strong><em>6.    </em></strong><strong><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Đánh giá</a> hiệu quả xúc tiến</span></em></strong></li>\r\n</ol>\r\n<p>-        Doanh thu/lợi nhuận</p>\r\n<p>-        Thị phần</p>\r\n<p>-        Mức độ nhận biết/sự ưu thích</p>\r\n<p>-        Sự tin tưởng vào nhãn hiệu</p>\r\n<p>-        Danh tiếng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '58', null, null, null, null, null);
INSERT INTO `question` VALUES ('2003', 'Phân tích ảnh hưởng nhóm yếu tố mang tính chất cá nhân?', null, null, '5', '0', '296', '467', '1551942616', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2004', null, null, null, '5', '2003', '296', '467', '1551942616', null, null, null, '<ul>\r\n<li>Tuổi tác và giai đoạn chu kỳ sống: Mỗi một giai đoạn tuổi tác, con người sẽ có những nhu cầu, hành vi mua hàng khác nhau. Việc tiêu dùng cũng được định hình theo giai đoạn của chu kỳ sống của gia đình. </li>\r\n<li>Nghề nghiệp: Những người có nghề nghiệp khác nhau thì nhu cầu mua sắm cũng khác nhau từ những hàng hóa chính thiết yếu như: quần áo, giày dép, thức ăn,.. đến những hàng hóa khác như: ô tô, điện thoại, mỹ phẩm</li>\r\n<li><em>Tình trạng kinh tế là điều kiện tiên quyết để người tiêu dùng</em> có thể mua được hàng hoá, dịch vụ. Khi ngân sách tiêu dùng càng cao thì tỷ lệ phân bố cho tiêu dùng các hàng xa xỉ càng tăng lên, tỷ lệ chi tiêu cho các hàng thiết yếu càng giảm xuống. Nói chung, vào thời kỳ kinh tế đất nước phồn thịnh, tăng trưởng thì người ta tiêu dùng nhiều hơn và ngược lại.</li>\r\n<li>Phong cách sống: Người tiêu dùng tuy cùng nhóm văn hóa đặc thù hoặc tầng lớp xã hội như nhau và thậm chí cùng nghề nghiệp giống nhau, cũng vẫn có thể có sự khác biệt trong phong cách sống. Nhiều người sẽ sống theo lối sống hưởng thụ và có người sẽ sống theo lối sống tiết kiệm,…</li>\r\n<li>Nhân cách và ý niệm về bản thân<em>: </em><em>là những đặc tính tâm lý nổi bật của mỗi người</em> dẫn đến các hành vi ứng xử mang tính ổn định và nhất quán đối với môi trường xung quanh. Có thể nêu ra một số các cá tính thường gặp như: tính cẩn thận; tính tự tin; tính bảo thủ; tính hiếu thắng; tính năng động...Cá tính sẽ ảnh hưởng đến hành vi tiêu dùng của khách hàng. Những người cẩn thận, những người bảo thủ thường không đi tiên phong trong việc sử dụng sản phẩm mới. Ngược lại, là những người năng động, sáng tạo sẵn sàng chịu mạo hiểm khi mua sản phẩm mới.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2005', 'Phân tích nhóm yếu tố tâm lý?', null, null, '5', '0', '296', '467', '1551942686', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2006', null, null, null, '5', '2005', '296', '467', '1551942686', null, null, null, '<ul>\r\n<li>Nhu cầu và động cơ: Nhu cầu là một thuộc tính về tâm lý, là những điều con người đòi hỏi để tồn tại và phát triển. Tại những thời điểm khác nhau, người ta lại bị thôi thúc bởi những nhu cầu khác nhau. Con người sẽ cố gắng thỏa mãn trước hết là những nhu cầu quan trọng nhất. Khi người ta đã thoả mãn được một nhu cầu quan trọng nào đó thì nó sẽ không còn là động cơ hiện thời nữa, và người ta lại cố gắng thỏa mãn nhu cầu quan trọng nhất tiếp theo. </li>\r\n<li>Nhận thức: Mỗi người khi có động cơ luôn sẵn sàng hành động. Nhưng hành động như thế nào thì còn phụ thuộc vào nhận thức của người đó về tình huống xảy ra ( nhận thức là quá trình kích thích được lựa chọn, sắp xếp và giải thích)</li>\r\n<li>Tri thức: những biến đổi nhất định diễn ra trong hành vi của con người dưới ảnh hưởng của kinh nghiệm được họ tích luỹ. Con người có được kinh nghiệm, hiểu biết là do sự từng trải và khả năng học hỏi. Người lớn từng trải có kinh nghiệm hơn, mua bán thạo hơn. Người từng trải về lĩnh vực nào thì có kinh nghiệm mua bán trong lĩnh vực đó.</li>\r\n<li>\r\n<p><em>Niềm tin và thái độ</em><em>:</em> Qua thực tiễn và sự hiểu biết con người ta có được niềm tin và thái độ, điều này lại ảnh hưởng đến hành vi mua của họ. Doanh nghiệp phải chiếm được lòng tin của khách hàng về các nhãn hàng của mình. Muốn có chỗ đứng vững chắc trên thị trường nội địa và vươn ra thị trường thế giới, hàng Việt Nam phải chiếm được niềm tin của khách hàng trong nước và thế giới. </p>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2007', 'Các hoạt động nghiên cứu hành vi khách hàng?', null, null, '5', '0', '296', '467', '1551942736', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2008', null, null, null, '5', '2007', '296', '467', '1551942736', null, null, null, '<p><strong>Các hoạt động nc:</strong></p>\r\n<p>-         Ngày càng tăng cường</p>\r\n<p>-         Sự hỗ trợ của các nhà khoa học khác</p>\r\n<p>-         Ngày càng phát triển cùng với tiến bộ khoa học về CNTT và kỹ thuật phân tích xử lý.</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2009', 'Trình bày có liên hệ thực tế các xu hương về lối sống?', null, null, '5', '0', '315', '467', '1551942909', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2010', null, null, null, '5', '2009', '315', '467', '1551942909', null, null, null, '<p>-         Đơn giản hóa và theo chủ nghĩa môi trường ( Bảo vệ môi trường )</p>\r\n<p>-         Tập trung hơn vào đạt ước nguyện bản thân</p>\r\n<p>-         Ngày càng thiếu thời gian ( Làm việc nhiều, căng thẳng )</p>\r\n<p>-         Gỉam tầm quan trọng của dinh dưỡng, tập luyện</p>\r\n<p>-         Tổ ấm ( Đi du lịch nhiều hơn)</p>\r\n<p><span> </span></p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2011', 'Khái niệm \"khách hàng tổ chức\"?', null, null, '5', '0', '378', '467', '1551943371', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2012', null, null, null, '5', '2011', '378', '467', '1551943371', null, null, null, '<ul>\r\n<li><em>Khách hàng tổ chức: </em>tổ chức là <a class=\"cloze\" href=\"#mce_temp_url#\">sự phối hợp</a> có kế hoạch các hoạt động của một số người nhằm đạt được một số mục đích cụ thể bằng việc <a class=\"cloze\" href=\"#mce_temp_url#\">phân công nhân lực </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">chức năng</a> thông qua một số <a class=\"cloze\" href=\"#mce_temp_url#\">cấu trúc quyền lực</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> trách nhiệm</a>. =&gt;&gt; khách hàng tổ chức: là tất cả các tổ chức mua hàn hóa và dịch vụ dể sản xuất ra các sản phẩm khác hả những dịch vụ để bán, cho thuê hay cung ứng cho những người khác.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2013', 'Khái niệm \"hành vi mua của khách hàng tổ chức\"?', null, null, '5', '0', '378', '467', '1551943491', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2014', null, null, null, '5', '2013', '378', '467', '1551943491', null, null, null, '<ul>\r\n<li><em>Hành vi mua của khách hàng tổ chức:</em> là các <a class=\"cloze\" href=\"#mce_temp_url#\">cách ứng xử</a>/ <a class=\"cloze\" href=\"#mce_temp_url#\">phản ứng</a> của khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> trong quá trình các tổ chức hình thành nhu cầu mua sắm sản phẩm và dịch vụ, nhận biết, đánh giá và lựa chọn mua trong số những nhãn hiệu và nhà cung cấp thế vị đang chào hàng trên thị trường.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2015', 'Đặc điểm của khách hàng tổ chức?', null, null, '5', '0', '379', '467', '1551943759', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2016', null, null, null, '5', '2015', '379', '467', '1551943759', null, null, null, '<p>-Đặc điểm khách hàng tổ chức:</p>\r\n<ul>\r\n<li>    Có số lượng khách hàng ít hơn nhưng qui mô khách hàng lớn hơn.</li>\r\n<li>    Tập trung hơn về địa lý</li>\r\n<li>    Có quan hệ chặt chẽ giữa người cung ứng và khách hàng.</li>\r\n<li>    Nhu cầu phái sinh, không co dãn, biến động mạnh.</li>\r\n<li>    Người đi mua hàng có tính chuyen nghiệp hơn so với người tiêu dùng.</li>\r\n<li>    Có nhiều người ảnh hưởng đến việc mua hàng.</li>\r\n<li>    Quyết đinh mua hàng thường phức tạp, quá trình quyết định lâu hơn.</li>\r\n<li>    ....</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2017', 'Liệt kê các loại hình khách hàng tổ chức?', null, null, '5', '0', '379', '467', '1551943797', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2018', null, null, null, '5', '2017', '379', '467', '1551943797', null, null, null, '<p>-Các loại hình khách hàng tổ chức:</p>\r\n<ul>\r\n<li>    Khách hàng tổ chức sản xuất. VD: các nhà máy sản xuất thực phẩm mua máy móc để phục vụ sản xuất.</li>\r\n<li>    Khách hàng tổ chức thương mại. VD: nhà bán buôn, bán lẻ</li>\r\n<li>    Khách hàng tổ chức nhà nước( cơ quan hành chính).VD: 86000  cơ quan chính phủ tiêu tốn hàng nghìn tỷ đola để mua sắm máy móc thiết bị, các loại phương tiện, sản phẩm thiết yếu và dịch vụ.</li>\r\n<li>    Các tổ chức phii lợi nhuận khác.VD: tổ chức từ thiện mua để đi từ thiện.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2019', 'Quy trình quyết định mua của khách hàng tổ chức?', null, null, '5', '0', '379', '467', '1551943897', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2020', null, null, null, '5', '2019', '379', '467', '1551943897', null, null, null, '<ul>\r\n<li>Các bước trong tiến trình mua \r\n<ul>\r\n<li>Bước 1: ý thức nhu cầu: </li>\r\n<li>Bước 2: Mô tả khái quát nhu cầu </li>\r\n<li>Bước 3: xác định quy cách sản phẩm </li>\r\n<li>Bước 4: Tìm kiếm nhà cung cấp </li>\r\n<li>Bước 5: yêu cầu chào hàng </li>\r\n<li>Bước 6: lựa chọn nhà cung cấp </li>\r\n<li>Bước 7: làm thủ tục đặt hàng </li>\r\n<li>Bước 8: đánh giá kết quả thực hiện </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2021', 'Phân tích bước 1: Nhận Thức Nhu cầu', null, null, '5', '0', '379', '467', '1551943952', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2022', null, null, null, '5', '2021', '379', '467', '1551943952', null, null, null, '<ul>\r\n<li>Bước 1: nhận thức nhu cầu \r\n<ul>\r\n<li>Khi có người trong doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">ý thức</a> một vấn đề hay nhu cầu có thể <a class=\"cloze\" href=\"#mce_temp_url#\">đáp ứng </a>qua việc mua sản phẩm hay hàng hóa </li>\r\n<li>Những người p<a class=\"cloze\" href=\"#mce_temp_url#\">hát hiện nhu cầu</a> có thể là người sử dụng /có hoạt động liên quan đến sản phẩm </li>\r\n<li>Thực hiện vai trò: <a class=\"cloze\" href=\"#mce_temp_url#\">người khởi xướng</a> </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2023', 'Bước 2: Mô tả khái quát nhu cầu', null, null, '5', '0', '379', '467', '1551943999', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2024', null, null, null, '5', '2023', '379', '467', '1551943999', null, null, null, '<ul>\r\n<li>Bước 2: Mô tả khái quát nhu cầu \r\n<ul>\r\n<li>Người mua/ bộ phận sử dụng/ thiết kế kỹ thuật phải xây dựng các <a class=\"cloze\" href=\"#mce_temp_url#\">đặc điểm chung</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">số lượng sản phẩm</a> của có nhu cầu </li>\r\n<li>Với những sản phẩm phức tạp cần có sự tham gia của các kỹ sư người sử dụng cố vấn chuyên môn để định ra đặc điểm sản phẩm cần mua( tầm quan trọng và mức độ tin cậy, độ bền, giá và các srn phẩm khác..) </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2025', 'Bước 3: Xác định quy cách sản phẩm', null, null, '5', '0', '379', '467', '1551944079', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2026', null, null, null, '5', '2025', '379', '467', '1551944079', null, null, null, '<ul>\r\n<li>Bước 3 xác định quy cách sản phẩm: \r\n<ul>\r\n<li>Xác định quy cách kỹ thuật của sản phẩm( chi tiết kỹ thuật..) \r\n<ul>\r\n<li>Những <a class=\"cloze\" href=\"#mce_temp_url#\">đặc điểm</a> ưu điểm của sản phẩm được bộ phận kỹ thuật<a class=\"cloze\" href=\"#mce_temp_url#\"> xác định</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ đáp ứng</a> của nhà cung cấp và yêu cầu của trung tâm mua </li>\r\n<li>Có thể sử dụng phương pháp phân tích giá trị GE nhằm dành được khách hàng. </li>\r\n</ul>\r\n</li>\r\n<li>Nhà cung cấp tham gia từ sớm: \r\n<ul>\r\n<li>Nhà cung cấp mô tả chi tiết, đặc biệt trong trường hợp sản phẩm mới chưa bao giờ gặp </li>\r\n<li>Sử dụng để xác định vị trí của mình nhằm đạt được khách hàng/ tham gia sớm vào những quy cách kỹ thuật của người mua </li>\r\n</ul>\r\n</li>\r\n<li>Bộ phận kỹ thuật và thiết kế hoặc nhóm công tác liên chức thường thực hiện nhiệm vụ này phát triển quy cách sản phẩm dịch vụ </li>\r\n<li>thực hiện vai trò: <a class=\"cloze\" href=\"#mce_temp_url#\">người ảnh hưởng</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">người sử dụng</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">người mua </a></li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2027', 'Bước 4: Tìm kiếm nhà cung cấp', null, null, '5', '0', '379', '467', '1551944188', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2028', null, null, null, '5', '2027', '379', '467', '1551944188', null, null, null, '<ul>\r\n<li>Bước 4 Tìm kiếm nhà cung cấp: \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xác định</a> nhà cung cấp <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a> nhất từ danh sách các nhà cung cấp có khả năng đáp ứng tiêu chuẩn đã được lập </li>\r\n<li>mua mới sản phẩm phức tạp, đắt tiền ==&gt; thời gian tìm kiếm, x xây dựng nhà cung cấp lâu dài </li>\r\n<li>thực hiện vai trò: <a class=\"cloze\" href=\"#mce_temp_url#\">người mua </a></li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2029', 'Bước 5: Yêu cầu chào hàng', null, null, '5', '0', '379', '467', '1551944251', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2030', null, null, null, '5', '2029', '379', '467', '1551944251', null, null, null, '<ul>\r\n<li>Bước 5: yêu cầu chào hàng \r\n<ul>\r\n<li>Người mua mời những nhà cung cấp <a class=\"cloze\" href=\"#mce_temp_url#\">đã được lựa chọn</a> gửi bản chào hàng và những văn bản chi tiết liên quan </li>\r\n<li>Bộ phận mua có trách nhiệm <a class=\"cloze\" href=\"#mce_temp_url#\">tập hợp</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">gửi</a> những thư mời các nhà cung cấp tiềm năng tham gia vào dự thầu chào hàng phải tạo ra niềm tin ở người mua chứ không phải là một tài liệu kỹ thuật </li>\r\n<li>chào hàng phải trình bày được <a class=\"cloze\" href=\"#mce_temp_url#\">năng lực kỹ thuật </a></li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2031', 'Bước 6: Lựa chọn nhà cung cấp', null, null, '5', '0', '379', '467', '1551944306', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2032', null, null, null, '5', '2031', '379', '467', '1551944306', null, null, null, '<ul>\r\n<li>Bước 6: Lựa chọn nhà cung cấp : \r\n<ul>\r\n<li>Trung tâm mua sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">xác định</a> những<a class=\"cloze\" href=\"#mce_temp_url#\"> chỉ tiêu đánh giá</a> các nhà cung cấp và tiến hành </li>\r\n<li>Đánh giá nhằm <a class=\"cloze\" href=\"#mce_temp_url#\">xác định</a> nhà cung cấp nào <a class=\"cloze\" href=\"#mce_temp_url#\">hấp dẫn</a> nhất </li>\r\n<li>Các nhà làm marketing xây dựng các nguyên tắc đối với bản chào hàng hấp dẫn </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2033', 'Bước7: Đặt hàng', null, null, '5', '0', '379', '467', '1551944346', '1551944357', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2034', null, null, null, '5', '2033', '379', '467', '1551944346', '1551944357', null, null, '<ul>\r\n<li>Bước 7: làm thủ tục đặt hàng </li>\r\n</ul>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Thương lượng</a> ảnh về đơn đặt hàng với nhà cung cấp được lựa chọn: chi tiết kỹ thuật; thời gian giao hàng kì vọng; số lượng cần cố; chính sách trả hàng và bảo hành </p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2035', 'Bước 8: Đánh giá kết quả thực hiện', null, null, '5', '0', '379', '467', '1551944399', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2036', null, null, null, '5', '2035', '379', '467', '1551944399', null, null, null, '<ul>\r\n<li>Bước 8: đánh giá kết quả thực hiện \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xem xét kết quả</a> của đợt mua và<a class=\"cloze\" href=\"#mce_temp_url#\"> quyết định</a> tiếp tục hay loại bỏ nhà cung cấp. </li>\r\n<li>Các là các yếu tố tương tự được sử dụng bởi người mua </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2037', 'Nêu các loại hành vi mua của khách hàng tổ chức?', null, null, '5', '0', '380', '467', '1551944671', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2038', null, null, null, '5', '2037', '380', '467', '1551944671', null, null, null, '<ul>\r\n<li>Các loại hình hành vi mua:</li>\r\n</ul>\r\n<p>    <a class=\"cloze\" href=\"#mce_temp_url#\">Tái chọn mua trực tiếp</a>: văn phòng phẩm, vật tư, ... danh mục mua đã được phê duyệt, không cần hoặc ít cần thông tin mới.</p>\r\n<p>   <a class=\"cloze\" href=\"#mce_temp_url#\"> Tái chọn mua có điều chỉnh</a>: trang thiết bị, phụ tùng,... điều chỉnh ít, thường thiếu cẩn thận.</p>\r\n<p>    <a class=\"cloze\" href=\"#mce_temp_url#\">Mua phục vụ nhiệm vụ mới</a>: cơ sở hạ tầng, dây chuyền công nghệ,... không chắc chắn, mang tính chiến lược, can thiệp sâu.</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2039', 'Phân tích các đặc trưng hành vi mua của khách hàng tổ chức?', null, null, '5', '0', '380', '467', '1551944793', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2040', null, null, null, '5', '2039', '380', '467', '1551944793', null, null, null, '<p>1. Đặc điểm nhu cầu: </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhu cầu phái sinh</a>: cầu của thị trường b2b là do tác động ảnh của nhu cầu thị trường đầu ra của khách hàng hay nhu cầu của các khách hàng và dịch vụ khác </li>\r\n<li> Cầu dao động ở biên độ cao hơn, thất thường:  \r\n<ul>\r\n<li>Một sự thay đổi nhỏ trong nhu cầu tiêu dùng có thể dẫn đến một lượng thay đổi trong nhu cầu với sản phẩm b2b </li>\r\n<li>Thị trường b2b biến động theo sự ảnh hưởng của môi trường vì vậy vậy để ra sản phẩm phải quan sát biến động thay đổi trong cầu b2c để tính sản lượng chủng loại cung cấp cho b2b. do b2b phải giải quyết bài toán sản xuất quy mô lớn hơn trong một thời gian xác định nên b2b trễ pha hơn b2c </li>\r\n</ul>\r\n</li>\r\n<li> Cầu liên kết: \r\n<ul>\r\n<li>Nhu cầu của các sản phẩm và dịch vụ phụ thuộc lẫn nhau </li>\r\n<li>Việc chấm dứt hay âm dịch vụ vụ có thể kéo theo việc chấm dứt hay phát triển về nhu cầu của một loại hình hàng hóa </li>\r\n</ul>\r\n</li>\r\n<li>Cầu ít co giãn so với giá trong thời gian ngắn: cầu b2b là cầu ít nhạy cảm với giá. Quy mô không phụ thuộc vào rẻ hay không mà phụ thuộc vào đầu ra. </li>\r\n</ul>\r\n<p>2. đặc điểm m<span style=\"text-decoration: underline;\">ục đích mua</span> </p>\r\n<ul>\r\n<li>Sản xuất các <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">dịch vụ khác </a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Bán lại </a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sử dụng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tiêu dùng</a> cho tổ chức của họ trong quá trình hoạt động và vận hành </li>\r\n<li>Xu hướng chuyển dần sang thuê tài chính tín dụng </li>\r\n</ul>\r\n<p>3. Đặc điểm khách hàng </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Số lượng khách hàng ít </a>nhưng <a class=\"cloze\" href=\"#mce_temp_url#\">quy mô khách hàng lớn</a>==&gt; đề cao mối quan hệ bắt; </li>\r\n</ul>\r\n<p>marketing đặc trưng thay vì đại chúng </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tập chung</a> về mặt Địa Lý \r\n<ul>\r\n<li>Thị trường sản xuất tập trung gần nguồn nguyên liệu==&gt; dự báo Sự dịch chuyển trong nguồn nguyên liệu tính toán vận chuyển, Logistic </li>\r\n<li>Các doanh nghiệp bán lại tập trung hơn về mặt Địa Lý </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>4. Đặc điểm sản phẩm dịch vụ </p>\r\n<ul>\r\n<li>Về bản chất: có tính<a class=\"cloze\" href=\"#mce_temp_url#\"> kỹ thuật cao</a> và mua trên cơ sở các yêu cầu chi tiết kỹ thuậtthường là nguyên vật liệu thô và bán thực phẩm </li>\r\n<li>Chú trọng tới <a class=\"cloze\" href=\"#mce_temp_url#\">giao hàng đúng hạn</a> hỗ trợ kỹ thuật và dịch vụ rao bán </li>\r\n</ul>\r\n<p>5. đặc điểm của quá trình mua </p>\r\n<ul>\r\n<li>Được <a class=\"cloze\" href=\"#mce_temp_url#\">huấn luyện </a>chuyên nghiệp </li>\r\n<li>Có nhiều <a class=\"cloze\" href=\"#mce_temp_url#\">kỹ thuật mua hàng</a> cụ thể </li>\r\n<li>Trong suốt sự nghiệp người mua học hỏi cách thức mua hàng </li>\r\n<li>Khách hàng và người cung cấp thân thiện </li>\r\n<li>mua sắm qua mạng </li>\r\n<li>Nhiều người tham gia vào quá trình mua, có nhiều người ảnh hưởng tới quyết định </li>\r\n</ul>\r\n<p>Mua </p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2041', 'Đặc điểm hành vi mua của khách hàng tổ chức thương mại', null, null, '5', '0', '381', '467', '1551945272', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2042', null, null, null, '5', '2041', '381', '467', '1551945272', null, null, null, '<ul>\r\n<li>    Các quyết định mua: chọn <a class=\"cloze\" href=\"#mce_temp_url#\">mua hàng mới</a>, chọn <a class=\"cloze\" href=\"#mce_temp_url#\">nhà cung cấp</a>.</li>\r\n<li>    Những người tham gia quá trình mua: tùy thuộc quy mô, hình thức kinh doanh, loại hình công ty</li>\r\n<li>    Các đặc trưng trong hành vi mua: tương tự của sản xuất, khác biệt căn cứ mức độ hài lòng về sp,, điều kiện bán hàng, sự biến động doanh số, lợi nhuận,sức thu hút của sản phẩm với những hỗ trợ của nhà sản xuất.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2043', 'Đặc điểm hành vi mua của khách hàng tổ chức sản xuất?', null, null, '5', '0', '381', '467', '1551945328', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2044', null, null, null, '5', '2043', '381', '467', '1551945328', null, null, null, '<ul>\r\n<li>    Các hình thức mua: mua lặp lại, mua có điều chỉnh, mua mới.</li>\r\n<li>    Những người tham gia quá trình mua( trung tâm mua): người sử dụng, người ảnh hưởng, người ra quyết đinh, người mua,...</li>\r\n<li>    Những nhân tố ảnh hưởng: môi trường, tổ chức, quan hệ cá nhân trong tổ chức, cá nhân,...</li>\r\n<li>    Quá trình ra quyết định mua: 8 giai đoạn.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2045', 'Đặc điểm hành vi mua của khách hàng tổ chức Nhà Nước?', null, null, '5', '0', '381', '467', '1551945372', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2046', null, null, null, '5', '2045', '381', '467', '1551945372', null, null, null, '<ul>\r\n<li>    Những người tham gia quá trình mua: tùy theo đặc trưng của mỗi đơn vị.</li>\r\n<li>    Các yếu tố ảnh hưởng đến hành vi tổ chức người sử dụng, người ảnh hưởng, người quyết định, người mua nưng bị giám sát, theo dõi,...</li>\r\n<li>    Các quyết định mua: bị giới hạn ngân sách</li>\r\n<li>    Cachs thức đưa ra quyếtđịnh mua: đấu thầu công khai, hợp đồng thương lượng.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2047', 'Vẽ mô hình tổng quát về “ hành vi mua của khách hàng tổ chức .', null, null, '5', '0', '381', '467', '1551945388', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2048', null, null, null, '5', '2047', '381', '467', '1551945388', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2049', 'Bậc hành vi cá nhân.', null, null, '5', '0', '382', '467', '1551945849', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2050', null, null, null, '5', '2049', '382', '467', '1551945849', null, null, null, '<ul>\r\n<li>Động cơ cá nhân: cảm tính hay lí tính</li>\r\n<li>Học tập: so sánh các nhà cung cấp với hệ thống chỉ tiêu </li>\r\n<li>Nhận thức cá nhân: rủi ro, lòng tin, kinh nghiệm mua trong quá khứ...</li>\r\n<li>Quá trình xem xét thông tin: kiểm chứng thông tin - phụ thuộc sự phức tạp của việc mua, hệ thống ken thưởng...</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2051', 'Bậc hành vi mua: bậc hành vi nhóm.', null, null, '5', '0', '382', '467', '1551946004', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2052', null, null, null, '5', '2051', '382', '467', '1551946004', null, null, null, '<ul>\r\n<li>Hành vi tương tác giữa các cá nhân: trao đổi và tương tác giữa các cá nhân - lượng định qua: thời gian, thông tin hàng dọc, và hàng ngang.</li>\r\n<li>Trao đổi thông tin: trao đổi thông tin với các chuyên gia hoặc lãnh đạo</li>\r\n<li>Ra quyết định của trung tâm mua: mua mới, tái chọn mua có điều chỉnh, mua lặp lại.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2053', 'Bậc hành vi mua: bậc hành vi tổ chức', null, null, '5', '0', '382', '467', '1551946162', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2054', null, null, null, '5', '2053', '382', '467', '1551946162', null, null, null, '<ul>\r\n<li>Hành vi xây dựng mục tiêu mua: mục tiêu lí tính, mục tiêu cảm tính</li>\r\n<li>Tương tác giữa các tôt chức với nhà cung cấp: phụ thuộc lẫn nhau, tổ chức lựa chọn 1 hoặc 1 số nhà cung cấp</li>\r\n<li>Hành vi ra quyết định của tổ chức: ra quyết định cá nhân hoặc tập thể - tùy thuộc vào loại sản phẩm và tình huống mua hàng.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2055', 'Nêu các nhân tố ảnh hưởng đến hành vi mua hàng của khách hàng tổ chức?', null, null, '5', '0', '382', '467', '1551946343', '1551946487', null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2056', null, null, null, '5', '2055', '382', '467', '1551946343', '1551946487', null, null, '<p>- Các nhân tố môi trường</p>\r\n<ul>\r\n<li>Địa lý</li>\r\n<li>Kinh tế </li>\r\n<li>Công nghệ</li>\r\n<li>Chính trị-Pháp luật</li>\r\n<li>Văn hóa</li>\r\n<li>Cạnh tranh</li>\r\n</ul>\r\n<p>- Các nhân tố tổ chức</p>\r\n<ul>\r\n<li>mục tiêu, nhiệm vụ và định hướng phát triển</li>\r\n<li>Chính sách và thủ tục</li>\r\n<li>Cấu trúc tổ chức và văn hóa </li>\r\n<li>Vai trò xã hội </li>\r\n<li>Công nghệ</li>\r\n<li>Khen thưởng</li>\r\n</ul>\r\n<p>- Các nhân tố trung tâm mua </p>\r\n<ul>\r\n<li>Qui mô</li>\r\n<li>Chuẩn mực</li>\r\n<li>Thành phần - quyền lực</li>\r\n</ul>\r\n<p>- Các nhân tố người tham gia</p>\r\n<ul>\r\n<li>Tuổi và thu nhập</li>\r\n<li>Kiến thức cơ sở </li>\r\n<li>Địa vị</li>\r\n<li>Nhân cách</li>\r\n<li>Chính trị</li>\r\n<li>Đạo đức</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2057', 'Sự khác nhau giữa B2B và B2C?', null, null, '5', '0', '383', '467', '1551946648', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2058', null, null, null, '5', '2057', '383', '467', '1551946648', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhu cầu</a> và cấu trúc <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động marketing</a>: mức độ tập trung rất cao, số lượng ts nhưng quy mô lớn; tập trung địa lý; cầu ít co giãn so với giá; biên độ giao động nhu cầu cao.</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sản phẩm và dịch vụ</a>: tính kỹ thuật chi tiết cao; thường là nguyên vật liệu thô/ bán thành phẩm;chú trọng giao hàng, hỗ trợ kỹ thuật, dịch vụ sau bán,...</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quá trình mua</a>: chuyên nghiệp; quy trình đánh giá công khai, hướng tới dài hạn.</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Đặc điểm mkt-mix</a>: bán hàng cá nhân, phân phối quảng cáo và xúc tiến mang tính kỹ thuật.</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Trách nhiệm</a> mua sắm</li>\r\n<li>Quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">ra quyết định mua</a>: phức hợp hơn, chính thức hóa hơn, quan hệ người mua người bán chặt chẽ.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2059', 'Phân tích vai trò trong trung tâm mua của khách hàng tổ chức?', null, null, '5', '0', '386', '467', '1551947461', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2060', null, null, null, '5', '2059', '386', '467', '1551947461', null, null, null, '<p>1. Người sử dụng </p>\r\n<ul>\r\n<li> sẽ thực thi làm việc với các sản phẩm mua về </li>\r\n<li>Yêu cầu của họ sẽ là những thông số đầu vào cho quyết định cuối cùng của cá nhân </li>\r\n<li>trong trung tâm mu</li>\r\n</ul>\r\n<p>2. Người ảnh hưởng </p>\r\n<ul>\r\n<li>Cá nhân có thể tác động đến quyết định mua hàng </li>\r\n<li>Cung cấp thông tin quan trọng Mặc dù họ không tham gia trực tiếp vào quá trình mua </li>\r\n</ul>\r\n<ul>\r\n<li>Có thể là các kỹ sư cơ khí chuyên gia hiểu biết về sản phẩm chi tiết chi tiết sản phẩm </li>\r\n</ul>\r\n<ul>\r\n<li>Quan điểm của nhóm này được các cá nhân của trung tâm mua tiếp nhận với sự tôn trọng và đánh giá cao</li>\r\n</ul>\r\n<p>3. Người mua </p>\r\n<ul>\r\n<li>Những người mua chuyên nghiệp chú trọng của họ là việc mua sắm </li>\r\n<li>Kết quả làm việc công tác của họ cũng thường được đánh giá dựa trên kết quả của hoạt động mua sắm </li>\r\n</ul>\r\n<ul>\r\n<li>Nhiệm vụ của người mua: </li>\r\n<ul>\r\n<li>Có được đánh giá </li>\r\n<li> Đánh giá và lựa chọn nhà cung cấp </li>\r\n<li>Thương lượng </li>\r\n<li>Xử lý đơn hàng </li>\r\n<li>Xúc tiến giao hàng </li>\r\n<li>Thực hiện chính sách thu mua và mua sắm của tổ chức </li>\r\n<ul>\r\n<li>Phải xây dựng mối quan hệ với người mua sự phù hợp </li>\r\n</ul>\r\n</ul>\r\n</ul>\r\n<p>4. Người ra quyết định </p>\r\n<ul>\r\n<li>Đưa ra lựa chọn thực sự về sản phẩm dịch vụ </li>\r\n<li>Sản phẩm thiết bị cố định đắt tiền ăn có thể ban điều hành cấp cao trong tổ chức là người ra quyết định.  </li>\r\n</ul>\r\n<p>5. Người gác cổng </p>\r\n<ul>\r\n<li> Có nhiệm vụ kiểm soát dòng thông tin liên quan </li>\r\n<li> Có thể là một số nhân viên thư ký trợ lý </li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2061', 'Phân tích ảnh hưởng của nhân tố môi trường đến hành vi mua khách hàng tổ chức?', null, null, '5', '0', '382', '467', '1551948079', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2062', null, null, null, '5', '2061', '382', '467', '1551948079', null, null, null, '<p>- Địa lý:</p>\r\n<p>+ Sự phân bố, khoảng cách giữa người bán và mua </p>\r\n<p>+ Cơ sở hạ tầng</p>\r\n<p>-&gt; Chi phí và điều kiện cung ứng hàng hóa, hậu cần bị tác động</p>\r\n<p>-Kinh tế </p>\r\n<p>+ Các yếu tố kinh tế quốc tế </p>\r\n<p>+ Các chỉ số kinh tế: sự tăng trưởng; lạm phát, lãi ngân hàng...</p>\r\n<p>-&gt; Điều kiện tham gia thị trường và sự ổn định của cung ứng hàng hóa có thể bị ảnh hưởng</p>\r\n<p>- Công nghệ</p>\r\n<p>+ Công nghệ hiện tại: sản xuất và cung ứng hàng hóa</p>\r\n<p>+ Tốc độ thay đổi của công nghệ</p>\r\n<p>+ Khả năng áp dụng công nghệ của người mua, người bán </p>\r\n<p>-&gt;Các nhu cầu phát sinh, cách thức tìm kiếm thông tin, mua hàng... bị tác động</p>\r\n<p>- Chính trị - Pháp luật:</p>\r\n<p>+ Thể chế chính trị</p>\r\n<p>+ Quan điểm trong hợp tác quốc tế</p>\r\n<p>+ Hệ thống pháp luật và thi hành pháp luật</p>\r\n<p>-&gt; Ảnh hưởng mạnh mẽ đến hành vi mua của tổ chức và hoạt động của nhà cung câp</p>\r\n<p>- Văn hóa </p>\r\n<p>+ Thái độ, niềm tin của xã hội</p>\r\n<p>+ Phong tục, tập quán, thói quen</p>\r\n<p>+ Các giá trị và xu hướng trên thị trường</p>\r\n<p>-&gt; Ảnh hưởng đến khách hàng tổ chức cũng như cách thức giao dịch, vận hành tổ chức.</p>\r\n<p>- Cạnh tranh</p>\r\n<p>+ Số lượng và quy mô đối thủ cạnh tranh</p>\r\n<p>+ Sản phẩm thay thế</p>\r\n<p>-&gt; Thay đổi cách thức tìm kiếm, đánh giá và mua hàng của tổ chức.</p>\r\n<p> </p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2063', 'Nhân tố tổ chức ảnh hưởng đến hành vi mua khách hàng?', null, null, '5', '0', '382', '467', '1551948432', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2064', null, null, null, '5', '2063', '382', '467', '1551948432', null, null, null, '<p>- Mục tiêu, nhiệm vụ và định hướng</p>\r\n<p>+ Mục tiêu: Doanh số, vị trí, lợi nhuận...</p>\r\n<p>+ Nhiệm vụ: cung cấp sản phẩm, dịch vụ,...</p>\r\n<p>+ Định hướng phát triển: dài hạn, ngắn hạn,...</p>\r\n<p>- CHính sách và thủ tục</p>\r\n<p>+ Chính sách: chất lượng, chi phí,...</p>\r\n<p>+ Thủ tục: người tham gia, trình tự mua,...</p>\r\n<p>- Cấu trúc tổ chức và văn hóa </p>\r\n<p>+ Cấu trúc: quy mô, sắp xếp phòng ban,...</p>\r\n<p>+ Văn hóa: giá trị, hệ tư tưởng của tổ chức</p>\r\n<p>- Vai trò xã hội</p>\r\n<p>+ Vị trí, vai trò của doanh nghiệp trong xã hội: sản xuất, thương mại,...</p>\r\n<p>- Công nghệ: Nền tảng công nghệ của tổ chức -&gt; các loại sản phẩm cần mua và công nghệ mua </p>\r\n<p>- Khen thưởng </p>\r\n<p>+ Hệ thống khen thưởng </p>\r\n<p>+ Phương thức tổ chức khen thưởng</p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2065', 'Các nhân tố trung tâm mua ( quan hệ giữa các cá nhân).', null, null, '5', '0', '382', '467', '1551948641', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2066', null, null, null, '5', '2065', '382', '467', '1551948641', null, null, null, '<p>- Qui mô: phụ thuộc loại hình tổ chức và nghiệp vụ mua hàng - có thể thay đổi.</p>\r\n<p>- Chuẩn mực: những tiêu chuẩn hành vi được các thành viên trong trung tâm mua chấp nhận.</p>\r\n<p>+ Tiêu chuẩn chính thức</p>\r\n<p>+ Tiêu chuẩn không chính thức</p>\r\n<p>- Thành phần - Quyền lực: Các nguồn lực hỗ trợ ra quyết định</p>\r\n<p>+ Hiểu biết, kỹ năng</p>\r\n<p>+ Thông tin</p>\r\n<p>+ Trình độ, nghiệp vụ</p>\r\n<p> </p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2067', 'Các nhân tố các nhân người tham gia.', null, null, '5', '0', '382', '467', '1551948837', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2068', null, null, null, '5', '2067', '382', '467', '1551948837', null, null, null, '<p>- Tuổi và thu nhập </p>\r\n<p>- Kiến thức cơ sở: kinh nghiệm, học vấn, lối sông, khuynh hướng nghề nghiệp,...</p>\r\n<p>- Địa vị: Vị trí trong tổ chức...</p>\r\n<p>- Nhân cách: chấp nhận rủi ro cao/thấp</p>\r\n<p>- Chính trị: quan điểm, mối quan hệ </p>\r\n<p>- Đạo đức: lợi ích cá nhân/tổ chức.</p>\r\n<p> </p>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2069', 'Phân tích quan điểm về hành vi mua của khách hàng tổ chức (mô hình kinh tế)?', null, null, '5', '0', '384', '467', '1551948989', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2070', null, null, null, '5', '2069', '384', '467', '1551948989', null, null, null, '<p><span style=\"text-decoration-line: underline;\">-Mô hình kinh tế:</span></p>\r\n<ul>\r\n<li>  Các mô hình nhiệm vụ mua được đưa ra nhằm giải thích hvm tổ chức thông qua tập trung vào các biến số liện quan trực tiếp đến các quyết định mua</li>\r\n<li>  Các mô hình: mô hình giá tối thiểu; mô hình tổng chi phí thấp nhất; mô hình người mua có lý trí; mô hình mua bán hai chiều.</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2071', 'Phân tích quan điểm về hành vi mua của khách hàng tổ chức (mô hình cảm nhận)?', null, null, '5', '0', '384', '467', '1551949031', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2072', null, null, null, '5', '2071', '384', '467', '1551949031', null, null, null, '<p><span style=\"text-decoration-line: underline;\">Mô hình cảm nhận</span></p>\r\n<ul>\r\n<li> Các mô hình cảm tính gắn nhân tố con người vào quá trình mua của tổ chức và đưa vào các mô hình các biến số phi kinh tế.</li>\r\n<li>Mô hình:mô hình nâng cao cái tôi, mô hình rủi ro nhận thức, mô hình tương tác kép, mô hình quá trình tuyền bá</li>\r\n</ul>', null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2073', 'Phân tích quan điểm về hành vi mua của khách hàng tổ chức (mô hình phối hợp)?', null, null, '5', '0', '384', '467', '1551949075', null, null, null, null, null, '59', null, null, null, null, null);
INSERT INTO `question` VALUES ('2074', null, null, null, '5', '2073', '384', '467', '1551949075', null, null, null, '<p><span style=\"text-decoration-line: underline;\">Mô hình phối hợp</span></p>\r\n<ul>\r\n<li> Các mô hình phức tạp hay phối hợp xem xét nhiều biến số và tạp hợp biến số và có thể kết hợp các mô hình nhiệm vụ và cảm tính với nhau nhằm nhận thức tốt hơn về hành vi mua tổ chức.</li>\r\n<li>  Mô hình; mô hình quá trình ra quyết định, mô hình mạng mua, mô hình đơn giản về hành vi mua của Tune, mô hình hành vi mua khách hàng công nghiệp của Sheth, mô hình hành vi mua tổ chức của Webster và Wind.</li>\r\n</ul>', null, '59', null, null, null, null, null);
