INSERT INTO `question` VALUES ('2128', 'Câu 16: Nguyên nhân và ý nghĩa thắng lợi của cuộc kháng chiến chống thực dân Pháp (1945 - 1954).', null, null, '2', '0', '413', '464', '1552142296', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2129', 'Câu 17: .Hoàn cảnh lịch sử của CM VN sau tháng 7/1954.', null, null, '2', '0', '414', '464', '1552142537', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2130', 'Câu 18: Trình bày nội dung và ý nghĩa Nghị Quyết 15 (1/1959) của Đảng?', null, null, '2', '0', '415', '464', '1552143368', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2131', 'Câu 19: Nội dung đường lối chung của CM VN được đề ra tại Đại hội III của Đảng (9/1960).', null, null, '2', '0', '416', '464', '1552143624', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2132', 'Câu 20:  Trình bày hoàn cảnh lịch sử sau 1965 và nội dung Nghị quyết 12 (12/1965) của Đảng?', null, null, '2', '0', '417', '464', '1552144431', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2133', 'Quan điểm HCM về sự ra đời của ĐCSVN. Vì sao HCM đưa thêm PTYN và coi nó là một trong ba yếu tố dẫn tới sự ra đời  ĐCSVN?', null, null, '5', '0', '409', '45', '1552144608', '1552658527', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2134', null, null, null, '5', '2133', '409', '45', '1552144608', '1552658527', null, null, '<p>-  Quan điểm HCM về sự ra đời của ĐCSVN:</p>\r\n<p>     + Là sự kết hợp của <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa Mác - Lênin</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phong trào công nhân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phong trào yêu nước</a></span></strong></p>\r\n<p>     + Đánh giá cao vai trò của giai cấp <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân</a> là <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a> trong CMVN, xây dựng Đảng theo <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa Mác - Lênin</a></p>\r\n<p>-  HCM đưa thêm phong trào yêu nước và coi nó là một trong ba yếu tố dẫn tới sự ra đời ĐCSVN vì:</p>\r\n<p>     + Phong trào yêu nước có <a class=\"cloze\" href=\"#mce_temp_url#\">vai trò</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">vị trí</a> to lớn trong quá trình phát triển dân tộc VN, là <strong><span style=\"text-decoration: underline;\">nhân tố <a class=\"cloze\" href=\"#mce_temp_url#\">chủ đạo</a></span></strong> quyết định sự nghiệp chống giặc ngoại xâm của dân tộc ta</p>\r\n<p>     + Phong trào công nhân kết hợp được với phong trào <a class=\"cloze\" href=\"#mce_temp_url#\">yêu nước</a> bởi vì <strong><span style=\"text-decoration: underline;\">hai phong trào đó đều có <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu chung</a></span></strong> là: giải phóng dân tộc, làm cho dân tộc VN được hoàn toàn độc lập, xây dựng đất nước vững mạnh hơn.</p>\r\n<p>     + Phong trào nông dân kết hợp với phong trào <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân</a> thành <a class=\"cloze\" href=\"#mce_temp_url#\">quân chủ lực</a> của cách mạng</p>\r\n<p>     + Phong trào <a class=\"cloze\" href=\"#mce_temp_url#\">yêu nước</a> của trí thức VN là nhân tố <a class=\"cloze\" href=\"#mce_temp_url#\">quan trọng</a> thúc đẩy sự kết hợp các yếu tố cho sự ra đời của ĐCSVN</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2135', 'Quan điểm HCM về tính tất yếu, khách quan của công tác XD, chỉnh đốn Đảng?', null, null, '1', '0', '419', '45', '1552144760', '1552658654', null, null, 'Quan điểm HCM về tính tất yếu, khách quan của công tác XD, chỉnh đốn Đảng?', '<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:45.0pt 63.0pt 67.5pt 1.0in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">X&acirc;y dựng, chỉnh đốn Đảng bị chế định bởi qu&aacute; tr&igrave;nh ph&aacute;t triển li&ecirc;n tục của sự nghiệp c&aacute;ch mạng do Đảng l&atilde;nh đạo.</span></span></span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:45.0pt 63.0pt 67.5pt 1.0in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Do ảnh hưởng, t&aacute;c động của m&ocirc;i trường x&atilde; hội, c&aacute;c quan hệ x&atilde; hội đến c&aacute;n bộ, Đảng vi&ecirc;n v&agrave; tổ chức Đảng</span></span></span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:45.0pt 63.0pt 67.5pt 1.0in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">X&acirc;y dựng Đảng l&agrave; cơ hội để mỗi c&aacute;n bộ, Đảng vi&ecirc;n tự r&egrave;n luyện tốt hơn</span></span></span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:45.0pt 63.0pt 67.5pt 1.0in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Khi trở th&agrave;nh Đảng cầm quyền, việc <b><u>x&acirc;y dựng Đảng phải tiến h&agrave;nh li&ecirc;n tục, thường quy&ecirc;n hơn v&igrave; bản th&acirc;n quyền lực c&oacute; t&iacute;nh hai mặt.</u></b></span></span></span></span></span></span></span></li>\r\n</ul>', '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2136', 'Quan điểm HCM về tính tất yếu, khách quan của công tác XD, chỉnh đốn Đảng?', null, null, '5', '0', '419', '45', '1552144954', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2137', null, null, null, '5', '2136', '419', '45', '1552144954', null, null, null, '<p>-    Xây dựng, chỉnh đốn Đảng bị chế định bởi quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển liên tục</a> của sự nghiệp cách mạng do Đảng lãnh đạo.</p>\r\n<p>-    Do ảnh hưởng, tác động của <a class=\"cloze\" href=\"#mce_temp_url#\">môi trường</a> xã hội, các <a class=\"cloze\" href=\"#mce_temp_url#\">quan hệ</a> xã hội đến <a class=\"cloze\" href=\"#mce_temp_url#\">cán bộ</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng viên</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức Đảng</a></p>\r\n<p>-    Xây dựng Đảng là cơ hội để mỗi cán bộ, Đảng viên tự <a class=\"cloze\" href=\"#mce_temp_url#\">rèn luyện</a> tốt hơn</p>\r\n<p>-    Khi trở thành <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng cầm quyền</a>, việc <strong><span style=\"text-decoration: underline;\">xây dựng Đảng phải tiến hành <a class=\"cloze\" href=\"#mce_temp_url#\">liên tục</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">thường xuyên</a> hơn vì bản thân quyền lực có tính <a class=\"cloze\" href=\"#mce_temp_url#\">hai mặt</a>.</span></strong></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2138', 'Câu 21: Nguyên nhân và ý nghĩa thắng lợi của cuộc kháng chiến chống Mĩ cứu nước ( 1954 - 1975) là gì ?', null, null, '2', '0', '418', '464', '1552145105', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2139', 'Tại sao HCM khẳng định: XD Đảng là quy luật tồn tại, phát triển của Đảng”', null, null, '2', '0', '420', '45', '1552145189', '1552659354', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2140', 'Tại sao HCM khẳng định: XD Đảng là quy luật tồn tại, phát triển của Đảng”', null, null, '5', '0', '420', '45', '1552145318', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2141', null, null, null, '5', '2140', '420', '45', '1552145318', null, null, null, '<p>* Xây dựng, chỉnh đốn Đảng là nhiệm vụ <a class=\"cloze\" href=\"#mce_temp_url#\">tất yếu</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">thường xuyên</a>, vừa có tính cấp bách, vừa có tính <a class=\"cloze\" href=\"#mce_temp_url#\">lâu dài</a></p>\r\n<p>* Tính tất yếu khách quan của công tác xây dựng, chỉnh đốn Đảng:</p>\r\n<p>   -    Xây dựng, chỉnh đốn Đảng bị chế định bởi quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển liên tục</a> của sự nghiệp cách mạng do Đảng lãnh đạo.</p>\r\n<p>   -    Do ảnh hưởng, tác động của <a class=\"cloze\" href=\"#mce_temp_url#\">môi trường</a> xã hội, các <a class=\"cloze\" href=\"#mce_temp_url#\">quan hệ</a> xã hội đến <a class=\"cloze\" href=\"#mce_temp_url#\">cán bộ</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng viên</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức Đảng</a></p>\r\n<p>   -    Xây dựng Đảng là cơ hội để mỗi cán bộ, Đảng viên tự <a class=\"cloze\" href=\"#mce_temp_url#\">rèn luyện </a>tốt hơn</p>\r\n<p>   -    Khi trở thành <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng cầm quyền</a>, việc <strong><span style=\"text-decoration: underline;\">xây dựng Đảng phải tiến hành <a class=\"cloze\" href=\"#mce_temp_url#\">liên tục</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">thường xuyên</a> hơn vì bản thân quyền lực có tính <a class=\"cloze\" href=\"#mce_temp_url#\">hai mặt.</a></span></strong></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2142', 'Quan điểm HCM về vai trò ĐCSVN. Liên hệ thực tiễn VN?', null, null, '2', '0', '434', '45', '1552145517', '1552660880', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2143', 'a.	Quan điểm HCM về vai trò ĐCSVN:', null, null, '5', '0', '434', '45', '1552145611', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2144', null, null, null, '5', '2143', '434', '45', '1552145611', null, null, null, '<p>-    <strong><span style=\"text-decoration: underline;\">ĐCSVN giữ vai trò <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a> CMVN</span></strong>. Sự lãnh đạo <a class=\"cloze\" href=\"#mce_temp_url#\">đúng đắn</a> của Đảng là nhân tố hàng đầu để đưa CMVN đi đến thắng lợi: đề ra đường lối, phương châm , phương pháp CM đúng đắn</p>\r\n<p>-    Giác ngộ, giáo dục, tuyên truyền dân chúng để <a class=\"cloze\" href=\"#mce_temp_url#\">thức tỉnh</a> họ</p>\r\n<p>-    <a class=\"cloze\" href=\"#mce_temp_url#\">Tập hợp</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> đoàn kết nhân dân thành đội quân thật mạnh</p>\r\n<p>-    Sự ra đời, tồn tại và phát triển của ĐCSVN <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a> với quy luật tất yếu của xã hội</p>\r\n<p>-    Vai trò của Đảng đã được lịch sử chứng minh: kháng chiến chống Pháp, kháng chiến chống Mỹ đều dành được thắng lợi.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2145', 'b.	Liên hệ thực tiễn VN', null, null, '5', '0', '434', '45', '1552145894', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2146', null, null, null, '5', '2145', '434', '45', '1552145894', null, null, null, '<p>-    Về chính trị:</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Đường lối chính trị</span></strong> <a class=\"cloze\" href=\"#mce_temp_url#\">đúng đắn</a>, bản lĩnh chính trị <a class=\"cloze\" href=\"#mce_temp_url#\">vững vàng</a> trong mọi <a class=\"cloze\" href=\"#mce_temp_url#\">tình huống</a> phức tạp, mọi bước ngoặt hiểm nghèo, mọi giai đoạn CM khác nhau</p>\r\n<p>   +  Đường lối cứng rắn về <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a>, mềm dẻo về <a class=\"cloze\" href=\"#mce_temp_url#\">sách lược</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">linh hoạt</a> về biện pháp đấu tranh, tập hợp được <a class=\"cloze\" href=\"#mce_temp_url#\">lực lượng</a> của toàn dân, tranh thủ sự <a class=\"cloze\" href=\"#mce_temp_url#\">đồng tình</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">ủng hộ</a> của quốc tế</p>\r\n<p>-    Về tư tưởng:</p>\r\n<p>   +  Tư tưởng CM <a class=\"cloze\" href=\"#mce_temp_url#\">triệt để</a>, tư tưởng CM tiết công, chống chủ nghĩa <a class=\"cloze\" href=\"#mce_temp_url#\">cơ hội</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">giáo điều</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">bảo thủ</a></p>\r\n<p>   +  <a class=\"cloze\" href=\"#mce_temp_url#\">Kế thừa</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phát huy</a> những truyền thống tốt đẹp của dân tộc, tinh hoa VH nhận loại để giành thắng lợi cho CM</p>\r\n<p>-    Về tổ chức:</p>\r\n<p>   +  Tổ chức chính trị <a class=\"cloze\" href=\"#mce_temp_url#\">trong sạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">vững mạnh</a>; tổ chức chiến đấu <a class=\"cloze\" href=\"#mce_temp_url#\">kiên cường</a> với các nguyên tắc nền móng được tuân thủ nghiệm ngặt khi hành động thì muôn người như một</p>\r\n<p>   +  Tổ chức trọng chất lượng hơn số lượng, nâng cao vai trò của Đảng</p>\r\n<p>-    Về đạo đức, lối sống:</p>\r\n<p>   +  Tu dưỡng đạo đức, cần, kiệm, liêm, chính, chí công vô tư, nâng cao năng lực, gắn bó với nhân dân, dám xả thân vì sự nghiệp CM</p>\r\n<p>   +  Không ngừng học tập theo tấm gương đạo đức HCM</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2147', 'Các nguyên tắc tổ chức, sinh hoạt Đảng theo quan điểm HCM?', null, null, '2', '0', '435', '45', '1552146342', '1552661031', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2148', 'Các nguyên tắc tổ chức, sinh hoạt Đảng theo quan điểm HCM?', null, null, '5', '0', '435', '45', '1552146677', '1552661053', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2149', null, null, null, '5', '2148', '435', '45', '1552146677', '1552661053', null, null, '<p>- <em>Tập trung dân chủ:</em></p>\r\n<p>   +  Là <strong><span style=\"text-decoration: underline;\">nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">cơ bản</a></span></strong> của Đảng</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Tập trung</span></strong> là sự <a class=\"cloze\" href=\"#mce_temp_url#\">thống nhất</a> về tư tưởng, tổ chức và hoạt động. <a class=\"cloze\" href=\"#mce_temp_url#\">Thiểu số</a> phải phục tùng <a class=\"cloze\" href=\"#mce_temp_url#\">đa số</a>, cấp dưới phục tùng cấp trên và các Đảng viên phải chấp hành <a class=\"cloze\" href=\"#mce_temp_url#\">nghị quyết</a> của Đảng</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Dân chủ</span></strong> là mọi vấn đề, mọi người được quyền <a class=\"cloze\" href=\"#mce_temp_url#\">tự do</a> bày tỏ ý kiến của mình, góp phần tìm ra <a class=\"cloze\" href=\"#mce_temp_url#\">chân lí</a></p>\r\n<p>   +  Mối quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">khăng khít</a>, là hai vế của một nguyên tắc, tập trung trên <a class=\"cloze\" href=\"#mce_temp_url#\">nền tảng</a> dân chủ, dân chủ dưới <a class=\"cloze\" href=\"#mce_temp_url#\">sự chỉ đạo</a> của tập trung</p>\r\n<p>- <em>Tập thể lãnh đạo, cá nhận phụ trách</em></p>\r\n<p>   +  Là <strong><span style=\"text-decoration: underline;\">nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a></span></strong> của Đảng</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Tập thể lãnh đạo</span></strong> vì một người dù khôn ngoan, tài giỏi đến mấy cũng không thể trông thấy và xem xét được mọi mặt của vấn đề. Nhiều người thì nhiều <a class=\"cloze\" href=\"#mce_temp_url#\">kinh nghiệm</a>, người thấy mặt này, người thấy mặt khác của vấn đề</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Cá nhân phụ trách</span></strong> vì như thế mới có <a class=\"cloze\" href=\"#mce_temp_url#\">chuyên trách</a>, công việc mới chạy</p>\r\n<p>- <em>Tự phê bình và phê bình:</em></p>\r\n<p>   +  Là <strong><span style=\"text-decoration: underline;\">nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">sinh hoạt</a></span></strong> của Đảng, là luật phát triển của Đảng</p>\r\n<p>   +  Mục đích: làm cho cán bộ, Đảng viên <a class=\"cloze\" href=\"#mce_temp_url#\">phát huy</a> ưu điểm, <a class=\"cloze\" href=\"#mce_temp_url#\">khắc phục</a> hạn chế, vươn tới chân, thiện, mĩ</p>\r\n<p>   +  Thái độ, phương pháp phải tiến hành thường ngày, phải <a class=\"cloze\" href=\"#mce_temp_url#\">thẳng thắn</a>, chân thành, trung thực; không nể nang, không giấu giếm, phải có tình yêu thương lẫn nhau</p>\r\n<p>- <em>Kỉ luật nghiêm minh,tự giác:</em></p>\r\n<p>   +  Sức mạnh của Đảng và của mỗi Đảng viên <a class=\"cloze\" href=\"#mce_temp_url#\">bắt nguồn</a> từ tổ chức ý thức kỉ luật nghiêm minh, tự giác</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Tính nghiêm minh</span></strong>: đòi hỏi tất cả các tổ chức Đảng, mọi Đảng viên đều <a class=\"cloze\" href=\"#mce_temp_url#\">bình đẳng</a> trước điều lệ Đảng, trước pháp luật VN, trước quyết định của Đảng</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Tính tự giác</span></strong>: mỗi cán bộ, Đảng viên và tổ chức Đảng <a class=\"cloze\" href=\"#mce_temp_url#\">tự nguyện</a> phấn đấu cho lí tưởng cộng sản CN</p>\r\n<p>- <em>Đoàn kết thống nhất trong Đảng</em></p>\r\n<p>   +  Sự <strong><span style=\"text-decoration: underline;\">đoàn kết thống nhất</span></strong> của Đảng phải <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">dựa trên</a> cơ sở lý luận của Đảng là CNMLN</span></strong>; cương lĩnh, điều lệ Đảng; đường lối, quan điểm của Đảng; nghị quyết của tổ chức đảng các cấp.</p>\r\n<p>   +  Muốn đoàn kết thống nhất trong Đảng phải thực hành <a class=\"cloze\" href=\"#mce_temp_url#\">dân chủ</a> rộng rãi ở trong Đảng</p>\r\n<p>   +  Có đoàn kết tốt thì mới tạo ra cơ sở vững chắc để thống nhất <a class=\"cloze\" href=\"#mce_temp_url#\">ý chí</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">hành động</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2150', 'Vai trò của đại đoàn kết DT trong sự nghiệp CMVN?', null, null, '1', '0', '436', '45', '1552146895', '1552661426', '<p style=\"margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">-&nbsp;</span></span></span><b><u><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Đại đo&agrave;n kết DT l&agrave; một chiến lược cơ bản, nhất qu&aacute;n v&agrave; l&acirc;u d&agrave;i</span></span></span></u></b></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:13px\"><span style=\"font-size:11pt\"><span style=\"line-height:115%\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">-&nbsp;</span></span></span><b><u><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">đo&agrave;n kết DT phải được qu&aacute;n triệt từ chủ trương, đường lối</span></span></span></u></b></span></span></span></p>', null, 'Vai trò của đại đoàn kết DT trong sự nghiệp CMVN?', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 63.0pt 67.5pt 1.0in 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Đại đo&agrave;n kết DT l&agrave; vấn đề chiến lược đảm bảo th&agrave;nh c&ocirc;ng của CM:</span></span></span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 63.0pt 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><b><u><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đại đo&agrave;n kết DT l&agrave; một chiến lược cơ bản, nhất qu&aacute;n v&agrave; l&acirc;u d&agrave;i</span></span></span></u></b><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">, xuy&ecirc;n suốt qtr&igrave;nh CM v&agrave; l&agrave; vấn đề sống c&ograve;n của CMVN</span></span></span></span></span></span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 63.0pt 67.5pt 1.0in 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đại đo&agrave;n kết DT l&agrave; cội nguồn tạo n&ecirc;n sức mạnh</span></span></span></span></span></span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 63.0pt 67.5pt 1.0in 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">- Đại đo&agrave;n kết DT l&agrave; mục ti&ecirc;u, nhiệm vụ h&agrave;ng đầu của Đảng, của cả DT</span></span></span></span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 58.5pt 63.0pt 67.5pt 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">ĐCSVN - lực lượng duy nhất l&atilde;nh đạo CMVN, v&igrave; vậy đại <b><u>đo&agrave;n kết DT phải được qu&aacute;n triệt từ chủ trương, đường lối</u></b> tới hoạt động thực tiễn của Đảng</span></span></span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 63.0pt 67.5pt 1.0in 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đại đo&agrave;n kết DT l&agrave; nhiệm vụ h&agrave;ng đầu của mọi giai đoạn CM</span></span></span></span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"line-height:130%\"><span style=\"tab-stops:.5in 40.5pt 63.0pt 67.5pt 1.0in 81.0pt 85.5pt 112.5pt 117.0pt 121.5pt 135.0pt 2.0in 148.5pt 153.0pt 157.5pt 2.25in\"><span style=\"font-family:Calibri,sans-serif\"><span lang=\"EN-US\" style=\"font-size:12.0pt\"><span style=\"line-height:130%\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Đại đo&agrave;n kết DT c&ograve;n l&agrave; nhiệm vụ h&agrave;ng đầu của cả DT v&igrave; CM l&agrave; sự nghiệp của quần ch&uacute;ng phải do quần ch&uacute;ng, v&igrave; quần ch&uacute;ng.</span></span></span></span></span></span></span></li>\r\n</ul>', '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2151', 'Vai trò của đại đoàn kết DT trong sự nghiệp CMVN?', null, null, '5', '0', '436', '45', '1552147015', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2152', null, null, null, '5', '2151', '436', '45', '1552147015', null, null, null, '<p>- Đại đoàn kết DT là vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> đảm bảo thành công của CM:</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Đại đoàn kết DT là một chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">cơ bản</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">nhất quán</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">lâu dài</a></span></strong>, xuyên suốt qtrình CM và là vấn đề sống còn của CMVN</p>\r\n<p>   +  Đại đoàn kết DT là cội nguồn tạo nên <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh</a></p>\r\n<p>- Đại đoàn kết DT là <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a>, nhiệm vụ <a class=\"cloze\" href=\"#mce_temp_url#\">hàng đầu</a> của Đảng, của cả DT</p>\r\n<p>   +  ĐCSVN - lực lượng duy nhất lãnh đạo CMVN, vì vậy đại <strong><span style=\"text-decoration: underline;\">đoàn kết DT phải được quán triệt từ <a class=\"cloze\" href=\"#mce_temp_url#\">chủ trương</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đường lối</a></span></strong> tới <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động thực tiễn </a>của Đảng</p>\r\n<p>   +  Đại đoàn kết DT là nhiệm vụ hàng đầu của <a class=\"cloze\" href=\"#mce_temp_url#\">mọi giai đoạn</a> CM</p>\r\n<p>   +  Đại đoàn kết DT còn là nhiệm vụ hàng đầu của cả DT vì CM là sự nghiệp của quần chúng phải do <a class=\"cloze\" href=\"#mce_temp_url#\">quần chúng</a>, vì <a class=\"cloze\" href=\"#mce_temp_url#\">quần chúng</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2154', 'Quan niệm của HCM về mục đích, lý tưởng của Đảng cầm quyền', null, null, '5', '0', '405', '51', '1552196929', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2155', null, null, null, '5', '2154', '405', '51', '1552196929', null, null, null, '<p>Quan niệm của HCM về mục đích, lý tưởng của Đảng cầm quyền: phấn đấu cho <a class=\"cloze\" href=\"#mce_temp_url#\">tổ quốc hoàn toàn độc lập</a>, cho <a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa xã hội hoàn toàn thắng lợi</a> trên đất nướcc ta và trên toàn thế giới.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2156', 'Luận điểm: “Đảng cầm quyền vừa là người lãnh đạo, vừa là người đầy tớ trung thành của nhân dân”', null, null, '5', '0', '405', '51', '1552197002', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2157', null, null, null, '5', '2156', '405', '51', '1552197002', null, null, null, '<p>- Đảng cầm quyền là người lãnh đạo<br />  + Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">quyền lãnh đạo duy nhất</a> của Đảng đối với toàn thể dân tộc sau khi Đảng lãnh đạo <a class=\"cloze\" href=\"#mce_temp_url#\">chính quyền nhà nước<br /></a>  + Xác định đối tượng lãnh đạo: <a class=\"cloze\" href=\"#mce_temp_url#\">toàn thể dân tộc Việt Nam<br /></a>  + Xác định điều kiện lãnh đạo: <a class=\"cloze\" href=\"#mce_temp_url#\">vừa có đạo đức vừa có trí tuệ<br /></a>  + Phương thức lãnh đạo của Đảng:</p>\r\n<ul>\r\n<li>Đề ra <a class=\"cloze\" href=\"#mce_temp_url#\">đường lối, nghị quyết, chủ trương</a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Vận dụng</a>, thuyết phục, <a class=\"cloze\" href=\"#mce_temp_url#\">tuyên truyền</a></li>\r\n<li>Thông qua <a class=\"cloze\" href=\"#mce_temp_url#\">công tác cán bộ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">sự nêu gương</a> của Đảng viên</li>\r\n</ul>\r\n<p>- Đảng cầm quyền là người đầy tớ trung thành của nhân dân:<br />  + Thái độ <a class=\"cloze\" href=\"#mce_temp_url#\">tận tâm tận tụy</a> để phục vụ nhân dân<br />  + Vừa có <a class=\"cloze\" href=\"#mce_temp_url#\">đạo đức</a> vừa có <a class=\"cloze\" href=\"#mce_temp_url#\">năng lực</a></p>\r\n<p>=&gt; Hai khái niệm “người lãnh đạo”, “người đầy tớ” tuy khác nhau nhưng <a class=\"cloze\" href=\"#mce_temp_url#\">gắn bó thống nhất</a> trong <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ biện chứng</a> với nhau. Dù là “người lãnh đạo” hay “người đầy tớ” đều cùng chung mục đích là <a class=\"cloze\" href=\"#mce_temp_url#\">vì dân</a>. Làm tốt <a class=\"cloze\" href=\"#mce_temp_url#\">chức năng</a> “lãnh đạo” và làm tròn <a class=\"cloze\" href=\"#mce_temp_url#\">nhiệm vụ</a> “đầy tớ” cho nhân dân là cơ sở vững chắc nhất <a class=\"cloze\" href=\"#mce_temp_url#\">đảm bảo uy tín</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">năng lực lãnh đạo</a> của Đảng.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2159', 'Nội dung xây dựng Đảng về tổ chức, cán bộ và công tác cán bộ theo quan điểm của HCM?', null, null, '5', '0', '437', '51', '1552197430', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2160', null, null, null, '5', '2159', '437', '51', '1552197430', null, null, null, '<p>- Xây dựng Đảng về tổ chức:</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Sức mạnh của Đảng</a> bắt nguồn từ tổ chức. ĐCSVN được tổ chức theo <a class=\"cloze\" href=\"#mce_temp_url#\">hệ thống từ trung ương đến cơ sở</a> rất chặt chẽ và có tính <a class=\"cloze\" href=\"#mce_temp_url#\">kỉ luật</a> cao.</p>\r\n<p>  + Trong hệ thống tổ chức của Đảng, HCM rất coi trọng <a class=\"cloze\" href=\"#mce_temp_url#\">vai trò của chi bộ</a>.</p>\r\n<p>- Cán bộ, công tác cán bộ của Đảng:</p>\r\n<p>  + Vị trí, vai trò của cán bộ là <a class=\"cloze\" href=\"#mce_temp_url#\">cái dây chuyền</a> của bộ máy, muốn việc thành công hay thất bại là do <a class=\"cloze\" href=\"#mce_temp_url#\">cán bộ</a> tốt hay kém.</p>\r\n<p>  + Công tác cán bộ là <a class=\"cloze\" href=\"#mce_temp_url#\">công tác gốc</a> của Đảng, bao hàm <a class=\"cloze\" href=\"#mce_temp_url#\">các mắt khâu liên hoàn</a>: <a class=\"cloze\" href=\"#mce_temp_url#\">tuyển chọn</a> cán bộ; <a class=\"cloze\" href=\"#mce_temp_url#\">đào tạo</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">huấn luyện</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">bồi dưỡng</a> cán bộ, <a class=\"cloze\" href=\"#mce_temp_url#\">đánh giá</a> đúng cán bộ...</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2162', 'Đại đoàn kết dân tộc phải trở thành lực lượng vật chất có tổ chức? Hình thức tồn tại của Mặt trận dân tộc thống nhất ở VN?', null, null, '5', '0', '438', '51', '1552197857', '1552635899', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2163', null, null, null, '5', '2162', '438', '51', '1552197857', '1552635899', null, null, '<p>- Đại đoàn kết dân tộc biến thành <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh vật chất</a>, trở thành <a class=\"cloze\" href=\"#mce_temp_url#\">lực lượng vật chất</a> có tổ chức là <a class=\"cloze\" href=\"#mce_temp_url#\">Mặt trận dân tộc thống nhất</a>.</p>\r\n<p>- Mặt trận dân tộc thống nhất có nhiều tên gọi khác nhau qua các thời kì:</p>\r\n<p>  + Hội phản đế đồng minh Đông Dương (<a class=\"cloze\" href=\"#mce_temp_url#\">1930</a>)</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Măt trận dân chủ</a> (1936)</p>\r\n<p>  + Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân phản đế</a> (1939)</p>\r\n<p>  + Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">Việt Minh</a> (1941)</p>\r\n<p>  + Mặt trận Liên Việt (<a class=\"cloze\" href=\"#mce_temp_url#\">1946</a>)</p>\r\n<p>  + Mặt trận dân tộc <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng miền Nam</a> VN (1960)</p>\r\n<p>  + Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">tổ quốc Việt Nam</a> (1955,1976)</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2165', 'Quan điểm HCM về lực lượng đoàn kết Quốc tế?', null, null, '5', '0', '439', '51', '1552198369', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2166', null, null, null, '5', '2165', '439', '51', '1552198369', null, null, null, '<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Phong trào cộng sản</a> và công nhân thế giới: HCM cho rằng, sự đoàn kết giữa <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp vô sản quốc tế</a> là sự bảo đảm vững chắc cho thắng lợi của <a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa Cộng sản</a>.</p>\r\n<p>- Phong trào đấu tranh <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng dân tộc</a>: HCM đã phát hiện ra <a class=\"cloze\" href=\"#mce_temp_url#\">âm mưu chia rẽ dân tộc</a> của <a class=\"cloze\" href=\"#mce_temp_url#\">các nước đế quốc</a>, tạo sự <a class=\"cloze\" href=\"#mce_temp_url#\">biệt lập</a>, đối kháng và <a class=\"cloze\" href=\"#mce_temp_url#\">thù ghét</a> dân tộc, chủng tộc.. nhằm làm <a class=\"cloze\" href=\"#mce_temp_url#\">suy yếu</a> phong trào đấu tranh giải phóng dân tộc ở các nước thuộc địa.</p>\r\n<p>- Các <a class=\"cloze\" href=\"#mce_temp_url#\">lực lượng tiến bộ</a>, những người <a class=\"cloze\" href=\"#mce_temp_url#\">yêu chuộng hòa bình</a>, dân chủ, tự do và công lý: Gắn cuộc đấu tranh vì độc lập của dân tộc với <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu hòa bình</a>, tự do và công lý, HCM đã <a class=\"cloze\" href=\"#mce_temp_url#\">khơi gợi lương tri</a> của loài người tiến bô, tạo nên những <a class=\"cloze\" href=\"#mce_temp_url#\">tiếng nói ủng hộ</a> mạnh mẽ từ các tổ chức </p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2168', 'Quan điểm HCM về hình thức đoàn kết Quốc tế?', null, null, '5', '0', '440', '51', '1552198691', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2169', null, null, null, '5', '2168', '440', '51', '1552198691', null, null, null, '<p>Tư tưởng đại đoàn kết vì <a class=\"cloze\" href=\"#mce_temp_url#\">thắng lợi cách mạng</a> của HCM đã định hướng cho việc <a class=\"cloze\" href=\"#mce_temp_url#\">hình thành 4 tầng mặt trận</a>:</p>\r\n<p>- Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">đại đoàn kết dân tộc</a></p>\r\n<p>- Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">đoàn kết Việt - Miên - Lào</a></p>\r\n<p>- Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân Á Phi</a> đoàn kết với Việt Nam</p>\r\n<p>- Mặt trận <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân thế giới</a> đoàn kết với Việt Nam chống <a class=\"cloze\" href=\"#mce_temp_url#\">Đế quốc xâm lược</a></p>\r\n<p>=&gt;  Đây là sự <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển rực rỡ nhất</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">thắng lơi to lớn nhất</a> của tư tưởng đại đoàn kết của HCM</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2171', 'Quan điểm HCM  về điều kiện thực hiện đại đoàn kết dân tộc?', null, null, '5', '0', '441', '51', '1552199177', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2172', null, null, null, '5', '2171', '441', '51', '1552199177', null, null, null, '<p>- Cần phải <a class=\"cloze\" href=\"#mce_temp_url#\">thừa kế truyền thống yêu nước</a>, nhân nghĩa, <a class=\"cloze\" href=\"#mce_temp_url#\">đoàn kết</a> của dân tộc. Đó là <a class=\"cloze\" href=\"#mce_temp_url#\">cội nguồn sức mạnh</a> vô địch đề cả dân tộc <a class=\"cloze\" href=\"#mce_temp_url#\">chiến đấu và chiến thắng</a> mọi thiên tai, địch họa, làm cho <a class=\"cloze\" href=\"#mce_temp_url#\">đất nước được trường tồn</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">bản sắc dân tộc</a> được giữ vững</p>\r\n<p>- Phải có <a class=\"cloze\" href=\"#mce_temp_url#\">lòng khoan dung độ lượng</a> với con người. Đó là <a class=\"cloze\" href=\"#mce_temp_url#\">sự tiếp nối</a> và phát triển <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống nhân ái bao dung</a> của dân tộc</p>\r\n<p>- Cần có <a class=\"cloze\" href=\"#mce_temp_url#\">niềm tin</a> vào nhân dân. Theo Người, dân là <a class=\"cloze\" href=\"#mce_temp_url#\">chỗ dựa vững chắc</a> của Đảng, là <a class=\"cloze\" href=\"#mce_temp_url#\">nguồn sức mạnh</a> vô tận và vô địch của khối đại đoàn kết, quyết định <a class=\"cloze\" href=\"#mce_temp_url#\">thắng lợi</a> của cách mạng, là nền, gốc và chủ thể của <a class=\"cloze\" href=\"#mce_temp_url#\">Mặt trận</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2174', 'Quan điểm của HCM: “đại đoàn kết dân tộc là vấn đề có ý nghĩa chiến lược quyết định thành công của cách mạng”?', null, null, '5', '0', '442', '51', '1552199535', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2175', null, null, null, '5', '2174', '442', '51', '1552199535', null, null, null, '<p>- Đại đoàn kết dân tộc là một <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược cơ bản</a>, nhất quán và lâu dài, <a class=\"cloze\" href=\"#mce_temp_url#\">xuyên suốt</a> tiến trình cách mạng</p>\r\n<p>- Đại đoàn kết dân tộc là <a class=\"cloze\" href=\"#mce_temp_url#\">cội nguồn tạo nên sức mạnh</a>.</p>\r\n<p>=&gt; Nhờ <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng nhất quán</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">chính sách mặt trận</a> đúng đắn, Đảng ta và Chủ tịch HCM đã xây dựng thành công <a class=\"cloze\" href=\"#mce_temp_url#\">khối Đại đoàn kết dân tộc</a>, đưa <a class=\"cloze\" href=\"#mce_temp_url#\">cách mạng Việt Nam</a> giành được nhiều thắng lợi to lớn.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2177', 'Quan điểm HCM về nhà nước do dân?', null, null, '5', '0', '443', '51', '1552200194', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2178', null, null, null, '5', '2177', '443', '51', '1552200194', null, null, null, '<p>- Nhà nước do <a class=\"cloze\" href=\"#mce_temp_url#\">dân lập nên</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">dân ủng hộ</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">dân làm chủ</a>. Vì thế nhiệm vụ của những <a class=\"cloze\" href=\"#mce_temp_url#\">người cách mạng</a> là phải làm cho dân hiểu, làm cho dân giác ngộ để <a class=\"cloze\" href=\"#mce_temp_url#\">nâng cao được trách nhiệm làm chủ</a>, ý thức trách nhiệm chăm lo <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng nhà nước</a> của mình.</p>\r\n<p>- Quyền tham gia <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý nhà nước</a> của dân được thể hiện:</p>\r\n<p>+ Toàn bộ <a class=\"cloze\" href=\"#mce_temp_url#\">công dân</a> có quyền bầu ra <a class=\"cloze\" href=\"#mce_temp_url#\">Quốc hội</a></p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Quốc hội</a> bầu ra Chủ tịch nước, <a class=\"cloze\" href=\"#mce_temp_url#\">Ủy ban thường vụ Quốc hội</a> và Hội đồng Chính phủ</p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Hội đồng Chính phủ</a> là cơ quan hành chính <a class=\"cloze\" href=\"#mce_temp_url#\">cao nhất</a> của nhà nước, thực hiện các <a class=\"cloze\" href=\"#mce_temp_url#\">nghị quyết của Quốc hội</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">chấp hành pháp luật</a></p>\r\n<p>+ Mọi công việc của bộ máy nhà nước trong việc <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý xã hội</a> đều được thực hiện <a class=\"cloze\" href=\"#mce_temp_url#\">ý chí của dân</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2179', 'Câu 1:Trình bày  Đặc trưng của quá trình Công nghiệp hóa ở VN thời kì trước đổi mới?', null, null, '2', '0', '424', '464', '1552203920', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2180', 'Câu 2: Thuận lợi của VN khi tiến hành công nghiệp hóa, hiện đại hóa đất nước thời kì đổi mới là gì ?', null, null, '2', '0', '425', '464', '1552204704', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2181', 'Câu 3:Trình bày Tác động của bối cảnh quốc tế đến quá trình thực hiện công nghiệp hóa, hiện đại hóa ở VN hiện nay ?', null, null, '2', '0', '426', '464', '1552205355', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2182', 'Câu 4: Quan điểm công nghiệp hóa, hiện đại hóa của Đảng thời kì đổi mới là gì ?', null, null, '2', '0', '427', '464', '1552205736', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2183', 'Câu 5: Chủ trương đẩy mạnh công nghiệp hóa, hiện đại hóa nông nghiệp, nông thôn của Đảng thời kì đổi mới.', null, null, '2', '0', '428', '464', '1552230766', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2184', 'Câu 6: Khái niệm và đặc trưng của Kinh tế tri thức là gì ?', null, null, '2', '0', '429', '464', '1552231271', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2185', 'Câu 7: Đặc trưng của cơ chế quản lí kinh tế kế hoạch hóa tập trung quan liêu bao cấp ở VN thời kì trước đổi mới.', null, null, '2', '0', '430', '464', '1552231922', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2186', 'Câu 8: Tại sao phát triển kinh tế thị trường ở VN là tất yếu khách quan?', null, null, '2', '0', '431', '464', '1552232356', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2187', 'Đại đoàn kết DT là đại đoàn kết toàn dân? Vận dụng quan điểm này trong XD khối đại đoàn kết DT hiện nay?', null, null, '2', '0', '466', '45', '1552233294', '1552661815', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2188', 'Đại đoàn kết DT là đại đoàn kết toàn dân? Vận dụng quan điểm này trong XD khối đại đoàn kết DT hiện nay?', null, null, '5', '0', '466', '45', '1552234237', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2189', null, null, null, '5', '2188', '466', '45', '1552234237', null, null, null, '<p>-  Đại đoàn kết DT là đại đoàn kết toàn dân:</p>\r\n<p>   +  Đại đoàn kết DT bao gồm nhiều <a class=\"cloze\" href=\"#mce_temp_url#\">tầng nấc</a>, nhiều cấp độ các quan hệ liên kết qua lại giữa các thành viên các <a class=\"cloze\" href=\"#mce_temp_url#\">bộ phận</a>, các <a class=\"cloze\" href=\"#mce_temp_url#\">lực lượng</a> của DT</p>\r\n<p>   +  Nói đại đoàn kết DT cũng có nghĩa là phải <strong><span style=\"text-decoration: underline;\">tập hợp được tất cả mọi người dân trong <a class=\"cloze\" href=\"#mce_temp_url#\">cuộc đấu tranh chung</a></span></strong> =&gt; Nội hàm khái niệm đại đoàn kết trong TTHCM rất <a class=\"cloze\" href=\"#mce_temp_url#\">phong phú</a></p>\r\n<p>   +  Trong quá trình XD khối đại đoàn kết toàn dân, phải đứng vững trên lập trường những giai cấp công nhân giải quyết hài hòa mỗi quan hệ giai cấp - dân tộc.</p>\r\n<p>-  XD khối đại đoàn kết DT hiện nay:</p>\r\n<p>   +  <a class=\"cloze\" href=\"#mce_temp_url#\">Củng cố</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển</a> nhằm rửa nhục đói nghèo, lạc hậu về <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">khoa học</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">công nghệ</a> so với các nước trên TG</p>\r\n<p>   +  Khơi dậy tinh thần <a class=\"cloze\" href=\"#mce_temp_url#\">tự hào</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tự tôn</a> DT; phát huy tinh thần tự lực tự cường, vượt qua mọi thách thức</p>\r\n<p>   +  Phát huy tính <a class=\"cloze\" href=\"#mce_temp_url#\">năng động</a> của mỗi người, của cả cộng đồng</p>\r\n<p>   +  XD Đảng cầm quyền thật sự <a class=\"cloze\" href=\"#mce_temp_url#\">trong sạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">vững mạnh</a>; một chế độ do dân là chủ và làm chủ; một Nhà nước của dân, do dân và vì dân</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2190', 'Câu 9: Ưu và nhược điểm của Kinh tế thị trường là gì ?', null, null, '2', '0', '432', '464', '1552235210', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2191', 'Câu 10: Tư duy của Đảng về Kinh tế thị trường từ Đại hội VI (1986) đến Đại hội XII (2016) là gì ?', null, null, '2', '0', '433', '464', '1552235569', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2192', 'Câu 11: Phát triển Kinh tế thị trường có mâu thuẫn với mục tiêu định hướng XHCH ở VN hay không? Vì sao?', null, null, '2', '0', '444', '464', '1552235955', null, null, null, null, null, '54', null, null, null, null, null);
