INSERT INTO `question` VALUES ('2194', 'Câu 13: Trình bày hoàn cảnh thế giới, trong nước (1975 - 1985) và nội dung đường lối đối ngoại của Đảng thời kì trước đổi mới?', null, null, '2', '0', '446', '464', '1552269643', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2195', 'Câu 14: Hoàn cảnh thế giới, trong nước sau năm 1986 và yêu cầu nhiệm vụ của Đường lối đối ngoại của Đảng là gì ?', null, null, '2', '0', '447', '464', '1552270024', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2196', 'Câu 15: Thời cơ và thách thức từ xu hướng toàn cầu hóa tới VN trong thời kì đổi mới là gì ?', null, null, '2', '0', '448', '464', '1552270556', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2197', 'Câu 16: Nội dung và ý nghĩa thực tiễn Nghị Quyết 13 (5/1988) của Bộ Chính Trị?', null, null, '2', '0', '449', '464', '1552270932', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2198', 'Câu 17: Phân biệt hội nhập quốc tế và hội nhập kinh tế quốc tế?', null, null, '2', '0', '450', '464', '1552271578', null, null, null, null, null, '54', null, null, null, null, null);
