INSERT INTO `question` VALUES ('2200', 'Câu 19: Vì sao tại Đại hội XI (2011) Đảng thay chủ trương hội nhập kinh tế quốc tế bằng chủ trương hội nhập quốc tế?', null, null, '2', '0', '452', '464', '1552273022', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2201', 'Câu 1: Nhận thức mới của Đảng về mối quan hệ giữa đổi mới kinh tế và đổi mới chính trị sau năm 1986 là gì ?', null, null, '2', '0', '453', '464', '1552294151', null, null, null, null, null, '54', null, null, null, null, null);
INSERT INTO `question` VALUES ('2203', 'Câu 1.2: Liên hệ với quá trình tạo lập các điều kiện để chuyển nền kinh tế mang nặng tính tự cung tự cấp sang nền kinh tế thị trường định hướng xã hội chủ nghĩa ở Việt Nam.', null, null, '2', '0', '358', '448', '1552294455', null, null, null, null, null, '42', null, null, null, null, null);
