INSERT INTO `question` VALUES ('2756', 'Nêu nguyên tắc hoạt động Mặt trận DT thống nhất? ĐCSVN vừa là thành viên, vừa là lực lượng lãnh đạo mặt trận?', null, null, '2', '0', '490', '45', '1552319484', '1552661999', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2757', 'Nêu nguyên tắc hoạt động Mặt trận DT thống nhất', null, null, '5', '0', '490', '45', '1552319663', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2758', null, null, null, '5', '2757', '490', '45', '1552319663', null, null, null, '<p>- Mặt trận DT thống nhất phải được <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> trên nền tảng <a class=\"cloze\" href=\"#mce_temp_url#\">công - nông - trí</a>, đặt dưới sự <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a> của Đảng</p>\r\n<p>- Mặt trận DT thống nhất phải <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động</a> trên cơ sở bảo đảm lợi ích cao của DT, quyền lợi cơ bản của các tầng lớp ND.</p>\r\n<p>- Mặt trận DT thống nhất phải hoạt động theo nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">hiệp thương dân chủ</a>, đảm bảo đoàn kết ngày càng <a class=\"cloze\" href=\"#mce_temp_url#\">rộng rãi</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">bền vững</a></p>\r\n<p>- Mặt trận DT thống nhất là <a class=\"cloze\" href=\"#mce_temp_url#\">khối đoàn kết</a> chặt chẽ, lâu dài, đoàn kết thật sự, chân thành thân ái giúp đỡ nhau cùng <a class=\"cloze\" href=\"#mce_temp_url#\">tiến bộ</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2759', 'ĐCSVN vừa là thành viên, vừa là lực lượng lãnh đạo mặt trận?', null, null, '5', '0', '490', '45', '1552320014', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2760', null, null, null, '5', '2759', '490', '45', '1552320014', null, null, null, '<p>-  ĐCSVN là sp của <strong><span style=\"text-decoration: underline;\">sự kết hợp <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa Mác - Lênin</a> với phong trào <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân</a> và phong trào <a class=\"cloze\" href=\"#mce_temp_url#\">yêu nước Việt Nam</a></span></strong>. Vì vậy, Đảng vừa là Đảng của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a>, lại vừa là Đảng của <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân lao động</a> và của <a class=\"cloze\" href=\"#mce_temp_url#\">cả dân tộc</a></p>\r\n<p>-  ĐCSVN là <strong><span style=\"text-decoration: underline;\">tổ chức <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị</a> to lớn nhất</span></strong>, chắc chắn nhất, cách mạng nhất, là <a class=\"cloze\" href=\"#mce_temp_url#\">bộ tham mưu</a> của GCCN và của cả DT, Đảng lãnh đạo xây dựng mặt trận DT thống nhất, đồng thời cũng là một <a class=\"cloze\" href=\"#mce_temp_url#\">thành viên</a> của Mặt trận.</p>\r\n<p>-  Đảng lãnh đạo Mặt trận trước hết bằng việc <a class=\"cloze\" href=\"#mce_temp_url#\">xác định chính sách</a> Mặt trận đúng đắn, phù hợp với từng <a class=\"cloze\" href=\"#mce_temp_url#\">giai đoạn</a>, từng <a class=\"cloze\" href=\"#mce_temp_url#\">thời kỳ cách mạng</a>.</p>\r\n<p>=&gt; ĐCSVN vừa là thành viên, vừa là lực lượng lãnh đạo mặt trận DT thống nhất</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2761', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '264', '6', '1552320486', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('2762', null, null, null, '5', '2761', '264', '6', '1552320486', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\\)</span> là<span class=\"math-tex\">\\(\\sqrt {{{(1 + {y^2})}^3}}  = \\square.{e^x} - \\_\\_.{e^x} + C\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">3x,3</a>', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('2763', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '264', '6', '1552320524', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('2764', null, null, null, '5', '2763', '264', '6', '1552320524', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\\)</span> là<span class=\"math-tex\">\\(\\sqrt {{{(1 + {y^2})}^3}}  = \\_\\_.{e^x} - \\_\\_.{e^x} + C\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">3x,3</a>', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('2765', 'Quan điểm HCM về Nhà nước của dân? Nhà nước cần làm gì để đảm bảo quyền làm chủ thật sự của NDLĐ hiện nay?', null, null, '2', '0', '497', '45', '1552321778', '1552662091', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2766', 'a.	Quan điểm HCM về Nhà nước của dân', null, null, '5', '0', '497', '45', '1552322002', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2767', null, null, null, '5', '2766', '497', '45', '1552322002', null, null, null, '<p>- Xác lập tất cả <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">quyền lực</span></strong></a> trong Nhà nước và trong xã hội đều <strong><span style=\"text-decoration: underline;\">thuộc về <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân</a></span></strong></p>\r\n<p>   +  Thể hiện trong <a class=\"cloze\" href=\"#mce_temp_url#\">Hiến pháp</a> 1946: tất cả <a class=\"cloze\" href=\"#mce_temp_url#\">quyền bình đẳng</a> trong nước đều là của toàn thể <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dâ</a>n VN, <a class=\"cloze\" href=\"#mce_temp_url#\">không phân biệt</a> nòi giống, gái trai, giàu nghèo, giai cấp, tôn giáo; những việc quan hệ đến vận mệnh quốc gia sẽ đưa ra toàn dân phục quyết.</p>\r\n<p>   +  Nhân dân có quyền<a class=\"cloze\" href=\"#mce_temp_url#\"> kiểm soát</a> nhà nước, thể hiện ở chỗ nhân dân có quyền bãi miễn những đại biểu Quốc hội và đại biểu Hội đồng nhân dân nếu những đại biểu đó không xứng đáng với sự tín nhiệm của nhân dân</p>\r\n<p>   +  Muốn đảm bảo được tính chất nhân dân của nhà nước phải <a class=\"cloze\" href=\"#mce_temp_url#\">xác định</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">thực hiện</a> được mối quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">trách nhiệm</a> của cử tri và đại biểu do cử tri bầu ra</p>\r\n<p>- HCM nêu lên <strong><span style=\"text-decoration: underline;\">quan điểm <a class=\"cloze\" href=\"#mce_temp_url#\">dân chủ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">dân làm chủ</a></span></strong></p>\r\n<p>   +  Dân chủ là <a class=\"cloze\" href=\"#mce_temp_url#\">khát vọng</a> muôn đời của con người</p>\r\n<p>   +  Dân là chủ có nghĩa là xác định <a class=\"cloze\" href=\"#mce_temp_url#\">vị thế</a> của dân, dân làm chủ có nghĩa là xác định <a class=\"cloze\" href=\"#mce_temp_url#\">quyền và nghĩa vụ </a>của dân.</p>\r\n<p>   +  <a class=\"cloze\" href=\"#mce_temp_url#\">Quyền lực</a> của nhân dân được đặt ở vị trí tối thượng. Một nhà nước như thế là một nhà nước tiến bộ trong bước đường phát triển của nhân loại</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2768', 'b.	Để đảm bảo quyền làm chủ thật sự của nhân dân lao động hiện nay:', null, null, '5', '0', '497', '45', '1552322173', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2769', null, null, null, '5', '2768', '497', '45', '1552322173', null, null, null, '<p>Hiện nay, trước những thách thức, có mặt diễn biến phức tạp, hơn lúc nào hết, các tổ chức đảng và các cấp chính quyền phải tập trung giải quyết những <a class=\"cloze\" href=\"#mce_temp_url#\">bức xúc</a>, cũng như những <a class=\"cloze\" href=\"#mce_temp_url#\">nguyện vọng</a> chính đáng của nhân dân. <strong><span style=\"text-decoration: underline;\">Đường lối, chủ trương, chính sách của Đảng, Nhà nước phải <a class=\"cloze\" href=\"#mce_temp_url#\">thuận lòng dân</a>, xuất phát từ <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích</a> của dân</span></strong>, nhằm <a class=\"cloze\" href=\"#mce_temp_url#\">cải thiện</a>, nâng cao <a class=\"cloze\" href=\"#mce_temp_url#\">đời sống</a> của nhân dân. <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Tôn trọng</a> quyền làm chủ của nhân dân</span></strong> bằng cách <a class=\"cloze\" href=\"#mce_temp_url#\">hướng dẫn</a>, tạo cơ chế để nhân dân <a class=\"cloze\" href=\"#mce_temp_url#\">phát huy</a> đầy đủ quyền làm chủ, giúp nhân dân thực hiện nghĩa vụ và được <a class=\"cloze\" href=\"#mce_temp_url#\">hưởng</a> quyền lợi <a class=\"cloze\" href=\"#mce_temp_url#\">chính đáng</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2770', 'Quan điểm HCM về đề phòng và khắc phục những tiêu cực trong hoạt động của NN? Vận dụng trong kiện toàn bộ máy Hành chính NN hiện nay?', null, null, '2', '0', '498', '45', '1552322545', '1552662150', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2771', 'a.	Quan điểm HCM về đề phòng và khắc phục những tiêu cực trong hoạt động nhà nước:', null, null, '5', '0', '498', '45', '1552322778', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2772', null, null, null, '5', '2771', '498', '45', '1552322778', null, null, null, '<p>-  Xây dựng một nhà nước của dân, do dân, vì dân không bao giờ <a class=\"cloze\" href=\"#mce_temp_url#\">tách rời</a> với việc làm cho NN luôn <a class=\"cloze\" href=\"#mce_temp_url#\">trong sạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">vững mạnh</a></p>\r\n<p>-  Khi nhà nước giành được độc lập, chính quyền CM còn <a class=\"cloze\" href=\"#mce_temp_url#\">non trẻ</a> cũng như lúc CM <a class=\"cloze\" href=\"#mce_temp_url#\">chuyển giai đoạn</a>, HCM càng chú ý hơn bao giờ hết đến việc <a class=\"cloze\" href=\"#mce_temp_url#\">bảo đảm</a> sự trong sạch, vững mạnh của các cấp chính quyền bởi những tiêu cực rất dễ trở thành nguy cơ làm <a class=\"cloze\" href=\"#mce_temp_url#\">biến chất</a> nhà nước</p>\r\n<p>-  Sau khi thành lập nhà nước VN dân chủ cộng hòa, HCM đã gửi thư cho UBND các kỳ, các tỉnh, huyện, làng nêu rõ chống <a class=\"cloze\" href=\"#mce_temp_url#\">đặc quyền đặc lợi</a>; bộ máy <strong><span style=\"text-decoration: underline;\">NN <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> phải bộ máy áp bức, bóc lột ND, cán bộ, công chức không phải những “ông quan CM”.</span></strong></p>\r\n<p>-  HCM chỉ ra 6 bệnh cần đề phòng<strong><span style=\"text-decoration: underline;\">: <a class=\"cloze\" href=\"#mce_temp_url#\">trái phép</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">cậy thế</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">hủ hóa</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tù túng</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">chia rẽ</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">kiêu ngạo</a>.</span></strong></p>\r\n<p>-  Trong quá trình XD nhà nước VN dân chủ cộng hòa, HCM chỉ rõ những <a class=\"cloze\" href=\"#mce_temp_url#\">tiêu cực</a> sau và nhắc nhở mọi người đề phòng:</p>\r\n<ul>\r\n<li><strong><span style=\"text-decoration: underline;\">Đặc quyền, đặc lợi:</span></strong> XD nhà nước trong sạch, vững mạnh đòi hỏi phải tẩy trừ những thói <a class=\"cloze\" href=\"#mce_temp_url#\">cậy mình</a> là người trong cơ quan chính quyền để cửa quyền, <a class=\"cloze\" href=\"#mce_temp_url#\">hách dịch</a> với dân, lạm quyền, đồng thời <a class=\"cloze\" href=\"#mce_temp_url#\">vơ vét tiền của</a>, lợi dụng chức quyền làm lợi cho mình</li>\r\n<li><strong><span style=\"text-decoration: underline;\">Tham ô, lãng phí, quan liêu</span></strong>: là “<a class=\"cloze\" href=\"#mce_temp_url#\">giặc nội xâm</a>”, “giặc ở trong lòng”, thứ giặc <a class=\"cloze\" href=\"#mce_temp_url#\">nguy hiểm</a> hơn cả giặc ngoại xâm.</li>\r\n<li><strong><span style=\"text-decoration: underline;\">Tù túng, chia rẽ, kiêu ngạo</span></strong>: gây mất <a class=\"cloze\" href=\"#mce_temp_url#\">đoàn kết</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">gây rối</a> cho công tác</li>\r\n</ul>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2773', 'b.	Trong kiện toàn bộ máy hành chính NN hiện nay:', null, null, '5', '0', '498', '45', '1552322980', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2774', null, null, null, '5', '2773', '498', '45', '1552322980', null, null, null, '<p>- Vận dụng TTHCM về lĩnh vực này đòi hỏi chú trọng <a class=\"cloze\" href=\"#mce_temp_url#\">cải cách</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a>, kiện toàn bộ máy hành chính NN, bảo đảm một<a class=\"cloze\" href=\"#mce_temp_url#\"> nền hành chính</a> dân chủ, trong sạch, vững mạnh.</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Đẩy mạnh</a> cải cách nền hành chính theo hướng dân chủ, trong sạch, vững mạnh, phục vụ ND.</p>\r\n<p>- Kiên quyết khắc phục thói <a class=\"cloze\" href=\"#mce_temp_url#\">quan liêu</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">hách dịch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">cửa quyền</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tham nhũng</a>, bộ máy <a class=\"cloze\" href=\"#mce_temp_url#\">cồng kềnh</a> <a class=\"cloze\" href=\"#mce_temp_url#\">kém hiệu quả</a>.</p>\r\n<p>- Chú ý cải cách các <a class=\"cloze\" href=\"#mce_temp_url#\">thủ tục hành chính</a>, đề cao trách nhiệm cá nhân trong việc giải quyết các khiếu kiện của công dân theo quy định của pháp luật.</p>\r\n<p>- XD đội ngũ <a class=\"cloze\" href=\"#mce_temp_url#\">cán bộ</a>, công chức vừa có <a class=\"cloze\" href=\"#mce_temp_url#\">đức</a> vừa có <a class=\"cloze\" href=\"#mce_temp_url#\">tài</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2775', 'Quan điểm HCM về bản chất giai cấp công nhân của NN? Vận dụng để tăng cường hơn nữa sự lãnh đạo của Đảng đối với NN?', null, null, '2', '0', '499', '45', '1552353068', '1552662370', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2776', '* Quan điểm HCM về bản chất GCCN của NN:', null, null, '5', '0', '499', '45', '1552353314', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2777', null, null, null, '5', '2776', '499', '45', '1552353314', null, null, null, '<p>- Nhà nước do ĐCS lãnh đạo</p>\r\n<p>   +  ĐCSVN lãnh đạo nhà nước <a class=\"cloze\" href=\"#mce_temp_url#\">giữ vững</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tăng cường</a> bản chất GCCN</p>\r\n<p>   +  Đảng lãnh đạo nhà nước bằng <a class=\"cloze\" href=\"#mce_temp_url#\">phương thức</a> thích hợp</p>\r\n<ul>\r\n<li>Đảng lãnh đạo bằng <a class=\"cloze\" href=\"#mce_temp_url#\">đường lối</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">quan điểm</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">chủ trương</a> để nhà nước thể chế hóa thành <a class=\"cloze\" href=\"#mce_temp_url#\">pháp luật</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">chính sách</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">kế hoạch</a></li>\r\n<li>Đảng lãnh đạo Nhà nước bằng <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động</a> của các <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức đảng</a> và đảng viên của mình trong bộ máy, cơ quan nhà nước</li>\r\n<li>Đảng lãnh đạo nhà nước bằng <a class=\"cloze\" href=\"#mce_temp_url#\">công tác kiểm tra</a>.</li>\r\n</ul>\r\n<p>- Bản chất giai cấp của Nhà nước ta thể hiện ở <a class=\"cloze\" href=\"#mce_temp_url#\">tính định hướng</a> XHCN của sự phát triển đất nước</p>\r\n<p>- Bản chất GCCN của Nhà nước ta thể hiện ở <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc tổ chức</a> và hoạt động cơ bản của nó là nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">tập trung dân chủ</a></p>\r\n<p>- Bản chất của GCCN thống nhất với <a class=\"cloze\" href=\"#mce_temp_url#\">tính nhân dân</a>, tính <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc </a>của nhà nước</p>\r\n<p>   +  Nhà nước ta ra đời là <strong><span style=\"text-decoration: underline;\">kết quả của <a class=\"cloze\" href=\"#mce_temp_url#\">cuộc đấu tranh</a> lâu dài, gian khổ</span></strong> của rất nhiều thế hệ VN.</p>\r\n<p>   +  Tính thống nhất còn biểu hiện ở chỗ NN ta <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">bảo vệ lợi ích</a> của ND,</span></strong> lấy lợi ích của DT làm cơ bản.</p>\r\n<p>   +  Trong thực tế, Nhà nước ta đã đứng ra làm <a class=\"cloze\" href=\"#mce_temp_url#\">nhiệm vụ</a> của cả DT giao phó, đã lãnh đạo ND tiến hành các cuộc kháng chiến để bảo vệ nền độc lập, tự do của Tổ quốc, XD một Nhà nước VN hòa bình, thống nhất, độc lập, dân chủ và giàu mạnh, góp phần tích cực vào sự phát triển tiến bộ của TG.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2778', '* Tăng cường sự lãnh đạo của Đảng đối với Nhà nước:', null, null, '5', '0', '499', '45', '1552353422', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2779', null, null, null, '5', '2778', '499', '45', '1552353422', null, null, null, '<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Thể chế hóa</a> đường lối, chủ trương của Đảng, bảo đảm <a class=\"cloze\" href=\"#mce_temp_url#\">sự lãnh đạo</a> của Đảng và phát huy vai trò <a class=\"cloze\" href=\"#mce_temp_url#\">quản lí</a> của Nhà nước.</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Đổi mới</a> phương thức lãnh đạo của Đảng đối với Nhà nước: lãnh đạo bằng đường lối, bằng tổ chức, bộ máy của Đảng trong các cơ quan Nhà nước, bằng vai trò tiên phong, gương mẫu của đội ngũ Đảng viên hoạt động trong bộ máy Nhà nước, bằng công tác kiểm tra</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Thống nhất lãnh đạo</a> công tác cán bộ trong hệ thống chính trị trên cơ sở bảo đảm chức năng, nhiệm vụ, quyền hạn của Nhà nước</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Sự trong sạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">vững mạnh</a> của ĐCSVN là yếu tố quyết định cho thành công việc XD Nhà nước pháp quyền XHCN của dân, do dân, vì dân.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2780', 'Quan điểm HCM về NN của dân, do dân, vì dân?', null, null, '2', '0', '500', '45', '1552355615', '1552664833', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2781', 'Khái niệm marketing B2B?', null, null, '5', '0', '507', '467', '1552356060', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2782', null, null, null, '5', '2781', '507', '467', '1552356060', null, null, null, '<p>-         Marketing b2b (nghĩa hẹp) là hoạt động <a class=\"cloze\" href=\"#mce_temp_url#\">mua bán hàng hóa</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">dịch vụ</a> giữa các <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức </a>nhằm <a class=\"cloze\" href=\"#mce_temp_url#\">sử dụng trực tiếp</a> phục vụ cho quá trình hoạt động, sản xuất các <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm khác</a>.</p>\r\n<p>-         Marketing b2b (nghĩa rộng) là hoạt động mua bán hàng hóa và dịch vụ giữa các tổ chức nhằm sử dụng <a class=\"cloze\" href=\"#mce_temp_url#\">trực tiếp </a>hay <a class=\"cloze\" href=\"#mce_temp_url#\">gián tiếp</a> tục vụ cho quá trình hoạt động, sản xuất sản phẩm khác hoặc <a class=\"cloze\" href=\"#mce_temp_url#\">bán lại</a> của tổ chức.</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2799', 'Phân tích bản chất marketing B2B?', null, null, '5', '0', '507', '467', '1552356133', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2800', null, null, null, '5', '2799', '507', '467', '1552356133', null, null, null, '<p>-         Về bản chất: marketing <a class=\"cloze\" href=\"#mce_temp_url#\">b2c</a> và Marketing <a class=\"cloze\" href=\"#mce_temp_url#\">b2b</a> là <a class=\"cloze\" href=\"#mce_temp_url#\">một</a>, sự khác biệt chính là <a class=\"cloze\" href=\"#mce_temp_url#\">cách tiếp cận</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">sự vận dụng</a> các công cụ, kỹ thuật của marketing trong từng <a class=\"cloze\" href=\"#mce_temp_url#\">hoàn cảnh cụ thể</a></p>\r\n<p>-         Bản chất của marketing b2b có thể hiểu đơn giản thông qua việc trả lời 2 câu hỏi:</p>\r\n<p>Đối tượng mua hàng hóa dịch vụ là ai?</p>\r\n<p>Tại sao giao dịch mua hàng lại được thực hiện</p>\r\n<p>Đặc trưng của marketing B2B</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2801', 'Quan điểm HCM về NN của dân, do dân, vì dân?', null, null, '5', '0', '500', '45', '1552356151', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2802', null, null, null, '5', '2801', '500', '45', '1552356151', null, null, null, '<p>- Nhà nước của dân:</p>\r\n<p>   +  Xác lập tất cả mọi <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">quyền lực</span></strong></a> trong Nhà nước và trong XH đều <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">thuộc về nhân dân</a>:</span></strong></p>\r\n<ul>\r\n<li>Thể hiện trong Hiến pháp 1946: tất cả <a class=\"cloze\" href=\"#mce_temp_url#\">quyền bình đẳng</a> trong nước đều là của toàn thể ND VN, không phân biệt nòi giống, gái trai, giàu nghèo, GC, tôn giáo; những việc quan hệ đến vận mệnh quốc gia sẽ đưa ra toàn dân phục quyết.</li>\r\n<li>ND có <a class=\"cloze\" href=\"#mce_temp_url#\">quyền kiểm soát</a> Nhà nước, ND có <a class=\"cloze\" href=\"#mce_temp_url#\">quyền bãi miễn</a> những đại biểu Quốc hội và đại biểu Hội đồng ND nếu những đại biểu đó ko xứng đáng với <a class=\"cloze\" href=\"#mce_temp_url#\">sự tín nhiệm</a> của ND.</li>\r\n</ul>\r\n<p>   +  HCM nêu lên quan điểm <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">dân chủ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">dân làm chủ</a></span></strong></p>\r\n<ul>\r\n<li>Dân chủ là <a class=\"cloze\" href=\"#mce_temp_url#\">khát vọng</a> muôn đời của con người</li>\r\n<li>Dân là chủ có nghĩa là xác định <a class=\"cloze\" href=\"#mce_temp_url#\">vị thế của dân</a>, dân làm chủ có nghĩa là xác định <a class=\"cloze\" href=\"#mce_temp_url#\">quyền và nghĩa vụ</a> của dân.</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quyền lực</a> của ND được đặt ở vị trí tối thượng. Một Nhà nước như thế là một Nhà nước tiến bộ trong bước đường phát triển của nhân loại</li>\r\n</ul>\r\n<p>- Nhà nước do dân:</p>\r\n<p>   +  <strong><span style=\"text-decoration: underline;\">Nhà nước do <a class=\"cloze\" href=\"#mce_temp_url#\">dân lập nên</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">dân làm chủ</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">dân ủng hộ</a></span></strong></p>\r\n<ul>\r\n<li>NN lấy dân <a class=\"cloze\" href=\"#mce_temp_url#\">làm gốc</a></li>\r\n<li>Hoạt động của Nhà nước vì <a class=\"cloze\" href=\"#mce_temp_url#\">quyền lợi của nhân dân</a> chứ <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> phải vì <a class=\"cloze\" href=\"#mce_temp_url#\">quyền lợi của 1 nhóm người</a> hay 1 tập đoàn XH nào đó.</li>\r\n</ul>\r\n<p>   +  Quyền tham gia <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">quản lí</a> Nhà nước của dân được thể hiện:</p>\r\n<ul>\r\n<li>Toàn bộ công dân có quyền <a class=\"cloze\" href=\"#mce_temp_url#\">bầu ra Quốc hội</a></li>\r\n<li>Quốc hội bầu ra <a class=\"cloze\" href=\"#mce_temp_url#\">chủ tịch nước</a>, UB thường vụ Quốc hội và Hội đồng chính phủ</li>\r\n<li>Hội đồng chính phủ là cơ quan <a class=\"cloze\" href=\"#mce_temp_url#\">hành chính</a> cao nhất của Nhà nước, thực hiện các nghị quyết của Quốc hội và chấp hành pháp luật</li>\r\n<li>Mọi công việc của bộ máy Nhà nước trong việc quản lí XH đều thực hiện <a class=\"cloze\" href=\"#mce_temp_url#\">ý của dân</a></li>\r\n</ul>\r\n<p>- Nhà nước vì dân:</p>\r\n<p>   + Là Nhà nước lấy <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích</a> chính đáng của <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân</a> làm <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a></span></strong>, tất cả đều vì lợi ích của ND, không có bất kì lợi ích nào khác.</p>\r\n<p>   + Là Nhà nước <a class=\"cloze\" href=\"#mce_temp_url#\">trong sạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> có bất kì một <a class=\"cloze\" href=\"#mce_temp_url#\">đặc quyền đặc lợi</a> nào.</p>\r\n<p>   + Trong Nhà nước đó, đội ngũ cán bộ không phải là “quan CM” mà là “đầy tớ” cho ND.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2803', 'Phân tích các khái niệm marketing công nghiệp (Industrial marketing)?', null, null, '5', '0', '507', '467', '1552356220', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2804', null, null, null, '5', '2803', '507', '467', '1552356220', null, null, null, '<p>-         Khái niệm marketing công nghiệp: Marketing công nghiệp là những hoạt động marketing nhằm vào việc <a class=\"cloze\" href=\"#mce_temp_url#\">bán</a> các hàng hóa, dịch vụ cho các <a class=\"cloze\" href=\"#mce_temp_url#\">doanh nghiệp</a> hay<a class=\"cloze\" href=\"#mce_temp_url#\"> tổ chức</a> để sử dụng trực tiếp hoặc gian tiếp vào hoạt động của họ</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2805', 'Trình bày/phân tích đặc trưng của marketing b2b?', null, null, '5', '0', '508', '467', '1552356381', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2806', null, null, null, '5', '2805', '508', '467', '1552356381', null, null, null, '<p><strong><em>1.     </em></strong><strong><em><a class=\"cloze\" href=\"#mce_temp_url#\">Mối quan hệ</a> giữa người mua và người bán rất <a class=\"cloze\" href=\"#mce_temp_url#\">chặt chẽ</a></em></strong></p>\r\n<ul>\r\n<li> Tầm quan trọng chiến lược của khách hang b2b là rất lớnèthường kí kết hợp đồng dài hạn và xây dựng mối quan hệ khách hang để đảm bảo lợi ích đôi bên do số lượng khách hang b2b ít hơn b2c nhưng lại mua hang với số lượng lớn.</li>\r\n</ul>\r\n<p><strong><em>2.     </em></strong><strong><em>Các kênh <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối ngắn</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">trực tiếp</a> hơn.</em></strong></p>\r\n<ul>\r\n<li> Kênh phân phối trong b2b trường dung là kênh trực tiếp, góp phần tạo nên mối quan hệ gần giữa người mua và người bán; người mua  có thể trực tiếp đóng góp và quá trình lên khách hang sản phẩm. Do số lượng khách hang b2b hạn chế lại thường tập trung về mặt địa lý, khách hang có tầm quan trọng chiến lược, do đó phải kiểm soát kênh chặt chẽ.</li>\r\n</ul>\r\n<p><strong><em>3.     </em></strong><strong><em>Nhấn mạnh đến <a class=\"cloze\" href=\"#mce_temp_url#\">bán hàng cá nhân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">thương lượng giá</a></em></strong></p>\r\n<ul>\r\n<li> Mối quan hệ chặt chẽ, kênh ngắn, nhu cầu trao đổi thông tin trực tiếp giữa người mua và người bán dẫn đến tang cường bán hàng cá nhân.</li>\r\n<li> Quá trình mua hàng phức tạp, liên quan đến nhiều doanh nghiệp thành viên.</li>\r\n</ul>\r\n<p><strong><em>4.     </em></strong><strong><em>Nhu cầu xây dựng và duy trình<a class=\"cloze\" href=\"#mce_temp_url#\"> quan hệ</a> với <a class=\"cloze\" href=\"#mce_temp_url#\">đối tác</a>.</em></strong></p>\r\n<ul>\r\n<li> Qui mô khách hàng lớn, đơn hàng lớnèsự linh hoạt trong chào hàng và xác lập giáèTăng khả năng thương lượng.<br /> Website là nơi có sự tương tác toàn diện</li>\r\n<li> Giá là yếu tố quan trọng trong mạng lưới thông tin giữa khách hàng và doanh nghiệpètheo dõi đơn hàng, thanh toán, cung cấp thông tin trao đổi qua lại.</li>\r\n</ul>\r\n<p><strong><em>5.     </em></strong><strong><em>Mối quan hệ <a class=\"cloze\" href=\"#mce_temp_url#\">bên trong</a> doanh nghiệp</em></strong></p>\r\n<ul>\r\n<li> Bên trong doanh nghiêp cần có sự trao đổi, sự phân công, chuyên môn hóa giữa các phòng ban…èGiải quyết đơn hàng</li>\r\n</ul>\r\n<p><strong><em>6.     </em></strong><strong><em>Các chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">xúc tiến</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thông hợp lý</a></em></strong></p>\r\n<ul>\r\n<li> Qui trình mua và nhu cầu khách hàng phức tạp, khác nhauèđòi hỏi chiến lược xúc tiến riêng biệt</li>\r\n<li> Thường tập trung vào hội chợ, triển lãm để tăng khả năng tiếp cận với khách hàng.</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2823', 'Tính sáng tạo của HCM trong giải quyết mối quan hệ giữa tính giai cấp với tính ND và tính DT của NN?', null, null, '2', '0', '513', '45', '1552356717', '1552664931', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2824', 'Nhóm yếu tố môi trường kinh doanh.', null, null, '5', '0', '510', '467', '1552356886', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2825', null, null, null, '5', '2824', '510', '467', '1552356886', null, null, null, '<p><strong>1.     </strong><strong>Các yếu tố môi trường kinh doanh: </strong></p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Kinh tế</a> ( mức cầu hiện tại, triển vọng kinh tế, giá trị đồng tiền)</p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Quy định</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">pháp luật</a></p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Công nghệ</a></p>\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\"> Chính trị</a> ( đặc biệt là khi hợp tác với các doanh nghiệp ngoài quốc gia)</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2826', 'a.	Về bản chất GCCN của Nhà nước', null, null, '5', '0', '513', '45', '1552356918', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2827', null, null, null, '5', '2826', '513', '45', '1552356918', null, null, null, '<p>- Nhà nước do ĐCS lãnh đạo</p>\r\n<p>   +  ĐCSVN lãnh đạo Nhà nước <a class=\"cloze\" href=\"#mce_temp_url#\">giữ vững</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tăng cường</a> bản chất GCCN</p>\r\n<p>   +  Đảng lãnh đạo bằng <a class=\"cloze\" href=\"#mce_temp_url#\">phương thức</a> thích hợp:</p>\r\n<ul>\r\n<li>Đảng lãnh đạo bằng <a class=\"cloze\" href=\"#mce_temp_url#\">đường lối</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">quan điểm</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">chủ trương</a> để nhà nước <a class=\"cloze\" href=\"#mce_temp_url#\">thể chế hóa</a> thành pháp luật, chính sách, kế hoạch</li>\r\n<li>Đảng lãnh đạo Nhà nước bằng <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động</a> của các <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức đảng</a> và đảng viên của mình trong bộ máy, cơ quan nhà nước</li>\r\n<li>Đảng lãnh đạo nhà nước bằng <a class=\"cloze\" href=\"#mce_temp_url#\">công tác kiểm tra</a>.</li>\r\n</ul>\r\n<p>- Bản chất GCCN công nhân của Nhà nước ta thể hiện ở <a class=\"cloze\" href=\"#mce_temp_url#\">tính định hướng</a> XHCN của sự phát triển đất nước</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Bản chất</a> GCCN của Nhà nước ta thể hiện ở <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên tắc tổ chức</a> và hoạt động cơ bản của nó là nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">tập trung dân chủ</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2828', 'Các yếu tố tổ chức.', null, null, '5', '0', '510', '467', '1552356923', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2829', null, null, null, '5', '2828', '510', '467', '1552356923', null, null, null, '<p>-         Mục tiêu và nhiệm vụ</p>\r\n<p>-         Chính sách</p>\r\n<p>-         Cơ cấu tổ chức</p>\r\n<p>-         Hệ thống</p>\r\n<p>-         Quy trình</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2830', 'Các yếu tố quan hệ cá nhân/trung tâm mua.', null, null, '5', '0', '510', '467', '1552356985', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2831', null, null, null, '5', '2830', '510', '467', '1552356985', null, null, null, '<p>-         Bao hàm các <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a> và các <a class=\"cloze\" href=\"#mce_temp_url#\">nhóm</a> có<a class=\"cloze\" href=\"#mce_temp_url#\"> tham dự</a> vào tiến trình quyết định mua, cùng <a class=\"cloze\" href=\"#mce_temp_url#\">chia sẻ</a> một số <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu chung</a> và những <a class=\"cloze\" href=\"#mce_temp_url#\">rủi ro</a> phát sinh từ quyết định mua</p>\r\n<p>-         Nhiệm vụ: <a class=\"cloze\" href=\"#mce_temp_url#\">công việc</a> cần thực hiện.</p>\r\n<p>-         Phim nhiệm vụ: tương tác nhóm  phân chia theo hàng dọc, hàng ngang, phân tán thời gian,cường độ liên kết , tập trung hóa.</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2832', 'b.	Bản chất GCCN thống nhất với tính nhân dân, tính dân tộc của Nhà nước:', null, null, '5', '0', '513', '45', '1552356993', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2833', null, null, null, '5', '2832', '513', '45', '1552356993', null, null, null, '<p>- Nhà nước ta ra đời <strong><span style=\"text-decoration: underline;\">là kết quả của <a class=\"cloze\" href=\"#mce_temp_url#\">cuộc đấu tranh lâu dài</a></span></strong>, <a class=\"cloze\" href=\"#mce_temp_url#\">gian khổ</a> của nhiều thế hệ người VN.</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Tính thống nhất</a> còn biểu hiện ở chỗ Nhà nước ta <a class=\"cloze\" href=\"#mce_temp_url#\">bảo vệ lợi ích</a> của ND, lấy lợi ích của DT làm cơ bản</p>\r\n<p>- Trong thực tế, Nhà nước ta đã đứng ra làm <a class=\"cloze\" href=\"#mce_temp_url#\">nhiệm vụ</a> của cả DT giao phó, đã <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a> ND tiến hành các cuộc kháng chiến để bảo vệ nền độc lập, tự do của Tổ quốc, XD một nước VN hòa bình, thống nhất, độc lập, dân chủ và giàu mạnh, góp phần<a class=\"cloze\" href=\"#mce_temp_url#\"> tích cực</a> vào sự phát triển tiến bộ của TG.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2834', 'Các yếu tố cá nhân tham gia.', null, null, '5', '0', '510', '467', '1552357024', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2835', null, null, null, '5', '2834', '510', '467', '1552357024', null, null, null, '<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Tuổi tá</a>c/<a class=\"cloze\" href=\"#mce_temp_url#\">trình độ</a> học vấn</p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Vị trí công tác</a></p>\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\"> Động cơ</a></p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Nhân cách</a></p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Xu hướng</a></p>\r\n<p>-         <a class=\"cloze\" href=\"#mce_temp_url#\">Phong cách mua</a></p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2836', 'Tái đặt mua trực tiếp.', null, null, '5', '0', '511', '467', '1552357121', '1552357194', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2837', null, null, null, '5', '2836', '511', '467', '1552357121', '1552357194', null, null, '<p>-         Quyết định theo <a class=\"cloze\" href=\"#mce_temp_url#\">thói quen</a></p>\r\n<p>-         Danh mục sản phẩm đặt với người bán ở trong một danh sách được phê duyệt và không cần đến thông tin từ trong tâm mua.</p>\r\n<p>-         Ít đầu tư về<a class=\"cloze\" href=\"#mce_temp_url#\"> thời gian</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">sự quan tâm</a> khi mua hàng</p>\r\n<p>-         Các bước đầu trong tiến trình mua là những <a class=\"cloze\" href=\"#mce_temp_url#\">bước cố định</a>.</p>\r\n<p>-&gt;Người làm marketing cần tập trung các nỗ lực  vào hai bước cuối của tiến trình mua.</p>\r\n<p>-&gt;Với nhà cung cấp hiện tại: Duy trì chất lượng và khả năng cung ứng; tự động hóa tiến trình mua; sử dụng quảng cáo nhắc nhở..</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2838', 'Tái đặt mua có điều chỉnh.', null, null, '5', '0', '511', '467', '1552357295', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2839', null, null, null, '5', '2838', '511', '467', '1552357295', null, null, null, '<p>-         Trung tâm mua có thể muốn <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổi </a>mức giá mua, các bên yêu  cầu <a class=\"cloze\" href=\"#mce_temp_url#\">chi tiết</a> sản phẩm, chất lượng, tính năng, điều khoản giao hàng,…</p>\r\n<p>-         Khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">đầu tư thời gian</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">sự quan tâm</a> tương đối</p>\r\n<p>-         Ít thiên về <a class=\"cloze\" href=\"#mce_temp_url#\">xác định</a> sản phẩm và tập trung nhiều hơn vào <a class=\"cloze\" href=\"#mce_temp_url#\">nghiên cứu</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">đánh giá</a> nhà cung cấp</p>\r\n<p>-&gt;Các ứng xử marketing: <a class=\"cloze\" href=\"#mce_temp_url#\">Tập trung</a> tất cả <a class=\"cloze\" href=\"#mce_temp_url#\">các bước</a></p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Quảng cáo</a>: Dùng quảng cáo <a class=\"cloze\" href=\"#mce_temp_url#\">so sánh </a>để chỉ ra nét đặc biệt, khác biệt của mình với các sản phẩm tương tự</p>\r\n<p>+Các khu trưng bày cho khách hàng, các sự kiện chiêu khách tại hội trợ thương mại giữ khách hàng hiện tại với các tour tham quan nhà máy, chiết giải đặc biệt cho khách hàng.</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\"> Phản hồi</a> nhanh sự thay đổi về nhu cầu khách hàng</p>\r\n<p>Chứng minh năng lực cung ứng</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2856', 'Mua thực hiện nghĩa vụ mới.', null, null, '5', '0', '511', '467', '1552357414', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2857', null, null, null, '5', '2856', '511', '467', '1552357414', null, null, null, '<p>- Giải quyết vấn đề <a class=\"cloze\" href=\"#mce_temp_url#\">mở rộng</a> khi khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">lần đầu tiên</a> mua sản phẩm ví dụ xây dựng văn phòng mới lĩnh vực mới</p>\r\n<p>- chi phí và rủi ro thấp</p>\r\n<p>- khách hàng sẽ  khác hàng sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">đầu tư nhiều</a> thời gian và<a class=\"cloze\" href=\"#mce_temp_url#\"> sự quan tâm</a> khi mua hàng quá trình mua rất nhiều giai đoạn và yếu tố tác động đến hành vi</p>\r\n<p>==&gt; ứng xử marketing</p>\r\n<p>- quảng cáo: <a class=\"cloze\" href=\"#mce_temp_url#\">chi tiết </a>mang tính giáo dục nỗ lực để người mua thử dùng thử sản phẩm hoặc thay thế sản phẩm</p>\r\n<p>-  tích cực <a class=\"cloze\" href=\"#mce_temp_url#\">trưng bày</a> tại các hội chợ thương mại để xem xét hiệu quả hoạt động</p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">thử nghiệm</a> miễn phí các phiên bản hoặc sản phẩm trưng bày tại nơi bán</p>\r\n<p>- bán hàng cần chú trọng đến việc hiểu rõ nhu cầu của khách hàng và trình bày sản phẩm mới làm hài lòng khách hàng tốt hơn sản phẩm cũ</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2858', 'TTHCM về tính chất và chức năng của nền văn hóa mới?', null, null, '2', '0', '516', '45', '1552357516', '1552665062', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2859', 'Mua hệ thống.', null, null, '5', '0', '511', '467', '1552357558', '1552357607', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2860', null, null, null, '5', '2859', '511', '467', '1552357558', '1552357607', null, null, '<ul>\r\n<li>Doanh nghiệp thích mua <a class=\"cloze\" href=\"#mce_temp_url#\">giải pháp toàn bộ</a> cho vấn đề của mình từ một người bán</li>\r\n<li>Bán đồng bộ có thể có <a class=\"cloze\" href=\"#mce_temp_url#\">nhiều hình thức</a></li>\r\n<li>Bán một bộ Sản phẩm  có<a class=\"cloze\" href=\"#mce_temp_url#\"> liên đới </a>với nhau</li>\r\n<li>Bán một <a class=\"cloze\" href=\"#mce_temp_url#\">hệ thống</a> sản xuất quản lý dự trữ phương pháp và dịch vụ khai thác để đáp ứng nhu cầu của người mua cần sự hoạt động thông suốt</li>\r\n<li>Ký hợp đồng với một nguồn cung ứng duy nhất đảm bảo toàn bộ yêu cầu về MRO</li>\r\n</ul>\r\n<p>-&gt; yêu cầu có chiến lược marketing đồng bộ để chứng minh năng lực</p>\r\n<p>==&gt; xây dựng marketing trọng điểm</p>\r\n<p>giữ khách hàng hiện tại với các tour tham quan nhà máy, chiết giải đặc biệt cho </p>\r\n<p>khách hàng. </p>\r\n<p>- Phản hồi nhanh sự thay đổi về nhu cầu khách hàng </p>\r\n<p>- Chứng minh năng lực cung ứng </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2861', 'Vai trò người khởi xướng.', null, null, '5', '0', '512', '467', '1552357714', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2862', null, null, null, '5', '2861', '512', '467', '1552357714', null, null, null, '<ul>\r\n<li>người<a class=\"cloze\" href=\"#mce_temp_url#\"> đưa ra ý tưởng</a> mua sản phẩm hoặc đề xuất nhu cầu </li>\r\n<li>có thể là người trực tiếp sử dụng sản phẩm bộ phận kỹ thuật lãnh đạo, R&amp;D... </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2863', 'Vai trò người sử dụng.', null, null, '5', '0', '512', '467', '1552357779', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2864', null, null, null, '5', '2863', '512', '467', '1552357779', null, null, null, '<ul>\r\n<li>Sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">thực thi</a> làm việc với các sản phẩm mua về </li>\r\n<li>Yêu cầu của họ sẽ là những thông số đầu vào cho quyết định cuối cùng của cá nhân trong trung tâm mua.</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2865', 'a.	Tính chất của nền văn hóa mới', null, null, '5', '0', '516', '45', '1552357784', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2866', null, null, null, '5', '2865', '516', '45', '1552357784', null, null, null, '<p>-  Tính dân tộc</p>\r\n<p>   + Mang <a class=\"cloze\" href=\"#mce_temp_url#\">đặc trưng</a> của dt</p>\r\n<p>   + <a class=\"cloze\" href=\"#mce_temp_url#\">Giữ gìn</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">kế thừa</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phát huy</a> những truyền thống dt</p>\r\n<p>   + Phát triển những truyền thống dt cho <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a> vs <a class=\"cloze\" href=\"#mce_temp_url#\">điều kiện lịch sử mới</a> của đất nước</p>\r\n<p>- Tính Khoa học</p>\r\n<p>   + Tính <a class=\"cloze\" href=\"#mce_temp_url#\">hiện đại</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tiên tiến</a>, thuận với trào lưu <a class=\"cloze\" href=\"#mce_temp_url#\">tiến hóa</a> của thời đại</p>\r\n<p>   + <a class=\"cloze\" href=\"#mce_temp_url#\">Đấu tranh</a> với những gì <a class=\"cloze\" href=\"#mce_temp_url#\">trái khoa học</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phản tiến bộ</a></p>\r\n<p>   + <a class=\"cloze\" href=\"#mce_temp_url#\">Gạn đục khơi trong</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">kế thừa</a> truyền thống tốt đẹp của dt và <a class=\"cloze\" href=\"#mce_temp_url#\">tiếp thu</a> tinh hoa văn hóa nhân loại</p>\r\n<p>- Tính đại chúng:</p>\r\n<p>   + Phải <a class=\"cloze\" href=\"#mce_temp_url#\">phục vụ nhân dân</a></p>\r\n<p>   + <a class=\"cloze\" href=\"#mce_temp_url#\">Phù hợp</a> với <a class=\"cloze\" href=\"#mce_temp_url#\">nguyện vọng</a> của nhân dân</p>\r\n<p>   + <a class=\"cloze\" href=\"#mce_temp_url#\">Đậm đà tính nhân văn</a></p>\r\n<p>   + Do nhân dân xây dựng nên</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2867', 'Vai trò người ảnh hưởng.', null, null, '5', '0', '512', '467', '1552357881', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2868', null, null, null, '5', '2867', '512', '467', '1552357881', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cá nhân</a> có thể<a class=\"cloze\" href=\"#mce_temp_url#\"> tác động</a> đến quyết định mua hàng </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cung cấp thông tin</a> quan trọng Mặc dù họ không tham gia trực tiếp vào quá trình mua </li>\r\n</ul>\r\n<ul>\r\n<li>Có thể là các kỹ sư cơ khí chuyên gia hiểu biết về sản phẩm chi tiết chi tiết sản phẩm </li>\r\n</ul>\r\n<ul>\r\n<li>Quan điểm của nhóm này được các cá nhân của trung tâm mua tiếp nhận với sự<a class=\"cloze\" href=\"#mce_temp_url#\"> tôn trọng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">đánh giá cao</a> </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2869', 'Vai trò người gác cổng.', null, null, '5', '0', '512', '467', '1552357921', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2870', null, null, null, '5', '2869', '512', '467', '1552357921', null, null, null, '<ul>\r\n<li>Có nhiệm vụ <a class=\"cloze\" href=\"#mce_temp_url#\">kiểm soát</a> dòng <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> liên quan </li>\r\n<li> Có thể là một số nhân viên thư ký trợ lý </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2871', 'Vai trò người ra quyết định.', null, null, '5', '0', '512', '467', '1552357960', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2872', null, null, null, '5', '2871', '512', '467', '1552357960', null, null, null, '<ul>\r\n<li>Đưa ra<a class=\"cloze\" href=\"#mce_temp_url#\"> lựa chọn thực sự</a> về sản phẩm dịch vụ </li>\r\n<li>Sản phẩm thiết bị cố định đắt tiền ăn có thể ban điều hành cấp cao trong tổ chức là người ra quyết định </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2873', 'Vai trò người phê duyệt.', null, null, '5', '0', '512', '467', '1552358000', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2874', null, null, null, '5', '2873', '512', '467', '1552358000', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cho phép thực hiện</a> các hoạt động đề xuất của người quyết định hoặc người mua </li>\r\n<li>Có thể là giám đốc tổng giám đốc </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2875', 'Vai trò người mua.', null, null, '5', '0', '512', '467', '1552358072', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2876', null, null, null, '5', '2875', '512', '467', '1552358072', null, null, null, '<ul>\r\n<li>Những người mua chuyên nghiệp chú trọng của họ là <a class=\"cloze\" href=\"#mce_temp_url#\">việc mua sắm</a> </li>\r\n<li>Kết quả làm việc công tác của họ cũng thường được đánh giá dựa trên kết quả của hoạt động mua sắm </li>\r\n</ul>\r\n<ul>\r\n<li>Nhiệm vụ của người mua: \r\n<ul>\r\n<li>Có được <a class=\"cloze\" href=\"#mce_temp_url#\">đánh giá </a></li>\r\n<li> <a class=\"cloze\" href=\"#mce_temp_url#\">Đánh giá</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> lựa chọn</a> nhà cung cấp </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thương lượng</a> </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xử lý</a> đơn hàng </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xúc tiến</a> giao hàng </li>\r\n<li>Thực hiện chính sách thu mua và mua sắm của tổ chức \r\n<ul>\r\n<li>Phải xây dựng mối quan hệ với người mua sự phù hợp </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2877', 'b.	Chức năng của nền văn hóa mới', null, null, '5', '0', '516', '45', '1552358098', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2878', null, null, null, '5', '2877', '516', '45', '1552358098', null, null, null, '<p>- <strong><span style=\"text-decoration: underline;\">Bồi dưỡng <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng</a></span></strong> đúng đắn và những <a class=\"cloze\" href=\"#mce_temp_url#\">tình cảm</a> cao đẹp</p>\r\n<p>   + Mục đích: Bồi dưỡng, <a class=\"cloze\" href=\"#mce_temp_url#\">nêu cao tư tưởng</a> <a class=\"cloze\" href=\"#mce_temp_url#\">đúng đắn</a> và tình cảm cao đẹp cho nhân dân, <a class=\"cloze\" href=\"#mce_temp_url#\">loại bỏ</a> những tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">sai lầm</a>, tình cảm thấp hèn có thể có trong tư tưởng, tình cảm mỗi người</p>\r\n<p>   + <a class=\"cloze\" href=\"#mce_temp_url#\">Văn hóa </a>phải đặc biệt <a class=\"cloze\" href=\"#mce_temp_url#\">quan tâm</a> đến những tư tưởng và tình cảm lớn, chi phối đời sống tinh thần của mỗi người và cả dt</p>\r\n<p>   + Chức năng này phải đc tiến hành <a class=\"cloze\" href=\"#mce_temp_url#\">thường xuyên liên tục</a></p>\r\n<p>- <strong><span style=\"text-decoration: underline;\">MR hiểu biết, nâng cao dân trí</span></strong></p>\r\n<p>   + Dân trí là trình độ <a class=\"cloze\" href=\"#mce_temp_url#\">hiểu biết,</a> <a class=\"cloze\" href=\"#mce_temp_url#\">kiến thức</a> của người dân</p>\r\n<p>   + Nội dung của nâng cao dân trí là bắt đầu từ chỗ <a class=\"cloze\" href=\"#mce_temp_url#\">biết đọc</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">biết viết</a>, tiếp đó là sự <a class=\"cloze\" href=\"#mce_temp_url#\">hiểu biết các lĩnh vực</a> khác nhau của đời sống xã hội như kinh tế, chính trị, khoa học, kĩ thuật, thực tiễn VN và TG.</p>\r\n<p>   + Nâng cao dân trí nhằm hướng vào mục tiêu chung là <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập dân tộc</a> và CNXH</p>\r\n<p>- Bồi dưỡng những <a class=\"cloze\" href=\"#mce_temp_url#\">phẩm chất</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phong cách</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">lối sống tốt đẹp</a>, lành mạnh, hưỡng con người đến chân, thiện, mỹ để <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">hoàn thiện bản thân</span></strong></a></p>\r\n<p>   + HCM đề ra phẩm chất, phong cách cần thiết để mỗi người tự <a class=\"cloze\" href=\"#mce_temp_url#\">tu dưỡng</a>, đặc biệt quan tâm đến phẩm chất đạo đức - chính trị của cán bộ, đảng viên</p>\r\n<p>   + VH giúp con người làm <a class=\"cloze\" href=\"#mce_temp_url#\">nảy nở cái tốt đẹp</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">giảm</a> bớt cái <a class=\"cloze\" href=\"#mce_temp_url#\">lạc hậu</a>, xấu xa, bảo thủ, vươn tới cái chân, thiện, mỹ để hoàn thiện bản thân</p>\r\n<p>   + Phải làm cho văn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">thấm sâu vào tâm lý quốc dân</a>. Văn hóa phải <a class=\"cloze\" href=\"#mce_temp_url#\">soi đường</a> cho quốc dân đi.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2879', 'Trình bày/phân tích triết lí mua?', null, null, '5', '0', '514', '467', '1552358226', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2880', null, null, null, '5', '2879', '514', '467', '1552358226', null, null, null, '<p>1. Triết lý mua: triết lý mua<a class=\"cloze\" href=\"#mce_temp_url#\"> chi phối hành vi</a> của khách hàng là một trong khía cạnh </p>\r\n<p>phản ánh các dạng quan hệ có thể có giữa người mua và người bán thông qua việc </p>\r\n<p>người mua lựa chọn nhà cung cấp </p>\r\n<ul>\r\n<li>mua hàng truyền thống:\\ \r\n<ul>\r\n<li>Triết lý mua hàng truyền thống hay còn gọi triết lý mua hàng đối nghịch nghĩa là doanh nghiệp mua sẽ có vài nhà cung cấp cho mỗi sản phẩm cần mua </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thông qua cạnh tranh</a> giúp doanh nghiệp hạ giá thành hoặc tăng chất lượng dịch vụ </li>\r\n<li>Thường con nhà cung cấp là địch thủ đối lập Do đó người mua chịu trách nhiệm sản phẩm đang nhận và không thể truy đòi nhà cung cấp những sản phẩm lỗi hỏng \r\n<ul>\r\n<li>Với triết lí mua này người mua và người bán ở hai <a class=\"cloze\" href=\"#mce_temp_url#\">phe đối lập</a> vì lợi ích của mình việc xây dựng mối quan hệ là rất thấp </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li>Triết lý mua hàng cộng tác \r\n<ul>\r\n<li>Người mua nhằm Tìm kiếm các nhà cung cấp tốt nhất có thểcó, sau đó cố gắng phát triển mối quan hệ gắn bó đặc biệt trong lĩnh vực mang tầm quan trọng chiến lược của doanh nghiệp . </li>\r\n<li>Lợi ích: \r\n<ul>\r\n<li>Tăng hiệu quả hoạt động, dễ dàng hợp tác và giải quyết các chi phí phát sinh </li>\r\n<li>Giảm chi phí nhờ mối quan hệ chẽ </li>\r\n<li>Tăng cường hợp tác về mặt kỹ thuật và công nghệ; người mua hàng có điều kiện tiếp cận với các công nghệ của nhà cung cấp</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>== &gt; lợi ích cho phát triển sp </p>\r\n<ul>\r\n<li>\r\n<ul>\r\n<li>\r\n<ul>\r\n<li>Với chiếc lý mua này thì người mua và người bán hướng tới mối quan hệ hợp tác lâu dài ổn định. </li>\r\n</ul>\r\n</li>\r\n<li>Áp dụng: \r\n<ul>\r\n<li>Nguyên vật liệu của khối lượng lớn, mua Các bộ phận hai sản phẩm có tầm quan trọng chiến lược </li>\r\n<li>Các sản phẩm chuyên biệt đòi hỏi có kiến thức chuyên môn sâu để Cắt giảm chi phí hay thực hiện </li>\r\n<li>Các nguyên nhiên liệu mà không có nhà cung cấp nào khác </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2881', 'Trình bày/phân tích mục tiêu mua?', null, null, '5', '0', '514', '467', '1552358427', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2882', null, null, null, '5', '2881', '514', '467', '1552358427', null, null, null, '<ul style=\"text-align: justify;\">\r\n<li>Các thành viên trung tâm mua <a class=\"cloze\" href=\"#mce_temp_url#\">chịu ảnh hưởng</a> của các mục tiêu mua sắm của tổ chức và cá nhân như địa vị cao hơn an toàn với công việc, các xem xét về chương trình Xúc tiến và xã hội… </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mục tiêu mua </a>\r\n<ul>\r\n<li>Mua đúng <a class=\"cloze\" href=\"#mce_temp_url#\">danh mục sản phẩm</a> </li>\r\n<li>Đúng <a class=\"cloze\" href=\"#mce_temp_url#\">số lượng</a> </li>\r\n<li>Đúng <a class=\"cloze\" href=\"#mce_temp_url#\">mức giá</a> </li>\r\n<li>Giao hàng đúng <a class=\"cloze\" href=\"#mce_temp_url#\">thời điểm</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">địa điểm </a></li>\r\n</ul>\r\n</li>\r\n<li> 6 yêu cầu: \r\n<ul>\r\n<li>Thời hạn và tính kịp thời \r\n<ul>\r\n<li>Mua các sản phẩm, dịch vụ đúng số lượng và được giao hàng vào đúng lúc đến đúng nơi khách hàng cần </li>\r\n<li>Just in time chuyển hàng đến địa điểm của khách hàng xác định vào lúc Khách hàng muốn </li>\r\n<li>Sản xuất hiện thời sản xuất theo nhu cầu chuyển hàng của khách hàng </li>\r\n<li>EDT trao đổi dữ liệu điện tử có thông tin về nhu cầu khách hàng </li>\r\n</ul>\r\n</li>\r\n<li>Chất lượng và tính cảm ứng của mặt hàng \r\n<ul>\r\n<li>Phải nhất quán với các mô tả chi tiết và có khả năng đáp ứng trường hợp sử dụng mặt hàng sản phẩm </li>\r\n<li>Quan trọng là chất lượng phù hợp thay vì chất lượng tốt hơn </li>\r\n</ul>\r\n</li>\r\n<li>Giá cả và điều kiện thanh toán \r\n<ul>\r\n<li>Tổng mức chi phí thấp nhất: mức chi phí mua sản phẩm dịch vụ thấp nhất </li>\r\n<li>Phù hợp với mức có sẵn và chất lượng sản phẩm </li>\r\n<li>Tổng chi phí sở hữu thấp nhất: các chi phí giao hàng và lắp đặt, dịch, vụ và sửa chữa, năng lượng vận hành, các nguyên liệu cung cấp,... trong suốt thời gian của mỗi sản phẩm </li>\r\n<li>Số lượng anh đặt hàng kinh tế: là lượng khách hàng giúp tối thiểu hóa chi phí đặt hàng và các chi phí dự trữ </li>\r\n</ul>\r\n</li>\r\n<li>Dịch vụ thương mại của người bán(, trước, trong, sau bán) \r\n<ul>\r\n<li>Thông tin nhanh chóng và chính xác từ các nhà cung cấp </li>\r\n<li>Hỗ trợ cấp Kỹ thuật </li>\r\n<li>Sự sẵn có của các bộ phận cấu thành dự trữ thay thế </li>\r\n<li>Năng lực sửa chữa và duy trì </li>\r\n<li>Huấn luyện nếu có </li>\r\n</ul>\r\n</li>\r\n<li>Khả năng thích nghi của nhà cung cấp với nhu cầu đặc biệt: phát triển quan hệ dài hạn với nhà cung cấp/ người bán nhằm phát triển nguồn cung cấp mới khi có nhu cầu đặc biệt </li>\r\n<li>Khối lượng và tính liên tục của cung ứng </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2883', 'Trình bày/phân tích chiến lược mua?', null, null, '5', '0', '514', '467', '1552358553', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2884', null, null, null, '5', '2883', '514', '467', '1552358553', null, null, null, '<ul style=\"text-align: left;\">\r\n<li>Đảm bảo <a class=\"cloze\" href=\"#mce_temp_url#\">nguồn cung ứng</a> \r\n<ul>\r\n<li>Dự báo sự thay đổi trong cung và cầu hướng tới sự đảm bảo của nhà cung cấp, đổi lại là sự đảm bảo về khối lượng định của người mua </li>\r\n<li>Kiếm các nhà cung cấp đạt yêu cầu đúng thời hạn trong bất kỳ hoàn cảnh nào trong tương lai \r\n<ul>\r\n<li>Mục tiêu mua hàng đúng thời điểm giao hàng đúng thời điểm </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li> <a class=\"cloze\" href=\"#mce_temp_url#\">giảm chi phí</a> \r\n<ul>\r\n<li>Theo đuổi mục tiêu giảm tổng chi phí mua hàng( chắc chết tất cả các yếu tố có thể ảnh hưởng đến chi phí như giao hàng chậm lỗi..) </li>\r\n<li>Thường xuyên kiểm soát, xem xét tất cả các yếu tố có thể ảnh hưởng đối chi phí </li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Hỗ trợ</a> lẫn nhau trong cung cấp: \r\n<ul>\r\n<li>nhằm cho phép người mua vào nhà cung cấp chia sẻ thông tin và năng lực </li>\r\n<li>Mục đích hướng tới việc cải thiện quan hệ giữa người mua và người bán thông qua trao đổi thông tin hai chiều nhằm đảm bảo chất lượng sản phẩm và dịch vụ tốt hơn ổn định hơn, </li>\r\n<li>Thường áp dụng jit==&gt; doanh nghiệp theo đuổi mục tiêu mua hàng đúng thời điểm… </li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thay đổi môi trường</a> \r\n<ul>\r\n<li>Thông báo và nhận diện các biến động, thay đổi và Vinh hướng môi trường vì các lý do kinh tế, thay đổi cơ cấu tổ chức, … </li>\r\n<li>Nhiệm vụ của công ty là thích ứng và phản ứng với những biến đổi này để </li>\r\n<li>Xây dựng lợi thế cạnh tranh trong dàii hạn </li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Chiến lược cạnh tranh </a></li>\r\n</ul>\r\n<p style=\"text-align: left;\">Phát hiện các cơ hội thị trường và lợi thế cạnh tranh của bản thân doanh nghiệp mà có thể đem lại cho người mua những lợi thế cạnh tranh quan trọng doanh nghiệp có thể tìm kiếm và mua những sản phẩm hàng đầu để phát triển hình ảnh của mình trên thị trường </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2885', 'Các bước tiến trình mua của khách hàng tổ chức?', null, null, '5', '0', '515', '467', '1552358603', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2886', null, null, null, '5', '2885', '515', '467', '1552358603', null, null, null, '<ul>\r\n<li>Các bước trong tiến trình mua \r\n<ul>\r\n<li>Bước 1: ý thức nhu cầu: </li>\r\n<li>Bước 2: Mô tả khái quát nhu cầu </li>\r\n<li>Bước 3: xác định quy cách sản phẩm </li>\r\n<li>Bước 4: Tìm kiếm nhà cung cấp </li>\r\n<li>Bước 5: yêu cầu chào hàng </li>\r\n<li>Bước 6: lựa chọn nhà cung cấp </li>\r\n<li>Bước 7: làm thủ tục đặt hàng </li>\r\n<li>Bước 8: đánh giá kết quả thực hiện </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2887', 'Bước 1: nhận thức nhu cầu', null, null, '5', '0', '515', '467', '1552358657', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2888', null, null, null, '5', '2887', '515', '467', '1552358657', null, null, null, '<ul>\r\n<li>Khi có người trong doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">ý thức</a> một vấn đề hay <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu </a>có thể<a class=\"cloze\" href=\"#mce_temp_url#\"> đáp ứng</a> qua việc mua sản phẩm hay hàng hóa </li>\r\n<li>Những người phát hiện nhu cầu có thể là người sử dụng /có hoạt động liên quan đến sản phẩm </li>\r\n<li>Thực hiện vai trò: người<a class=\"cloze\" href=\"#mce_temp_url#\"> khởi xướng</a> </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2889', 'Bước 2: Mô tả khái quát nhu cầu', null, null, '5', '0', '515', '467', '1552358791', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2890', null, null, null, '5', '2889', '515', '467', '1552358791', null, null, null, '<ul>\r\n<li>Người mua/ bộ phận sử dụng/ thiết kế kỹ thuật phải <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> các <a class=\"cloze\" href=\"#mce_temp_url#\">đặc điểm chung</a> và số lượng sản phẩm của có nhu cầu </li>\r\n<li>Với những <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm phức tạp</a> cần có sự tham gia của các <a class=\"cloze\" href=\"#mce_temp_url#\">kỹ sư người sử dụng</a> <a class=\"cloze\" href=\"#mce_temp_url#\">cố vấn</a> chuyên môn để định ra đặc điểm sản phẩm cần mua( tầm quan trọng và mức độ tin cậy, độ bền, giá và các sản phẩm khác..) </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2907', 'Bước 3: Xác định quy cách sản phẩm.', null, null, '5', '0', '515', '467', '1552358874', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2908', null, null, null, '5', '2907', '515', '467', '1552358874', null, null, null, '<ul>\r\n<li>Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">quy cách kỹ thuật</a> của sản phẩm( chi tiết kỹ thuật..) \r\n<ul>\r\n<li>Những đặc điểm ưu điểm của sản phẩm được bộ phận kỹ thuật xác định và mức độ đáp ứng của nhà cung cấp và yêu cầu của trung tâm mua </li>\r\n<li>Có thể sử dụng phương pháp phân tích giá trị GE nhằm dành được khách hàng. </li>\r\n</ul>\r\n</li>\r\n<li>Nhà cung cấp tham gia từ sớm: \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhà cung cấp </a>ta <a class=\"cloze\" href=\"#mce_temp_url#\">mô tả chi tiết</a>, đặc biệt trong trường hợp sản phẩm mới chưa bao giờ gặp </li>\r\n<li>Sử dụng để <a class=\"cloze\" href=\"#mce_temp_url#\">xác định vị trí</a> của mình nhằm đạt được khách hàng/ tham gia sớm vào những quy cách kỹ thuật của người mua </li>\r\n</ul>\r\n</li>\r\n<li>Bộ phận kỹ thuật và thiết kế hoặc nhóm công tác liên chức thường thực hiện nhiệm vụ này phát triển quy cách sản phẩm dịch vụ </li>\r\n<li>thực hiện vai trò: người<a class=\"cloze\" href=\"#mce_temp_url#\"> ảnh hưởng</a>, người <a class=\"cloze\" href=\"#mce_temp_url#\">sử dụng</a>, người <a class=\"cloze\" href=\"#mce_temp_url#\">mua</a> </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2909', 'Bước 4: Tìm kiếm nhà cung cấp.', null, null, '5', '0', '515', '467', '1552358923', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2910', null, null, null, '5', '2909', '515', '467', '1552358923', null, null, null, '<ul>\r\n<li>Xác định<a class=\"cloze\" href=\"#mce_temp_url#\"> nhà cung cấp</a> <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a> nhất từ danh sách các nhà cung cấp có khả năng đáp ứng tiêu chuẩn đã được lập </li>\r\n<li>mua mới sản phẩm phức tạp, đắt tiền ==&gt; thời gian tìm kiếm, x xây dựng nhà cung cấp lâu dài </li>\r\n<li>thực hiện vai trò: người <a class=\"cloze\" href=\"#mce_temp_url#\">mua </a></li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2927', 'Bước 5: yêu cầu chào hàng.', null, null, '5', '0', '515', '467', '1552358991', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2928', null, null, null, '5', '2927', '515', '467', '1552358991', null, null, null, '<ul>\r\n<li>Người mua <a class=\"cloze\" href=\"#mce_temp_url#\">mời</a> những <a class=\"cloze\" href=\"#mce_temp_url#\">nhà cung cấp</a> đã được lựa chọn gửi <a class=\"cloze\" href=\"#mce_temp_url#\">bản chào hàng</a> và những <a class=\"cloze\" href=\"#mce_temp_url#\">văn bản chi tiết</a> liên quan </li>\r\n<li>Bộ phận mua có trách nhiệm<a class=\"cloze\" href=\"#mce_temp_url#\"> tập hợp </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">gửi </a>những thư mời các nhà cung cấp tiềm năng tham gia vào dự thầu chào hàng phải tạo ra niềm tin ở người mua chứ không phải là một tài liệu kỹ thuật </li>\r\n<li>chào hàng phải <a class=\"cloze\" href=\"#mce_temp_url#\">trình bày</a> được <a class=\"cloze\" href=\"#mce_temp_url#\">năng lực kỹ thuật </a></li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2929', 'Các nguyên tắc xây dựng đạo đức mới theo TTHCM. Liên hệ bản thân?', null, null, '2', '0', '519', '45', '1552359028', '1552665243', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('2930', 'Bước 6: Lựa chọn nhà cung cấp.', null, null, '5', '0', '515', '467', '1552359078', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2931', null, null, null, '5', '2930', '515', '467', '1552359078', null, null, null, '<ul>\r\n<li>Trung tâm mua sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">xác định </a>những <a class=\"cloze\" href=\"#mce_temp_url#\">chỉ tiêu đánh giá</a> các nhà cung cấp và tiến hành </li>\r\n<li>Đánh giá nhằm<a class=\"cloze\" href=\"#mce_temp_url#\"> xác định nhà cung cấp</a> nào hấp dẫn nhất </li>\r\n<li>Các nhà làm marketing xây dựng các nguyên tắc đối với bản chào hàng hấp dẫn </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2932', 'Bước 7: Làm thủ tục đặt hàng.', null, null, '5', '0', '515', '467', '1552359129', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2933', null, null, null, '5', '2932', '515', '467', '1552359129', null, null, null, '<ul>\r\n<li>Thương lượng ảnh về đơn đặt hàng với nhà cung cấp được lựa chọn:chi tiết kỹ thuật; thời gian giao hàng kì vọng; số lượng cần cố; chính sách trả hàng và bảo hành </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2950', 'Bước 8: Đánh giá kết quả thực hiện', null, null, '5', '0', '515', '467', '1552359156', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2951', null, null, null, '5', '2950', '515', '467', '1552359156', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xem xét kết quả</a> của đợt mua và quyết định tiếp tục hay loại bỏ nhà cung cấp. </li>\r\n<li>Các là các yếu tố tương tự được sử dụng bởi người mua </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2969', 'Đặc điểm thị trường: nhu cầu.', null, null, '5', '0', '524', '467', '1552359618', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2970', null, null, null, '5', '2969', '524', '467', '1552359618', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhu cầu phái sinh</a>: cầu của thị trường b2b là do tác động ảnh của nhu cầu thị trường đầu ra của khách hàng hay nhu cầu của các khách hàng và dịch vụ khác </li>\r\n<li> Cầu <a class=\"cloze\" href=\"#mce_temp_url#\">dao động</a> ở<a class=\"cloze\" href=\"#mce_temp_url#\"> biên độ cao hơn</a>,<a class=\"cloze\" href=\"#mce_temp_url#\"> thất thường</a>:  \r\n<ul>\r\n<li>Một sự thay đổi nhỏ trong nhu cầu tiêu dùng có thể dẫn đến một lượng thay đổi trong nhu cầu với sản phẩm b2b </li>\r\n<li>Thị trường b2b biến động theo sự ảnh hưởng của môi trường vì vậy vậy để ra sản phẩm phải quan sát biến động thay đổi trong cầu b2c để tính sản lượng chủng loại cung cấp cho b2b. do b2b phải giải quyết bài toán sản xuất quy mô lớn hơn trong một thời gian xác định nên b2b trễ pha hơn b2c </li>\r\n</ul>\r\n</li>\r\n<li> <a class=\"cloze\" href=\"#mce_temp_url#\">Cầu liên kết</a>: \r\n<ul>\r\n<li>Nhu cầu của các sản phẩm và dịch vụ phụ thuộc lẫn nhau </li>\r\n<li>Việc chấm dứt hay âm dịch vụ vụ có thể kéo theo việc chấm dứt hay phát triển về nhu cầu của một loại hình hàng hóa </li>\r\n</ul>\r\n</li>\r\n<li>Cầu<a class=\"cloze\" href=\"#mce_temp_url#\"> ít co giãn</a> so với giá trong<a class=\"cloze\" href=\"#mce_temp_url#\"> thời gian ngắn</a>: cầu b2b là cầu ít nhạy cảm với giá. Quy mô không phụ thuộc vào rẻ hay không mà phụ thuộc vào đầu ra. </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2987', 'Đặc điểm thị trường về mục đích mua.', null, null, '5', '0', '524', '467', '1552359670', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2988', null, null, null, '5', '2987', '524', '467', '1552359670', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sản xuất</a> các sản phẩm và dịch vụ khác </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Bán lại </a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Sử dụng</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> tiêu dùng</a> cho <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> của họ trong quá trình hoạt động và vận hành </li>\r\n<li>Xu hướng chuyển dần sang<a class=\"cloze\" href=\"#mce_temp_url#\"> thuê tài chính</a> tín dụng </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2989', 'Đặc điểm thị trường về khách hàng.', null, null, '5', '0', '524', '467', '1552359735', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2990', null, null, null, '5', '2989', '524', '467', '1552359735', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Số lượng </a>khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">ít</a> nhưng <a class=\"cloze\" href=\"#mce_temp_url#\">quy mô</a> khách hàng<a class=\"cloze\" href=\"#mce_temp_url#\"> lớn</a>==&gt; đề cao mối quan hệ bắt; marketing đặc trưng thay vì đại chúng </li>\r\n</ul>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tập chung</a> về mặt Địa Lý \r\n<ul>\r\n<li>Thị trường sản xuất tập trung gần nguồn nguyên liệu==&gt; dự báo Sự dịch chuyển trong nguồn nguyên liệu tính toán vận chuyển, Logistic </li>\r\n<li>Các doanh nghiệp bán lại tập trung hơn về mặt Địa Lý </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2991', 'Đặc điểm thị trường về sản phẩm dịch vụ.', null, null, '5', '0', '524', '467', '1552359800', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2992', null, null, null, '5', '2991', '524', '467', '1552359800', null, null, null, '<ul>\r\n<li>Về bản chất: có tính <a class=\"cloze\" href=\"#mce_temp_url#\">kỹ thuật cao</a> và mua trên cơ sở các yêu cầu chi tiết kỹ thuật thường là nguyên vật liệu thô và bán thực phẩm </li>\r\n<li>Chú trọng tới <a class=\"cloze\" href=\"#mce_temp_url#\">giao hàng đúng hạn</a> hỗ trợ kỹ thuật và dịch vụ rao bán</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2993', 'Đặc điểm thị trường về của quá trình mua.', null, null, '5', '0', '524', '467', '1552359842', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('2994', null, null, null, '5', '2993', '524', '467', '1552359842', null, null, null, '<ul>\r\n<li>Được <a class=\"cloze\" href=\"#mce_temp_url#\">huấn luyện</a> chuyên nghiệp </li>\r\n<li>Có nhiều<a class=\"cloze\" href=\"#mce_temp_url#\"> kỹ thuật</a> mua hàng cụ thể </li>\r\n<li>Trong suốt sự nghiệp người mua học hỏi <a class=\"cloze\" href=\"#mce_temp_url#\">cách thức</a> mua hàng </li>\r\n<li>Khách hàng và người cung cấp thân thiện </li>\r\n<li>mua sắm qua mạng </li>\r\n<li>Nhiều người tham gia vào quá trình mua, có nhiều người ảnh hưởng tới quyết định mua </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3013', 'Các dạng thị trường B2B: thị trường doanh nghiệp sản xuất.', null, null, '5', '0', '524', '467', '1552359955', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3014', null, null, null, '5', '3013', '524', '467', '1552359955', null, null, null, '<ul>\r\n<li>khái niệm: bao gồm các <a class=\"cloze\" href=\"#mce_temp_url#\">doanh nghiệp</a>,<a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a> mua sản phẩm với mục đích <a class=\"cloze\" href=\"#mce_temp_url#\">hỗ trợ  </a>trực tiếp và gián tiếp <a class=\"cloze\" href=\"#mce_temp_url#\">quá trình sản xuất</a> hàng hóa và dịch vụ khác </li>\r\n</ul>\r\n<ul>\r\n<li>bản chất là <a class=\"cloze\" href=\"#mce_temp_url#\">OEM</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">user </a></li>\r\n<li>đặc điểm thị trường: \r\n<ul>\r\n<li>Một số sản phẩm<a class=\"cloze\" href=\"#mce_temp_url#\"> hỗ trợ</a> cho quá trình sản xuất ra sản phẩm khác </li>\r\n<li>Một số được <a class=\"cloze\" href=\"#mce_temp_url#\">tận dụng</a> hết trong quá trình sản xuất hàng hóa dịch vụ </li>\r\n<li>Có những sản phẩm góp phần thực hiện các hoạt động hàng ngày của doanh nghiệp </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n<li>Đặc điểm của khách hàng sản xuất: \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nhu cầu</a>: cầu phải sinh; cầu liên kết; cầu dao động;caafu co giãn </li>\r\n<li>Đặc điểm mục đích mua: <a class=\"cloze\" href=\"#mce_temp_url#\">để sản xuất </a></li>\r\n<li>Triết lý mua <a class=\"cloze\" href=\"#mce_temp_url#\">cộng tác </a></li>\r\n<li>Tình huống mua: tái đặt mua; ctái đặt mua có điều chỉnh; mua mới </li>\r\n<li>Loại sản phẩm là sản phẩm hàng hóa đầu vào: nguyên vật liệu thu; Nguyên; cấu kiện bộ phận </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3015', 'Các dạng thị trường B2B: thị trường cấc nhà bán lại.', null, null, '5', '0', '524', '467', '1552360022', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3016', null, null, null, '5', '3015', '524', '467', '1552360022', null, null, null, '<ul>\r\n<li>khái niệm: bao gồm các cá nhân và tổ chức mua hàng hóa nhằm mục đích<a class=\"cloze\" href=\"#mce_temp_url#\"> bán lại </a>hoặc <a class=\"cloze\" href=\"#mce_temp_url#\">cho thuê </a>nhằm kiếm lời </li>\r\n</ul>\r\n<ul>\r\n<li>Đặc điểm: \r\n<ul>\r\n<li>Nhu cầu: cầu phái sinh, cầu co giãn, ít có tính liên kết </li>\r\n<li>Đặc điểm mục đích mua: <a class=\"cloze\" href=\"#mce_temp_url#\">bán lại </a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mua mới</a> </li>\r\n<li>Triết lý mua <a class=\"cloze\" href=\"#mce_temp_url#\">đối nghịch</a> </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>Các loại sản phẩm: thường là<a class=\"cloze\" href=\"#mce_temp_url#\"> thành phẩm</a></p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3017', 'Các dạng thị trường B2B: thị trường có quyền và xã hội.', null, null, '5', '0', '524', '467', '1552360101', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3018', null, null, null, '5', '3017', '524', '467', '1552360101', null, null, null, '<ul>\r\n<li>Khái niệm: bao gồm những <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức </a>của<a class=\"cloze\" href=\"#mce_temp_url#\"> Chính phủ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">các cơ quan địa phương</a>,mua theo <a class=\"cloze\" href=\"#mce_temp_url#\">sự phân công</a> của chính quyền </li>\r\n<li>Đặc điểm: những người tham gia vào tiến trình mua: các tổ chức dân sự, tự các tổ chức quân sự, các tổ chức xã hội \r\n<ul>\r\n<li>Các tổ chức này thực hiện các nhiệm vụ khác nhau đều mang tính chất thương mại==&gt; các nhà cung cấp phải nghiên cứu tính chất đặc biệt này và nhu cầu đặc biệt của người mua </li>\r\n</ul>\r\n</li>\r\n<li>Các quyết định: do bị giới hạn về tài chính theo ngân sách==&gt; <a class=\"cloze\" href=\"#mce_temp_url#\">giảm thiểu chi phí</a>: Tìm kiếm nhà cung cấp đáp ứng yêu cầu với chi phí rẻ nhất </li>\r\n<li>Cách thức mua: <a class=\"cloze\" href=\"#mce_temp_url#\">đấu thầu</a> công khai và <a class=\"cloze\" href=\"#mce_temp_url#\">hợp đồng</a> Dựa vào thương lượng </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3019', 'Các dạng thị trường B2B: thị trường cung ứng dịch vụ.', null, null, '5', '0', '524', '467', '1552360141', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3020', null, null, null, '5', '3019', '524', '467', '1552360141', null, null, null, '<ul>\r\n<li>Quy mô và dàn trải đều mặt <a class=\"cloze\" href=\"#mce_temp_url#\">địa lý </a></li>\r\n<li>Gia tăng về <a class=\"cloze\" href=\"#mce_temp_url#\">số lượng</a> </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Dữ liệu</a> về họ không đầy đủ </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3021', 'Tiêu chuẩn phân đoạn theo địa lý.', null, null, '5', '0', '525', '467', '1552360195', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3022', null, null, null, '5', '3021', '525', '467', '1552360195', null, null, null, '<p>-          Theo địa lý</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"312\">\r\n<p>Phạm vi thị trường</p>\r\n<ul>\r\n<li>Toàn cầu</li>\r\n<li>Quốc gia</li>\r\n<li>Khu vực</li>\r\n<li>Địa phương</li>\r\n<li>Văn hóa ( chủng tộc và ngôn ngữ...)</li>\r\n</ul>\r\n</td>\r\n<td valign=\"top\" width=\"312\">\r\n<p>Đơn vị đo lường</p>\r\n<ul>\r\n<li>Vùng miền</li>\r\n<li>Cấp đọ hành chính (tỉnh, huyện...)</li>\r\n<li>Mức độ thành thị</li>\r\n<li>Mã bưu điện</li>\r\n<li>Khu vực thương mại (Trung tâm, lân cận, biên)</li>\r\n<li>Vùng khí hậu</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3023', 'Tiêu chuẩn phân đoạn theo sử dụng sản phẩm.', null, null, '5', '0', '525', '467', '1552360242', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3024', null, null, null, '5', '3023', '525', '467', '1552360242', null, null, null, '<ul>\r\n<li>Ứng dụng sản phẩm nguyên liệu thô, hàng hóa trong quá trình chế biến, thành phẩm.</li>\r\n<li>Loại hình sử dụng: hiện tại, quá khứ, chưa sử dụng</li>\r\n<li>Mức độ quan trọng: Thành phần quan trọng có mức ý nghĩa không thể thay thế</li>\r\n<li>Thị trường OEM, MRO</li>\r\n<li>Mức độ chấp nhận: canh tân, theo sau, lạc hậu</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3025', 'Tiêu chuẩn phân đoạn theo lợi ích mong muốn.', null, null, '5', '0', '525', '467', '1552360289', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3026', null, null, null, '5', '3025', '525', '467', '1552360289', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Động cơ mua</a>: Lý trí, cảm tính, tâm lý</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thứ tự ưu tiên</a>: Lợi ích hàng đầu, lợi ích</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Các biên số</a>; Giá, giá trị, danh tiếng, quy mô, dịch vụ, chất lượng, hình ảnh và giá.</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3027', 'Tiêu chuẩn phân đoạn theo đặc điểm khách hàng tổ chức.', null, null, '5', '0', '525', '467', '1552360363', '1552360403', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3028', null, null, null, '5', '3027', '525', '467', '1552360363', '1552360403', null, null, '<p>-          Theo đặc điểm khách hàng tổ chức</p>\r\n<ul>\r\n<li>Số năm hoạt động</li>\r\n<li>Quy mô, số lượng – lao động, nhà máy, địa điểm</li>\r\n<li>Tài chính: Doanh số, lợi nhuận, tỉ lệ tín dụng</li>\r\n<li>Quyết đinh: Văn phòng trung tâm, đơn vị trực thuộc chi nhánh</li>\r\n<li>Sở hữu; Nhà nước, tư nhân, cổ phần, phi lợi nhuận</li>\r\n<li>Thị trường: Quy mô, lượng khách hàng tiềm năng, cấu trúc thị trường</li>\r\n<li>Vị trí: (vị thế) Thị phần vị trí trong ngành</li>\r\n<li>Giai đoạn kinh doanh: CKS sản phẩm, CKS ngành</li>\r\n<li>Xu thế: phát triển, suy thoái, ổn định</li>\r\n<li>Khách hàng: phục vụ</li>\r\n<li>Tài sản: sở hữu và thuê</li>\r\n<li>Công nghệ</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3047', 'Tiêu chuẩn phân đoạn theo hành vi mua.', null, null, '5', '0', '525', '467', '1552360464', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3048', null, null, null, '5', '3047', '525', '467', '1552360464', null, null, null, '<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tổ chức mua</a>: Tập chung, phi tập chung</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Cấu trúc quyền lực</a> trong mua: Tài chính, bán hàng, MKT, sản xuất, kỹ thuật</li>\r\n<li>Mối quan hệ người mua – người bán mạnh, yếu</li>\r\n<li>Tiêu chí mua: giá, chất lượng, hình ảnh, dịch vụ, chi tiết kỹ thuật</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quy mô </a>đơn hàng: Lớn, trung bình, nhỏ</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3049', 'Phát triển thị trường trong khách hàng hiện hữu.', null, null, '5', '0', '526', '467', '1552360508', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3050', null, null, null, '5', '3049', '526', '467', '1552360508', null, null, null, '<ul>\r\n<li>Quy trình 4 bước: \r\n<ul>\r\n<li> xếp hạng các khách hàng tốt nhất của dn </li>\r\n<li>Tối đa hóa doanh thu từ nỗ lực hợp tác </li>\r\n<li>Phát triển các sản phẩm dịch vụ đặc thù cho từng nhóm khách hàng </li>\r\n<li>Tìm kiếm các nhu cầu đặc biệt từ khách hàng của doanh nghiệp </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3051', 'Tìm kiếm thêm cơ hội với các khách hàng hiện hữu.', null, null, '5', '0', '526', '467', '1552360537', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3052', null, null, null, '5', '3051', '526', '467', '1552360537', null, null, null, '<ul>\r\n<li>Phương thức tiếp cận: \r\n<ul>\r\n<li>Hình thành chương trình phản hồi chính thức tại điểm tiếp xúc khách hàng </li>\r\n<li>Phát triển cơ dữ liệu khách hàng </li>\r\n<li>Diễn giải các dữ liệu được mã hóa trong báo cáo phát triển các trương trình hỗ trợ khách hàng </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3053', 'Tìm kiếm khách hàng mới.', null, null, '5', '0', '526', '467', '1552360570', '1552360616', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3054', null, null, null, '5', '3053', '526', '467', '1552360570', '1552360616', null, null, '<ul>\r\n<li>Tính toán giá trị suốt đời của khách hàng hiện tại tại của doanh nghiệp để làm thước đo căn cứ cho kế hoạch </li>\r\n</ul>\r\n<ul>\r\n<li>Tìm kiếm các khách hàng ở phân đoạn mới- những người tương tự như khách hàng hiện hữu của doanh nghiệp </li>\r\n</ul>\r\n<ul>\r\n<li>Mở rộng sang tìm kiếm các khách hàng địa lý mới </li>\r\n<li>Chuyển đổi/ hiện hữu Sang phục vụ cho/ phù hợp với một lĩnh vực khác/ ngành khác </li>\r\n</ul>\r\n<ul>\r\n<li>Tìm hiểu nhu cầu khách hàng mới thông qua: nhóm tập trung, khảo sát </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3055', 'Phát triển cơ hội mới thông qua marketing.', null, null, '5', '0', '526', '467', '1552360646', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3056', null, null, null, '5', '3055', '526', '467', '1552360646', null, null, null, '<p>Quản trị khách hàng và tìm kiếm khách hàng. </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3057', 'CÁC THÀNH TỐ CẤU THÀNH CHIẾN LƯỢC MARKETING: 1. Xác định thị trường- sản phẩm', null, null, '5', '0', '527', '467', '1552360728', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3058', null, null, null, '5', '3057', '527', '467', '1552360728', null, null, null, '<p>1. Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường</a>- <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm </a></p>\r\n<ul>\r\n<li>Doanh nghiệp phục vụ cho những thị trường nào với những <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm</a> nào? \r\n<ul>\r\n<li>Không có doanh nghiệp nào có đủ khả năng phục vụ cho toàn bộ thị trường,khách hàng tiềm năng (xem xét khả năng phục vụ của nhóm khách hàng khác nhau, trong mối liên hệ với SCT tại các phân đoạn thị trường đó) </li>\r\n<li> Chiều rộng của tuyến sản phẩm được thúc đẩy bởi sự đa dạng của sở thíchnhưng bị hạn chế bới các khả năng sản xuất của doanh nghiệp và sự vậnhành của nền kinh tế, sự phức tạp trong quản lý mkt, hạn chế của nguồn lựctài chính. \r\n<ul>\r\n<li>Tập trung và định hướng hoạt động của doanh nghiêp trên thị trường. </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li>Mô hình I.Ansoff \r\n<ul>\r\n<li>Thâm nhập thị trường: Mang sản phẩm hiện tại tấn công sâu hơn vào thị trường hiện tại . Bản chất là gia tăng doanh số tại thị trường hiện tại. \r\n<ul>\r\n<li>Làm cho khách hàng trên thị trường hiện tại mua sản phẩm nhiều hơn </li>\r\n<li>Trên phân khúc có khách hàng chưa mua sản phẩm⇒ mua sản phẩm </li>\r\n<li>Lấy khách hàng của đối thủ cạnh tranh </li>\r\n<li>Tối đa hóa doanh số trên thị trường hiện tại </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>-<a class=\"cloze\" href=\"#mce_temp_url#\"> Phát triển thị trường</a>: mang sản phẩm hiện tại sang kinh doanh tại thị trường </p>\r\n<p>mới==&gt;gia tăng doanh số trên đoạn thị trường mới (mới về mặt địa lý, về tập khách </p>\r\n<p>hàng, tập nhu cầu..) </p>\r\n<p>- Phát triển thị trường: mang sản phẩm mới sang thị trường mới. </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3059', 'CÁC THÀNH TỐ CẤU THÀNH CHIẾN LƯỢC MARKETING: 2. Xác định nguồn lực và phân bố nguồn lực', null, null, '5', '0', '527', '467', '1552360761', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3060', null, null, null, '5', '3059', '527', '467', '1552360761', null, null, null, '<ul>\r\n<li>Nhận dạng các hoạt động kinh doanh chủ chốt tạo nên sự thành công của doanh nghiệp- Đơn vị kinh doanh chiến lược (SBU) \r\n<ul>\r\n<li>SBU là một cặp thị trường-sản phẩm là sản phẩm kinh doanh chủ chốt của doanh nghiệp, doanh nghiệp có chiến lược mkt riêng cho nó </li>\r\n</ul>\r\n</li>\r\n<li>Doanh nghiệp sử dụng kĩ thuật phân tishc BCG hoặc GE để phân tích mức độ hấp dẫn của SBUs- quyết định mỗi SBU đóng góp như thế nào vào tổng thể hoạt động của doanh nghiệp và doanh nghiệp cần đầu tư ngân quỹ cho mỗi mức SBU bao nhiêu, theo cơ cấu nào? </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3061', 'CÁC THÀNH TỐ CẤU THÀNH CHIẾN LƯỢC MARKETING: 3. Xác lập mục tiêu và kế hoạch', null, null, '5', '0', '527', '467', '1552360798', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3062', null, null, null, '5', '3061', '527', '467', '1552360798', null, null, null, '<ul>\r\n<li>Xác định mục tiêu: Là <a class=\"cloze\" href=\"#mce_temp_url#\">trạng thái cuối</a> cùng của sứ mệnh kinh doanh của tổ chức, là những gì mà tổ chức tìm kiếm trong hoạt động kinh doanh dài hơn và diễn tiếp của mình. </li>\r\n<li>Yêu cầu: \r\n<ul>\r\n<li>Có thể đạt được qua hoạt động cụ thể </li>\r\n<li>Cung cấp sự định hướng </li>\r\n<li>Dễ dàng cho công việc quản lý-thể hiện qua các tiêu chuẩn đánh giá hoạt động của toàn tổ chức </li>\r\n<li>Có thể thiết lập thứ tự ưu tiên dài hạn cho tổ chức </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3063', 'PHÁT TRIỂN CHIẾN LƯỢC MARKETING: 1. Phân tích bối cảnh (Phân tích SWOT)', null, null, '5', '0', '527', '467', '1552360835', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3064', null, null, null, '5', '3063', '527', '467', '1552360835', null, null, null, '<ul>\r\n<li>Marketers<a class=\"cloze\" href=\"#mce_temp_url#\"> phân tích </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">xác lập vị trí</a> của tổ chức/doanh nghiệp và đặc biệt là marketing điều khiển quá, hiện tại, tương lai </li>\r\n</ul>\r\n<ul>\r\n<li>Phân tích SWOT </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3065', 'PHÁT TRIỂN CHIẾN LƯỢC MARKETING: 2. Lựa chọn thị trường mục tiêu', null, null, '5', '0', '527', '467', '1552360859', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3066', null, null, null, '5', '3065', '527', '467', '1552360859', null, null, null, '<ul>\r\n<li> Thị trường-sản phẩm được phân đoạn như thế nào </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3067', 'PHÁT TRIỂN CHIẾN LƯỢC MARKETING: \r\n3. Xác định mục tiêu marketing \r\n4. Lựa chọn marketing mix \r\n5. Thực thi và điều khiển', null, null, '5', '0', '527', '467', '1552360954', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3068', null, null, null, '5', '3067', '527', '467', '1552360954', null, null, null, '<p style=\"display: inline !important;\">3. Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu marketing </a></p>\r\n<ul>\r\n<li>Mục tiêu marketing <a class=\"cloze\" href=\"#mce_temp_url#\">định hướng</a> từ các <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> của <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a>. </li>\r\n<li>Trường hợp tổ chức định hướng marketing hoàn toàn thì hai nhóm mục tiêu này trùng nhau </li>\r\n</ul>\r\n<ul>\r\n<li>Các mục tiêu marketing phổ biến: \r\n<ul>\r\n<li>Lợi nhuận: Phần trăm trên doanh số hoặc một lượng tuyệt đối tổ chức/doanhnghiệp dự định sẽ đạt được sau một thời gian thực hiện chiến lược marketing </li>\r\n<li>Lượng bán: Thị phần của tổ chức dự định đạt được hoặc lượng bán tuyệt đối </li>\r\n<li>Số lượng trung gian có thể tham gia tiêu thụ sản phẩm </li>\r\n<li>Nhận biết khách hàng và uy tin hính ảnh của sản phẩm và tổ chức trên thị trường </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>4. Lựa chọn marketing mix </p>\r\n<p>5. Thực thi và điều khiển </p>\r\n<p> </p>\r\n<p> </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3071', 'Trình bày đặc điểm và nội dung marketing sản phẩm công nghệ cao.', null, null, '5', '0', '535', '467', '1552361308', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3072', null, null, null, '5', '3071', '535', '467', '1552361308', null, null, null, '<ul>\r\n<li>Các sản phẩm công nghệ cao bao gồm các lĩnh vực <a class=\"cloze\" href=\"#mce_temp_url#\">công nghiệp</a> như viễn thông, máy tính, phần mềm, công nghệ sinh học và các thiết bị điện tử \r\n<ul>\r\n<li>Các sản phẩm này có<a class=\"cloze\" href=\"#mce_temp_url#\"> chu kỳ sống ngắn</a> </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Giá cao </a></li>\r\n</ul>\r\n</li>\r\n<li>Hai đặc điểm chính thể phân biệt rõ hoạt động marketing cho Hi-tech \r\n<ul>\r\n<li>Hi-tech <a class=\"cloze\" href=\"#mce_temp_url#\">không chắc chắn</a> </li>\r\n<li>Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">luôn thay đổi  </a></li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3073', 'Phân tích nội dung liên quan đến dịch vụ hỗ trợ sản phẩm.', null, null, '5', '0', '534', '467', '1552361357', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3074', null, null, null, '5', '3073', '534', '467', '1552361357', null, null, null, '<ul>\r\n<li>Dịch vụ hỗ trợ sản phẩm là<a class=\"cloze\" href=\"#mce_temp_url#\"> một tập hỗn hợp</a> có đương lượng quan trọng của các hình thức <a class=\"cloze\" href=\"#mce_temp_url#\">dịch vụ thương mại</a> trong mối quan hệ với toàn bộ quá trình tiếp thị- bán và tiêu dùng sản phẩm xác định mà doanh nghiệp định cung ứng cho khách hàng trọng điểm </li>\r\n<li>Các câu hỏi cần trả lời khi quyết định dịch vụ hỗ trợ sản phẩm \r\n<ul>\r\n<li>Làm dịch vụ gì? nhiều lượng dịch vụ? ( nội dung, tần suất) </li>\r\n<li>Ai làm? </li>\r\n<li>Nhiều dịch vụ =&gt; giá tăng =&gt; độ thỏa mãn khách hàng giảm \r\n<ul>\r\n<li>o Doanh nghiệp cần phải cân nhắc kỹ </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li>Tăng độ thỏa mãn khách hàng gồm: \r\n<ul>\r\n<li>Hướng dẫn sử dụng </li>\r\n<li>Chương trình bảo hành </li>\r\n<li>Hỗ trợ kỹ thuật </li>\r\n<li>Đào tạo kỹ thuật cho nhân viên của khách hàng </li>\r\n<li>Cho mượn máy móc trong khi các sản phẩm đang sửa chữa </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3075', 'Giai đoạn phát triển sản phẩm B2B mới: \r\n1. Hình thành ý tưởng', null, null, '5', '0', '533', '467', '1552361438', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3076', null, null, null, '5', '3075', '533', '467', '1552361438', null, null, null, '<p>1. Hình thành ý tưởng </p>\r\n<ul>\r\n<li>Là giai đoạn <a class=\"cloze\" href=\"#mce_temp_url#\">ý tưởng</a> được <a class=\"cloze\" href=\"#mce_temp_url#\">hình thành</a> </li>\r\n<li>Ý tưởng mới có thể xuất phát từ các kĩ sư, các giám đốc sản xuất, nhà cung cấp…. </li>\r\n<li> Các bước khuyến khích ý tưởng: \r\n<ul>\r\n<li> Thành lập bộ phận để trao đổi,thu thập các ý tưởng </li>\r\n<li>Tổ chức họp các phòng ban xem xét ý tưởng </li>\r\n<li>Hồi âm chi người đóng góp bằng văn bản, điện thoại, gặp gỡ… </li>\r\n<li> Ghi nhận sự đóng góp dù không thành công, khen thưởng những người có ýtưởng thành công </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3079', 'Giai đoạn phát triển sản phẩm B2B mới: 2. Sàng lọc và điều tra sơ bộ.', null, null, '5', '0', '533', '467', '1552361503', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3080', null, null, null, '5', '3079', '533', '467', '1552361503', null, null, null, '<ul>\r\n<li>Sàng lọc ý tưởng: Giai đoạn này các ý tưởng mới sẽ được <a class=\"cloze\" href=\"#mce_temp_url#\">xem xét</a> căn cứ trên <a class=\"cloze\" href=\"#mce_temp_url#\">sự phù hợp</a> với<a class=\"cloze\" href=\"#mce_temp_url#\"> mục tiêu</a>, chào hàng hiện tại và các nhân tố khác Xem xét tính xác thực và cân nhắc có đáng để phát triển sản phẩm hay không? \r\n<ul>\r\n<li>Tiềm năng thị trường 30% </li>\r\n<li>mức độ thống nhất với mục tiêu của tổ chức 20% </li>\r\n<li>hả năng phục vụ cho nhu cầu của khách hàng 15% </li>\r\n<li>Lợi thế cạnh tranh có thể thu được từ sản phẩm 10% </li>\r\n<li> Khả năng sản xuất và chế tạo sản phẩm 15% </li>\r\n<li>Khả năng bắt chước của đối thủ cạnh tranh 10% </li>\r\n</ul>\r\n</li>\r\n<li>Điều tra sơ bộ: giai đoạn này doanh nghiệp bắt đầu đi điều tra thông tin về phân đoạn thị trường mục tiêu, hành vi khách hàng ảnh trên phân đoạn thị trường ,quy mô và tiềm năng của thị trường, đối thủ cạnh tranh và phản ứng của đối thủ cạnh tranh trên thị trường như thế nào </li>\r\n</ul>\r\n<ul>\r\n<li>Những thông tin này sẽ giúp ích cho doanh nghiệp trong giai đoạn tiếp theo hình thành sản phẩm: \r\n<ul>\r\n<li>Liệu sản phẩm mới có phù hợp với mục tiêu chất lượng không </li>\r\n<li>Doanh nghiệp có đủ nguồn lực khônhg </li>\r\n<li>Nó có thực sự hữu ích với khách hàng </li>\r\n<li>Khả năng tăng trưởng, thị phần và đối thủ cạnh tranh </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3099', 'Giai đoạn phát triển sản phẩm B2B mới: 3. mô tả quy cách sản phẩm', null, null, '5', '0', '533', '467', '1552361565', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3100', null, null, null, '5', '3099', '533', '467', '1552361565', null, null, null, '<p>3. mô tả quy cách sản phẩm </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xác định </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">chi tiết về sản phẩm</a> sẽ được phát triển: tính năng và đặc điểm cần có </li>\r\n<li>Các <a class=\"cloze\" href=\"#mce_temp_url#\">quy cách</a>: độ cao. chiều rộng. độ sâu, trọng lượng, màu sắc, độ sai số, vận hành, đóng gói, yêu cầu pháp lý </li>\r\n</ul>\r\n<ul>\r\n<li>Cần hướng tới quy cách sản phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">theo yêu cầu </a>của khách hàng và định nghĩa rõ về sản phẩm để bộ phận khác hiểu </li>\r\n<li>Cần sự tham gia của nhà cung cấp để thực hiện hóa và giúp tiết kiệm chi phí </li>\r\n<li>Sử dụng hệ thống để tạo ra chuỗi giá trị thông qua sản phẩm mới và các doanh nghiệp gia tăng trên nền tảng sự phối hợp của nhà sản xuất và khách hàng, cung ứng </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3101', 'Giai đoạn phát triển sản phẩm B2B mới: 4. phát triển sản phẩm', null, null, '5', '0', '533', '467', '1552361655', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3102', null, null, null, '5', '3101', '533', '467', '1552361655', null, null, null, '<p style=\"display: inline !important;\">4. phát triển sản phẩm </p>\r\n<p style=\"display: inline !important;\"> </p>\r\n<ul>\r\n<li>Các đặc điểm sản phẩm sẽ được đưa vào các<a class=\"cloze\" href=\"#mce_temp_url#\"> thiết kế</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> sản xuất</a> tạo ra nguyên mẫu để có thể thử nghiệm trong phòng thí nghiệm </li>\r\n<li>Côngty có thể chia sản phẩm thành nhiều module bộ phận khác nhau và sản xuất đồng thời nhằm tiết kiệm chi phí </li>\r\n<li>Cần có sự tham gia của bộ phận <a class=\"cloze\" href=\"#mce_temp_url#\">sản xuất</a> <a class=\"cloze\" href=\"#mce_temp_url#\">nhà cung ứng</a> và t<a class=\"cloze\" href=\"#mce_temp_url#\">rung tâm mua</a> để có thể ngắm chất sản phẩm sản xuất được theo đúng thiết kế và tiết kiệm chi phí nhờ vào kiến thức chuyên sâu của nhà cung cấp </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3103', 'Giai đoạn phát triển sản phẩm B2B mới: 5. thử nghiệm', null, null, '5', '0', '533', '467', '1552361706', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3104', null, null, null, '5', '3103', '533', '467', '1552361706', null, null, null, '<p>5. thử nghiệm </p>\r\n<ul>\r\n<li>Là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">thử nghiệm</a> thị trường: là quá trình chủ nhiệm sản phẩm thông qua việc để cho khách hàng sử dụng nó trong điều kiện thực tế </li>\r\n<li>Thường áp dụng trong<a class=\"cloze\" href=\"#mce_temp_url#\"> lĩnh vực công nghệ </a></li>\r\n<li>Đưa ra quyết định có thử nghiệm beta testing không? thời gian thử nghiệm là bao lâu?( không phải sản phẩm nào cũng cần trải qua giai đoạn beta testing) </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3105', 'Giai đoạn phát triển sản phẩm B2B mới: 6. tung sản phẩm âm', null, null, '5', '0', '533', '467', '1552361754', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3106', null, null, null, '5', '3105', '533', '467', '1552361754', null, null, null, '<p>6. tung sản phẩm âm </p>\r\n<ul>\r\n<li>Sản phẩm được <a class=\"cloze\" href=\"#mce_temp_url#\">chính thức đưa ra</a> thị trường </li>\r\n<li>Doanh nghiệp phải phát triển một <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> giới thiệu sản phẩm </li>\r\n<li>Doanh nghiệp tập phải phát triển một chiến lược giới thiệu cho sản phẩm dựa trên nền tảng những sự thay đổi mới của sản phẩm, dịch vụ phụ gia tăng và các nhân tố khác khác ( như thế nào; lúc nào; ở đâu..) </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3107', 'Giai đoạn phát triển sản phẩm B2B mới: 7. đánh giá', null, null, '5', '0', '533', '467', '1552361803', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3108', null, null, null, '5', '3107', '533', '467', '1552361803', null, null, null, '<p>7. đánh giá </p>\r\n<ul>\r\n<li>Sản phẩm được<a class=\"cloze\" href=\"#mce_temp_url#\"> đánh giá</a> và các yếu tố bố của chương trình marketing được thích ứng nhằm phù hợp với sự thay đổi điều kiện của môi trường. </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3109', 'Giai đoạn giới thiệu.', null, null, '5', '0', '532', '467', '1552361879', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3110', null, null, null, '5', '3109', '532', '467', '1552361879', null, null, null, '<p>-         Đặc trưng: </p>\r\n<p>Thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">thẩm định</a> sản phẩm</p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Chi phí</a> marketing<a class=\"cloze\" href=\"#mce_temp_url#\"> cao</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">doanh số thấp</a>=&gt;<a class=\"cloze\" href=\"#mce_temp_url#\"> lợi nhuận thấp</a></p>\r\n<p>Người tiêu dùng chưa biết đến sản phẩm</p>\r\n<p>Khách hàng: những khách hàng canh Tân,các doanh nghiệp b2b đứng đầu muốn tạo lợi thế cạnh tranh</p>\r\n<p>-      Kỹ thuật marketing:  </p>\r\n<p>Chiến lược marketing tổng thể tạo ra nhận thức và chấp nhận của khách hàng</p>\r\n<p>Chiến lược sản phẩm: phát triển và kiểm nghiệm mẫu sản phẩm trong nhà máy</p>\r\n<p>Thiết kế sản phẩm: giới hạn số lượng mẫu mã để thu hút sự chấp nhận của thị trường</p>\r\n<p> </p>\r\n<p>Chiến lược truyền thông: tạo ra nhận thức và nền tảng cho các chiến dịch bán hàng</p>\r\n<p> </p>\r\n<p>Chiến lược phân phối: giới thiệu sản phẩm đến các khách hàng và thị trường mới</p>\r\n<p> </p>\r\n<p>Chiến lược giá: điều chỉnh giá sản phẩm cho phù hợp nhu cầu của thị trường</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3111', 'Giai đoạn tăng trưởng.', null, null, '5', '0', '532', '467', '1552361937', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3112', null, null, null, '5', '3111', '532', '467', '1552361937', null, null, null, '<p>-         Đặc trưng:</p>\r\n<p> </p>\r\n<p>Tốc độ tăng<a class=\"cloze\" href=\"#mce_temp_url#\"> doanh số nhanh</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phạm vi tiếp cận</a> thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">rộng</a>,<a class=\"cloze\" href=\"#mce_temp_url#\"> lợi nhuận tăng</a></p>\r\n<p> </p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Xuất hiện đối thủ </a>cạnh tranh</p>\r\n<p> </p>\r\n<p>Khách hàng: những người thích dùng sản phẩm mới, không quá nhạy cảm về giá</p>\r\n<p> </p>\r\n<p>-         Kỹ thuật marketing:</p>\r\n<p> </p>\r\n<p>Chiến lược marketing tổng thể: thiết lập thị phần</p>\r\n<p> </p>\r\n<p>Chiến lược sản phẩm: thay thế những sản phẩm cũ để sản xuất sản phẩm mới (trọng tâm)</p>\r\n<p> </p>\r\n<p>Thiết kế sản phẩm: khác biệt hóa sản phẩm để đáp ứng yêu cầu của thị trường</p>\r\n<p> </p>\r\n<p>Chiến lược truyền thông: tạo ra sự chuộng nơi khách hàng và kênh trung gian</p>\r\n<p>         </p>\r\n<p>Chiến lược phân phối: kiểm soát kênh nhằm tạo ra hiệu ứng thị trường tối ưu</p>\r\n<p> </p>\r\n<p>Chiến lược giá: điều trị giá khi có đối thủ cạnh tranh</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3113', 'Giai đoạn bão hòa.', null, null, '5', '0', '532', '467', '1552361974', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3114', null, null, null, '5', '3113', '532', '467', '1552361974', null, null, null, '<p>-         Đặc trưng:</p>\r\n<p> </p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Thời cơ tiêu thụ</a></p>\r\n<p> </p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Doanh số cực đại</a></p>\r\n<p> </p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Cạnh tranh gay gắt</a></p>\r\n<p> </p>\r\n<p>Sự co giãn của cầu khá lớn</p>\r\n<p> </p>\r\n<p>Khách hàng: những khách hàng đến sớm</p>\r\n<p> </p>\r\n<p>-         Kỹ thuật marketing:</p>\r\n<p> </p>\r\n<p>Chiến lược marketing tổng thể: duy trì thị phần trong điều kiện cạnh tranh</p>\r\n<p> </p>\r\n<p>Chiến lược sản phẩm: giới thiệu sản phẩm mới trên thị trường</p>\r\n<p> </p>\r\n<p>Thiết kế sản phẩm: duy trì lợi thế cạnh tranh thông qua chiến lược tạo sự khác biệt</p>\r\n<p> </p>\r\n<p>Chiến lược truyền thông: Duy trì sự yêu thích nơi khách hàng và kênh trung gian</p>\r\n<p> </p>\r\n<p>Chiến lược phân phối: duy trì kiểm soát kênh</p>\r\n<p> </p>\r\n<p>Chiến lược giá: ổn định và tránh thay đổi giá</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3115', 'Giai đoạn suy thoái.', null, null, '5', '0', '532', '467', '1552362001', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3116', null, null, null, '5', '3115', '532', '467', '1552362001', null, null, null, '<p>-         Đặc trưng:</p>\r\n<p> </p>\r\n<p>          <a class=\"cloze\" href=\"#mce_temp_url#\">Doanh số giảm</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">giá giảm</a></p>\r\n<p> </p>\r\n<p>Yêu cầu của người mua khắt khe hơn</p>\r\n<p> </p>\r\n<p>Khách hàng: những khách hàng lạc hậu</p>\r\n<p> </p>\r\n<p>-         Kỹ thuật marketing</p>\r\n<p> </p>\r\n<p>Chiến lược marketing tổng thể: quản lý chi phí và duy trì lợi nhuận</p>\r\n<p> </p>\r\n<p>Chiến lược sản phẩm: lại bỏ sản phẩm cũ ra khỏi dòng họ sản phẩm</p>\r\n<p> </p>\r\n<p>Thiết kế sản phẩm: chỉ cung cấp các mẫu cơ bản để duy trì khách hàng</p>\r\n<p> </p>\r\n<p>Chiến lược phân phối: giảm chi phí phân phối nhưng vẫn giữ khách hàng</p>\r\n<p> </p>\r\n<p>Chiến lược truyền thông: tối thiểu hóa chi phí để đối phó sự giảm sút của cầu</p>\r\n<p> </p>\r\n<p>Chiến lược giá: giảm lượng hàng tồn kho</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3117', 'Khái niệm sản phẩm B2B?', null, null, '5', '0', '531', '467', '1552362121', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3118', null, null, null, '5', '3117', '531', '467', '1552362121', null, null, null, '<p>Khái niệm: sản phẩm b2b là <a class=\"cloze\" href=\"#mce_temp_url#\">tổng hợp</a> các <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">giải pháp </a>thỏa mãn nhu cầu và mong muốn của khách hàng tổ chức về sản xuất, kinh doanh và hoạt động của tổ chức</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3119', 'Cấu trúc của sản phẩm b2b.', null, null, '5', '0', '531', '467', '1552362235', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3120', null, null, null, '5', '3119', '531', '467', '1552362235', null, null, null, '<p>- Sản phẩm cốt lõi: là <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích cốt lõi</a> mà khách hàng nhận được khi sử dụng sản phẩm</p>\r\n<p> </p>\r\n<p>- Sản phẩm hiện hữu: là phần <a class=\"cloze\" href=\"#mce_temp_url#\">tạo ra sự khác biệt</a> bao gồm bổ sung các đặc điểm vật lý nhằm tăng cường thêm tính năng sản phẩm như tính năng thiết kế, chất lượng (doanh nghiệp thường ít tập trung)</p>\r\n<p> </p>\r\n<p>- Sản phẩm gia tăng: bao gồm các<a class=\"cloze\" href=\"#mce_temp_url#\"> lợi ích vô hình</a> như hỗ trợ kỹ thuật phụ tùng sẵn có, dịch vụ bảo trì và sửa chữa bảo hành, đào tạo, cung cấp kịp thời và điều hành thanh toán thương mại hấp dẫn (các doanh nghiệp tập trung chủ yếu)</p>\r\n<p> </p>\r\n<p>-&gt;Kết luận:</p>\r\n<p> </p>\r\n<p>- Sản phẩm b2b ở đây bao gồm tất cả các<a class=\"cloze\" href=\"#mce_temp_url#\"> lợi ích</a>, các <a class=\"cloze\" href=\"#mce_temp_url#\">giải pháp gia tăng giá trị</a> cho sản phẩm nhằm thỏa mãn nhu cầu và mong muốn của khách hàng tổ chức</p>\r\n<p> </p>\r\n<p>- sản phẩm được xác định không chỉ là một thực thể vật lý mà còn là một tập hợp các mối quan hệ kinh tế, kỹ thuật pháp lý và cá nhân giữa người mua và người bán</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3121', 'Các dạng sản phẩm b2b: Sản phẩm hàng hóa đầu vào', null, null, '5', '0', '531', '467', '1552362374', '1552362489', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3122', null, null, null, '5', '3121', '531', '467', '1552362374', '1552362489', null, null, '<p>- Nguyên liệu thô: bao gồm tất cả các sản phẩm tạo ra bởi nền công nghiệp và bán những sản phẩm cho khách hàng rất ít hoặc không lựa chọn nhằm sản xuất ra các sản phẩm khác. Có 2 lớp chính là nông sản và sản phẩm tự nhiên</p>\r\n<p> </p>\r\n<p>- Nông sản: bông vải trái cây, rau cỏ - khối lượng lớn, đơn giá thấp, phải vận chuyển từ nhà sản xuất đến người sử dụng. Ít khi người sản xuất bán trực tiếp [Ký hiệu] tăng vai trò cung ứng trung gian</p>\r\n<p> </p>\r\n<p>- Sản phẩm mang tính thời vụ và dễ hỏng, tính đồng nhất cao, không cần khởi tạo nhu cầu </p>\r\n<p> </p>\r\n<p>- Hoạt động quảng cáo và khuyến mãi thường ít, thời gian trồng dài ngày. Một số nhà sản xuất gắn nhãn hiệu cho sản phẩm của mình như bưởi, cam,...</p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3123', 'Các dạng sản phẩm b2b: Sản phẩm tự nhiên:', null, null, '5', '0', '531', '467', '1552362579', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3124', null, null, null, '5', '3123', '531', '467', '1552362579', null, null, null, '<p>Sản phẩm tự nhiên: than, dầu khô, quặng</p>\r\n<p> </p>\r\n<p>Nguồn <a class=\"cloze\" href=\"#mce_temp_url#\">cung cấp hạn chế</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">khối lượng lớn</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đơn giá thấp</a>, đòi hỏi vận chuyển từ nhà sản xuất đến người tiêu dùng</p>\r\n<p> </p>\r\n<p>Việc mua bán sản phẩm thường thông qua <a class=\"cloze\" href=\"#mce_temp_url#\">hợp đồng dài hạn</a></p>\r\n<p> </p>\r\n<p>Tính đồng nhất sản phẩm cao, không đòi hỏi hoạt động khởi tạo nhu cầu</p>\r\n<p> </p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">giá cả</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">độ tin cậy</a> trong giao hàng ảnh hưởng chủ yếu đến lựa chọn nguồn cung ứng</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3125', 'Các dạng sản phẩm b2b: Các nguyên liệu đã chế biến', null, null, '5', '0', '531', '467', '1552362634', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3126', null, null, null, '5', '3125', '531', '467', '1552362634', null, null, null, '<p>Các nguyên liệu đã chế biến: dầu, thép, hoá chất - được ra công tiêu chuẩn hóa [Ký hiệu] <a class=\"cloze\" href=\"#mce_temp_url#\">giá cả</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ tin cậy</a> của cung ứng là yếu tố quan trọng đối với việc mua.</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3127', 'Các dạng sản phẩm b2b: Các bộ phận ( ống bô xe máy,....)/cấu kiện (vòng bi lốp xe pin,...)', null, null, '5', '0', '531', '467', '1552362659', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3128', null, null, null, '5', '3127', '531', '467', '1552362659', null, null, null, '<p>Các bộ phận ( ống bô xe máy,....)/cấu kiện (vòng bi lốp xe pin,...)</p>\r\n<p> </p>\r\n<p>Là những bộ phận cấu thành sản phẩm hoàn chỉnh, bao gồm những sản phẩm mua, kể cả sản phẩm cuối cùng của khách hàng tổ chức</p>\r\n<p> </p>\r\n<p>Bán trực tiếp cho người sử dụng theo đơn hàng [Ký hiệu] giá cả và dịch vụ là vấn đề trọng yếu, thương hiệu và quảng cáo có xu hướng ít quan trọng</p>\r\n<p> </p>\r\n<p>Kết luận:</p>\r\n<p> </p>\r\n<p>Các sản phẩm này thường được tiêu chuẩn hóa</p>\r\n<p> </p>\r\n<p>Giá cả và dịch vụ là những yếu tố quan trọng, nhãn hiệu và quảng cáo ít quan trọng</p>\r\n<p> </p>\r\n<p>Các sản phẩm này thường tham gia trực tiếp vào lắp ráp, không thay đổi hình dạng, nên được bán trực tiếp theo đơn đặt hàng trước 15 hoặc sớm hơn</p>\r\n<p> </p>\r\n<p>Các sản phẩm nền tạo phục vụ cho điều kiện sản xuất</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3129', 'Các dạng sản phẩm b2b: Trang thiết bị', null, null, '5', '0', '531', '467', '1552362693', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3130', null, null, null, '5', '3129', '531', '467', '1552362693', null, null, null, '<p>Trang thiết bị: </p>\r\n<p> </p>\r\n<p>bao gồm những sản phẩm sử dụng hoặc tiêu dùng bởi doanh nghiệp trong hoạt động như những công cụ và dụng cụ bảo quản, sửa chữa, điều hành (máy tính, bàn làm việc,...)</p>\r\n<p> </p>\r\n<p>Mua dễ dàng, tuổi thọ ngắn, tái đặt mua</p>\r\n<p> </p>\r\n<p>Chủ yếu sử dụng trung gian trong tiêu thụ</p>\r\n<p> </p>\r\n<p>Chất lượng, tính năng, giá cả dịch vụ là yếu tố quan trọng. Lực lượng bán quan trọng hơn quảng cáo</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3131', 'Các dạng sản phẩm b2b: Thiết bị cơ bản', null, null, '5', '0', '531', '467', '1552362718', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3132', null, null, null, '5', '3131', '531', '467', '1552362718', null, null, null, '<p>Thiết bị cơ bản</p>\r\n<p> </p>\r\n<p>Bao gồm nhà xưởng, văn phòng</p>\r\n<p> </p>\r\n<p>Thường có tuổi thọ lớn [Ký hiệu] quảng cáo sử dụng ít, chủ yếu là bán hàng trực tiếp</p>\r\n<p> </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3133', 'Các dạng sản phẩm b2b: Dịch vụ', null, null, '5', '0', '531', '467', '1552362766', '1552362785', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3134', null, null, null, '5', '3133', '531', '467', '1552362766', '1552362785', null, null, '<p> </p>\r\n<p>Dịch vụ hỗ trợ</p>\r\n<p>Sự cung cấp:</p>\r\n<p> </p>\r\n<p>Mua sắm một cách dễ dàng bằng tái đặt hàng, bán hàng qua trung gian (khách hàng đông và phân tán địa Lý), giá trị thấp (bút chì,...) [Ký hiệu] Giá cả và dịch vụ là vấn đề trọng yếu</p>\r\n<p> </p>\r\n<p>Dịch vụ kinh doanh</p>\r\n<p> </p>\r\n<p>Bao gồm dịch vụ như bảo vệ an ninh, dịch vụ kế toán kiểm toán, dịch vụ sửa chữa, dịch vụ tư vấn</p>\r\n<p> </p>\r\n<p>Dịch vụ bảo trì và sửa chữa: thực hiện theo hợp đồng</p>\r\n<p> </p>\r\n<p>Dịch vụ tư vấn: lựa chọn nhà cung cấp trên cơ sở uy tín và con người họlựa chọn nhà cung cấp trên cơ sở uy tín và con người họ</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3140', null, null, null, '5', '3139', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {{x^2} + 2{\\rm{x}}y - {y^2}} \\right)d{\\rm{x}} + \\left( {{y^2} + 2{\\rm{x}}y - {x^2}} \\right)dy = 0\\)</span>   là<span class=\"math-tex\">\\(\\ln \\left[ {\\left( {\\square} \\right)\\left( {{x^2} + 4{\\rm{x}}y + {y^2}} \\right)} \\right] = C\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x-y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3142', null, null, null, '5', '3141', '359', '450', '1552396113', '1552396583', null, null, '<p>Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' = \\frac{{2{\\rm{x}}y}}{{{x^2} - {y^2}}}\\)</span> là y = C.\\left( {{x^{\\square}} + {y^{\\square}}} \\right)<a class=\"clozetip\" title=\"\" href=\"#\">2,2</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3144', null, null, null, '5', '3143', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' = \\frac{y}{x} + \\sin \\frac{y}{x}\\)</span>    là<span class=\"math-tex\">\\(\\[\\tan\\square = C{\\rm{x}}\\]\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">y/2x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3146', null, null, null, '5', '3145', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\\left( {x - y} \\right)d{\\rm{x}} - {x^2}y = 0\\)</span>  là<span class=\"math-tex\">\\(\\[y = \\frac{{\\squar\\}}{{\\ln \\left| {\\square} \\right| + C}}\\]\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">-x,x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3148', null, null, null, '5', '3147', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {{x^2} - 3{y^2}} \\right)d{\\rm{x}} + 2{\\rm{x}}y{\\rm{d}}y = 0\\)</span>  là<span class=\"math-tex\">\\(y = C\\square + \\square\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">x^3,x^2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3150', null, null, null, '5', '3149', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(xy\' + x\\tan \\frac{y}{x} - y = 0\\)</span>   là<span class=\"math-tex\">\\({\\sin ^2}\\square = \\frac{1}{{\\ln \\left| x \\right| - C}}\\)</span>    <a class=\"clozetip\" title=\"\" href=\"#\">y/x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3152', null, null, null, '5', '3151', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {{x^2}{y^2} - 1} \\right)dy + 2{\\rm{x}}{y^3}d{\\rm{x}} = 0\\)</span> là<span class=\"math-tex\">\\(Cy = 1 + {(\\square)^2}\\)</span>     <a class=\"clozetip\" title=\"đặt  y=1/z thế vào phương trình đã cho rồi đặt   z=ux\" href=\"#\">xy</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3154', null, null, null, '5', '3153', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {x - y + 4} \\right)dy + \\left( {x + y - 2} \\right)d{\\rm{x}} = 0\\)</span>  là<span class=\"math-tex\">\\({x^2} + 2{\\rm{x}}y - {y^2} - \\square +\\square+ C = 0\\)</span>      <a class=\"clozetip\" title=\"\" href=\"#\">4x,8y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3156', null, null, null, '5', '3155', '359', '450', '1552396113', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{{x^2} + {y^2}}}{{2{\\rm{x}}y}}\\)</span>    là<span class=\"math-tex\">\\({y^2} = {x^2} - C\\square\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
