INSERT INTO `question` VALUES ('3170', null, null, null, '5', '3169', '359', '450', '1552396989', '1552482737', null, null, '<p><img title=\"dz=udx+xdu\" src=\"/images/buttons/info.gif\" alt=\"\" align=\"baseline\" border=\"0\" /> Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {{x^2}{y^2} - 1} \\right)dy + 2{\\rm{x}}{y^3}d{\\rm{x}} = 0\\)</span> là<span class=\"math-tex\">\\(Cy = 1 + {(\\square)^2}\\)</span> <a class=\"clozetip\" title=\"đặt  y=1/z thế vào phương trình đã cho rồi đặt   z=ux\" href=\"#\">xy</a></p>', null, '51', null, null, null, null, null);
