INSERT INTO `question` VALUES ('3174', null, null, null, '5', '3173', '359', '450', '1552396989', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{{x^2} + {y^2}}}{{2{\\rm{x}}y}}\\)</span>    là<span class=\"math-tex\">\\({y^2} = {x^2} - C\\square\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
