INSERT INTO `question` VALUES ('3176', null, null, null, '5', '3175', '335', '450', '1552397618', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\\)</span> là<span class=\"math-tex\">\\(\\sqrt {{{(1 + {y^2})}^3}}  = \\square.{e^x} - \\square.{e^x} + C\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">3x,3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3177', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '335', '450', '1552397618', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3178', null, null, null, '5', '3177', '335', '450', '1552397618', null, '', null, ' nghiệm tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = 2 + 2y + x + xy\\)</span>    là<span class=\"math-tex\">\\(y =  - 1;y = C.{e^{\\frac{{{x^2}}}{2} + \\square}} - 1\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">2x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3179', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '335', '450', '1552397618', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3180', null, null, null, '5', '3179', '335', '450', '1552397618', null, '', null, 'Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = 1 + {y^2}\\)</span> là  <span class=\"math-tex\">\\(\\[{\\mathop{\\rm arctany}\\nolimits}  = \\square+ C\\]\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3181', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', null, null, '5', '0', '335', '450', '1552397618', '1552397682', null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3182', null, null, null, '5', '3181', '335', '450', '1552397618', '1552397682', null, null, '<p>Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' = {e^{x + y}} + {e^{x - y}}\\)</span> là <span class=\"math-tex\">\\(\\arctan{\\square^y} = {\\square^x} + C\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">e,e</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3183', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '335', '450', '1552397618', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3184', null, null, null, '5', '3183', '335', '450', '1552397618', null, '', null, ' nghiệm tổng quát của:<span class=\"math-tex\">\\(y\' = 4{{\\rm{x}}^3}y\\)</span>   là<span class=\"math-tex\">\\(y = 0;y = C.{e^{\\square}}\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">x^4</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3185', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '335', '450', '1552397618', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3186', null, null, null, '5', '3185', '335', '450', '1552397618', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\tan y{\\rm{dx}} - x\\ln {\\rm{xd}}y = 0\\)</span>  <span class=\"math-tex\">\\(\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\ln {\\rm{x}}) = C\\)</span>  là<span class=\"math-tex\">\\(\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\square) = C\\)</span>.  <a class=\"clozetip\" title=\"\" href=\"#\">lnx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3187', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '335', '450', '1552397618', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3188', null, null, null, '5', '3187', '335', '450', '1552397618', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{4 + {y^2}}}{{\\sqrt {{x^2} + 4{\\rm{x}} + 13} }} = \\frac{{3y + 2}}{{x + 1}}y\'\\)</span> là<span class=\"math-tex\">\\(\\square\\ln ({y^2} + 4) + \\arctan \\square = \\sqrt {{{(x + 2)}^2} + 9}  - \\ln \\left| {x + 2 + \\sqrt {{{(x + 2)}^2} + 9} } \\right| + C\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">3/2,y/2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3189', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '335', '450', '1552397618', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3190', null, null, null, '5', '3189', '335', '450', '1552397618', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{x{\\rm{dx}}}}{{{x^2} + 1}} + (y + 1)dy = 0\\)</span> là<span class=\"math-tex\">\\(\\frac{{{y^2}}}{{\\square}} + y = \\frac{{ - 1}}{{\\square}}\\ln ({x^2} + 1) + C\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">2,2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3191', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3192', null, null, null, '5', '3191', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\[xy\' - y = {x^2}\\]\\)</span> là<span class=\"math-tex\">\\(y = C{{\\rm{x}}^3} + \\square\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">1/3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3193', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3194', null, null, null, '5', '3193', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' - \\frac{y}{x} = {x^2}\\)</span>  là<span class=\"math-tex\">\\(y = \\frac{{{x^3}}}{{\\square}} + C{\\rm{x}}\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3195', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3196', null, null, null, '5', '3195', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' - y\\cot {\\rm{x}} = 2{\\rm{x}}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\\)</span> là<span class=\"math-tex\">\\(y = {x^2}\\square + C\\square\\)</span>  <a class=\"clozetip\" title=\"\" href=\"#\">sinx,sinx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3197', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3198', null, null, null, '5', '3197', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' - \\frac{y}{x} = x{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\\)</span> là<span class=\"math-tex\">\\(y =  - x\\square{\\rm{ + Cx}}\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">cosx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3199', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3200', null, null, null, '5', '3199', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' + 2y = 2.{e^x}\\)</span>  là<span class=\"math-tex\">\\(y = \\square{e^x}\\)</span>    <a class=\"clozetip\" title=\"\" href=\"#\">2/3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3201', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3202', null, null, null, '5', '3201', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(xy\' + y\\ln y = y\\)</span>   là<span class=\"math-tex\">\\(y = {e^{1 + \\frac{C}{{\\square}}}}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3203', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '360', '450', '1552397988', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3204', null, null, null, '5', '3203', '360', '450', '1552397988', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' + y\\tan x = c{\\rm{o}}{{\\rm{s}}^2}x\\)</span>    là<span class=\"math-tex\">\\(y = \\sin {\\rm{x\\square + }}C.\\square\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">cosx,cosx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3205', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3206', null, null, null, '5', '3205', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' + \\frac{y}{x} = x\\sqrt y\\)</span>  là<span class=\"math-tex\">\\(y = {\\left( {\\frac{{{x^2}}}{{\\square}} + \\frac{C}{{\\sqrt x }}} \\right)^2}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">5</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3207', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3208', null, null, null, '5', '3207', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' + \\frac{y}{x} = x{y^2}.\\left( {\\frac{{{e^x}}}{{{e^x} + 1}}} \\right)\\)</span>   là<span class=\"math-tex\">\\(y = \\frac{1}{{C - \\square\\ln (1 + {e^x})}}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3209', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3210', null, null, null, '5', '3209', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\({x^2}y\' = y\\left( {x + y} \\right)\\)</span>   là<span class=\"math-tex\">\\(y = \\frac{{\\square}}{{C - \\ln \\left| x \\right|}}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3211', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3212', null, null, null, '5', '3211', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y{\\rm{dx}} + 2{\\rm{xd}}y = \\frac{{2y\\sqrt x }}{{{\\rm{co}}{{\\rm{s}}^2}y}}dy;y(0) = \\pi \\)</span>   là<span class=\"math-tex\">\\(\\[x = {(y\\square - {\\left( {\\square} \\right)^2})^2}\\]\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">tany,tany</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3213', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3214', null, null, null, '5', '3213', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(xy{\\rm{d}}y = \\left( {{y^2} + x} \\right)d{\\rm{x}}\\)</span>    là<span class=\"math-tex\">\\({y^2} = C{{\\rm{x}}^2} - \\frac{{\\square}}{{\\square.x}}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">2,3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3215', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3216', null, null, null, '5', '3215', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(xy\' - 2{{\\rm{x}}^2}\\sqrt y  = 4y\\)</span>   là <span class=\"math-tex\">\\(y = \\square{(C - \\ln \\left| x \\right|)^2}\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x^4</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3217', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3218', null, null, null, '5', '3217', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(2{{\\rm{x}}^2}y\' = {y^2}\\left( {2{\\rm{x}}y - y} \\right)\\)</span>    là <span class=\"math-tex\">\\({y^2} = x{{\\mathop{\\rm lnC\\square}\\nolimits} ^2}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3219', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3220', null, null, null, '5', '3219', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' - xy =  - x{y^3}\\)</span>    là <span class=\"math-tex\">\\({y^2} = \\frac{{{e^{\\square}}}}{{{e^{\\square}} + C}}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x^2,x^2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3221', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình Bernoulli(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '361', '450', '1552398409', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3222', null, null, null, '5', '3221', '361', '450', '1552398409', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(xy\' + y = {y^2}\\ln {\\rm{x}}\\)</span>     là <span class=\"math-tex\">\\(y = \\frac{1}{{C{\\rm{x}} + \\square + 1}}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">lnx</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3223', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '362', '450', '1552398681', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3224', null, null, null, '5', '3223', '362', '450', '1552398681', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = {\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\\)</span>   là<span class=\"math-tex\">\\(y = C{\\rm{x}} + \\square+ D\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">sinx</a>', null, '51', null, null, null, null, null);
