INSERT INTO `question` VALUES ('3226', null, null, null, '5', '3225', '362', '450', '1552398681', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = {x^2}\\)</span>    là<span class=\"math-tex\">\\(y = \\frac{{{x^4}}}{{\\square}} + C{\\rm{x}} + D\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">12</a>', null, '51', null, null, null, null, null);
