INSERT INTO `question` VALUES ('3228', null, null, null, '5', '3227', '362', '450', '1552398681', '1552398760', null, null, '<p>Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = {e^x} + x + 1\\)</span> là<span class=\"math-tex\">\\(y = {e^x} + \\frac{{{x^3}}}{{\\square}} + \\frac{{{x^2}}}{{\\square}} + C{\\rm{x}} + D\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">6,2</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3229', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '362', '450', '1552398681', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3230', null, null, null, '5', '3229', '362', '450', '1552398681', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = \\frac{{3{{\\rm{x}}^2} - 6}}{{{x^2} + 1}}\\)</span>     là<span class=\"math-tex\">\\(\\[y = \\square{x^2}{\\rm{ + Cx  - 9}}\\arctan x + \\square\\ln \\left( {{x^2} + 1} \\right) + D\\]\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">3/2,9/2</a>', null, '51', null, null, null, null, null);
