INSERT INTO `question` VALUES ('3232', null, null, null, '5', '3231', '362', '450', '1552398681', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = {e^x}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\\)</span>      là<span class=\"math-tex\">\\(y = C{\\rm{x}} - \\frac{{{e^x}{\\rm{\\square}}}}{2}\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">cosx</a>', null, '51', null, null, null, null, null);
