INSERT INTO `question` VALUES ('3234', null, null, null, '5', '3233', '362', '450', '1552398681', '1552398817', null, null, '<p>Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = {\\sin ^2}x.{\\cos ^3}x\\)</span> là<span class=\"math-tex\">\\(y = \\frac{{{{\\cos }^5}x}}{{\\square}} + \\frac{{{{\\cos }^3}x}}{9} - \\frac{{{{\\cos }^2}x}}{5} - \\square{\\rm{cosx}} + C{\\rm{x}} + D\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">25,2/5</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3235', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '362', '450', '1552398681', null, null, null, null, null, '51', null, null, null, null, null);
