INSERT INTO `question` VALUES ('3240', null, null, null, '5', '3239', '363', '450', '1552399006', '1552399075', null, null, '<p>Tích phân tổng quát của:<span class=\"math-tex\">\\(\\[2{\\rm{x}}y\'y\'\' = {\\left( {y\'} \\right)^2} - 1\\]\\)</span> là<span class=\"math-tex\">\\(y = \\frac{2}{{\\square.C}}\\sqrt {C{\\rm{x + 1}}} + D\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">3</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3241', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '363', '450', '1552399006', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3242', null, null, null, '5', '3241', '363', '450', '1552399006', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = x - \\frac{{y\'}}{x}\\)</span>   là<span class=\"math-tex\">\\(y = \\frac{{{x^3}}}{{\\square}} + C\\ln \\left| x \\right| + D\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">9</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3243', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '363', '450', '1552399006', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3244', null, null, null, '5', '3243', '363', '450', '1552399006', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' - \\frac{{y\'}}{{2{\\rm{x}} + 1}} = 0\\)</span>   là<span class=\"math-tex\">\\(y = \\frac{1}{3}C\\sqrt {{{\\left( {2{\\rm{x}} + 1} \\right)}^{\\square}}}  + D\\)</span>    <a class=\"clozetip\" title=\"\" href=\"#\">3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3245', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '363', '450', '1552399006', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3246', null, null, null, '5', '3245', '363', '450', '1552399006', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = y\' - {\\left( {y\'} \\right)^2}\\)</span>     là<span class=\"math-tex\">\\(y - \\ln \\left| {1 - C.\\square} \\right| = x + D\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">e^y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3247', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '363', '450', '1552399006', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3248', null, null, null, '5', '3247', '363', '450', '1552399006', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(xy\'\' = y\'\\)</span>     là<span class=\"math-tex\">\\(y = C\\square+ D\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x^2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3249', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết y)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '363', '450', '1552399006', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3250', null, null, null, '5', '3249', '363', '450', '1552399006', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = 2\\sqrt {y\'}\\)</span>     là <span class=\"math-tex\">\\(y = \\frac{1}{3}{(\\square + C)^3} + D\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3251', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3252', null, null, null, '5', '3251', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\'\\sqrt y  = y\'\\)</span>    là<span class=\"math-tex\">\\(x = \\sqrt y  - \\frac{C}{2}\\ln \\left| {\\square\\sqrt y  + C} \\right| + D\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3253', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3254', null, null, null, '5', '3253', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\'(1 + y) = {\\left( {y\'} \\right)^2} + y\'\\)</span>     là<span class=\"math-tex\">\\(y = C;y = C - x;x = \\frac{1}{{{C_1}}}\\ln \\left| {{C_1}\\square + {C_1} - \\square} \\right| + {C_2}\\)</span>    <a class=\"clozetip\" title=\"\" href=\"#\">y,1</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3255', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3256', null, null, null, '5', '3255', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(yy\'\' + {\\left( {y\'} \\right)^2} = 1\\)</span>     là<span class=\"math-tex\">\\({\\square^2} + C = {\\left( {\\square + D} \\right)^2}\\)</span>     <a class=\"clozetip\" title=\"\" href=\"#\">y,x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3257', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3258', null, null, null, '5', '3257', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' = {e^{2y}}\\)</span>     là<span class=\"math-tex\">\\(\\[\\arctan \\sqrt {{e^{\\square}} - 1}  = \\square+ C\\]\\)</span>     <a class=\"clozetip\" title=\"\" href=\"#\">2y,x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3259', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3260', null, null, null, '5', '3259', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {1 + {y^2}} \\right)yy\'\' = \\left( {{y^2} - 1} \\right){\\left( {y\'} \\right)^2}\\)</span>   là  <span class=\"math-tex\">\\(\\frac{1}{2}\\ln (\\square) = C{\\rm{x}} + D\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">1+y^2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3261', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3262', null, null, null, '5', '3261', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' - \\frac{{2{{\\left( {y\'} \\right)}^2}}}{y} = 0\\)</span>    là  <span class=\"math-tex\">\\(y = C;y = \\frac{{\\square}}{{C{\\rm{x}} + D}}\\)</span>    <a class=\"clozetip\" title=\"\" href=\"#\">-1</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3263', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân cấp 2 (khuyết x)(điền theo thứ tự ,ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '543', '450', '1552399363', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3264', null, null, null, '5', '3263', '543', '450', '1552399363', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' + {\\left( {y\'} \\right)^3}y = 0\\)</span>     là  <span class=\"math-tex\">\\(y = C;\\frac{{{y^3}}}{{\\square}} - \\square{C_1}y = 2{\\rm{x}} + {C_2}\\)</span>     <a class=\"clozetip\" title=\"\" href=\"#\">3,2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3265', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3266', null, null, null, '5', '3265', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' - 2y\' + y = x + 1\\)</span> là<span class=\"math-tex\">\\(y = C.{e^x} + D{\\rm{x}}.{e^x} + x + \\square\\)</span>   <a class=\"clozetip\" title=\"\" href=\"#\">3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3267', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3268', null, null, null, '5', '3267', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' + y\' + 2y = 2\\sin x + c{\\rm{osx}}\\)</span> là<span class=\"math-tex\">\\(y = {e^{\\square.x}}\\left( {{C_1}{\\rm{cos}}\\frac{{\\sqrt 7 }}{2}x + {C_2}\\sin \\frac{{\\sqrt 7 }}{2}x} \\right) - \\square{\\rm{cosx + }}\\frac{1}{2}{\\mathop{\\rm s}\\nolimits} {\\rm{inx}}\\)</span>    <a class=\"clozetip\" title=\"\" href=\"#\">-1/2,3/2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3269', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3270', null, null, null, '5', '3269', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' - 2y\' + y = 2.{e^x}\\)</span>  là<span class=\"math-tex\">\\(y = {C_1} + {C_2}.{e^{\\square}} + \\square.x - \\frac{3}{4}\\)</span>     <a class=\"clozetip\" title=\"\" href=\"#\">-2x,3/4</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3271', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3272', null, null, null, '5', '3271', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' + 2y\' = 3{\\rm{x}}\\)</span>  là  <span class=\"math-tex\">\\(y = {C_1} + {C_2}.{e^{{\\rm{\\square.x}}}} + \\frac{3}{4}x - \\frac{3}{4}\\)</span>     <a class=\"clozetip\" title=\"\" href=\"#\">-2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3273', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3274', null, null, null, '5', '3273', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' + 4y = {x^2}\\)</span>   là  <span class=\"math-tex\">\\(y = {C_1}{\\rm{\\square}} + {C_2}\\sin 2{\\rm{x + }}\\frac{1}{4}{x^2} - \\frac{1}{{\\square}}\\)</span>      <a class=\"clozetip\" title=\"\" href=\"#\">cos2x,8</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3275', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3276', null, null, null, '5', '3275', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' - 5y\' + 6y = {e^{ - x}}\\)</span>    là <span class=\"math-tex\">\\(y = {C_1}{e^{\\square}} + {C_2}{e^{\\square}} + \\frac{1}{{12}}{e^{ - x}}\\)</span>      <a class=\"clozetip\" title=\"\" href=\"#\">2x,3x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3277', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3278', null, null, null, '5', '3277', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' + y\' = x{e^{ - x}}\\)</span>     là <span class=\"math-tex\">\\(y = {C_1} + {C_2}{e^{ - x}} - \\left( {\\frac{{{x^2}}}{2} + \\square} \\right).{e^{ - x}}\\)</span>       <a class=\"clozetip\" title=\"\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3279', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình vi phân tuyến tính cấp 2 hệ số hằng (điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '545', '450', '1552399591', null, null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3280', null, null, null, '5', '3279', '545', '450', '1552399591', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(y\'\' + y\' = x + {e^{ - x}}\\)</span>     là <span class=\"math-tex\">\\(y = {C_1} + {C_2}{e^{ - x}} + \\frac{1}{2}{x^2} - \\square - x.{e^{ - x}}\\)</span>         <a class=\"clozetip\" title=\"tìm nghiệm riêng dưới dạng y=y1+y2 trong đó y1,y2 lần lượt là nghiệm rêng của các phương trình y\'\'+y\'=x và y\'\'+y\'=e^-x\" href=\"#\">x</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3281', 'Một số khái niệm cơ bản.', null, null, '5', '0', '540', '467', '1552400363', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3282', null, null, null, '5', '3281', '540', '467', '1552400363', null, null, null, '<ul>\r\n<li>Kênh marketing là <a class=\"cloze\" href=\"#mce_temp_url#\">một tập hợp</a> các <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a> độc lập và phụ thuộc lẫn nhau tham gia vào quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">đưa hàng hóa</a> hoặc dịch vụ từ<a class=\"cloze\" href=\"#mce_temp_url#\"> nơi sản xuất </a>đến người tiêu dùng <a class=\"cloze\" href=\"#mce_temp_url#\">cuối cùng </a>hoặc người sử dụng công nghiệp </li>\r\n</ul>\r\n<ul>\r\n<li>Kênh truyền thông: <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thông</a> và nhận các thông điệp được khách hàng </li>\r\n<li>Kênh phân phối: <a class=\"cloze\" href=\"#mce_temp_url#\">phân bố </a>hoặc <a class=\"cloze\" href=\"#mce_temp_url#\">vận chuyển</a> các sản phẩm hữu hình hoặc dịch vụ </li>\r\n<li> Kênh bán hàng: tác động các giao dịch với khách hàng tiềm năng </li>\r\n<li>Chuỗi cung ứng: ứng là một mạng lưới gồm các tổ chức có liên quan thông qua mối liên hệ phía trước và phía sau, trong quá trình và hoạt động khác nhau giảm sinh ra giá trị dưới hình thức sản phẩm dịch vụ </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3283', 'Các đặc trưng của kênh MKT B2B.', null, null, '5', '0', '540', '467', '1552400425', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3284', null, null, null, '5', '3283', '540', '467', '1552400425', null, null, null, '<ul>\r\n<li>Kênh <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối ngắn</a> so với kênh phân phối trong thị trường tiêu dùng </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Không có nhiều loại hình trung gian</a> để lựa chọn </li>\r\n<li>Việc lựa cho trung gian khó gan </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tồn kho</a> luôn là vấn đề lớn đối với các doanh nghiệp trong thị trường b2b </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quan hệ</a> giữa sản xuất và khách hàng, sản xuất -trung gian-khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">khăng khít </a></li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3285', 'Hệ thồng kênh MKT B2B: kênh trực tiếp.', null, null, '5', '0', '541', '467', '1552400751', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3286', null, null, null, '5', '3285', '541', '467', '1552400751', null, null, null, '<p>Kênh trực tiếp: Là <a class=\"cloze\" href=\"#mce_temp_url#\">kênh thường găp</a> nhất trong thị trường B2B và chiếm tỉ trọng lớn (¾) trong tổng giá trị hàng hóa đi qua hệ thông kênh b2b </p>\r\n<ul>\r\n<li>Thường có ý nghĩa khi \r\n<ul>\r\n<li>Lợi nhuận cận biên cao </li>\r\n<li>Sản phẩm có vai trò quan trọng với người mua </li>\r\n<li>Sản phẩm có đặc tính kĩ thuật cao </li>\r\n<li>Khách hàng có yêu cầu dịch vụ khách hàng hóa cao </li>\r\n<li>Đơn hàng có giá trị lớn </li>\r\n<li>Sản phẩm có tính cá biệt hóa cao </li>\r\n<li>Sản phẩm chiu sự thay đổi công nghệ nhanh chóng </li>\r\n</ul>\r\n</li>\r\n<li>Người bán hàng bên ngoài: Là những <a class=\"cloze\" href=\"#mce_temp_url#\">người bán hàng trực tiếp</a> tới khách hàng ,tiến hành bán hàng và giao hàng trực tiếp tới khách hàng từ nhà máy hoặc từ các chi nhánh địa phương hoặc kho.</li>\r\n<li>Người bán hàng bên ngoài thực hiện việc đi chào hàng </li>\r\n<li>Người bán hàng nội bộ: Một số doanh nghiệp tham gia vào thị trường b2b sử dụng những người bán hàng nọi bộ hoặc <a class=\"cloze\" href=\"#mce_temp_url#\">qua điện thoại</a> mở rộng như một kênh tiêu thụ. Nhân viên ngồi tại văn phòng và gọi điện liên hệ với khách hàng. </li>\r\n<li>Catalogue: \r\n<ul>\r\n<li>Áp dụng đối với các sản phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">tiêu chuẩn hóa</a>, các sản phẩm<a class=\"cloze\" href=\"#mce_temp_url#\"> cấu kiện</a> hay <a class=\"cloze\" href=\"#mce_temp_url#\">nguyên vật liệu</a> </li>\r\n<li>Doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">gửi catalogue</a> tới khách hàng và khách hadng xem, đặt hàng trực tiếp với công ty và công ty giao hàng cho khách hàng thông qua trung gian giao hàng bên ngoài thực hiện việc đi chào hàng. </li>\r\n</ul>\r\n</li>\r\n<li>Telemarketing: \r\n<ul>\r\n<li>Là một kênh trực tiếp mới được sử dụng ở thị trường B2B, trực tiếp tới những khách hàng hiện tại và tiềm năng </li>\r\n<li>Có thể được xem như sự mở rộng bán hàng nội bộ theo <a class=\"cloze\" href=\"#mce_temp_url#\">cách thức mới </a>của <a class=\"cloze\" href=\"#mce_temp_url#\">công nghệ thông tin</a> như máy tính hay điện thoại giữa người mua và người bán </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3287', 'Hệ thồng kênh MKT B2B: kênh gián tiếp.', null, null, '5', '0', '541', '467', '1552400818', '1552400842', null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3288', null, null, null, '5', '3287', '541', '467', '1552400818', '1552400842', null, null, '<ul>\r\n<li> Có ý nghĩa khi: \r\n<ul>\r\n<li>Sản phẩm tương đối chuẩn hóa </li>\r\n<li> Giá đơn vị sản phẩm thấp </li>\r\n<li> giá trị đơn hàng nhó </li>\r\n<li>Lợi nhuận cận biên thấp </li>\r\n<li>Nỗ lực mua hàng của khách hàng thấp </li>\r\n<li>khách hàng mua thường xuyên </li>\r\n<li>Thị trường phân tán </li>\r\n<li> khách hàng đòi hỏi theo thời gian từ khi đặt đến khi giao hàng ngắn </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3289', 'Kênh gián tiếp: Nhà phân phối công nghiệp.', null, null, '5', '0', '541', '467', '1552400984', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3290', null, null, null, '5', '3289', '541', '467', '1552400984', null, null, null, '<ul>\r\n<li>Là người hoạt động <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý độc lập</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tiến hành</a> việc mua hàng, lưu kho và các công cụ sản xuất (thiết bị điều hành và các phụ kiện bảo dưỡng) </li>\r\n<li>Là <a class=\"cloze\" href=\"#mce_temp_url#\">nhà bán buôn</a> tới thi trường B2B </li>\r\n<li>Có hai dạng nhà phân phối công nghiệp \r\n<ul>\r\n<li>Nhà phân phối chuyên môn: chuyên bán một hay một vài sản phẩm nhấtđịnh. Họ thường tập trung vào một dòng sản phẩm chính (chiếm khoảng &gt;50% tổng doanh số) </li>\r\n<li>hà phân phối tổng hợp: là những siêu thị công nghiệp, bán nhiều loại sản phẩm công nghiệp, không dòng sản phẩm nào là chính, chủ yếu. </li>\r\n</ul>\r\n</li>\r\n<li> Ưu điểm \r\n<ul>\r\n<li>Có lưc lượng bán sẵn và am hiểu thị trường </li>\r\n<li>Có kho bãi và thực hiện chức năng<a class=\"cloze\" href=\"#mce_temp_url#\"> lưu kho</a> </li>\r\n<li>Chia sẻ một phần các yếu tố tài chính </li>\r\n<li>Có nguồn thông tin hỗ trợ cho phát triển sản phẩm </li>\r\n<li>Giảm chi phí mua hàng cho khách hàng do có thể chia nhỏ lô hàng, giao hàng nhanh chóng </li>\r\n</ul>\r\n</li>\r\n<li>Hạn chế: \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Khó kiểm soát</a> </li>\r\n<li>Kinh doanh nhiều sản phẩm của nhiều công ty </li>\r\n<li>Chạy theo doanh thu, lợi nhuận nên có thể xuất hiện mâu thuẫn </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>Khuynh hướng: ngày càng <a class=\"cloze\" href=\"#mce_temp_url#\">chuyên môn hóa</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">qui mô lớn</a>, tăng cường<a class=\"cloze\" href=\"#mce_temp_url#\"> mối quan hệ lâu dài</a></p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3291', 'Kênh gián tiếp: Đại diện của nhà sản xuất', null, null, '5', '0', '541', '467', '1552401104', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3292', null, null, null, '5', '3291', '541', '467', '1552401104', null, null, null, '<ul>\r\n<li>Đại diện của nhà sản xuất: là những nhà <a class=\"cloze\" href=\"#mce_temp_url#\">trung gian</a>, cá nhân/tổ chứ được biết tới như <a class=\"cloze\" href=\"#mce_temp_url#\">đại lý </a>nhà sản xuất, <a class=\"cloze\" href=\"#mce_temp_url#\">đại diện</a> kĩ thuật \r\n<ul>\r\n<li> MR là nhà <a class=\"cloze\" href=\"#mce_temp_url#\">bán hàng độc lập</a> đại diện cho một hoặc nhiều nhà sản xuất trên cơ sở ăn hoa hồng, không thực hiện chức năng lưu kho </li>\r\n<li> Ưu điểm: \r\n<ul>\r\n<li> Phù hợp với công ty cần <a class=\"cloze\" href=\"#mce_temp_url#\">bán trực tiếp</a> nhưng <a class=\"cloze\" href=\"#mce_temp_url#\">không đủ khả năng</a> xây dựng đội ngũ bán hàng trực tiếp </li>\r\n<li>Tiện lợi khi doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">tiếp cận thị trường</a> mới, Đại diện có sẵn mối quan hệ cho phép nhanh chóng thâm nhập thị trường </li>\r\n<li> Có lợi khi bán sản phẩm có<a class=\"cloze\" href=\"#mce_temp_url#\"> tính thời vụ</a> </li>\r\n<li>Có những thị trường không đủ lớn để sử dụng lực lượng bán nội bộ </li>\r\n<li>Đại diện thường kinh doanh trên một khu vực kinh doanh nên doanh số khu vực ổn định hơn </li>\r\n</ul>\r\n</li>\r\n<li>Hạn chế: \r\n<ul>\r\n<li>Khó quản lý </li>\r\n<li>THường <a class=\"cloze\" href=\"#mce_temp_url#\">không lưu kho </a>nên đôi khi hàng hóa không đủ đáp ứng kịp thời nhu cầu </li>\r\n<li>Khuynh hướng: Sử dụng đại diện bán hàng thay thế cho việc xây dựng lực lượng bán hàng tốn kém nhiều chi phí </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3293', 'Trình bày nội dung quản trị kênh B2B.', null, null, '5', '0', '542', '467', '1552401310', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3294', null, null, null, '5', '3293', '542', '467', '1552401310', null, null, null, '<p>-          Quy trình quản trị kênh</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Lựa chọn thành viên</a> trong kênh\r\n<ul>\r\n<li>Lựa chọn thành viên phù hợp với mục tiêu chiến lược, quy mô tài chính... của doanh nghiệp</li>\r\n<li>Độ sản phẩm của trung gian với sản phẩm của doanh nghiệp</li>\r\n<li>Tiêu chí về danh tiếng</li>\r\n<li>Tiêu chuẩn kênh trực tiếp khác kênh gián tiếp</li>\r\n<li>Tùy vào từng đối tượng trung gian mà có các có các tiêu chí khác nhau</li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Động viên</a> khích lệ thành viên kênh:\r\n<ul>\r\n<li>Thưởng phạt rõ ràng</li>\r\n<li>Hợp tác, phân chia trung gian phân phối theo từng cấp độ</li>\r\n<li>Liên minh, cộng tác</li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Kiểm soát</a> sự xung đột kênh:\r\n<ul>\r\n<li>Xung đột ngang – giữa các trung gian</li>\r\n<li>Xung đột dọc – giữa trung gian và  doanh nghiệp</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3295', 'Các dạng quan hệ trong kênh: Kênh giao dịch.', null, null, '5', '0', '542', '467', '1552401377', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3296', null, null, null, '5', '3295', '542', '467', '1552401377', null, null, null, '<ul>\r\n<li>Kênh giao dich ( truyền thống doanh nghiệp với thành viên trong kênh hoạt động đọc lập với nhau, không có mối quan tâm về toàn bộ vấn đề của kênh phân phối)</li>\r\n</ul>\r\n<p>-&gt;<a class=\"cloze\" href=\"#mce_temp_url#\"> Rủi ro thấp</a> nhưng <a class=\"cloze\" href=\"#mce_temp_url#\">không tối ưu hóa </a>nỗ lực của các thành viên kênh phân phối.</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3297', 'Các dạng quan hệ trong kênh: Kênh quản trị.', null, null, '5', '0', '542', '467', '1552401432', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3298', null, null, null, '5', '3297', '542', '467', '1552401432', null, null, null, '<ul>\r\n<li>Kênh quản trị: Doanh nghiệp và thành viên trong kênh <a class=\"cloze\" href=\"#mce_temp_url#\">hợp tác</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phối hợp</a> hoạt động thông qua những những <a class=\"cloze\" href=\"#mce_temp_url#\">thỏa thuận phi hợp đồng</a> không chính thức.</li>\r\n</ul>\r\n<p>-&gt;  Tạo cho doanh nghiệp tính hữu hiệu trong hoạt động mà khoong cần cam kết dài hạn đến duy trì tính linh hoạt của kênh</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3299', 'Các dạng quan hệ trong kênh: Kênh hợp đồng.', null, null, '5', '0', '542', '467', '1552401484', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3300', null, null, null, '5', '3299', '542', '467', '1552401484', null, null, null, '<ul>\r\n<li>Kênh hợp đồng: Doanh nghiệp và thành viên trong kênh<a class=\"cloze\" href=\"#mce_temp_url#\"> hợp nhất</a> các hoạt động của <a class=\"cloze\" href=\"#mce_temp_url#\">hệ thống </a>thông qua hợp đồng chính thức nhằm tính <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a> cảu kênh và <a class=\"cloze\" href=\"#mce_temp_url#\">tối đa hóa</a> tác động thị trường</li>\r\n</ul>\r\n<p>-&gt;  Có tính hữu hiệu và phối hợp giảm bớt rủi ro hoạt động của từng thành viên, đạt hiệu suất cao nhất</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3301', 'Các dạng quan hệ trong kênh: Kênh công ty/doanh nghiệp.', null, null, '5', '0', '542', '467', '1552401529', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3302', null, null, null, '5', '3301', '542', '467', '1552401529', null, null, null, '<ul>\r\n<li>Kênh công ti/doanh nghiệp: <a class=\"cloze\" href=\"#mce_temp_url#\">Kết hợp</a> các<a class=\"cloze\" href=\"#mce_temp_url#\"> giai đoạn kế tiếp</a> của quá trình<a class=\"cloze\" href=\"#mce_temp_url#\"> sản xuất</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phân phối</a> trong khuôn khổ một quyền sở hữu duy nhất</li>\r\n</ul>\r\n<p>-&gt;  Doanh nghiệp thực hiện tất cả các chức năng của kênh nhứ giao hàng, bán hàng, quảng bá.</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3303', 'Xác định mục tiêu kênh.', null, null, '5', '0', '544', '467', '1552401652', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3304', null, null, null, '5', '3303', '544', '467', '1552401652', null, null, null, '<ul>\r\n<li>Xác định mục tiêu kênh \r\n<ul>\r\n<li>Căn cứ vào đoạn thị trường đã chọn (tập khách hàng? Đặc trưng sản phẩm?) Và tính chất hàng hóa dịch vụ kinh doanh để xác định mục tiêu kênh </li>\r\n<li>Mục tiêu kinh quyết định kinh giải hay ngắn, số lượng ít hay nhiều, dùng một lúc nhiều kênh được hay không? </li>\r\n<li>Các nhân tố ảnh hưởng \r\n<ul>\r\n<li>Khách hàng thích mua trực tiếp hay gián tiếp </li>\r\n<li>Các trung gian có bán sản phẩm của doanh nghiệp hay không </li>\r\n<li>Doanh nghiệp có đủ khả năng tài chính cho việc sử dụng kênh hay không </li>\r\n</ul>\r\n</li>\r\n<li>Các mục tiêu điển hình \r\n<ul>\r\n<li>Chi phí vận hành thấp (kênh gián tiếp) </li>\r\n<li>Kiểm soát (trực tiếp) </li>\r\n<li>Nỗ lực bán hàng ( đại lý, nhà phân phối chọn lọc) </li>\r\n<li>Dịch vụ và trợ giúp kỹ thuật ( đại lí, trung gian phân phối công nghiệp) </li>\r\n<li>Thông tin phản hồi về thị trường </li>\r\n<li>Danh tiếng của tổ chức </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3305', 'Đánh giá sự lựa chọn kênh.', null, null, '5', '0', '544', '467', '1552401687', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3306', null, null, null, '5', '3305', '544', '467', '1552401687', null, null, null, '<ul>\r\n<li>Thực chất doanh nghiệp có thể lựa chọn kênh trực tiếp, gián tiếp hoặc cả hai </li>\r\n<li>Kênh trực tiếp \r\n<ul>\r\n<li>Sử dụng lực lượng bán hàng tại chỗ hoặc lực lượng tổ chức kết hợp </li>\r\n<li>Tele marketing và catalogue? </li>\r\n<li>Khả năng tài chính của tổ chức? </li>\r\n</ul>\r\n</li>\r\n<li>Kênh gián tiếp \r\n<ul>\r\n<li>Sử dụng nhà phân phối/ MR hoặc kết hợp? </li>\r\n<li>Những trung gian nào có thể bán hàng và dịch vụ tốt nhất </li>\r\n<li>Rượu nhà phân phối có thể bán hàng với nhiều thị trường và MR tới các người khác hay không? </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3307', 'Lựa chọn các kênh phù hợp.', null, null, '5', '0', '544', '467', '1552401725', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3308', null, null, null, '5', '3307', '544', '467', '1552401725', null, null, null, '<ul>\r\n<li>Lựa chọn các kênh phù hợp. Các nhân tố cần xem xét \r\n<ul>\r\n<li>Thực tế mua hàng và sự ưa chuộng trên thị trường mục tiêu là gì? </li>\r\n<li>Kênh nào mà đối thủ đang dùng? </li>\r\n<li>Giá trị một đơn hàng trung bình? </li>\r\n<li>Điểm mạnh, điểm yếu của sản phẩm </li>\r\n<li>Chi phí sử dụng các kênh khác nhau </li>\r\n<li>Trình độ dịch vụ và bán hàng mà thị trường đòi hỏi như thế nào? </li>\r\n<li>Mức độ của các nỗ lực bán hàng có hiệu quả nhất? </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3309', 'Thiết lập quan hệ kênh.', null, null, '5', '0', '544', '467', '1552401752', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3310', null, null, null, '5', '3309', '544', '467', '1552401752', null, null, null, '<ul>\r\n<li>Thiết lập các quan hệ kênh \r\n<ul>\r\n<li>Áp dụng cho trường hợp kênh gián tiếp </li>\r\n<li>Mang tính chất pháp lý (hợp đồng) </li>\r\n<li>Phát triển kênh hiện đại </li>\r\n<li>Quy định trách nhiệm của các thành viên kênh </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3311', 'Kiểm tra và đánh giá những kênh đã chọn.', null, null, '5', '0', '544', '467', '1552401793', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3312', null, null, null, '5', '3311', '544', '467', '1552401793', null, null, null, '<ul>\r\n<li>Kiểm tra và đánh giá những kênh đã chọn \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Giám sát hoạt động</a> của kênh theo mục tiêu thiết kế kênh và mục tiêu tổng thể của tổ chức ( trực tiếp hay gián tiếp) \r\n<ul>\r\n<li>Chi phí </li>\r\n<li>Sản lượng bán </li>\r\n<li>Lợi nhuận </li>\r\n<li>Sự phối hợp và Hòa hợp tổng thể các bên liên quan </li>\r\n</ul>\r\n</li>\r\n<li>Sử dụng mis giám sát( đảm bảo tính hiệu quả và năng suất của kênh) </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3333', 'Quyết định truyền thông cá nhân: Bán hàng cá nhân.', null, null, '5', '0', '550', '467', '1552445877', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3334', null, null, null, '5', '3333', '550', '467', '1552445877', null, null, null, '<p>Quyết định bán hàng cá nhân </p>\r\n<ul>\r\n<li>bán hàng cá nhân là hình thức <a class=\"cloze\" href=\"#mce_temp_url#\">trình bày trực tiếp</a> giữa <a class=\"cloze\" href=\"#mce_temp_url#\">các nhân viên</a> bán hàng với một hay nhiều khách hàng với mục đích là <a class=\"cloze\" href=\"#mce_temp_url#\">bán hàng</a> </li>\r\n<li>Bán hàng cá nhân đóng vai trò quan trọng trong xúc tiến marketing b2b </li>\r\n</ul>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Sản phẩm phức tạp</a>, đắt và bán được lượng tiền lớn <br />+ Khách hàng yêu cầu<a class=\"cloze\" href=\"#mce_temp_url#\"> trả lời ngay</a> các câu hỏi phức tạp </p>\r\n<ul>\r\n<li>Là kênh bán hàng<a class=\"cloze\" href=\"#mce_temp_url#\"> tốn kém</a> cần xem xét các chi phí </li>\r\n</ul>\r\n<p class=\"paragraph\">+ Chi phí cho một lần chào bán hàng (chi phí tiếp xúc) </p>\r\n<p class=\"paragraph\">+ Chi phí tuyển dụng và Đào tạo một nhân viên bán hàng </p>\r\n<p class=\"paragraph\">+ Sự thay thế nhân viên bán hàng </p>\r\n<ul>\r\n<li>Quản trị bán hàng:  </li>\r\n</ul>\r\n<p class=\"paragraph\">+ <a class=\"cloze\" href=\"#mce_temp_url#\">Lựa chọn người bán</a> </p>\r\n<p class=\"paragraph\">+ Quản lý <a class=\"cloze\" href=\"#mce_temp_url#\">về thời gian</a> <a class=\"cloze\" href=\"#mce_temp_url#\">nhân viên</a> bán hàng </p>\r\n<p class=\"paragraph\">+ <a class=\"cloze\" href=\"#mce_temp_url#\">Thù lao</a> cho những người bán hàng </p>\r\n<p class=\"paragraph\">+ Kỹ sư bán ( sản phẩm có mức độ phức tạp lưỡi để kỹ thuật) </p>\r\n<p class=\"paragraph\">+ Người bán hàng chuyên nghiệp ( sản phẩm, dịch vụ hỗ trợ như phần mềm tư vấn) đòi hỏi tính thuyết phục cao </p>\r\n<p class=\"paragraph\">+ Người bán hàng cung cấp ( sản phẩm tiêu chuẩn hóa) đòi hỏi học về chuyên ngành bán, do các sản phẩm đòi hỏi kỹ năng xúc tiến bán </p>\r\n<p class=\"paragraph\">+ Người bán hàng bên trong: bằng điện thoại qua marketing tầm xa: telesale </p>\r\n<p class=\"paragraph\">+ Đại lý chào và bán hàng: làm việc với khách hàng trung gian, nhiệm vụ tư vấn kỹ thuật, quản lý tồn kho, đào tạo nhân viên sử dụng máy móc </p>\r\n<ul>\r\n<li>Mô hình quy trình các bước thực hiện hoạt động bán hàng </li>\r\n</ul>\r\n<p class=\"paragraph\"> B1: tìm kiếm và sàng lọc (lên danh sách khách hàng) </p>\r\n<p class=\"paragraph\"> B2: tiền tiếp cận (đặt lịch tiếp cận)  </p>\r\n<ul>\r\n<li>o B1 và B2 yêu cầu nhận biết và tìm hiểu thực sự nhu cầu và triển vọng mua hàng </li>\r\n</ul>\r\n<p class=\"paragraph\">B3: tiếp cận chính thức (tiến hành thuyết phục) </p>\r\n<p class=\"paragraph\">B4: giới thiệu và trình diễn </p>\r\n<p class=\"paragraph\">B5: khắc phục ý kiến phản đối </p>\r\n<p class=\"paragraph\">B6: kết thúc thương vụ ( cố gắng chốt sale) </p>\r\n<ul>\r\n<li>o B3-6 yêu cầu: tiến hành thực hiện </li>\r\n</ul>\r\n<p class=\"paragraph\">+ Thu hút sự chú ý </p>\r\n<p class=\"paragraph\">+ Thuyết phục và tác động  </p>\r\n<p class=\"paragraph\">+ Trả lời thắc mắc </p>\r\n<p class=\"paragraph\">+ Giải quyết đúng vấn đề </p>\r\n<p class=\"paragraph\">+ Ký hợp đồng </p>\r\n<p class=\"paragraph\">                                 B7: theo dõi và duy trì </p>\r\n<ul>\r\n<li>o Yêu cầu: xây dựng quan hệ đảm bảo sự hài lòng của khách hàng </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3335', 'Quyết định truyền thông cá nhân: marketing trực tiếp.', null, null, '5', '0', '550', '467', '1552446002', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3336', null, null, null, '5', '3335', '550', '467', '1552446002', null, null, null, '<p>Quyết định marketing trực tiếp </p>\r\n<ul>\r\n<li>là hình thức <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thông</a> nhằm đến việc <a class=\"cloze\" href=\"#mce_temp_url#\">tác động </a>cụ thể vào từng <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân </a>khách hàng nhưng không có sự giao tiếp đối mặt </li>\r\n<li>Quyết định marketing trực tiếp </li>\r\n</ul>\r\n<p class=\"paragraph\">+ Lựa chọn<a class=\"cloze\" href=\"#mce_temp_url#\"> danh sách</a> khách hàng </p>\r\n<p class=\"paragraph\">+ Gửi thư đến cho khách hàng, chú ý tới thành tố phong bì, nội dung rõ ràng, ngắn gọn nhưng chuyên nghiệp, các chi tiết đính kèm, yêu cầu hồi đáp. </p>\r\n<p class=\"paragraph\">+ Tele marketing là việc sử dụng điện thoại hoặc trung tâm dịch vụ khách hàng để thu hút khách hàng triển vọng, bán cho những khách hàng nên có, và cung cấp dịch vụ bằng việc nhận đơn đặt hàng và trả lời câu hỏi </p>\r\n<ul>\r\n<li>Marketing trực tiếp: <a class=\"cloze\" href=\"#mce_temp_url#\">thúc đẩy</a> hành vi mua và <a class=\"cloze\" href=\"#mce_temp_url#\">giải đáp thắc mắc</a>,<a class=\"cloze\" href=\"#mce_temp_url#\"> lọc khách hàng </a>tiềm năng </li>\r\n<li>Quy trình tiến hành marketing trực tiếp \r\n<ul>\r\n<li>Mục tiêu marketing trực tiếp: rất phong phú </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p class=\"paragraph\">+ Làm khách hàng triển vọng mua hàng ngay tức thì (tiêu thụ được hàng ngày) </p>\r\n<p class=\"paragraph\">+ Lập danh sách khách hàng triển vọng cho lực lượng bán </p>\r\n<p class=\"paragraph\">+ Củng cố hình ảnh của thương hiệu sự ưa thích đối với doanh nghiệp </p>\r\n<ul>\r\n<li>Khách hàng mục tiêu: </li>\r\n</ul>\r\n<p class=\"paragraph\">+ <a class=\"cloze\" href=\"#mce_temp_url#\">Đánh giá đặc điểm</a> của khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">hiện có</a> và các khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">triển vọng</a> có nhiều khả năng nhất, mong muốn thiết tha và sẵn sàng mua hàng </p>\r\n<p class=\"paragraph\">+ Công thức R-F-M (thời gian-tần suất-tiền tệ) để đánh giá và tuyển chọn khách hàng </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3337', 'Quyết định truyền thông đại chúng: Quảng cáo.', null, null, '5', '0', '550', '467', '1552446095', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3338', null, null, null, '5', '3337', '550', '467', '1552446095', null, null, null, '<p>Quyết định quảng cáo </p>\r\n<ul>\r\n<li>Quảng cáo trong b2b <a class=\"cloze\" href=\"#mce_temp_url#\">không quan trọng</a>. Nó chỉ <a class=\"cloze\" href=\"#mce_temp_url#\">hỗ trợ</a> cho hoạt động bán hàng cá nhân (giúp cho khách hàng biết đến thương hiệu) </li>\r\n<li>Các loại quảng cáo trong b2b \r\n<ul>\r\n<li>Ấn phẩm thương mại và tạp chí chuyên ngành, bao gồm: tạp chí chúng, xuất bản ấm phẩm kinh doanh chung, tạp chí chuyên ngành </li>\r\n<li>Quảng cáo <a class=\"cloze\" href=\"#mce_temp_url#\">qua thư trực tiếp</a> (gửi ngẫu nhiên) </li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p class=\"paragraph\">                  + Quảng cáo thư trực tiếp </p>\r\n<p class=\"paragraph\">                  + Xúc tiến bán thư trực tiếp </p>\r\n<p class=\"paragraph\">                  + Bán hàng qua thư trực tiếp </p>\r\n<ul>\r\n<li>Quảng cáo <a class=\"cloze\" href=\"#mce_temp_url#\">qua danh bạ</a> </li>\r\n</ul>\r\n<p class=\"paragraph\">         + Liệt kê các nguồn cung cấp cho nhiều loại sản phẩm âm giữ được lâu bổ sung cho catalogue </p>\r\n<p class=\"paragraph\">          + Đắt, có thể trùng lặp, mối quan hệ của bạn đọc gián đoạn (bao gồm cả đối thủ) </p>\r\n<ul>\r\n<li>Lưu ý về <a class=\"cloze\" href=\"#mce_temp_url#\">thông điệp</a> quảng cáo </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3339', 'Quyết định truyền thông đại chúng: Xúc tiến bán.', null, null, '5', '0', '550', '467', '1552446201', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3340', null, null, null, '5', '3339', '550', '467', '1552446201', null, null, null, '<p>Quyết định xúc tiến bán </p>\r\n<ul>\r\n<li>là hình thức<a class=\"cloze\" href=\"#mce_temp_url#\"> khuyến khích ngắn hạn</a> nhằm thúc đẩy khách hàng ra quyết định mua sản phẩm hay dịch vụ </li>\r\n</ul>\r\n<p>Một số loại hình </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Catalogue</a> là tài liệu hoàn chỉnh được in ấn chứa đựng thông tin về sản phẩm, được thiết kế cung cấp thông tin cho khách hàng </li>\r\n</ul>\r\n<p class=\"paragraph\">                             + Có tính thời vụ </p>\r\n<p class=\"paragraph\">                             + Giá tương đối cao và chi phí tăng nhanh </p>\r\n<p class=\"paragraph\">                             + Những người bán hàng phân phối catalogs cho những người ảnh hưởng mua </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tài liệu</a>: cho nhà buôn và nhà phân phối, nhờ họ xúc tiến hiệu quả trong thị trường địa phương, tài liệu hướng dẫn bán hàng, tài liệu kỹ thuật của nhà bán buôn, hỗ trợ trang trí kệ tủ trưng bày, quảng cáo các sản phẩm đặc thù </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quảng cáo hợp tác</a>: là quảng cáo trong đó trách nhiệm và chi phí được chia sẻ cho hai hay nhiều nhà quảng cáo theo tỷ lệ khác nhau với giá ưu đãi ( dọc, ngang, phối hợp) </li>\r\n</ul>\r\n<p class=\"paragraph\">           + Ngang: áp dụng cho nhà bán lẻ </p>\r\n<p class=\"paragraph\">            + Dọc: điện máy xanh kết hợp với Panasonic cùng nhau làm quảng cáo </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Hội chợ-triển lãm</a>: vàng khách hàng đến với các tổ chức, tạo ra sự tập trung khách hàng mà không thể đạt được bằng mọi cách khác, mục đích không phải là bán hàng tại quầy hoặc gian hàng mà nhằm xây dựng uy tín và mở đường cho việc bán hàng trong tương Lai </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3341', 'Quyết định truyền thông đại chúng: Quan hệ công chúng.', null, null, '5', '0', '550', '467', '1552446250', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3342', null, null, null, '5', '3341', '550', '467', '1552446250', null, null, null, '<p>Là bất kỳ <a class=\"cloze\" href=\"#mce_temp_url#\">hình thức thông tin</a> nào về <a class=\"cloze\" href=\"#mce_temp_url#\">doanh nghiệp</a> được <a class=\"cloze\" href=\"#mce_temp_url#\">đăng tải </a>trên các phương tiện truyền thông đại chúng </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3343', 'Khái niệm giá.', null, null, '5', '0', '552', '467', '1552446510', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3344', null, null, null, '5', '3343', '552', '467', '1552446510', null, null, null, '<ul>\r\n<li>Khái niệm: giá cả là một <a class=\"cloze\" href=\"#mce_temp_url#\">khoản tiền</a> <a class=\"cloze\" href=\"#mce_temp_url#\">gắn với sản phẩm</a> hoặc dịch vụ==&gt;bản chất là <a class=\"cloze\" href=\"#mce_temp_url#\">sự quy đổi</a> ra tiền tệ của sản phẩm </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3345', 'Đặc điểm của giá.', null, null, '5', '0', '552', '467', '1552446547', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3346', null, null, null, '5', '3345', '552', '467', '1552446547', null, null, null, '<ul>\r\n<li>Đặc điểm \r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Ít quan trọng</a> hơn </li>\r\n<li>Cầu thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">ít nhạy cảm </a></li>\r\n<li>Hình thức định giá theo kiểu <a class=\"cloze\" href=\"#mce_temp_url#\">đấu thầu </a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thương lượng</a> về giá </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3347', 'Ảnh hưởng nhóm yếu tố: Nhu cầu khách hàng.', null, null, '5', '0', '552', '467', '1552446662', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3348', null, null, null, '5', '3347', '552', '467', '1552446662', null, null, null, '<ul>\r\n<li> Đô co giãn của cầu theo giá: với các sản phẩm b2b cầu thường <a class=\"cloze\" href=\"#mce_temp_url#\">ít co dãn</a> theo giá </li>\r\n</ul>\r\n<ul>\r\n<li>Độ co giãn chéo của cầu theo giá: cách tính <a class=\"cloze\" href=\"#mce_temp_url#\">theo phản ứng</a> với một <a class=\"cloze\" href=\"#mce_temp_url#\">sự thay đổi về giá</a> của một hàng hóa trước sự thay đổi vè giá của một số hàng hóa khác và thường xảy ra với sản phẩm thay thế và sản phẩm bổ trợ. </li>\r\n</ul>\r\n<ul>\r\n<li>Sự cảm nhận của khách hàng: <a class=\"cloze\" href=\"#mce_temp_url#\">sự cảm nhận</a> của khách hàng vè<a class=\"cloze\" href=\"#mce_temp_url#\"> giá trị nhận thức</a> bao gồm lợi ích về vật chất của sản phẩm mang lại, dịch vụ hỗ trợ và trợ giúp kĩ thuật của sản phẩm quyết định khác nhau cho sản phẩm b2b. Khách hàng tổ chức xem xét giá trị sản phẩm trên các khía cạnh khác nhau như giá trị kinh tế, giá trị trao đổi đàm phán; giá trị thẩm mỹ;giá trị tương đối </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3349', 'Ảnh hưởng của nhóm yếu tố: Chi phí', null, null, '5', '0', '552', '467', '1552446733', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3350', null, null, null, '5', '3349', '552', '467', '1552446733', null, null, null, '<p>- Chi phí là <a class=\"cloze\" href=\"#mce_temp_url#\">cơ sở nền tảng</a> để <a class=\"cloze\" href=\"#mce_temp_url#\">hình thành</a> nên giá bán nên khi xác định giá sản phẩm b2b cần dựa vào: </p>\r\n<ul>\r\n<li> Các chi phsi hiện tại cấu thành nên sản phẩm </li>\r\n<li> Các chi phí phát sinh </li>\r\n<li> Sử dụng các kĩ thuật như phân tích điểm hòa vốn... </li>\r\n</ul>\r\n<p>- Mục tiêu định giá: tùy vào từng <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu định giá</a> khác nhau một doanh nghiệp định ra các mức giá khách nhau </p>\r\n<ul>\r\n<li>Hoàn vốn mục tiêu </li>\r\n<li>Ổn định giá và lợi nhuận </li>\r\n<li> Đánh giá mục tiêu theo đuổi thị phần </li>\r\n<li>Giá cạnh tranh </li>\r\n<li>Giá tạo sự khác biệt </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3351', 'Ảnh hưởng của nhóm yếu tố: Cạnh tranh.', null, null, '5', '0', '552', '467', '1552446793', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3352', null, null, null, '5', '3351', '552', '467', '1552446793', null, null, null, '<p>- Giá và việc định giá trong sản phẩm công nghiệp phụ thuộc nhiều vào<a class=\"cloze\" href=\"#mce_temp_url#\"> giá của đối </a></p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">thủ</a> cạnh tranh và <a class=\"cloze\" href=\"#mce_temp_url#\">phản ứng của đối thủ</a> về sự <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổi giá </a>của doanh nghiệp. Điều </p>\r\n<p>này phụ thuộc vào </p>\r\n<ul>\r\n<li>Cấu trúc của đối thủ cạnh tranh </li>\r\n<li>Rào cảnh thâm nhập thị trường </li>\r\n<li> Mức độ cạnh tranh: cường độ cạnh tranh ngày càng gay gắt thì giá là yếu tố quan trọng để tăng cường cạnh tranh trên thị trường </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3353', 'Ảnh hưởng của nhóm yếu tố: Chiến lược.', null, null, '5', '0', '552', '467', '1552446851', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3354', null, null, null, '5', '3353', '552', '467', '1552446851', null, null, null, '<p>- Các chiến lược kinh doanh của doanh nghiệp khác nhau sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">định hướng</a> ổn định và </p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">thay đổi giá</a> khác nhau </p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Lựa chọn</a> thị trường mục tiêu </p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Định vị</a> sản phẩm </p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu định giá </a></p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">chương trình marketing </a></p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3355', 'Ảnh hưởng của nhóm yếu tố: Thương mại.', null, null, '5', '0', '552', '467', '1552446937', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3356', null, null, null, '5', '3355', '552', '467', '1552446937', null, null, null, '<p>- Nhấn mạnh đối với <a class=\"cloze\" href=\"#mce_temp_url#\">vai trò</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">quyền lực</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ </a>với các <a class=\"cloze\" href=\"#mce_temp_url#\">trung gian phân phối</a> </p>\r\n<p>công nghiệp trên thị trường đưa sản phẩm tới tay khách hàng b2b có thể ảnh hưởng </p>\r\n<p>tới việc <a class=\"cloze\" href=\"#mce_temp_url#\">định giá</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">điều tiết giá</a> của doanh nghiệp </p>\r\n<ul>\r\n<li>Sức mạnh trong kênh </li>\r\n<li>Vai trò của trung gian </li>\r\n<li>Mức lợi nhuận cận biên </li>\r\n</ul>\r\n<p> </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3357', 'Ảnh hưởng của nhóm yếu tố: Pháp luật.', null, null, '5', '0', '552', '467', '1552446972', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3358', null, null, null, '5', '3357', '552', '467', '1552446972', null, null, null, '<p>- Đề cập đến các <a class=\"cloze\" href=\"#mce_temp_url#\">quy định chính phủ</a> liên quan đến tới <a class=\"cloze\" href=\"#mce_temp_url#\">định giá</a> của doanh nghiệp </p>\r\n<p>nhằm điều tiết vĩ mô nền kinh tế. </p>\r\n<ul>\r\n<li>Thuế và lệ phí </li>\r\n<li> các điều luật </li>\r\n<li>Qua việc cung cấp các sản phẩm kinh doanh độc quyền của nhà nước </li>\r\n<li> Các cơ quan công quyền các cấp là một khách hàng lớn của doanh nghiệp. </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3359', 'Giá niêm yết và giá thanh toán.', null, null, '5', '0', '553', '467', '1552447069', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3360', null, null, null, '5', '3359', '553', '467', '1552447069', null, null, null, '<p>Giá niêm yết là <a class=\"cloze\" href=\"#mce_temp_url#\">giá cố định</a> mà công ty ghi trong báo giá chào hàng còn giá thanh toán là mức giá trên <a class=\"cloze\" href=\"#mce_temp_url#\">thực tế </a>khách hàng trả cho công ty </p>\r\n<p> </p>\r\n<p>Luôn tồn tại một sự khác biệt giữa giá niêm yết và giá thanh toán trong b2b, do bắt nguồn từ các biện pháp chiết giá mà công ty áp dụng cho khách hàng</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3361', 'Một số biện pháp giảm giá thông dụng.', null, null, '5', '0', '553', '467', '1552447174', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3362', null, null, null, '5', '3361', '553', '467', '1552447174', null, null, null, '<p>Giảm giá tiền mặt: là mức <a class=\"cloze\" href=\"#mce_temp_url#\">chiết giá</a> nhằm<a class=\"cloze\" href=\"#mce_temp_url#\"> khuyến kích </a>kho hàng trả sớm tiền hàng thanh toán</p>\r\n<p> </p>\r\n<ul>\r\n<li>Khuyến khích khách hàng trả ngay giảm rủi ro tín dụng và chi phí cho khoản thu quá hạn, thu hồi tiền mặt nhanh chóng</li>\r\n</ul>\r\n<p> </p>\r\n<p>Chiết giá thương mại: <a class=\"cloze\" href=\"#mce_temp_url#\">giảm giá chức năng</a>, tác dụng khi trung gian thực hiện hộ cho doanh nghiệp một phần chức năng nào đó hoặc căn cứ vào loại hình trung gian trong kênh</p>\r\n<p> </p>\r\n<p>Chiết giá số lượng: giảm giá cho khách hàng công nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">mua với số lượng lớn</a> nhất định. Tuần giảm giá được tính từ ngay hóa đơn</p>\r\n<p> </p>\r\n<p>Chiếc giá theo địa lý</p>\r\n<p> </p>\r\n<p>Giảm giá trả cuối kỳ: là một dạng đặc biệt của giảm giá theo số lượng, người mua nhận được phần giảm giá bằng tiền mặt ở cuối kỳ mua =&gt; khuyến khích khách hàng tập trung mua ở một nhà cung cấp</p>\r\n<p> </p>\r\n<p>Những cân nhắc về giá FOB: là những khuyến khích thúc đẩy khách hàng sử dụng giá FOB (giá mà khách hàng sẽ chịu giá gốc + cước phí</p>\r\n<p> </p>\r\n<p>Định giá tuyến (dòng sản phẩm)</p>\r\n<p> </p>\r\n<p>Là một sản phẩm có quan hệ chặt chẽ với nhau, do thực hiện một chức năng tương tự, được bán cho cùng một nhóm khách hàng, qua cùng một kênh hai <a class=\"cloze\" href=\"#mce_temp_url#\">thuộc cùng khung giá</a> nhất Định</p>\r\n<p> </p>\r\n<p>Định giá phân biệt</p>\r\n<p> </p>\r\n<p>Bán một sản phẩm hay dịch vụ theo <a class=\"cloze\" href=\"#mce_temp_url#\">nhiều mức giá</a> trong đó có sự khác biệt về giá không dựa theo sự khác biệt về chi phí</p>\r\n<p> </p>\r\n<p>Có ba cấp độ phân biệt</p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3363', 'Định giá bằng đấu thầu.', null, null, '5', '0', '554', '467', '1552447286', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3364', null, null, null, '5', '3363', '554', '467', '1552447286', null, null, null, '<p>Đấu thầu là <a class=\"cloze\" href=\"#mce_temp_url#\">hình thức phổ biến</a> trên thị trường b2b. </p>\r\n<p>- <a class=\"cloze\" href=\"#mce_temp_url#\">Đại diện bên mua</a> sẽ <a class=\"cloze\" href=\"#mce_temp_url#\">gửi các yêu cầu </a>chào hàng tới các nhà cung cấp phù hợp </p>\r\n<p class=\"paragraph\">+ <a class=\"cloze\" href=\"#mce_temp_url#\">Đấu thầu mở</a>: công khai trên các phương tiện truyền thông cho phép mọi nhà cung cấp tham gia </p>\r\n<p class=\"paragraph\">+ <a class=\"cloze\" href=\"#mce_temp_url#\">Đấu thầu hạn chế</a>: gửi trực tiếp mời thầu đến hạn chế một số nhà cung cấp tiềm năng theo danh sách nhất định </p>\r\n<p>- Người mua có thể mở thầu cho một sản phẩm hoặc cho một dòng sản phẩm, cho một dự án ngắn hạn hoặc các hợp đồng trong dài hạn. Có nhà thầu chính và nhà thầu phụ. muốn rảnh được hợp đồng và thắng thầu phải chấp nhận một mức giá thấp hơn so với đối thủ <br />- Tuy nhiên không phải lúc nào giá thấp cũng có khả năng thắng thầu, uy tín khả năng giao hạn đúng hạn, năng lực kinh doanh và thành tích trong quá khứ đều là những yêu cầu quan trọng <br />- Không thể định giá thấp hơn một mức nhất định vì: </p>\r\n<p class=\"paragraph\">+ Nếu nhỏ hơn chi phí ảnh hưởng xấu tới doanh nghiệp </p>\r\n<p class=\"paragraph\">+ nếu đặt giá cao hơn chi phí của mình càng nhiều thì càng ít có cơ hội giành được hợp đồng </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3365', 'Định giá bằng đàm phán.', null, null, '5', '0', '554', '467', '1552447361', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3366', null, null, null, '5', '3365', '554', '467', '1552447361', null, null, null, '<p>- Quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">tìm kiếm </a>mức giá dựa trên sự thống nhất <a class=\"cloze\" href=\"#mce_temp_url#\">thỏa mãn lợi ích</a> của cả bên mua và bên bán <br />- Các mức giá hình thành trong b2b chủ yếu là <a class=\"cloze\" href=\"#mce_temp_url#\">mức giá đàm phán</a> giữa bên mua và bên bán <br />- Thường diễn ra trong tình huống <a class=\"cloze\" href=\"#mce_temp_url#\">mua hàng phức tạp</a> mà cả người mua và người bán đều có những kiến nghị và phản kháng trước khi thống nhất được một mức giá hoặc tình huống mua lần đầu <br />- Giá thương lượng hình thành <a class=\"cloze\" href=\"#mce_temp_url#\">tùy theo đặc điểm</a> của chiến lược đàm phán </p>\r\n<p class=\"paragraph\">+ Đảm bảo cạnh tranh </p>\r\n<p class=\"paragraph\">+ Đàm phán cộng tác </p>\r\n<p class=\"paragraph\">+ Đàm phán thích ứng </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3367', 'Định giá bằng thuê mướn kỳ hạn.', null, null, '5', '0', '554', '467', '1552447445', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3368', null, null, null, '5', '3367', '554', '467', '1552447445', null, null, null, '<p>- Là giải pháp mà doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">đi thuê</a> tài sản, hàng hóa, thiết bị vốn của nhà sản xuất công nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">theo một kỳ hạn</a> nhất định thay thế cho giải pháp mua đứt hàng hóa đó <br />- Có hai loại thuê mướn kỳ hạn </p>\r\n<p class=\"paragraph\">+ Thuê mướn kỳ hạn tài chính </p>\r\n<p class=\"paragraph\">+ Thuê mướn kỳ hạn hoạt động </p>\r\n<p>- Ưu điểm </p>\r\n<p class=\"paragraph\">+ Trên góc độ công ty cho thuê: là giải pháp <a class=\"cloze\" href=\"#mce_temp_url#\">thâm nhập thị trường</a> mới mở rộng cơ hội thu về lợi nhuận </p>\r\n<p class=\"paragraph\">+ Trên góc độ công ty đi thuê: không phải bỏ nhiều tiền để mua không phải trả lãi và chi phí hoạt động, tiết kiệm được thuế </p>\r\n<p>- Nhược điểm: khách hàng phải<a class=\"cloze\" href=\"#mce_temp_url#\"> trả nhiều </a>hơn so với số tiền mua đứt sản phẩm, không sở hữu sản phẩm đó </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3369', 'Bản chất của một khách hàng B2B.', null, null, '5', '0', '556', '467', '1552447710', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3370', null, null, null, '5', '3369', '556', '467', '1552447710', null, null, null, '<ul>\r\n<li>Bản chất của một khách hàng b2b thể hiện qua hai khía cạnh : \r\n<ul>\r\n<li> Các<a class=\"cloze\" href=\"#mce_temp_url#\"> cấp độ </a>của <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ </a>khách hàng </li>\r\n<li>Các vấn đề chủ yếu về <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ </a>giữa <a class=\"cloze\" href=\"#mce_temp_url#\">người bán</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> người mua </a>trên thị trường B2B </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3371', 'Các cấp độ của mối quan hệ khách hàng.', null, null, '5', '0', '556', '467', '1552447807', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3372', null, null, null, '5', '3371', '556', '467', '1552447807', null, null, null, '<p>1.Các cấp độ của mối quan hệ khách hàng </p>\r\n<ul>\r\n<li>Các doanh nghiệp có thể <a class=\"cloze\" href=\"#mce_temp_url#\">xấy dựng</a> mối quan hệ khách hàng ở<a class=\"cloze\" href=\"#mce_temp_url#\"> nhiều cấp độ </a>tùy thuộc vào <a class=\"cloze\" href=\"#mce_temp_url#\">bản chất</a> của <a class=\"cloze\" href=\"#mce_temp_url#\">khách hàng mục tiêu </a></li>\r\n<li> Tại một cực: doanh nghiệp với nhiều khách hàng mang lại lợi nhuận thấp có thể tìm kiếm cách thức để <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển mối quan hệ</a> nền tảng với họ- Khách hàng tìm kiếm đa dạng \r\n<ul>\r\n<li>Tạo mối quan hệ thốn qua quảng cáo xây dựng thương hiệu, PR, web.. </li>\r\n</ul>\r\n</li>\r\n<li>Tại một cực khác: Doanh nghiệp bán sản phẩm cho 1 số khách hàng với mức lợi nhuận cao và mong muốn mối quan hệ với khách hàng thông qua h<a class=\"cloze\" href=\"#mce_temp_url#\">ợp tác chặt chẽ</a> với <a class=\"cloze\" href=\"#mce_temp_url#\">khách hàng thân thiết</a> - Khách hàng thân thiết \r\n<ul>\r\n<li>Tạo mối quan hệ với khách hàng thông qua hợp tác chặt chẽ với người mua và các nhà phân phối </li>\r\n</ul>\r\n</li>\r\n<li>Giữa hai thái cực tồn tại nhiều<a class=\"cloze\" href=\"#mce_temp_url#\"> mức độ khác nhau</a> của mối quan hệ khách hàng </li>\r\n<li>Yếu tố chính làm chuyển dịch giữa các mức độ khác nhau: \r\n<ul>\r\n<li>Chi phí chuyển đổi </li>\r\n<li>giúp cho khách hàng tăng trưởng lợi nhuận </li>\r\n<li> đổi lại: các nhà cung cấp (doanh nghiệp) được đảm nảo doanh thi và gia tăng hiểu biết về nhu cầu khách hàng và một mối quan hệ chặt chẽ trong tương lai </li>\r\n</ul>\r\n</li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3373', 'Các vấn đề chủ yếu về mối quan hệ người bán và người mua.', null, null, '5', '0', '556', '467', '1552447924', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3374', null, null, null, '5', '3373', '556', '467', '1552447924', null, null, null, '<p>Các vấn đề chủ yếu về mối quan hệ người bán và người mua </p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mối quan hệ</a> giữa<a class=\"cloze\" href=\"#mce_temp_url#\"> người bán</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">người mua</a> trên thị trường b2b là sự hợp tác nhằm tạo ra thị thế mạnh có lợi cho cả hai bên </li>\r\n</ul>\r\n<ul>\r\n<li>Cung cấp vật tư với <a class=\"cloze\" href=\"#mce_temp_url#\">giá thành thấp </a></li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">giao hàng nhanh hơn </a></li>\r\n<li> sản phẩm có <a class=\"cloze\" href=\"#mce_temp_url#\">chất lượng </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">độ tin cậy</a> cao hơn </li>\r\n<li>Tính năng sản phẩm được tùy chọn <a class=\"cloze\" href=\"#mce_temp_url#\">theo yêu cầu</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">điều khoản ưu đãi</a> hơn. </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Niềm tin và thủ tục</a>:Phản ảnh mức quan hệ trong phạm vi và giới hạn của hợp đồng kí kết \r\n<ul>\r\n<li>chịu ảnh hưởng bởi văn hóa kinh doanh trong bối cảnh toàn cầu </li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Quyền lực</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ</a> phụ thuộc: Nếu một bên phụ thuộc nhiều vào bên kia thì bên đó phải tuân theo các thỏa thuận theo ý muốn của bên kia trong quan hệ. </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mức độ gắn kết</a>: Càng nhiều điểm tương tác tồn tại giữa người mua và ngươi bán thì mức độ gắn kết trong quan hệ càn phát sinh và cùng lúc mối quan hệ càng thêm chặt chẽ hơn. </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mối quan hệ với người bán</a>( nhà cung cấp) liên quan tới vấn đề hợp tác lẫn nhau. Mối quan hệ cũng phát sinh từ đó. </li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Mâu thuẫn và hợp tác</a>: Trái với gắn kết, mâu thuẫn là không thể tránh khỏi khi hai doanh nghiệp khác nhau hợp tác về mục đích, nền tảng và lịch trình hợp tác với nhau. \r\n<ul>\r\n<li>Nếu mâu thuẫn có thể giải quyết tốt đẹp thì kết quả sau cùng là hợp tác </li>\r\n</ul>\r\n</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Thích ứng</a>: Là kết quả lâu dài của giải quyết mâu thuẫn. Khi mối quan hệ phát triển, hai bên </li>\r\n<li>Cần thích ứng để sự hợp tác tái trở lên tốt đẹp hơn. </li>\r\n</ul>\r\n<p>Quan hệ được xem như đầu tư (dài hạn): Mối bên đều cần phải nỗ lực để cùng nhau cóđược lợi ích lâu dài </p>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3375', 'Quan điểm cơ bản về CRM.', null, null, '5', '0', '557', '467', '1552447965', null, null, null, null, null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3376', null, null, null, '5', '3375', '557', '467', '1552447965', null, null, null, '<ul>\r\n<li>Crm như một <a class=\"cloze\" href=\"#mce_temp_url#\">giải pháp công nghệ</a> </li>\r\n<li>Crm như là một <a class=\"cloze\" href=\"#mce_temp_url#\">quy trình</a> kinh doanh hay bán hàng </li>\r\n<li>CRV là một <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> kinh doanh của doanh nghiệp </li>\r\n</ul>', null, '63', null, null, null, null, null);
INSERT INTO `question` VALUES ('3377', 'Vai trò của CRM.', null, null, '5', '0', '557', '467', '1552448014', null, null, null, null, null, '63', null, null, null, null, null);
