INSERT INTO `question` VALUES ('3754', null, null, null, '5', '3753', '359', '450', '1552483843', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {{x^2}{y^2} - 1} \\right)dy + 2{\\rm{x}}{y^3}d{\\rm{x}} = 0\\)</span> là<span class=\"math-tex\">\\(Cy = 1 + {(\\square)^2}\\)</span>     <a class=\"clozetip\" title=\"đặt  y=1/z thế vào phương trình đã cho rồi đặt   z=ux→dz=udx+xdu thế vào phương trình \" href=\"#\">xy</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3756', null, null, null, '5', '3755', '359', '450', '1552483843', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\left( {x - y + 4} \\right)dy + \\left( {x + y - 2} \\right)d{\\rm{x}} = 0\\)</span>  là<span class=\"math-tex\">\\({x^2} + 2{\\rm{x}}y - {y^2} - \\square +\\square+ C = 0\\)</span>      <a class=\"clozetip\" title=\"đặt x=u-1 và v=v+3, được (u+v)du+(u-v)dv=0→đây là phương trình vi phân cấp 1 đẳng cấp \" href=\"#\">4x,8y</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('3757', 'Khái niệm chào hàng  thị trường?', null, null, '5', '0', '472', '463', '1552484018', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('3758', null, null, null, '5', '3757', '472', '463', '1552484018', null, null, null, '<p><strong>Chào hàng thị trường </strong>: sự <a class=\"cloze\" href=\"#mce_temp_url#\">kết hợp</a> sản phẩm , dịch vụ , <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> và kinh nghiệm cống hiến cho <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường </a>nhằm thỏa mãn mong muốn hay yêu cầu của khách hàng .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('3759', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình đẳng cấp cấp 1(điền theo thứ tự ngăn cách nhau bằng dấu phẩy).', '', null, '5', '0', '359', '450', '1552484067', null, null, null, null, null, '51', null, null, null, null, null);
