INSERT INTO `question` VALUES ('4061', ' Câu 7: Nội dung và mối quan hệ giữa các giai đoạn trong quá trình bán hàng của DN? Bình luận ý kiến: “ kết cục tốt nhất của bán hàng là theo kiểu thắng- thắng” ', null, null, '2', '0', '609', '444', '1552550346', null, null, null, null, null, '45', null, null, null, null, null);
INSERT INTO `question` VALUES ('4062', ' Câu 8: So sánh cách hình thức bán hàng chủ yếu trong DN? Vì sao trong kinh doanh hiện đại các DN thường tăng cường cách hình thức bán hàng trực tiếp?', null, null, '2', '0', '609', '444', '1552550346', null, null, null, null, null, '45', null, null, null, null, null);
INSERT INTO `question` VALUES ('4063', 'Liên hệ bản thân', null, null, '5', '0', '519', '45', '1552550492', '1552551741', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4064', null, null, null, '5', '4063', '519', '45', '1552550492', '1552551741', null, null, '<p>+  Sinh viên cần đẩy mạnh việc <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">trau dồi </span></strong></a>những <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">phẩm chất đạo đức</span></strong></a> cách mạng</p>\r\n<p>+  Rất nhiều bạn sinh viên đang có hoàn cảnh khó khăn, chúng ta có thể <a class=\"cloze\" href=\"#mce_temp_url#\">tìm hiểu</a> và <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span style=\"text-decoration: underline;\">giúp đỡ</span></strong></a> nhau qua các <a class=\"cloze\" href=\"#mce_temp_url#\">hành động</a> cụ thể: quyên góp ủng hộ bạn, giới thiệu việc làm thêm v.v. Rất nhiều cảnh đời xung quanh chúng ta cần được <a class=\"cloze\" href=\"#mce_temp_url#\">cảm thông</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">chia sẻ</a>. Sự giúp đỡ của chúng ta đối với họ không chỉ có ý nghĩa về mặt <a class=\"cloze\" href=\"#mce_temp_url#\">vật chất</a> mà quan trọng hơn là động viên họ về mặt <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần</a>, để họ có thêm <a class=\"cloze\" href=\"#mce_temp_url#\">nghị lực</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tình yêu</a> vào cuộc sống.</p>\r\n<p>+  <strong><span style=\"text-decoration: underline;\">Học tập đạo đức</span></strong> của Bác Hồ, chúng ta còn có thể học tập qua chính những tấm gương thầy cô, bè bạn xung quanh. Và hãy để việc học tập đó đi vào chính cuộc sống hàng ngày của thanh niên, sinh viên, chứ không phải là những hoạt động có tính chất <a class=\"cloze\" href=\"#mce_temp_url#\">phong trào</a>. Bởi học tập, làm theo tấm gương đạo đức Hồ Chí Minh là để giúp chúng ta <a class=\"cloze\" href=\"#mce_temp_url#\">sống tốt hơn</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đẹp hơn</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4067', ' Câu 9: Sự ảnh hưởng của các nhân tố chủ quan đến tiêu thụ hàng hóa của DN ?', null, null, '2', '0', '610', '444', '1552550720', null, null, null, null, null, '45', null, null, null, null, null);
