INSERT INTO `question` VALUES ('4087', 'Ảnh hưởng của quê hương và gia đình đến sự hình thành TTHCM? Lý do HCM quyết định đi sang phương Tây để tìm đường cứu nước giải phóng dân tộc?', null, null, '2', '0', '228', '51', '1552556632', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4088', 'SV học tập và làm theo tấm gương đạo đức HCM?', null, null, '5', '0', '615', '45', '1552557428', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4089', null, null, null, '5', '4088', '615', '45', '1552557428', null, null, null, '<p>- <strong><span style=\"text-decoration: underline;\">Học trung với nước, hiếu với dân</span></strong>, suốt đời <a class=\"cloze\" href=\"#mce_temp_url#\">đấu tranh </a>cho sự nghiệp GPDT, GPGC, GP con người.</p>\r\n<p>   +  Chủ tịch HCM là con người VN <a class=\"cloze\" href=\"#mce_temp_url#\">đẹp nhất</a> và là một trong những con người đẹp nhất của thời đại chúng ta.</p>\r\n<p>   +  Ngay từ nhỏ, HCM đã chọn một cách rõ rang và dứt khoát mục tiêu <a class=\"cloze\" href=\"#mce_temp_url#\">hiến dâng cả cuộc đời</a> cho CM; chấp nhận <a class=\"cloze\" href=\"#mce_temp_url#\">hi sinh</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">luôn kiên định</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">dũng cảm</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">sáng suốt</a> để vượt qua khó khăn, gian khổ.</p>\r\n<p>   +  <a class=\"cloze\" href=\"#mce_temp_url#\">Tấm gương</a> vì nước, vì dân, suốt đời đấu tranh cho sự nghiệp GPDT, GPXH, GP con người của HCM đã được ND TG và bạn bè quốc tế <a class=\"cloze\" href=\"#mce_temp_url#\">thừa nhận</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">kính phục</a>.</p>\r\n<p>- <strong><span style=\"text-decoration: underline;\">Học cần, kiệm, liêm, chính chí công vô tư</span></strong>, đời riêng <a class=\"cloze\" href=\"#mce_temp_url#\">trong sáng</a>, nếp sống <a class=\"cloze\" href=\"#mce_temp_url#\">giản dị</a> và đức khiêm tốn <a class=\"cloze\" href=\"#mce_temp_url#\">phi thường</a>.</p>\r\n<p>   +  HCM thường dạy cán bộ, đảng viên phải cần, kiệm, liêm, chính, chí công vô tư, ít <a class=\"cloze\" href=\"#mce_temp_url#\">lòng ham muốn</a> vật chất, đó là tư cách người cán bộ CM và tự mình, Người đã gương mẫu thực hiện.</p>\r\n<p>   +  HCM luôn coi khinh <a class=\"cloze\" href=\"#mce_temp_url#\">sự xa hoa</a>, giữ nếp sống <a class=\"cloze\" href=\"#mce_temp_url#\">thanh bạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tao nhã</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">giản dị</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">khiêm tốn</a></p>\r\n<p>- <strong><span style=\"text-decoration: underline;\">Học đức tin <a class=\"cloze\" href=\"#mce_temp_url#\">tuyệt đối</a> vào <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh</a> của ND, <a class=\"cloze\" href=\"#mce_temp_url#\">kính trọng</a> ND và <a class=\"cloze\" href=\"#mce_temp_url#\">hết lòng</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">hết sức phục vụ</a> ND</span></strong>; luôn <a class=\"cloze\" href=\"#mce_temp_url#\">nhân ái</a>, vị tha, khoan dung và nhân hậu với con người.</p>\r\n<p>   +  HCM có tình thương yêu <a class=\"cloze\" href=\"#mce_temp_url#\">bao la</a> đối với con người, tình thương đó <a class=\"cloze\" href=\"#mce_temp_url#\">gắn liền với niềm tin</a> tuyệt đối vào sức mạnh và trí tuệ của ND</p>\r\n<p>   +  Phê phán quyết liệt đầu óc “<a class=\"cloze\" href=\"#mce_temp_url#\">quan cách mạng</a>” và tự mình, Người thường xuyên xuống cơ sở để tìm hiểu, <a class=\"cloze\" href=\"#mce_temp_url#\">lắng nghe ý kiến của dân</a>; luôn tâm niệm suốt đời là <a class=\"cloze\" href=\"#mce_temp_url#\">công bộc </a>của ND</p>\r\n<p>   +  Lòng nhân ái, khoan dung, nhân hậu của HCM <a class=\"cloze\" href=\"#mce_temp_url#\">bắt nguồn</a> từ đại nghĩa DT, nên có <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh</a> và sự <a class=\"cloze\" href=\"#mce_temp_url#\">cảm hóa</a> to lớn trong việc XD và tái tạo lương tri; biểu hiện chủ nghĩa nhân văn CS, vừa thánh thiện, vừa gần gũi</p>\r\n<p>- <strong><span style=\"text-decoration: underline;\">Học tấm gương về <a class=\"cloze\" href=\"#mce_temp_url#\">ý chí </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">nghị lực</a> tinh thần to lớn, quyết tâm vượt qua mọi thử thách</span></strong>, gian nguy để đạt được mục đích cuộc sống</p>\r\n<p>   +  <a class=\"cloze\" href=\"#mce_temp_url#\">Cuộc đời</a> CM của HCM là một chuỗi những năm tháng vô cùng <a class=\"cloze\" href=\"#mce_temp_url#\">gian khổ</a>. Song, nhờ ý chí và nghị lực tinh thần to lớn, HCM đã bình tĩnh, kiên cường, chủ động vượt qua, bảo vệ chân lí, giữ vững quan điểm CM.</p>\r\n<p>   +  Dũng cảm, quyết tâm, bền bỉ, bất khuất là những <a class=\"cloze\" href=\"#mce_temp_url#\">đặc trưng trong nhân cách</a> HCM</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4090', 'Hoạt động của HCM thời kì 1911-1920?', null, null, '2', '0', '230', '51', '1552557438', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4091', 'TTHCM về vai trò của con người và chiến lược “trồng người”', null, null, '2', '0', '616', '45', '1552558236', '1552665497', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4092', 'TTHCM về vai trò của con người:', null, null, '5', '0', '616', '45', '1552558791', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4093', null, null, null, '5', '4092', '616', '45', '1552558791', null, null, null, '<p>- Con người là <a class=\"cloze\" href=\"#mce_temp_url#\">vốn quý nhất</a>, <strong><span style=\"text-decoration: underline;\">nhân tố <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định thành công</a> của sự nghiệp CM</span></strong></p>\r\n<p>   + ND sáng tạo ra mọi giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">vật chất</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần</a>. HCM tổng kết ngắn gọn: Dân ta tốt lắm. Dân ta là tài năng, trí tuệ và sáng tạo, họ biết giải quyết nhiều vấn đề một cách <a class=\"cloze\" href=\"#mce_temp_url#\">đơn giản</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">mau chóng</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đầy đủ</a>, đặc biệt là lòng sốt sắng, hăng hái của dân để thực hiện con đường CM.</p>\r\n<p>   + ND là <a class=\"cloze\" href=\"#mce_temp_url#\">yếu tố quyết định</a> thành công của CM</p>\r\n<p>- Con người <strong><span style=\"text-decoration: underline;\">vừa là <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a>, vừa là <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> CM</span></strong>; phải coi trọng, chăm sóc, phát huy nhân tố <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a></p>\r\n<p>   + ND vừa là mục tiêu, vừa là động lực của CM.</p>\r\n<p>   + Con người là mục tiêu của CM nên <strong><span style=\"text-decoration: underline;\">mọi <a class=\"cloze\" href=\"#mce_temp_url#\">chủ trương</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đường lối</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">chính sách</a> của Đảng, CP đều vì lợi ích chính đáng của con người</span></strong>. Đảng là <a class=\"cloze\" href=\"#mce_temp_url#\">lãnh đạo</a> nhưng ND là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ</a>. Dân như nước, bộ đội như cá, lực lượng bao nhiêu là nhờ ở dân hết. <a class=\"cloze\" href=\"#mce_temp_url#\">Chống các bệnh</a>: xa ND, khinh ND, sợ ND; <a class=\"cloze\" href=\"#mce_temp_url#\">không tin cậy</a> ND; <a class=\"cloze\" href=\"#mce_temp_url#\">không hiểu biết</a> ND; <a class=\"cloze\" href=\"#mce_temp_url#\">không yêu thương</a> ND.</p>\r\n<p>   + Con người là động lực CM được nhìn nhận trên phạm vi cả nước, toàn thể đồng bào, nhưng trước hết ở <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">nông dân</a>. Không phải mọi con người đều trở thành động lực mà phải là những con người được <a class=\"cloze\" href=\"#mce_temp_url#\">giác ngộ</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a></p>\r\n<p>   + Con người là động lực chỉ có thể thực hiện được khi <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động có tổ chức</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">có lãnh đạo</a>. Vì vậy, cần có sự lãnh đạo của ĐCSVN.</p>\r\n<p>   + <strong><span style=\"text-decoration: underline;\">Kiên quyết khắc phục</span></strong> <a class=\"cloze\" href=\"#mce_temp_url#\">kịp thời</a> các phản động lực trong con người và tổ chức. Đó là <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cá nhân</a>.</span></strong></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4094', 'Giá trị của TTHCM đối với Cách mạng Việt Nam?', null, null, '2', '0', '231', '51', '1552559056', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4095', 'Nội dung quan điểm “giải phóng dân tộc là vấn đề trên hết, trước hết, độc lập dân tộc phải gắn liền với Chủ nghĩa xã hội” của HCM?', null, null, '2', '0', '233', '51', '1552559424', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4096', 'Chiến lược “trồng người”', null, null, '5', '0', '616', '45', '1552559567', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4097', null, null, null, '5', '4096', '616', '45', '1552559567', null, null, null, '<p>-    “<strong><span style=\"text-decoration: underline;\">Trồng người</span></strong>” là yêu cầu <a class=\"cloze\" href=\"#mce_temp_url#\">khách quan</a>, <strong><span style=\"text-decoration: underline;\">vừa <a class=\"cloze\" href=\"#mce_temp_url#\">cấp bách</a>, vừa <a class=\"cloze\" href=\"#mce_temp_url#\">lâu dài</a> của CM.</span></strong></p>\r\n<p>   + Trên cơ sở khẳng định con người vừa là <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a>, vừa là <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> CM, HCM rất quan tâm đến <a class=\"cloze\" href=\"#mce_temp_url#\">sự nghiệp</a> GD, đào tạo, rèn luyện con người.</p>\r\n<p>   + Con người phải được đặt vào vị trí <a class=\"cloze\" href=\"#mce_temp_url#\">trung tâm</a> của <a class=\"cloze\" href=\"#mce_temp_url#\">sự phát triển</a>. Nó vừa nằm trong chiến lược phát triển <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế - xã hội</a> của đất nước với nghĩa <a class=\"cloze\" href=\"#mce_temp_url#\">rộng</a>, vừa nằm trong chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">giáo dục và đào tạo</a> theo nghĩa <a class=\"cloze\" href=\"#mce_temp_url#\">hẹp</a>.</p>\r\n<p>-    “Muốn XD CNXH, trước hết cần có những con người XHCN”.</p>\r\n<p>   + <span style=\"text-decoration-line: underline;\"><strong><span>Con người XHCN phải do CNXH tạo ra</span></strong></span>. Phải đặt nhiệm vụ <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng con người</a> mới xã hội chủ nghĩa, làm gương, lôi cuốn xã hội.</p>\r\n<p>   + Mỗi bước xây dựng những con người như vậy là một <a class=\"cloze\" href=\"#mce_temp_url#\">nấc thang</a> xây dựng chủ nghĩa xã hội.</p>\r\n<p>   + Quan niệm của HCM về con người mới xã hội chủ nghĩa có 2 mặt gắn bó <a class=\"cloze\" href=\"#mce_temp_url#\">chăt chẽ</a> với nhau. Một là, <a class=\"cloze\" href=\"#mce_temp_url#\">kế thừa</a> những giá trị tốt đẹp của con người <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống</a>. Hai là, <a class=\"cloze\" href=\"#mce_temp_url#\">hình thành</a> những phẩm chất mới như: có tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội chủ nghĩa</a>; có đạo đức <a class=\"cloze\" href=\"#mce_temp_url#\">xã hội chủ nghĩa</a>; có trí tuệ bản lĩnh để làm chủ;...  </p>\r\n<p>-     Chiến lược “trồng người” là một <a class=\"cloze\" href=\"#mce_temp_url#\">trọng tâm</a>, một <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">bộ phận hợp thành</a> của chiến lược phát triển KT – XH.</span></strong></p>\r\n<p>    + Để thực hiện <a class=\"cloze\" href=\"#mce_temp_url#\">trồng ngườ</a>i cần nhiều biện pháp, nhưng <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">giáo dục và đào tạo</a> là biện pháp quan trọng bậc nhất</span></strong> vì <a class=\"cloze\" href=\"#mce_temp_url#\">giáo dục tốt</a> tạo ra tính thiện và ngươc lại</p>\r\n<p>    + <a class=\"cloze\" href=\"#mce_temp_url#\">Nội dung</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phương pháp giáo dục</a> phải toàn diện cả về đức, trí, thể, mỹ, phải đặt đạo đức, lý tưởng và tình cảm cách mạng, <a class=\"cloze\" href=\"#mce_temp_url#\">lối sống xã hội chủ nghĩa</a> lên đầu.</p>\r\n<p>    + <strong><span style=\"text-decoration: underline;\">“Trồng người” là công việc “<a class=\"cloze\" href=\"#mce_temp_url#\">trăm năm</a>” không thể <a class=\"cloze\" href=\"#mce_temp_url#\">nóng vội</a> , </span></strong>không phải làm một lúc là xong cũng không phải <a class=\"cloze\" href=\"#mce_temp_url#\">tùy tiện</a>, đến đâu hay đến đó</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4098', 'Quan điểm của HCM về tính chất, nhiệm vụ, mục tiêu của Cách mạng giải phóng dân tộc?', null, null, '2', '0', '234', '51', '1552559943', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4099', 'Nhiệm vụ của TTHCM', null, null, '5', '0', '224', '45', '1552567533', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4100', null, null, null, '5', '4099', '224', '45', '1552567533', null, null, null, '<p>+ Cơ sở <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>hình thành TTHCM</span></strong></a></p>\r\n<p>+ Các <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>thời kỳ hình thành, phát triển</span></strong></a> của TTHCM</p>\r\n<p>+ Nội dung <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>bản chất cách mạng, khoa học, đặc điểm</span></strong></a> của các quan điểm trong hệ thống TTHCM</p>\r\n<p>+ Vai trò <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>nền tảng tư tưởng</span></strong></a>, kim chỉ nam cho hành động của TTHCM đối với CMVN</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\"> <strong><span>Quá trình nhận thức, vận dụng, phát triển</span></strong></a> TTHCM qua các giai đoạn CM của Đảng, Nhà nước ta</p>\r\n<p>+ Các giá trị tư tưởng, lý luận của HCM đối với kho tàng tư tưởng, lý luận CMTG của thời đại</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4101', 'Khái niệm TTHCM', null, null, '5', '0', '225', '45', '1552567622', '1552567659', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4102', null, null, null, '5', '4101', '225', '45', '1552567622', '1552567659', null, null, '<p>TTHCM là <a class=\"cloze\" href=\"#\"><strong><span>hệ thống các quan điểm</span></strong></a> toàn diện và sâu sắc về những vấn đề cơ bản của dân tộc Việt Nam từ dân tộc dânchủ nhân dân đến cách mạng chủ nghĩa xã hội; là kết quả của sự <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>vận dụng sáng tạo CNMLN</span></strong> </a>vào điều kiện cụ thể nước ta, đồng thời là sự <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>kết tinh tinh hoa VH dân tộc và trí tuệ thời đại</span></strong></a> nhằm GPDT, GPGC và GPCN</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4103', 'Ý nghĩa của TTHCM', null, null, '5', '0', '225', '45', '1552567894', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4104', null, null, null, '5', '4103', '225', '45', '1552567894', null, null, null, '<p>-    Nâng cao năng lực<a class=\"cloze\" href=\"#mce_temp_url#\"> <strong><span>tư duy lí luận</span></strong></a> và <a class=\"cloze\" href=\"#mce_temp_url#\"><strong><span>phương pháp công tác</span></strong></a></p>\r\n<p>     + Nâng cao nhận thức về <strong><span><a class=\"cloze\" href=\"#mce_temp_url#\">vai trò</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">vị trí</a> của TTHCM</span></strong>, làm cho TTHCM ngày càng giữ một vị trí <a class=\"cloze\" href=\"#mce_temp_url#\">quan trọng</a> trong đời sống tinh thần của <a class=\"cloze\" href=\"#mce_temp_url#\">giới trẻ</a></p>\r\n<p>     + Bồi dưỡng cho sinh viên <a class=\"cloze\" href=\"#mce_temp_url#\">lập trường</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">quan điểm</a> cách mạng, <strong><span>kiên định mục tiêu <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập dân tộc</a> gắn liền với <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa xã hội</a>.</span></strong></p>\r\n<p>     + Giúp sv <a class=\"cloze\" href=\"#mce_temp_url#\">tích cực</a>, chủ động trong đấu tranh phê phán những quan điểm <a class=\"cloze\" href=\"#mce_temp_url#\">sai trái</a>, <strong><span><a class=\"cloze\" href=\"#mce_temp_url#\">bảo vệ</a> chủ nghĩa Mác - Lênin , TTHCM, đường lối, chính sách, luật pháp của Đảng, nhà nước</span></strong></p>\r\n<p>-    Bồi dưỡng <a class=\"cloze\" href=\"#mce_temp_url#\">phẩm chất</a> đạo đức cách mạng và <strong><span>rèn luyện <a class=\"cloze\" href=\"#mce_temp_url#\">bản lĩnh chính trị</a></span></strong></p>\r\n<p>     +  Nâng cao <a class=\"cloze\" href=\"#mce_temp_url#\">lòng tự hào</a> về “Anh hùng dân tộc”, “Nhà văn hóa kiệt xuất”, từ đó <strong><span>“Sống, chiến đấu và lao động theo gương Bác Hồ vĩ đại”</span></strong></p>\r\n<p>     +  Phát triền <a class=\"cloze\" href=\"#mce_temp_url#\">niềm tự hào</a> dân tộc, có <a class=\"cloze\" href=\"#mce_temp_url#\">niềm tin</a> vào Đảng trong tương lai</p>\r\n<p>     +  Có khả năng <strong><span><a class=\"cloze\" href=\"#mce_temp_url#\">vận dụng</a> các kiến thức vào cuộc sống</span></strong>; tu dưỡng, rèn luyện bản thân để hoàn thành tốt nhiệm vụ của mình, có sự <a class=\"cloze\" href=\"#mce_temp_url#\">đóng góp</a> thiết thực, hiệu quả vào sự nghiệp cách mạng theo con đường HCM và Đảng đã chọn</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4105', 'trình bày / phân tích  có so sánh quá trình marketing theo cách tiếp cận truyền thống và hiện đại?', null, null, '5', '0', '617', '463', '1552568431', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4106', null, null, null, '5', '4105', '617', '463', '1552568431', null, null, null, '<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p align=\"center\"><strong>Marketing cổ điển</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p align=\"center\"><strong>Marketing hiện đại</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        <em><a class=\"cloze\" href=\"#mce_temp_url#\">Sản xuất</a> xong rồi đi tìm thị trường</em>.</p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Sản xuất</a> là khâu <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định</a> toàn bộ <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường</a> tái sản xuất , các biện pháp đều nhằm mục tiêu bán được những hàng hóa đã sản xuất ra( bán cái đã có)</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        <em><a class=\"cloze\" href=\"#mce_temp_url#\">Nghiên cứu</a> thị trường rồi mới đi sản xuất</em> :</p>\r\n<p><a class=\"cloze\" href=\"#mce_temp_url#\">Thị trường</a> là nơi <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định</a> toàn bộ quá trình tái <a class=\"cloze\" href=\"#mce_temp_url#\">sản xuất </a>hàng hóa , người mua <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> có vai trò quyết định.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        <em>Hoạt động marketing <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> mang tính hệ thống:</em></p>\r\n<p>+chỉ nắm một khâu trong quá trình tái sản xuất</p>\r\n<p>+chỉ nghiên cứu một lĩnh vực kinh tế đang diễn ra</p>\r\n<p>+Chưa nghiên cứu được ý đồ cũng như dự báo tương lai.</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        <em>Hoạt động marketing mang tính hệ thống :</em></p>\r\n<p>+ Nghiên cứu tất cả các khâu trong quá trình sản xuất bắt đầu từ thị trường đến nơi sản xuất phân phối hàng hóa</p>\r\n<p>+ Nghiên cứu tất cả các lĩnh vực vĩ mô và vi mô.</p>\r\n<p>+Nghiên cứu được ý đồ và dự báo được tương lai.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        Tối đa hóa thị trường trên cơ sở tối đa hóa <a class=\"cloze\" href=\"#mce_temp_url#\">lượng</a> sản phẩm bán ra .</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-tối đa hóa trên cơ sở tiêu thụ tối đa <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> khách hàng.</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Không</a> có sự liên kết giữa các doanh nghiệp trong kinh doanh.</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        <a class=\"cloze\" href=\"#mce_temp_url#\">Có</a> sự liên kết giữa các doanh nghiệp trong kinh doanh</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        Giúp tiêu <a class=\"cloze\" href=\"#mce_temp_url#\">thụ nhanh</a> được hàng hóa sản xuất ra thông qua phân phối , quảng cáo và bán hàng.</p>\r\n</td>\r\n<td valign=\"top\" width=\"390\">\r\n<p>-        Nghệ thuật kết hợp vận dụng những nỗ lực nhằm khám phá , sáng tao, thỏa mãn và <a class=\"cloze\" href=\"#mce_temp_url#\">gợi mở </a>nhu cầu của khách hàng để tạo ra lợi nhuận.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4107', 'Trình bày  Giá trị chức năng;giá trị tâm lý;?', null, null, '5', '0', '618', '463', '1552568640', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4108', null, null, null, '5', '4107', '618', '463', '1552568640', null, null, null, '<p>+ <strong>Giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">chức năng</a></strong>: Là tiện ích xuất phát từ<a class=\"cloze\" href=\"#mce_temp_url#\"> chất lượng</a> và đặc tính được mong đợi của sản phẩm/dịch vụ và đối thủ dễ dàng bắt chước <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> tạo ra lợi thể</p>\r\n<p><strong>+Giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">tâm lý</a></strong><a class=\"cloze\" href=\"#mce_temp_url#\">:</a> Là giá trị tạo ra mỗi khi khách hàng cảm nhận học được chào đón,quan trọng…Khó sao chép(giá trị có thẻ tạo ra sự cảm nhận ncho khách hàng-giá trị bền vững)</p>\r\n<p>-        Kếtt luận: Khách hàng luôn tìm cách tối đa giá trị bằng cách:</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Đánh giá</a> cung ứng nào đèm lại nhiều giá trị nhất</p>\r\n<p>+Hình thành <a class=\"cloze\" href=\"#mce_temp_url#\">kì vọng</a> về giá trị và hành động theo chiều đó</p>\r\n<p>+Đánh giá <a class=\"cloze\" href=\"#mce_temp_url#\">trải nghiệm</a> sử dụng sản phẩm của họ so với kì vọng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4109', 'Mối quan hệ giữa giá trị cẩm nhận của khách hàng và sự thỏa mãn của khách hàng', null, null, '5', '0', '618', '463', '1552568794', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4110', null, null, null, '5', '4109', '618', '463', '1552568794', null, null, null, '<p>-        Khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">hài lòng</a> tiếp tục mua hàng và nói cho người khác biêt về sự trả nghiệm tốt của họ vè sản phẩm- <a class=\"cloze\" href=\"#mce_temp_url#\">Truyền miệng</a></p>\r\n<p>-        Để tối đa hóa sự thỏa mãn <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> nên phóng đại khả năng của sản phẩm/dịch vụ trong quản cáo và các phương tiện truyền thông khác</p>\r\n<p>-        Doanh nghiệp khi gặp <a class=\"cloze\" href=\"#mce_temp_url#\">bất lợi </a>nên <a class=\"cloze\" href=\"#mce_temp_url#\">giảm</a> cảm nhận về <a class=\"cloze\" href=\"#mce_temp_url#\">chi phí</a>, tăng cảm nhận về <a class=\"cloze\" href=\"#mce_temp_url#\">giá trị</a></p>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4111', 'Trình bày nội dung các yếu tố tạo nên thành công của các doanh nghiệp có hiệu năng cao', null, null, '5', '0', '619', '463', '1552569042', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4112', null, null, null, '5', '4111', '619', '463', '1552569042', null, null, null, '<p><span style=\"color: #ff0000;\"><strong>Mô hình</strong></span> (Tóm tắt nội dung chương)</p>\r\n<p><strong>1.<a class=\"cloze\" href=\"#mce_temp_url#\"> Giới hữu quan</a></strong></p>\r\n<p>-        Gồm: khách hàng,nhân viên, cồ đông, nhà cung cáp, các bên phân phối</p>\r\n<p>-        Doanh nghiệp cố gắng làm hài long ở mức tối thiểu sự kì ovngj của mổi bên hữu quan</p>\r\n<p><strong>2.<a class=\"cloze\" href=\"#mce_temp_url#\"> Các tiến trình</a></strong></p>\r\n<p>-        Một doanh nghiệp có thể hoàn thành mục đích thỏa mãn khách hàng bằng cách quản trị và liên kết các quy trình hoạt động</p>\r\n<p>+Phát triển sản phẩm mới</p>\r\n<p>+Thu hút và năm giữa khách hàng</p>\r\n<p>+Thiết kế luồng công việc</p>\r\n<p><strong><a class=\"cloze\" href=\"#mce_temp_url#\">3. Nguồn lực</a>:bao gồm</strong></p>\r\n<p>-        Nguồn nguyên vật liệu, máy móc, năng lượng và thông tin. Các nguồn lực này có thể sở hữu có thể thuê ngoài</p>\r\n<p>-        THuê ngoài</p>\r\n<p><strong>4. <a class=\"cloze\" href=\"#mce_temp_url#\">Tổ chức </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa tổ chức</a></strong>-nền tảng quan trọng nhất</p>\r\n<p>-        Tổ chức liên quan đến các chính sách, cáu trúc và văn hóa của tổ chức.Trong khi cơ cáu tổ chức và chính sách có thể thay đổi thì văn hóa doanh nghiệp lại có tính bền vững</p>\r\n<p>-        Văn hóa tổ chức: Chia sẻ kinh nghiệp, câu chuyện, niềm tin và các chuẩn mực trong tổ chức. Tạo môi trường văn hóa doanh nghiệp lành mạnh giúp hoạt động sáng tạo, giải quyest được những mối đe dọa.</p>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4113', 'Bước 1: Lựa chọn giá trị?', null, null, '5', '0', '620', '463', '1552569195', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4114', null, null, null, '5', '4113', '620', '463', '1552569195', null, null, null, '<p>-        Dự kiến kinh doanh trên<a class=\"cloze\" href=\"#mce_temp_url#\"> thị trường </a>nào?</p>\r\n<p>-        Công ty định nhắm vào tập hợp <a class=\"cloze\" href=\"#mce_temp_url#\">khách hàng</a> nào và cung ứng cho họ những hàng hóa dịch vụ gì, hàng hóa và dịch vụ có đặc điểm gì <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt</a> so với hàng hóa và dịch vụ của đối thủ cạnh tranh?</p>\r\n<p>=&gt;Để trả lời vẹn tròn những vấn đề đó doanh nghiệp phải tùy thuộc vào môi trường kinh doanh và khả năng và nguồn lực của mình.</p>\r\n<p>=&gt;Bước đầu tiên của quá trình cung ứng giá trị cho người tiêu dùng không phải là tạo ra hàng hóa theo chủ quan của các nhà kinh doanh mà là xác định chính xác nhu cầu và </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4115', 'Bước 2: Đảm bảo giá trị.?', null, null, '5', '0', '620', '463', '1552569303', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4116', null, null, null, '5', '4115', '620', '463', '1552569303', null, null, null, '<p>-        Những dự tính ở bước trên phải có <a class=\"cloze\" href=\"#mce_temp_url#\">tiền đề</a> để biến thành hiện thực, có nghĩa là công ty chuẩn bị điều kiện để đảm bảo giá trị.</p>\r\n<p>-        Phát triển sản phẩm và dịch vụ, điều quan trọng ở đây là tạo ra các <a class=\"cloze\" href=\"#mce_temp_url#\">đặc tính </a>và tính năng của sản phẩm hàng hóa,</p>\r\n<p>Doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\"> xác định</a> rõ các tính năng đó, để hoặc là <a class=\"cloze\" href=\"#mce_temp_url#\">tự </a>sản xuất; hoặc là đi<a class=\"cloze\" href=\"#mce_temp_url#\"> mua </a>từ bên ngoài.</p>\r\n<p>công ty phải định giá bán cho sản phẩm, phải tổ chức hệ thống phân phối và cung ứng để sẵn sàng phục vụ khách hàng mục tiêu.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4117', 'Khái niệm, ý nghĩa môn TTHCM', null, null, '2', '0', '225', '45', '1552569502', '1552569690', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4118', 'Bước 3: Thông báo và cung ứng giá trị.?', null, null, '5', '0', '620', '463', '1552569868', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4119', null, null, null, '5', '4118', '620', '463', '1552569868', null, null, null, '<p>-        Doanh nghiệp phải thông qua hoạt động <a class=\"cloze\" href=\"#mce_temp_url#\">quảng cáo</a>, khuyến mãi và thực hiện bán hàng cho người tiêu dùng.</p>\r\n<p>-        Doanh nghiệp phải luôn cân nhắc đến các<a class=\"cloze\" href=\"#mce_temp_url#\"> điều kiện</a> bên trong và bên ngoài doanh nghiệp chứ không thể quyết định chủ quan .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4120', 'Phương thức 1?', null, null, '5', '0', '621', '463', '1552569972', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4121', null, null, null, '5', '4120', '621', '463', '1552569972', null, null, null, '<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Thêm </a>các lợi ích tài chính cho mối quan hệ với khách hàng. <strong>Ví dụ</strong> các hãng hàng không có các chương trình thưởng cho khách hàng có chuyến bay thường xuyên.</p>\r\n<p>-        Tuy nhiên những chương trình này tạo cho khách hàng sự yêu thích nhưng lại dễ bị đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">bắt chước </a>vì vậy chúng không tạo ra sự khác biệt vĩnh viễn cho sản phẩm.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4122', 'Phương thức 2?', null, null, '5', '0', '621', '463', '1552570029', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4123', null, null, null, '5', '4122', '621', '463', '1552570029', null, null, null, '<p> </p>\r\n<p>-        Thêm các <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích Xã Hội</a> . Ở đây nhân viên của công ty tăng cường gắn bó về mặt XH với khách hàng bằng cách nắm chắc những nhu cầu và mong muốn cá nhân của họ và đảm bảo dv riêng từng cá nhân theo ý của họ. Họ biến các khách hàng thành những thân chủ.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4124', 'Phương thức 3.?', null, null, '5', '0', '621', '463', '1552570067', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4125', null, null, null, '5', '4124', '621', '463', '1552570067', null, null, null, '<p> </p>\r\n<p>-        Thêm các<a class=\"cloze\" href=\"#mce_temp_url#\"> ràng buộc</a> về mặt <a class=\"cloze\" href=\"#mce_temp_url#\">cấu trúc</a>. Công ty có thể cung ứng cho KH những thiết bị đặc chủng hay chương trình kết nối máy tính để giúp KH quản lý các đơn hàng, bảng lương, hàng dự trữ…</p>\r\n<p>-        <strong>Ví dụ</strong>: Công ty McKesson Corporation, một nhà bán sỉ dược phẩm hàng đầu, đã đầu tư hàng trăm triệu USD vào năng lực của hệ thống trao đổi dữ liệu điện tử để giúp các cửa hàng dược phẩm nhỏ quản lý số hàng dự trữ đơn hàng và không gian trưng bày.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4126', 'Hoạt động của HCM trong thời kì 1921-1930', null, null, '2', '0', '226', '45', '1552570174', '1552570284', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4127', 'Chuỗi giá trị chuỗi của các hoạt động?', null, null, '5', '0', '623', '463', '1552570475', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4128', null, null, null, '5', '4127', '623', '463', '1552570475', null, null, null, '<p> -<a class=\"cloze\" href=\"#mce_temp_url#\"> Sản phẩm</a> đi qua tất cả các<a class=\"cloze\" href=\"#mce_temp_url#\"> hoạt động</a> của các chuỗi theo<a class=\"cloze\" href=\"#mce_temp_url#\"> thứ tự</a> và tại mỗi hoạt động sản phẩm thu được một số giá trị nào đó . Chuỗi các hoạt động cung cấp cho các sản phẩm nhiều giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">gia tăng</a> hơn tổng giá trị gia tăng của tất cả các hoạt động cộng lại . </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4129', 'Mạng lưới phân phối giá trị cho khách hàng ( chuỗi cung ứng giá trị ) ?', null, null, '5', '0', '623', '463', '1552570533', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4130', null, null, null, '5', '4129', '623', '463', '1552570533', null, null, null, '<p>- được xây dựng từ <a class=\"cloze\" href=\"#mce_temp_url#\">chuỗi giá trị</a> của doanh nghiệp và các nhà cung cấp , phân phối và trên hết là khách hàng , tất cả <a class=\"cloze\" href=\"#mce_temp_url#\">hợp tác</a> với nhau mang lại giá trị cho khách hàng .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4131', 'Bản chất của Mạng lưới phân phối giá trị?', null, null, '5', '0', '623', '463', '1552570656', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4132', null, null, null, '5', '4131', '623', '463', '1552570656', null, null, null, '<p><strong>Bản chất của Mạng lưới phân phối giá trị :</strong> là sự <a class=\"cloze\" href=\"#mce_temp_url#\">hợp tác</a> cùng <a class=\"cloze\" href=\"#mce_temp_url#\">có lợi</a> giữa doanh nghiệp với nhà cung ứng và khách hàng để nhận được <a class=\"cloze\" href=\"#mce_temp_url#\">giá trị</a> gia tăng từ sản phẩm , hàng hóa </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4133', 'Đối tượng nghiên cứu và nhiệm vụ của môn TTHCM?', null, null, '2', '0', '215', '51', '1552570773', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4134', 'Quản trị quan hệ khách hàng ( CRM ) ?', null, null, '5', '0', '624', '463', '1552570895', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4135', null, null, null, '5', '4134', '624', '463', '1552570895', null, null, null, '<p><strong>Quản trị quan hệ khách hàng ( CRM )</strong> : một <a class=\"cloze\" href=\"#mce_temp_url#\">tiến trình</a> tổng quát của việc <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> và duy trì các <a class=\"cloze\" href=\"#mce_temp_url#\">mối quan hệ</a> khách hàng có lãi bằng việc cung cấp các <a class=\"cloze\" href=\"#mce_temp_url#\">giá trị</a> và sự <a class=\"cloze\" href=\"#mce_temp_url#\">thỏa mãn </a>cao hơn cho khách hàng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4136', 'các cấp độ marketing quan hệ của doanh nghiệp?', null, null, '5', '0', '624', '463', '1552570960', '1552571227', null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4137', null, null, null, '5', '4136', '624', '463', '1552570960', '1552571227', null, null, '<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"195\">\r\n<p> </p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>lợi nhuận biên thấp</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>                                                Lợi nhuận biên cao            </p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>lợi nhuận biên trung bình     </p>\r\n<p> </p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Số khách hàng và nhà phân phối nhiều</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing<a class=\"cloze\" href=\"#mce_temp_url#\"> tương tác</a></p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing <a class=\"cloze\" href=\"#mce_temp_url#\">phản ứng</a> lại</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing căn bản/ phản ứng lại</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Số khách hàng và nhà phân phối trung bình</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing phản ứng <a class=\"cloze\" href=\"#mce_temp_url#\">trước</a></p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing tương tác</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing phản ứng lại</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Số khách hàng và nhà phân phối ít</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing <a class=\"cloze\" href=\"#mce_temp_url#\">cộng tác</a></p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing phản ứng trước</p>\r\n</td>\r\n<td valign=\"top\" width=\"195\">\r\n<p>Marketing tương tác</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4138', 'Tiền đề tư tưởng - lí luận dẫn đến sự hình thành TTHCM', null, null, '5', '0', '227', '45', '1552571152', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4139', null, null, null, '5', '4138', '227', '45', '1552571152', null, null, null, '<p>-    Giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống</a> của dân tộc Việt Nam</p>\r\n<p>   +  Truyền thống <a class=\"cloze\" href=\"#mce_temp_url#\">yêu nước</a></p>\r\n<p>   +  Ý chí <a class=\"cloze\" href=\"#mce_temp_url#\">quật cường</a>, đấu tranh <a class=\"cloze\" href=\"#mce_temp_url#\">bất khuất</a></p>\r\n<p>   +  Tinh thần <a class=\"cloze\" href=\"#mce_temp_url#\">đoàn kết</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tương thân tương ái</a></p>\r\n<p>   +  Ý chí <a class=\"cloze\" href=\"#mce_temp_url#\">vươn lên</a> mọi khó khăn, thử thách</p>\r\n<p>   +  Thông minh, sáng tạo, khiêm tốn <a class=\"cloze\" href=\"#mce_temp_url#\">tiếp thu</a> văn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">nhân loại</a></p>\r\n<p>-    Tinh hoa văn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">nhân loại</a></p>\r\n<p>   +  Tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">Nho giáo</a>: <strong><span style=\"text-decoration: underline;\">triết lí <a class=\"cloze\" href=\"#mce_temp_url#\">hành động</a></span></strong>, tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">nhập thế</a>, lãnh đạo, giúp đời; là ước vọng về xã hội <a class=\"cloze\" href=\"#mce_temp_url#\">bình trị</a>, hòa đồng; là <strong><span style=\"text-decoration: underline;\">triết lí <a class=\"cloze\" href=\"#mce_temp_url#\">nhân sinh</a></span></strong>, tu thân <a class=\"cloze\" href=\"#mce_temp_url#\">dưỡng tính</a>, đề cao văn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">lễ giáo</a>, tạo ra truyền thống <a class=\"cloze\" href=\"#mce_temp_url#\">hiếu học</a>.</p>\r\n<p>   +  Tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">Phật giáo</a>: tư tưởng <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">vị tha</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">từ bi bác ái</a></span></strong>, thương người như thể thương thân; là nếp sống có <a class=\"cloze\" href=\"#mce_temp_url#\">đạo đức</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">trong sạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">giản dị</a>, chăm lo làm <a class=\"cloze\" href=\"#mce_temp_url#\">việc thiện</a>; là tinh thần <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">bình đẳng</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">dân chủ</a></span></strong>, chống phân biệt đẳng cấp; là việc <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">đề cao lao động</a>,</span></strong> chống lười biếng</p>\r\n<p>   +  Nền văn hóa và tư tưởng <a class=\"cloze\" href=\"#mce_temp_url#\">phương Tây</a>: tư tưởng về <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">tự do</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">bác ái</a></span></strong> qua các tác phẩm, giá trị của bản “Tuyên ngôn nhân quyền và dân quyền” của Pháp; các giá trị về <strong><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">quyền sống</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">quyền tự do</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">quyền mưu cầu hạnh phúc</a></span></strong> của “Tuyên ngôn độc lập” Mỹ.</p>\r\n<p>-    Chủ nghĩa Mác - Lênin:</p>\r\n<p>   +  Là <a class=\"cloze\" href=\"#mce_temp_url#\">cơ sở thế giới quan</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phương pháp luận</a> của TTHCM</p>\r\n<p>   +  Quá trình tiếp nhận Chủ nghĩa Mác - Lênin: Từ nhận thức <a class=\"cloze\" href=\"#mce_temp_url#\">ban đầu</a> đến nhận thức <a class=\"cloze\" href=\"#mce_temp_url#\">lí tính</a>, HCM tiếp thu một cách <a class=\"cloze\" href=\"#mce_temp_url#\">có chọn lọc</a>, phù hợp với <a class=\"cloze\" href=\"#mce_temp_url#\">điều kiện Việt Nam</a>. Người vận dụng lập trường, quan điểm, phương pháp biện chứng của Chủ nghĩa Mác - Lênin để giải quyết những vấn đề thực tiễn cách mạng Việt Nam.</p>\r\n<p>=&gt;  <strong><span style=\"text-decoration: underline;\">Chủ nghĩa Mác - Lênin là nhân tố quyết định</span></strong> trực tiếp đến<a class=\"cloze\" href=\"#mce_temp_url#\"> bản chất cách mạng</a> và khoa học của TTHCM</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4140', 'Khái niệm Cơ sở dữ liệu khách hàng?', null, null, '5', '0', '625', '463', '1552571356', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4141', null, null, null, '5', '4140', '625', '463', '1552571356', null, null, null, '<p>Cơ sở dữ  liệu khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">tập hợp </a>có <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> những <a class=\"cloze\" href=\"#mce_temp_url#\">dữ liệu</a> toàn diện về khách hàng hiện tại  tại mà có thể hoạt động để tạo ra , phân loại , bán sản phẩm hay dịch vụ , đồng thời duy trì mối quan hệ với khách hàng.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4143', null, null, null, '5', '4142', '625', '463', '1552571357', null, null, null, '<p>Cơ sở dữ  liệu khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">tập hợp </a>có <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> những <a class=\"cloze\" href=\"#mce_temp_url#\">dữ liệu</a> toàn diện về khách hàng hiện tại  tại mà có thể hoạt động để tạo ra , phân loại , bán sản phẩm hay dịch vụ , đồng thời duy trì mối quan hệ với khách hàng.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4144', 'vai trò của marketing cơ sở dữ liệu khách hàng ?', null, null, '5', '0', '625', '463', '1552571431', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4145', null, null, null, '5', '4144', '625', '463', '1552571431', null, null, null, '<p>-       <a class=\"cloze\" href=\"#mce_temp_url#\"> Cung cấp</a> hồ sơ đầy đủ về khách hàng để sử dụng trong bán hàng hay nghiên cứu thị trường , . . .</p>\r\n<p>-        Cho phép <a class=\"cloze\" href=\"#mce_temp_url#\">khai thác </a>các thông tin tại thị trường mục tiêu để phân đoạn tốt hơn qua những phản ứng đa dạng , phân tích và chủ động trước các phản ứng của khách hàng</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4146', 'Mối quan hệ giữa Cách mạng giải phóng dân tộc ở thuộc địa và Cách mạng vô sản ở chính quốc?', '', '', '1', '0', '236', '51', '1552571462', null, '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-size:14.0pt; line-height:130%;font-family:VNI-Lithos;mso-fareast-font-family:VNI-Lithos; mso-bidi-font-family:VNI-Lithos;mso-ansi-language:EN-US\"><span style=\"mso-list:Ignore\">-<span style=\"font:7.0pt &quot;Times New Roman&quot;\">&nbsp; &nbsp; &nbsp; &nbsp;</span></span></span><span style=\"font-size: 14pt; line-height: 130%;\"><font face=\"Times New Roman, serif\">- Mối liên hệ bình đẳng&nbsp;<br></font></span><span style=\"font-size: 14pt; text-indent: -0.25in;\"><font face=\"Times New Roman, serif\">-&nbsp;</font></span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">Khối liên minh các dân tộc thuộc địa&nbsp;<br></span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">- “Cách mạng thuộc địa không những không phụ thuộc vào Cách mạng vô sản ở chính quốc mà có thể giành thắng lợi trướ Cách mạng vô sản ở chính quốc”.<br></span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">- Thực tiễn thắng lợi của Cách mạng Việt Nam và sự sụp đổ của Chủ nghĩa đế quốc&nbsp;</span></p>', '', 'Mối quan hệ giữa Cách mạng giải phóng dân tộc ở thuộc địa và Cách mạng vô sản ở chính quốc?', '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-size:14.0pt; line-height:130%;font-family:VNI-Lithos;mso-fareast-font-family:VNI-Lithos; mso-bidi-font-family:VNI-Lithos;mso-ansi-language:EN-US\"><span style=\"mso-list:Ignore\">-<span style=\"font:7.0pt &quot;Times New Roman&quot;\">&nbsp; &nbsp; &nbsp;&nbsp;</span></span></span><span style=\"font-size: 14pt; line-height: 130%;\"><font face=\"Times New Roman, serif\">- Theo HCM, giữa Cách mạng giải phóng dân tộc ở thuộc địa và Cách mạng vô sản ở chính quốc có mối liên hệ mật thiết với nhau, có tác động qua lại lẫn nhau trong cuộc đấu tranh chống kẻ thù chung là Chủ nghĩa đế quốc. Đó là mối liên hệ bình đẳng chứ không phải là lệ thuộc.<br></font></span><span style=\"font-size: 14pt; text-indent: -0.25in;\"><font face=\"Times New Roman, serif\">-&nbsp;</font></span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">Khối liên minh các dân tộc thuộc địa sẽ là một trong những cải cách của Cách mạng thế giới. Nhân dân các dân tộc thuộc địa có khả năng cách mạng to lớn.<br></span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">- Người khẳng định: “Cách mạng thuộc địa không những không phụ thuộc vào Cách mạng vô sản ở chính quốc mà có thể giành thắng lợi trướ Cách mạng vô sản ở chính quốc”.<br></span><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">- Thực tiễn thắng lợi của Cách mạng Việt Nam và sự sụp đổ của Chủ nghĩa đế quốc đã chứng minh quan điểm của HCM là hoàn toàn đúng đắn.</span></p>', '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4147', 'Khái niệm phân tích marketing?', null, null, '5', '0', '626', '463', '1552571573', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4148', null, null, null, '5', '4147', '626', '463', '1552571573', null, null, null, '<p>Phân tích marketing <a class=\"cloze\" href=\"#mce_temp_url#\">tập hợp </a>các kỹ thuật <a class=\"cloze\" href=\"#mce_temp_url#\">thống kê </a>và các <a class=\"cloze\" href=\"#mce_temp_url#\">mô hình</a> để <a class=\"cloze\" href=\"#mce_temp_url#\">phân tích </a>các dữ liệu đã được thu thập về các<a class=\"cloze\" href=\"#mce_temp_url#\"> vấn đề </a>marketing nhằm tạo cơ sở cho các quyết định và điều hành kinh doanh hữu   hiệu hơn .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4149', 'Các công cụ phân tích marleting?', null, null, '5', '0', '626', '463', '1552571834', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4150', null, null, null, '5', '4149', '626', '463', '1552571834', null, null, null, '<p>-        Các công cụ <a class=\"cloze\" href=\"#mce_temp_url#\">thống kê</a></p>\r\n<p>+ Xác định mối quan hệ bên trong một tập hợp dữ liệu, độ tin cậy về mặt thông kê của chúng</p>\r\n<p>+Biến số chính nào đang ảnh hưởng đến doanh số và mức độ quan trọng của mối biến số?</p>\r\n<p>-        Các công cụ <a class=\"cloze\" href=\"#mce_temp_url#\">mô hình</a>:</p>\r\n<p>+Môi mô hình đại diện cho hệ thống tiến trình hay kết quả thực hiện nào đó: Là gì nếu?Cái gì là nên?</p>\r\n<p>-        Phân tích và sử dụng <a class=\"cloze\" href=\"#mce_temp_url#\">dữ liệu khách hàng</a></p>\r\n<p>+ Bào gồm hành động mua hàng của khách hàng, sự tiếp xúc của các lực lượng bán, ghé thăm website…</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4151', 'Các cách tiếp cận của HCM về Chủ nghĩa xã hội? HCM quan niệm về Chủ nghĩa xã hội ở VN như thế nào?', null, null, '2', '0', '237', '51', '1552571972', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4152', 'Nêu các nhóm thông tin thuộc môi trường marketing ?', null, null, '5', '0', '627', '463', '1552572741', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4153', null, null, null, '5', '4152', '627', '463', '1552572741', null, null, null, '<p>-        <em>Môi trường<a class=\"cloze\" href=\"#mce_temp_url#\"> nhân khẩu học</a></em> . ( sự bùng nổ dân số thế giới / cơ cấu dân số quyết định nhu cầu ví dụ Mexico trẻ , Nhật già / Thị trường dân tộc , ví dụ : Mỹ / Trình độ học vấn , ví dụ Mỹ là nước có tỷ lệ % công dân tốt nghiệp Đh cao nhất ( 20 % ) nghĩa là nhu cầu về sách rất lớn , về tạp chí và du lịch chất lượng cao / Các kiểu hộ gia đình ) .</p>\r\n<p>-        <em>Môi trường<a class=\"cloze\" href=\"#mce_temp_url#\"> kinh tế</a></em> . ( phân phối thu nhập / tiết kiệm , nợ , khả năng vay tiền )</p>\r\n<p>-        <em>Môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">tự nhiên</a> thiếu hụt nhiên liệu</em> – MKT bảo vệ mt / chi phí năng lượng tăng - các cty quan tâm đến sự biến động của xăng dầu / mức độ ô nhiễm tăng - tìm kiếm các phương thức sau và bao gói hàng hóa bảo vệ mt sự thay đổi vai trò của các chính phủ trong việc bảo vệ môi trường - các Cty nên nhận thức rõ trách nhiệm bảo vệ môi trường và giảm thiểu mức ô nhiễm . . . )</p>\r\n<p>-        <em>Môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">công nghệ </a></em>, sự tăng tốc của việc thay đổi công nghệ / những cơ hội đổi mới vô han / thay đổi ngân sách nghiên cứu và pt / quy định về thay đổi công nghệ ngày càng chặt chẽ )</p>\r\n<p>-        <em>Môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị</a> - luật pháp</em> . ( có khá nhiều đạo luật điều chỉnh hoạt động KD / Sự pt của các nhỏ bảo vệ lợi ích công cộng.</p>\r\n<p>-        <em>Môi trường <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa</a> - xã hội</em> . ( những giá trị văn hóa cốt lõi bền vững - ng làm MKT &amp; khả năng thay đổi những giá trị cốt lõi / những giá trị và thứ yểu biến đổi theo thời gian - ng làm MKT hết. Sức quan tâm đến việc phát hiện những biến đổi về Vh Có thể báo trước những cơ hội MKT và mối đe doạ mới)</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4154', 'Quan điểm của HCM về nội lực trong xây dựng Chủ nghĩa xã hội ở VN ? Trong đó, yếu tố nào là quan trọng và quyết định nhất?', null, null, '2', '0', '247', '51', '1552572781', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4155', 'phân tích khái niệm môi trường quan trắc marketing?', null, null, '5', '0', '628', '463', '1552572873', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4156', null, null, null, '5', '4155', '628', '463', '1552572873', null, null, null, '<p>-        Quan trắc môi trường marketing lệ thống <a class=\"cloze\" href=\"#mce_temp_url#\">báo động</a> về những lực lượng môi trường có thể tác động đến sản phẩm và thị trường của doanh nghiệp trong<a class=\"cloze\" href=\"#mce_temp_url#\"> tương   lai </a>.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4157', 'Mục đích  quan trắc môi trường marketing?', null, null, '5', '0', '628', '463', '1552572962', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4158', null, null, null, '5', '4157', '628', '463', '1552572962', null, null, null, '<p>- Cải thiện khả năng<a class=\"cloze\" href=\"#mce_temp_url#\"> đáp ứng</a> với những <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổ</a>i nhanh chóng của môi trường <br />- <a class=\"cloze\" href=\"#mce_temp_url#\">Đáp ứng</a> lại môi trường marketing <br />+ Những doanh nghiệp làm cho sự việc diễn ra<br /> + Những doanh nghiệp đứng nhìn sự việc diễn ra <br />+ Những doanh nghiệp luôn tự hỏi cái gì đã diễn ra</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4160', null, null, null, '5', '4159', '628', '463', '1552572962', null, null, null, '<p>- Cải thiện khả năng<a class=\"cloze\" href=\"#mce_temp_url#\"> đáp ứng</a> với những <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổ</a>i nhanh chóng của môi trường <br />- <a class=\"cloze\" href=\"#mce_temp_url#\">Đáp ứng</a> lại môi trường marketing <br />+ Những doanh nghiệp làm cho sự việc diễn ra<br /> + Những doanh nghiệp đứng nhìn sự việc diễn ra <br />+ Những doanh nghiệp luôn tự hỏi cái gì đã diễn ra</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4161', 'nhiệm vụ quan trắc môi trường mảketing?', null, null, '5', '0', '628', '463', '1552573021', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4162', null, null, null, '5', '4161', '628', '463', '1552573021', null, null, null, '<p><strong>Nhiệm vụ : </strong></p>\r\n<p>-<a class=\"cloze\" href=\"#mce_temp_url#\">Kiểm soát</a> các xu hướng</p>\r\n<p> - <a class=\"cloze\" href=\"#mce_temp_url#\">Chuẩn bị</a> các dự báo .</p>\r\n<p> - Tư vấn nội bộ .</p>\r\n<p>- Truyền thông</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4165', null, null, null, '5', '4163', '628', '463', '1552573022', null, null, null, '<p><strong>Nhiệm vụ : </strong></p>\r\n<p>-<a class=\"cloze\" href=\"#mce_temp_url#\">Kiểm soát</a> các xu hướng</p>\r\n<p> - <a class=\"cloze\" href=\"#mce_temp_url#\">Chuẩn bị</a> các dự báo .</p>\r\n<p> - Tư vấn nội bộ .</p>\r\n<p>- Truyền thông</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4166', null, null, null, '5', '4164', '628', '463', '1552573022', null, null, null, '<p><strong>Nhiệm vụ : </strong></p>\r\n<p>-<a class=\"cloze\" href=\"#mce_temp_url#\">Kiểm soát</a> các xu hướng</p>\r\n<p> - <a class=\"cloze\" href=\"#mce_temp_url#\">Chuẩn bị</a> các dự báo .</p>\r\n<p> - Tư vấn nội bộ .</p>\r\n<p>- Truyền thông</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4167', 'Quan điểm của HCM về động lực xây dựng Chủ nghĩa xã hội ở VN ? Những yếu tố nào kìm hãm sự nghiệp xây dựng Chủ nghĩa xã hội ở VN?', null, null, '2', '0', '248', '51', '1552573470', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4168', '1.	 Hành vi mua phức tạp :', null, null, '5', '0', '629', '463', '1552574158', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4169', null, null, null, '5', '4168', '629', '463', '1552574158', null, null, null, '<p><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\"> Hành vi mua phức tạp :</span></em></strong></p>\r\n<p>-        Khách hàng thực hiện hành vi mua phức tạp khi họ nhận dạng được sự khác biệt <a class=\"cloze\" href=\"#mce_temp_url#\">lớn</a> về những hiệu . Khách hàng có thể rất quan tâm khi sản phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">đắt tiền</a> , rủi ro nhiều , mua <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> thường xuyên và liên quan nhiều đến sự tự thể hiện của họ . Thông thường khách hàng phải tìm hiểu rất <a class=\"cloze\" href=\"#mce_temp_url#\">nhiều </a>chủng loại về sản phẩm đó .</p>\r\n<p>-        Trong trường hợp , khách hàng tham gia vào tiến trình mua với tốc độ<a class=\"cloze\" href=\"#mce_temp_url#\"> cao</a>  ==&gt;phải nỗ lực tìm hiểu khách hàng để biết được những đánh giá , niềm tin , thái độ và hành vi của họ , giúp khách hàng tìm hiểu những sản phẩm phù hợp với yêu cầu mà họ mong muốn .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4170', '2. Hành vi mua làm giảm trái nghịch :', null, null, '5', '0', '629', '463', '1552574217', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4171', null, null, null, '5', '4170', '629', '463', '1552574217', null, null, null, '<p><strong><em><span style=\"text-decoration: underline;\">2. Hành vi mua làm giảm trái nghịch :</span></em></strong></p>\r\n<p>-        Khi khách hàng tham gia vào việc mua những sản phẩm<a class=\"cloze\" href=\"#mce_temp_url#\"> ít</a> có sự khác biệt phản ứng chủ yếu theo <a class=\"cloze\" href=\"#mce_temp_url#\">thời giá</a> hay điều kiện mua thuận lợi . Sau khi mua khách hàng có thể cảm thấy sản phẩm không hài hòa do thấy những tính năng mà người bán băn khoăn hay nghe thấy những thông tin không tốt về sản phẩm .</p>\r\n<p>          ==&gt;hoạch định những chiến lược marketing  để đưa ra giá cả phù hợp  và đúng thời điểm. Vận dụng mọi nỗ lực marketing  hướng khách hàng đến sản phẩm tốt nhất  phù hợp nhất cho khách hàng .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4172', '3.	Hành vi mua tìm kiếm sự đa dạng :?', null, null, '5', '0', '629', '463', '1552574278', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4173', null, null, null, '5', '4172', '629', '463', '1552574278', null, null, null, '<p>-        Một số tình huống mua sắm có đặc điểm là khách hàng ít để ý , nhưng các nhãn hiệu khác nhau và nhiêu. Trong trường hợp này ta thấy khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">thay đổ</a>i nhãn hiệu rất <a class=\"cloze\" href=\"#mce_temp_url#\">nhiều</a> lần . Sự thay đổi nhãn hiệu chỉ để tìm kiếm sự <a class=\"cloze\" href=\"#mce_temp_url#\">đa dạng </a>chứ không phải vì không hài lòng.</p>\r\n<p>          ==&gt;chiến lược marketing khác nhau đối với những nhãn hiệu dẫn đầu thị trường và những nhãn hiệu thứ yếu .</p>\r\n<p>èCác doanh nghiệp khuyên khích khách hàng tìm kiếm sự đa dạng bằng cách chào hàng với giá thấp hơn , ký kết hợp đồng , phiếu tiền thường , đưa mẫu hàng miễn phí và quảng cáo trung bày sản phẩm mới .</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4174', 'Quan điểm của HCM về loại hình quá độ lên Chủ nghĩa xã hội ở VN. Đặc điểm lớn nhất và mâu thuẫn cơ bản nhất trong thời kì quá độ lên Chủ nghĩa xã hội ở nước ta theo TTHCM là gì?', null, null, '2', '0', '249', '51', '1552574339', '1552637163', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4175', '4.	 Hành vi mua theo thói quen?', null, null, '5', '0', '629', '463', '1552574359', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4176', null, null, null, '5', '4175', '629', '463', '1552574359', null, null, null, '<p>-        Nhiều sản phẩm được mua khỉ<a class=\"cloze\" href=\"#mce_temp_url#\"> ít</a> có sự <a class=\"cloze\" href=\"#mce_temp_url#\">quan tâm </a>của khách hàng và <a class=\"cloze\" href=\"#mce_temp_url#\">ít</a> có sự <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt</a> nhau về nhãn hiệu .</p>\r\n<p>-        Nếu họ cố tìm nhãn hiệu cũ thì cũng chỉ là mua theo <a class=\"cloze\" href=\"#mce_temp_url#\">thói quen</a> chứ không phải là một sự trung thành về nhãn hiệu .</p>\r\n<p>          =&gt;hành vi của người tiêu dùng không trải qua trình độ bình thường niềm tin/thái độ / hành vi . Khách thụ động tiếp nhận thông tin khi xem truyền hình hay các mục quảng cáo trên báo trì . Sự cạnh tranh quảng cáo tạo ra sự khác biệt với nhãn hiệu ra sự tin tưởng vào nhãn hiệu .</p>\r\n<p>           =&gt;Những người  làm marketing sử dụng giá cả và những biện pháp kích thích tiêu thụ để kích thích dùng thử sản phẩm</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4177', '“các vai trò trong mua sắm” trong hành vi mua của khác hàng người tiêu dùng?', null, null, '5', '0', '631', '463', '1552574613', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4178', null, null, null, '5', '4177', '631', '463', '1552574613', null, null, null, '<p>- Người <a class=\"cloze\" href=\"#mce_temp_url#\">khởi xướng</a>: người đưa ra ý tưởng mua sắm sản phẩm/dịch vụ<br />- Người<a class=\"cloze\" href=\"#mce_temp_url#\"> ảnh hưởng</a>: người có quan điểm, ý kiến có ảnh hưởng đến quyết định mua sản phẩm<br />- Người <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định</a>: người quyết định mọi yếu tố trong quyết định mua sắm: Mua cái gì? ở đâu? mua như thế nào?;;;<br />- Người <a class=\"cloze\" href=\"#mce_temp_url#\">mua</a>: Người trực tiếp thực hiện mua sản phẩm<br />- Người <a class=\"cloze\" href=\"#mce_temp_url#\">sử dụng</a>: người tiêu dùng hay sử dụng sản phẩm/dịch vụ<br />==&gt;công ty cần xác định vai trò này vì nó có ý nghĩa trong việc thiết kế sản phẩm, xác định nội dung thông điệp, chương trình xúc tiến…</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4179', 'Bước 1: Nhu cầu được cảm nhận?', null, null, '5', '0', '632', '463', '1552575040', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4180', null, null, null, '5', '4179', '632', '463', '1552575040', null, null, null, '<ul>\r\n<li>Quá trình mua sắm bắt đầu khi người mua <a class=\"cloze\" href=\"#mce_temp_url#\">ý thức</a> được vấn đề hay nhu cầu cụ thể.</li>\r\n<li>Người mua cảm thấy có sự <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt</a> giữa trạng thái <a class=\"cloze\" href=\"#mce_temp_url#\">thực tế</a> và tình trạng <a class=\"cloze\" href=\"#mce_temp_url#\">mong muốn</a></li>\r\n<li>Nhu cầu có thể bắt nguồn từ bên trong và bên ngoài</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4181', 'Bước 2: Tìm kiếm thông tin?', null, null, '5', '0', '632', '463', '1552575081', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4182', null, null, null, '5', '4181', '632', '463', '1552575081', null, null, null, '<p>Các nguồn thông tin khách hàng tìm kiếm</p>\r\n<ul>\r\n<li>Nguồn thông tin <a class=\"cloze\" href=\"#mce_temp_url#\">cá nhân</a>:Gia đình, bạn bè, hàng xóm</li>\r\n<li>Nguồn thông tin <a class=\"cloze\" href=\"#mce_temp_url#\">thương mạ</a>i: Quảng cáo, triển lãm..</li>\r\n<li>Nguồn thông tin <a class=\"cloze\" href=\"#mce_temp_url#\">thực nghiệm</a>: Qua tiếp xúc, nghiên cứu</li>\r\n<li>Nguồn thông tin <a class=\"cloze\" href=\"#mce_temp_url#\">công cộng</a>: Các phương tiện thông tin đại chúng...</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4183', 'Bước 3: Đánh giá thế vị', null, null, '5', '0', '632', '463', '1552575174', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4184', null, null, null, '5', '4183', '632', '463', '1552575174', null, null, null, '<ul>\r\n<li>người tiêu dùng<a class=\"cloze\" href=\"#mce_temp_url#\"> xử lý</a> thông tin về các <a class=\"cloze\" href=\"#mce_temp_url#\">nhãn hiệu</a> cạnh tranh rồi đưa ra phán quyết sau cùng dựa trên sự xem xét, cân nhắc về <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm</a></li>\r\n<li>Quy trình:Phân tích động cơ về sản phẩm=&gt;Xác định ý niệm- tham số hành vi đối với sản phẩm==&gt; Đánh giá niềm tin đối với từng ý niệm/từng điểm nhãn hiệu theo thang điểm 10==&gt;    Phân tích độ quan trọng và xác định hệ số đương lượng của từng tham số tới hành vi==&gt; Xác định giá trị hành vi kì vọng chọn nhãn hiệu sản phẩm</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4185', 'Bước 4: Quyết định mua', null, null, '5', '0', '632', '463', '1552575236', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4186', null, null, null, '5', '4185', '632', '463', '1552575236', null, null, null, '<p>CÁc nhân tố tình huống <a class=\"cloze\" href=\"#mce_temp_url#\">không</a> tham dự</p>\r\n<p>          -Thái độ của người khác</p>\r\n<p>           - Các nhân tố khác</p>\r\n<p>+Các  quyết định</p>\r\n<p>           -Quyết định <a class=\"cloze\" href=\"#mce_temp_url#\">nhãn hiệu</a></p>\r\n<p>           -Quyết định <a class=\"cloze\" href=\"#mce_temp_url#\">người bán</a></p>\r\n<p>           - Quyết định<a class=\"cloze\" href=\"#mce_temp_url#\"> số lượng</a>,thời gian mua</p>\r\n<p>+Ứng xử: Hạn chế những tác động tiêu cực đến quyết định mua của khách hàng=&gt;hoạt động mkt phù hợp</p>\r\n<p>-       </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4187', 'B5: Cảm nhận sau mua:', null, null, '5', '0', '632', '463', '1552575267', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4188', null, null, null, '5', '4187', '632', '463', '1552575267', null, null, null, '<p> </p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Hài lòng</a></p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Bất mãn</a></p>\r\n<p>+Ứng xử: Tạo cảm giác hài lòng ngay cả khia khách hàng không mua hàng hoạt động mkt phù hợp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
