INSERT INTO `question` VALUES ('4195', 'Câu 3.1: Khái niệm chủ nghĩa duy vật? ', '', '', '1', '0', '89', '6', '1552622509', null, 'trường phái lớn, lập trường duy vật, ', '', 'Khái niệm chủ nghĩa duy vật? ', '- Chủ nghĩa duy vật là một trong những trường phái triết học lớn trong lịch sử, bao gồm trong đó toàn bộ các học thuyết triết học được xây dựng trên lập trường duy vật trong việc giải quyết vấn đề cơ bản của triết học: vật chất là tính thứ nhất, ý thức là tính thứ hai của mọi tồn tại trên thế giới; cũng tức là thừa nhận và minh chứng rằng: suy đến cùng, bản chất và cơ sở của mọi tồn tại trong thế giới tự nhiên và xã hội chính là vật chất. ', '31', null, null, null, null, null);
