INSERT INTO `question` VALUES ('4318', 'Quan điểm HCM về hình thức đoàn kết quốc tế?', '', '', '1', '0', '440', '51', '1552641674', null, '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;line-height:130%\"><span style=\"font-size: 14.0pt;line-height:130%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font: major-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US\">Tư tưởng đại đoàn kết =&gt; hình thành 4 tầng mặt trận</span></p> <span style=\"font-size:14.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;; mso-ascii-theme-font:major-latin;mso-fareast-font-family:Arial;mso-fareast-theme-font: minor-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">=&gt; Sự phát triển rực rỡ nhất và thắng lơi to lớn nhất&nbsp;</span>', '', 'Quan điểm HCM về hình thức đoàn kết quốc tế?', '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;line-height:130%\"><span style=\"font-size: 14.0pt;line-height:130%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font: major-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US\">Tư tưởng đại đoàn kết vì thắng lợi cách mạng của HCM đã định hướng cho việc hình thành 4 tầng mặt trận:<o:p></o:p></span></p> <p class=\"MsoListParagraphCxSpMiddle\" style=\"margin-top:0in;margin-right:0in; margin-bottom:0in;margin-left:1.0in;margin-bottom:.0001pt;mso-add-space:auto; text-align:justify;text-indent:-.25in;line-height:130%;mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-size:14.0pt;line-height:130%;font-family:Symbol;mso-fareast-font-family: Symbol;mso-bidi-font-family:Symbol;mso-ansi-language:EN-US\">+<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style=\"font-size:14.0pt;line-height:130%; font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font:major-latin; mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin;mso-ansi-language: EN-US\">Mặt trận đại đoàn kết dân tộc<o:p></o:p></span></p> <p class=\"MsoListParagraphCxSpMiddle\" style=\"margin-top:0in;margin-right:0in; margin-bottom:0in;margin-left:1.0in;margin-bottom:.0001pt;mso-add-space:auto; text-align:justify;text-indent:-.25in;line-height:130%;mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-size:14.0pt;line-height:130%;font-family:Symbol;mso-fareast-font-family: Symbol;mso-bidi-font-family:Symbol;mso-ansi-language:EN-US\">+<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style=\"font-size:14.0pt;line-height:130%; font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font:major-latin; mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin;mso-ansi-language: EN-US\">Mặt trận đoàn kết Việt - Miên&nbsp; - Lào<o:p></o:p></span></p> <p class=\"MsoListParagraphCxSpMiddle\" style=\"margin-top:0in;margin-right:0in; margin-bottom:0in;margin-left:1.0in;margin-bottom:.0001pt;mso-add-space:auto; text-align:justify;text-indent:-.25in;line-height:130%;mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-size:14.0pt;line-height:130%;font-family:Symbol;mso-fareast-font-family: Symbol;mso-bidi-font-family:Symbol;mso-ansi-language:EN-US\">+<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style=\"font-size:14.0pt;line-height:130%; font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font:major-latin; mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin;mso-ansi-language: EN-US\">Mặt trận nhân dân Á Phi đoàn kết với VN<o:p></o:p></span></p> <p class=\"MsoListParagraphCxSpLast\" style=\"margin-top:0in;margin-right:0in; margin-bottom:0in;margin-left:1.0in;margin-bottom:.0001pt;mso-add-space:auto; text-align:justify;text-indent:-.25in;line-height:130%;mso-list:l0 level1 lfo1\"><!--[if !supportLists]--><span style=\"font-size:14.0pt;line-height:130%;font-family:Symbol;mso-fareast-font-family: Symbol;mso-bidi-font-family:Symbol;mso-ansi-language:EN-US\">+<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style=\"font-size:14.0pt;line-height:130%; font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font:major-latin; mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin;mso-ansi-language: EN-US\">Mặt trận nhân dân thế giới đoàn kết với VN chống đế quốc xâm lược<o:p></o:p></span></p> <span style=\"font-size:14.0pt;line-height:115%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;; mso-ascii-theme-font:major-latin;mso-fareast-font-family:Arial;mso-fareast-theme-font: minor-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US;mso-fareast-language:EN-US;mso-bidi-language:AR-SA\">=&gt; Đây là sự phát triển rực rỡ nhất và thắng lơi to lớn nhất của tư tưởng đại đoàn kết của HCM</span>', '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4319', 'Quan điểm HCM  về điều kiện thực hiện đại đoàn kết dân tộc?', null, null, '2', '0', '441', '51', '1552642077', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4320', 'Quan điểm của HCM: “Đại đoàn kết dân tộc là vấn đề có ý nghĩa chiến lược quyết định thành công của cách mạng”?', '', '', '1', '0', '442', '51', '1552642456', null, '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l1 level1 lfo1\"><span style=\"font-size: 14pt; line-height: 130%;\"><font face=\"VNI-Lithos\">&nbsp; &nbsp; -&nbsp;</font></span><span style=\"font-size:14.0pt;line-height:130%; font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font:major-latin; mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin;mso-ansi-language: EN-US\">Chiến lược cơ bản, xuyên suốt tiến trình cách mạng</span></p><p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l1 level1 lfo1\"><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">&nbsp; &nbsp; &nbsp; -&nbsp; Cội nguồn tạo nên sức mạnh.</span></p><p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l1 level1 lfo1\"><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">&nbsp; &nbsp; =&gt; Cách mạng VN giành được nhiều thắng lợi.</span></p>', '', 'Quan điểm của HCM: “Đại đoàn kết dân tộc là vấn đề có ý nghĩa chiến lược quyết định thành công của cách mạng”?', '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l1 level1 lfo1\"><span style=\"font-size: 14pt; line-height: 130%;\"><font face=\"VNI-Lithos\">&nbsp; &nbsp;-&nbsp;</font></span><span style=\"font-size:14.0pt;line-height:130%; font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font:major-latin; mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin;mso-ansi-language: EN-US\">Đại đoàn kết dân tộc là một chiến lược cơ bản, nhất quán và lâu dài, xuyên suốt tiến trình cách mạng</span></p><p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l1 level1 lfo1\"><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">&nbsp; &nbsp; &nbsp; -&nbsp; Đại đoàn kết dân tộc là cội nguồn tạo nên sức mạnh.</span></p><p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;text-indent:-.25in;line-height:130%; mso-list:l1 level1 lfo1\"><span style=\"font-family: &quot;Times New Roman&quot;, serif; font-size: 14pt; text-indent: -0.25in;\">&nbsp; &nbsp; =&gt; Nhờ tư tưởng nhất quán và chính sách mặt trận đúng đắn, Đảng ta và Chủ tịch HCM đã xây dựng thành công khối Đại đoàn kết dân tộc, đưa cách mạng VN giành đc nhiều thắng lợi to lớn.</span></p>', '55', null, null, null, null, null);
