INSERT INTO `question` VALUES ('4361', '1.	Doanh nghiệp lấy đối thủ cạnh  tranh làm trung tâm?', null, null, '5', '0', '647', '463', '1552653876', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4362', null, null, null, '5', '4361', '647', '463', '1552653876', null, null, null, '<p>-        Doanh nghiệp đề ra cá biện pháp của mình về cơ bản đều xuất phát từ các <a class=\"cloze\" href=\"#mce_temp_url#\">hành động</a> và phản ứng của <a class=\"cloze\" href=\"#mce_temp_url#\">đối thủ.</a></p>\r\n<p>-      <strong>  Ưu điểm:</strong></p>\r\n<p>+Doanh nghiệp xây dựng được<a class=\"cloze\" href=\"#mce_temp_url#\"> hướng</a> chiến đấu</p>\r\n<p>+Người làm marketing luôn <a class=\"cloze\" href=\"#mce_temp_url#\">cảnh giác</a>, theo dõi những mặt yếu của các đối thủ cạnh tranh</p>\r\n<p>-       <strong> Hạn chế:</strong></p>\r\n<p>+ Doanh nghiệp phải đưa ra <a class=\"cloze\" href=\"#mce_temp_url#\">nhiều phản ứng</a></p>\r\n<p>+ Thay vì đưa ra một chiến lược nhất quán, doanh nghiệp lại đi xác dịnh các biện pháp trên cơ sở biện pháp của đối thủ==&gt; Không hướng tới mục tiêu chính của mình.</p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Phụ thuộc </a>quá nhiều vào đối thủ canh tranh</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4363', '2.	Doanh nghiệp lấy khách hàng làm trung tâm?', null, null, '5', '0', '647', '463', '1552653948', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4364', null, null, null, '5', '4363', '647', '463', '1552653948', null, null, null, '<p>-        Doanh nghiệp<a class=\"cloze\" href=\"#mce_temp_url#\"> tập trung</a> vào sự <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển</a> của khách hàng khi hình thành chiến lược của mình</p>\r\n<p>-        Tập trung nhiều hơn vào việc phát triển khách hàng khi hoạch định chiến lược</p>\r\n<p>-        Doanh nghiệp  quyết định được những nhóm <a class=\"cloze\" href=\"#mce_temp_url#\">khách hàng</a> nào và <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> nào mới xuất hiện là quan trọng nhất để<a class=\"cloze\" href=\"#mce_temp_url#\"> tập chung chiến lược</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4365', '1.	Thiết kế hệ thống?', null, null, '5', '0', '648', '463', '1552654204', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4366', null, null, null, '5', '4365', '648', '463', '1552654204', null, null, null, '<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Phát hiện</a> những loại <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> cạnh tranh quan trọng</p>\r\n<p>-        Xác định<a class=\"cloze\" href=\"#mce_temp_url#\"> nguồn</a> thu thập tốt nhất các <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a></p>\r\n<p>-        Chỉ định<a class=\"cloze\" href=\"#mce_temp_url#\"> quản trị</a> hệ thống và các dịch vụ liên quan</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4367', '2.	Thu thập dữ liệu?', null, null, '5', '0', '648', '463', '1552654251', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4368', null, null, null, '5', '4367', '648', '463', '1552654251', null, null, null, '<p>-        Dữ liệu thu thập từ <a class=\"cloze\" href=\"#mce_temp_url#\">hiện trường</a> (Lực lượng bán; người cung ứng, các  công ty nghiên cứu thị trường</p>\r\n<p>-        Dữ lieeku đã được <a class=\"cloze\" href=\"#mce_temp_url#\">công bố</a> (Ấn phẩm, tạp chí..)</p>\r\n<p>-        Công ty xây dựng cách mua thông tin về đối thủ cạnh tranh mà <a class=\"cloze\" href=\"#mce_temp_url#\">không vi phạm pháp luật</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"><a class=\"cloze\" href=\"#mce_temp_url#\"> </a></div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4369', '3.	Đánh giá và phân tích?', null, null, '5', '0', '648', '463', '1552654298', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4370', null, null, null, '5', '4369', '648', '463', '1552654298', null, null, null, '<p>-        Các dữ liệu được <a class=\"cloze\" href=\"#mce_temp_url#\">kiểm tra </a>về giá trị và<a class=\"cloze\" href=\"#mce_temp_url#\"> độ tin cậy</a>, diễn giải và <a class=\"cloze\" href=\"#mce_temp_url#\">tổ chức</a> lại thông tin một cách <a class=\"cloze\" href=\"#mce_temp_url#\">hợp lý</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4371', '4.	Phổ biến thông tin và giải quyết các thắc mắc?', null, null, '5', '0', '648', '463', '1552654372', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4372', null, null, null, '5', '4371', '648', '463', '1552654372', null, null, null, '<p>-        Hệ thống  gửi các <a class=\"cloze\" href=\"#mce_temp_url#\">thông tin</a> quan trọng đến những nhà hoạch định chính sách có liên quan và <a class=\"cloze\" href=\"#mce_temp_url#\">đáp ứng </a>yêu cầu thông tin từ các nhà quản trị về <a class=\"cloze\" href=\"#mce_temp_url#\">đối thủ cạnh tranh</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4373', '1.	Đối thủ cạnh tranh hiện tại?', null, null, '5', '0', '649', '463', '1552654490', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4374', null, null, null, '5', '4373', '649', '463', '1552654490', null, null, null, '<ul>\r\n<li>Cạnh tranh giữa các doanh nghiệp trong cùng một <a class=\"cloze\" href=\"#mce_temp_url#\">ngành </a>sản xuát thường bao gồm các nội dung chủ yếu như :<a class=\"cloze\" href=\"#mce_temp_url#\">cơ cấu ngành</a>,rào cản ra nhập/rút lui, <a class=\"cloze\" href=\"#mce_temp_url#\">thực trạng</a> cơ cấu ngành.</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4375', '2.	 Đối thủ cạnh tranh tiềm ẩn?', null, null, '5', '0', '649', '463', '1552654557', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4376', null, null, null, '5', '4375', '649', '463', '1552654557', null, null, null, '<p>-        Là các doanh nghiệp hiện tại<a class=\"cloze\" href=\"#mce_temp_url#\"> chưa</a> cạnh tranh trong cùng 1 ngành sản xuất nhưng có <a class=\"cloze\" href=\"#mce_temp_url#\">khả năng</a> cạnh tranh nếu họ đưa ra quyết định ra nhập ngành. Do đó các doanh nghiệp trong ngadnh thường tạo ra các ròa cản ra nhập:</p>\r\n<p>+ Những<a class=\"cloze\" href=\"#mce_temp_url#\"> ưu thế </a>tuyệt đối về chí phí: Về công nghệ, nguồn nguyên liệu, nguồn nhân lực</p>\r\n<p>+Khác biệt hóa sản phẩm</p>\r\n<p>+Ưu thế về <a class=\"cloze\" href=\"#mce_temp_url#\">quy mô</a> nhằm giảm chi phí sản phẩm</p>\r\n<p>+duy trì, củng cố kênh phân phối</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4377', '3. Nhà cung cấp?', null, null, '5', '0', '649', '463', '1552654609', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4378', null, null, null, '5', '4377', '649', '463', '1552654609', null, null, null, '<ul>\r\n<li>Nhà cung cấp có thể tạo ra <a class=\"cloze\" href=\"#mce_temp_url#\">áp lực</a> đe dọa khi họ có khả năng<a class=\"cloze\" href=\"#mce_temp_url#\"> tăng giá</a> đầu vào hoặc giảm chất lượng sản phẩm, dịch vụ mà họ cung cấp==&gt;giảm khả năng cung ứng, gây ảnh hưởng đến sản phẩm<a class=\"cloze\" href=\"#mce_temp_url#\"> đầu ra</a> của doanh nghiệp.</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4379', '4. Khách hàng:', null, null, '5', '0', '649', '463', '1552654649', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4380', null, null, null, '5', '4379', '649', '463', '1552654649', null, null, null, '<ul>\r\n<li>Người mua được xem như là một sự <a class=\"cloze\" href=\"#mce_temp_url#\">đe dọa </a>buộc doanh nghiệp phải giảm giá hoặc có nhu cầu chất lượng và dịch vụ tốt hơn. Ngược lại, khi người mua yếu sẽ mang đến cho doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">cơ hội</a> tăng giá bán,lợi nhuận.</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4381', '5. Sản phẩm thay thế', null, null, '5', '0', '649', '463', '1552654705', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4382', null, null, null, '5', '4381', '649', '463', '1552654705', null, null, null, '<ul>\r\n<li>Sản phẩm thay thế là những sản phẩm khác có thể<a class=\"cloze\" href=\"#mce_temp_url#\"> thỏa mãn</a> <a class=\"cloze\" href=\"#mce_temp_url#\">cùng nhu cầu</a> của người tiêu dùng.</li>\r\n<li>Đặc điểm cơ bản của nó thường có <a class=\"cloze\" href=\"#mce_temp_url#\">ưu thế</a> sản phẩm bị thay thế ở các đặc trưng riêng.</li>\r\n</ul>\r\n<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4383', '1.	Theo cách tiếp cận ngành?', null, null, '5', '0', '650', '463', '1552654892', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4384', null, null, null, '5', '4383', '650', '463', '1552654892', null, null, null, '<ul>\r\n<li>Sản phẩm cùng loại</li>\r\n<li>thay đổi theo thời gian</li>\r\n<li>định dạng bởi nhiều yếu tố khác nhau: Số lượng doanh nghiệp, rào cản ra nhập/rút lui,cấu trúc ngành..</li>\r\n</ul>\r\n<p><strong><em>- Cấu trúc ngành</em></strong></p>\r\n<p>+<a class=\"cloze\" href=\"#mce_temp_url#\">Độc quyền</a>: Chỉ <a class=\"cloze\" href=\"#mce_temp_url#\">một</a> doanh nghiệp cung ứng một sản phẩm, dịch vụ</p>\r\n<p>+Độc quyền <a class=\"cloze\" href=\"#mce_temp_url#\">đơn thuần</a>: <a class=\"cloze\" href=\"#mce_temp_url#\">Một vài</a> doanh nghiệp sản xuất những hàng hóa cơ bản giống nhau và ít có sự khác biệt.</p>\r\n<p>+Bán đôc quyền<a class=\"cloze\" href=\"#mce_temp_url#\"> khác biệt</a>: Một số doanh nghiệp sản xuất các hàng hóa <a class=\"cloze\" href=\"#mce_temp_url#\">khác biệt</a> nhau <a class=\"cloze\" href=\"#mce_temp_url#\">một vài phần</a> thể hiện ở một số đặc tính quan trọng của sản phẩm và có thể định giá cao.</p>\r\n<p>+Bán <a class=\"cloze\" href=\"#mce_temp_url#\">cạnh tranh</a>: Nhiều doanh nghiệp tạo ra sự khác biệt cho sản phẩm của mình một cách <a class=\"cloze\" href=\"#mce_temp_url#\">hoàn toàn</a> hoặc <a class=\"cloze\" href=\"#mce_temp_url#\">một phần</a>. Lựa chọn phân đoạn thì trường phù hợp là chìa khóa của thành công.</p>\r\n<p>+Cạnh tranh<a class=\"cloze\" href=\"#mce_temp_url#\"> hoàn hảo</a>: Nhiều đối thủ cùng cung ứng sản phẩm <a class=\"cloze\" href=\"#mce_temp_url#\">giống nhau</a>. Giá tương đối giống nhau và không có sự khác biệt nhiều.</p>\r\n<p> </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4385', '2. Cách tiếp cận thị trường về cạnh tranh?', null, null, '5', '0', '650', '463', '1552654936', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4386', null, null, null, '5', '4385', '650', '463', '1552654936', null, null, null, '<ul>\r\n<li>Các đối thủ cạnh tranh là những doanh nghiệp<a class=\"cloze\" href=\"#mce_temp_url#\"> cùng thoải mãn</a> nhu cầu của khách hàng</li>\r\n<li>Cách tiếp cận này gợi mở một <a class=\"cloze\" href=\"#mce_temp_url#\">tập hợp</a> các đối thủ cạnh tranh tiềm năng và hiện tại mở rộng hơn ==&gt;Lập kế hoạch marketing lâu dài.</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4387', '1.	Chiến lược', null, null, '5', '0', '651', '463', '1552655677', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4388', null, null, null, '5', '4387', '651', '463', '1552655677', null, null, null, '<ul>\r\n<li>Mối đối thủ có những <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> riêng nhằm <a class=\"cloze\" href=\"#mce_temp_url#\">phát huy</a> các<a class=\"cloze\" href=\"#mce_temp_url#\"> ưu thế</a> của mình để khái thác tốt có hội thị trường và gia tăng khả năng cạnh tranh</li>\r\n<li>Nhóm chiến lược- gồm các doanh nghiệp áp dụng cùng một chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">giống nhau</a> trên một thị trường <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> nhất định\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Phân tích </a>và tiên lượng các chiến lược của đối thủ</li>\r\n<li>Thường xuyên <a class=\"cloze\" href=\"#mce_temp_url#\">xem xét</a> chiến lược của đối thủ của doanh nghiệp</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4389', '2. Mục tiêu', null, null, '5', '0', '651', '463', '1552655724', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4390', null, null, null, '5', '4389', '651', '463', '1552655724', null, null, null, '<ul>\r\n<li>Đặt doanh nghiệp vào <a class=\"cloze\" href=\"#mce_temp_url#\">vị trí</a> của đối thủ cạnh tranh: Đối thủ muốn làm gì và họ sẽ làm như thế nào?..</li>\r\n<li>Mỗi đối thủ có thể theo đuối một sô <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> nhất định: Lợi nhuận, thị phần, vị trí..)==&gt;Đánh giá tầm quan trọng tương đối của các mục tiêu</li>\r\n</ul>\r\n<p>--&gt;Các mục tiêu hình thành trên cơ sở phân tích có hệ thống quy mô, bộ máy quản lý, tài chính</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4391', '3. Điểm mạnh-điểm yếu', null, null, '5', '0', '651', '463', '1552655802', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4392', null, null, null, '5', '4391', '651', '463', '1552655802', null, null, null, '<ul>\r\n<li><em><a class=\"cloze\" href=\"#mce_temp_url#\">thu thập</a> thông tin:</em></li>\r\n</ul>\r\n<p>+doanh số , thị phần, ROI, đầu tư..</p>\r\n<p>+ Tổ chức các cuộc nghiên cứu</p>\r\n<ul>\r\n<li>Sử dụng Benchmarking để <a class=\"cloze\" href=\"#mce_temp_url#\">cải thiện </a>quá trình cạnh tranh: Tại sao và bằng cách nào một số doanh nghiệp hoạt động tốt hơn?..</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Đánh giá </a>thế vị mỗi đối thủ trên các nhân tố thành công then chốt(KSF)</li>\r\n</ul>\r\n<p>==&gt;Kết luận:</p>\r\n<ul>\r\n<li>Doanh nghiệp nên<a class=\"cloze\" href=\"#mce_temp_url#\"> kiểm soát</a> biến số khi phân tích đối thủ</li>\r\n<li>Thị phần của đối thủ cạnh tranh trên thị trường mục tiêu</li>\r\n<li>Share of mind (SM): thị phần về <a class=\"cloze\" href=\"#mce_temp_url#\">tâm trí</a></li>\r\n<li>Share of heart (SH) thị phần<a class=\"cloze\" href=\"#mce_temp_url#\"> trái tim</a></li>\r\n<li>Mối quan hệ: SM và SH thấp==&gt;thị phần và lợi nhuận thấp</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4393', '4. Đánh giá/dự đoán phản ứng của đối thủ', null, null, '5', '0', '651', '463', '1552655960', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4394', null, null, null, '5', '4393', '651', '463', '1552655960', null, null, null, '<ul>\r\n<li><em><span style=\"text-decoration: underline;\">Đối thủ cạnh tranh<a class=\"cloze\" href=\"#mce_temp_url#\"> điềm tĩnh</a></span></em>:không phản ứng nhanh chóng, mạnh mẽ</li>\r\n</ul>\r\n<ul>\r\n<li>Cảm thấy khách hàng của họ là những khách hàng <a class=\"cloze\" href=\"#mce_temp_url#\">trung thành</a></li>\r\n<li>Có thể <a class=\"cloze\" href=\"#mce_temp_url#\">thiếu</a> kinh phí để phản ứng</li>\r\n</ul>\r\n<p>==&gt;Doanh nghiệp có gắng tìm hiểu lý do vì sao đối thủ lại có phản ứng như vậy?</p>\r\n<ul>\r\n<li><em><span style=\"text-decoration: underline;\">Đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">kén chọn</a></span></em>: chỉ phản ứng trước những kiểu tấn công <a class=\"cloze\" href=\"#mce_temp_url#\">nhất định</a> mà không phản ứng với các kiểu tấn công khác.</li>\r\n</ul>\r\n<ul>\r\n<li>Phản ứng với việc giảm giá để báo hiệu rằng việc đó không có ý nghĩa gì</li>\r\n<li>Có thể không phản ứng việc tăng chi phí quảng cáo vì nghĩ nó ít đe dọa</li>\r\n</ul>\r\n<p>==&gt;Doanh nghiệp nhận biết phản ứng của một số đối thủ chủ yếu để làm căn cứ hoạch định tấn công khả thi nhất.</p>\r\n<ul>\r\n<li><em><span style=\"text-decoration: underline;\">Đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">hung dữ</a></span></em><a class=\"cloze\" href=\"#mce_temp_url#\">:</a></li>\r\n</ul>\r\n<ul>\r\n<li>Phản ứng mau lẹ  và<a class=\"cloze\" href=\"#mce_temp_url#\"> mạnh mẽ </a>với mọi cuộc đột kích vào lãnh địa của mình</li>\r\n<li>luôn cảnh báo rằng tốt nhất là các công ty khác đừng nên tấn công vì người phòng thủ sẽ chiến đấu đến cùng.</li>\r\n<li>Một số đối thủ cạnh tranh không để lộ ra cách phản ứng để có thể lường trước.</li>\r\n</ul>\r\n<ul>\r\n<li><em><span style=\"text-decoration: underline;\">Đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">khôn ngoan</a>:</span></em></li>\r\n</ul>\r\n<p>==&gt;Dang đối thủ nguy hiểm nhất.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4395', '5. Lựa chọn tấn công, né tránh', null, null, '5', '0', '651', '463', '1552656116', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4396', null, null, null, '5', '4395', '651', '463', '1552656116', null, null, null, '<p><strong><em><span style=\"text-decoration: underline;\">-  Các đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">mạnh và yếu:</a></span></em></strong></p>\r\n<ul>\r\n<li>Hầu hết các doanh nghiệp tấn công vào các đối thủ yếu vì sẽ cần ít tài nguyên và thời gian hơn. Tuy nhiên, doanh nghiệp có thể ít đạt kết quả cao trong việc nâng cao năng lực của mình</li>\r\n<li>Doanh nghiệp có thể chọn tấn công vào điểm yếu của đối thủ mạnh.</li>\r\n</ul>\r\n<p><strong>-        Các đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">gần và xa</a></strong></p>\r\n<ul>\r\n<li>Hầu hết các doanh nghiệp lựa chọn tấn công vào các đối thủ gần giống với mình nhất.</li>\r\n<li>Tuy nhiên, doanh nghiệp nên tránh không phá rồi đối thủ cạnh tranh gần vì trong mỗi thắng lợi của doanh nghiệp trước đối thủ gần mình đều dẫn tới phải cạnh tranh với các đối thủ lớn mạnh hơn</li>\r\n</ul>\r\n<p><strong>-        <em><span style=\"text-decoration: underline;\">Đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">tốt và xấu</a></span></em></strong></p>\r\n<ul>\r\n<li>Đối thủ cạnh tranh<a class=\"cloze\" href=\"#mce_temp_url#\"> tốt</a>: Họ chơi đẹp theo đúng luật của ngành, họ đưa ra những giả thiết thực tế về tiềm năng và tăng trưởng của ngành…</li>\r\n<li>Đối thủ cạnh tranh <a class=\"cloze\" href=\"#mce_temp_url#\">xấu</a>: Cố gắng mua thị phần thay vì nỗ lực giành lấy, chấp nhận những rủi ro, phá vỡ thế cân bằng của ngành..</li>\r\n</ul>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4397', '1.	Chiến lược marketing', null, null, '5', '0', '653', '463', '1552656284', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4398', null, null, null, '5', '4397', '653', '463', '1552656284', null, null, null, '<p>-        Là việc <a class=\"cloze\" href=\"#mce_temp_url#\">phân tích </a>các cơ hội thị trường và <a class=\"cloze\" href=\"#mce_temp_url#\">xác lập chiến lược </a>marketing dài hạn và một<a class=\"cloze\" href=\"#mce_temp_url#\"> cấu trúc tương hợp</a> các chiến lược marketing mục tiêu, chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">chào hàng thị trường</a>, chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">kênh</a> marketing, chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">xúc tiến hỗn hợp</a> và quy hoạch các nguồn lực chiến lược marketing đê đảm bảo đạt tới mục tiêu chiến lược đã được xác lập của doanh nghiêp</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4399', '2.	Marketing chiến lược:', null, null, '5', '0', '653', '463', '1552656343', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4400', null, null, null, '5', '4399', '653', '463', '1552656343', null, null, null, '<p>-        Là một tập<a class=\"cloze\" href=\"#mce_temp_url#\"> xác định</a> các thị trường<a class=\"cloze\" href=\"#mce_temp_url#\"> trọng điểm</a>,<a class=\"cloze\" href=\"#mce_temp_url#\"> lựa chọn định v</a>ị cạnh tranh và triển khai một marketing-mix hữu hiệu để<a class=\"cloze\" href=\"#mce_temp_url#\"> thu hút</a> và thỏa mãn được khác hàng đã lựa chọn</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4401', '3.	Quản trị chiến lược', null, null, '5', '0', '653', '463', '1552656396', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4402', null, null, null, '5', '4401', '653', '463', '1552656396', null, null, null, '<p>-        Là sự<a class=\"cloze\" href=\"#mce_temp_url#\"> phác thảo</a> chương trình marketing dựa trên cơ sở <a class=\"cloze\" href=\"#mce_temp_url#\">khách hàng </a>được thiết kế để giúp doanh nghiệp <a class=\"cloze\" href=\"#mce_temp_url#\">nhận dạng</a> khách hàng mục tiêu hấp dẫn nhất và <a class=\"cloze\" href=\"#mce_temp_url#\">định vị </a>sản phẩm/dịch vụ để <a class=\"cloze\" href=\"#mce_temp_url#\">đáp ứng</a> các khách hàng đó</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4403', '4.	Quản trị thị trường chiến lược', null, null, '5', '0', '653', '463', '1552656488', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4404', null, null, null, '5', '4403', '653', '463', '1552656488', null, null, null, '<p>Là việc doanh nghiệp thực hiện các hoạt động <a class=\"cloze\" href=\"#mce_temp_url#\">quản trị thích ứng </a>với những <a class=\"cloze\" href=\"#mce_temp_url#\">đột biến</a> về chiến lược và những cơ hội  và nguy cơ mới xuất hiện, thực hiện các hoạt động xem xét các<a class=\"cloze\" href=\"#mce_temp_url#\"> biến động</a> của các điều kiện môi trường và thị trường một cách cập thời để điều chỉnh kịp thời với những thay đổi </p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4405', '5.	Phát triển chiến lược thị trường?', null, null, '5', '0', '653', '463', '1552656550', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4406', null, null, null, '5', '4405', '653', '463', '1552656550', null, null, null, '<p>-        Là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">triển khai</a> các yếu tố cấu trúc và nội dung chiến lược và <a class=\"cloze\" href=\"#mce_temp_url#\">quản trị thích ứng</a> chiến lược với những thay đổi và biến động có tính chiến lược từ môi trường marketing của doanh nghiệp nhằm<a class=\"cloze\" href=\"#mce_temp_url#\"> đảm bảo tăng trường </a>hiệu suất mục tiêu chiến lược marketing và <a class=\"cloze\" href=\"#mce_temp_url#\">kiến tạo</a> những phát triển chất lượng hệ thông marketing của doanh nghiêp theo thời gian.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4407', '1.	Cơ sở phân đoạn thị trường người tiêu dùng?', null, null, '5', '0', '654', '463', '1552656681', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4408', null, null, null, '5', '4407', '654', '463', '1552656681', null, null, null, '<p><strong>-        Phân đoạn theo tiêu thức<a class=\"cloze\" href=\"#mce_temp_url#\"> địa lý</a></strong></p>\r\n<p>+ Các tiêu thức địa lý thường dùng:Quốc gia,nông thôn-thành thị,…</p>\r\n<p>+Các tiêu thức đại lý thường đi kèm với tiêu thức nhân khẩu học</p>\r\n<p>-       <strong> Phân đoạn theo tiêu thức <a class=\"cloze\" href=\"#mce_temp_url#\">nhân khẩu học</a></strong></p>\r\n<p>+Theo Các tiêu thức nhân khẩu học người ta thường chia thị trường thành các nhóm căn cứ theo các biến số như tuooritasc, giới thính, quy mô gia đình, dân tộc..</p>\r\n<p>-      <strong>  Phân đoạn theo nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">tâm lý học</a></strong></p>\r\n<p>+ Khi phân đoạn theo nguyên tắc tâm lý học được chia thành các nhóm theo các đặc tính như thái độ, nhân cách, động cơ,lối sống…</p>\r\n<p>-        <strong>Phân đoạn theo <a class=\"cloze\" href=\"#mce_temp_url#\">hành vi </a>người tiêu dùng</strong></p>\r\n<p>+Nhóm các tiêu thức theo hành vi người tiêu dùng bao gồm: lợi ích theo đuổi, mức độ tiêu thụ, mức độ trung thành….</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4409', '2.	Phân đoạn thị trường khách hàng tổ chức', null, null, '5', '0', '654', '463', '1552656725', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4410', null, null, null, '5', '4409', '654', '463', '1552656725', null, null, null, '<p>-        Theo biến sô <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động</a></p>\r\n<p>-        Theo <a class=\"cloze\" href=\"#mce_temp_url#\">cách mua hàng</a></p>\r\n<p>-        Theo <a class=\"cloze\" href=\"#mce_temp_url#\">đặc điểm</a> cá nhân</p>\r\n<p>-        Các <a class=\"cloze\" href=\"#mce_temp_url#\">nhân tố tình huống</a></p>\r\n<p>-        Các  biến số<a class=\"cloze\" href=\"#mce_temp_url#\"> nhân khẩu học</a></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4411', '3.	Phân đoạn thị trường hiệu quả?', null, null, '5', '0', '654', '463', '1552656776', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4412', null, null, null, '5', '4411', '654', '463', '1552656776', null, null, null, '<p>-        Có thể <a class=\"cloze\" href=\"#mce_temp_url#\">đo lường</a> được: quy mii, sức mua..</p>\r\n<p>-        Có <a class=\"cloze\" href=\"#mce_temp_url#\">quy mô</a> đáng kể</p>\r\n<p>-        Có thể <a class=\"cloze\" href=\"#mce_temp_url#\">tiếp cận</a> được</p>\r\n<p>-        Phân biệt được các đoạn có thể phân biệt được về khái niệm và đáp ứng marketing mix khác nhau</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4413', 'phân tích đánh giá các phân đoạn thị trường và lựa chọn đoạn thị trường mục tiêu?', null, null, '5', '0', '655', '463', '1552656911', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4414', null, null, null, '5', '4413', '655', '463', '1552656911', null, null, null, '<ul>\r\n<li><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Quy mô </a>và <a class=\"cloze\" href=\"#mce_temp_url#\">sức tăng trưởng</a> của phân đoạn</span></em>: “ Đoạn thì trường có những đặc điểm về qui mô và sức tăng trưởng vừa sức không?\r\n<ul>\r\n<li>Các doanh nghiệp lớn thích những đoạn thị trường có khối lượng tiêu thụ lớn và thường coi nhẹ những khúc thị trường nhỏ</li>\r\n<li>Các doanh nghiệp nhỏ lại né tránh những đoạn thị trường lớn vì chúng đòi hỏi quá nhiều nguồn tài nguyên.</li>\r\n<li>Mức tăng trưởng lại là một đặc điểm mong muốn, nói chung các doanh nghiệp đều muốn có mức tiêu thụ và lợi nhuận ngày càng tăng</li>\r\n</ul>\r\n</li>\r\n<li><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Mức hấp dẫn</a> về cấu trúc của đoạn thị trường</span></em>:\r\n<ul>\r\n<li>Một phân đoạn thị trường có quy mô và mức tăng trưởng phù hợp tuy nhiên lại thiếu tiềm năng sinh lời. M.Porter đã chỉ ra 5 lực lượng quyết định mức hấp dẫn nội tại về lời nhuận lâu dài của một thị trường hay đoạn thị trường==&gt;Doanh nghiệp cần có sự đánh giá các yếu tố này</li>\r\n</ul>\r\n</li>\r\n<li><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Mục tiêu</a> và<a class=\"cloze\" href=\"#mce_temp_url#\"> nguồn lực</a> của công ty</span></em>:\r\n<ul>\r\n<li>Một phân đoạn thị trường hấp dẫn vẫn có thể bị loại bỏ nếu nó không phù hợp với mục tiêu và nguồn lực của công ty.</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4415', 'phân tích nội dung phát triển và truyền thông chiến lược định vị?', null, null, '5', '0', '656', '463', '1552658701', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4416', null, null, null, '5', '4415', '656', '463', '1552658701', null, null, null, '<p><strong>Định vị</strong> là quá trình <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">thông đạt </a>các giá trị <a class=\"cloze\" href=\"#mce_temp_url#\">đặc trưng </a>của thương hiệu, sản phẩm vào tâm trí khách hàng mục tiêu.</p>\r\n<p><strong> </strong></p>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\"><a class=\"cloze\" href=\"#mce_temp_url#\">Số lượng</a> những dị biệt cần quảng bá</span></em></strong></p>\r\n<ul>\r\n<li>Nên quảng bá tích cực <a class=\"cloze\" href=\"#mce_temp_url#\">một lợi ích </a>duy nhất đối với thị trường mục tiêu</li>\r\n<li>Nên định vị doanh nghiệp ở <a class=\"cloze\" href=\"#mce_temp_url#\">mức độ cao</a> hơn chứ không đơn thuần là một dị biệt hóa</li>\r\n</ul>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Cần <a class=\"cloze\" href=\"#mce_temp_url#\">nhấn mạnh </a>và những dị biệt</span></em></strong></p>\r\n<ul>\r\n<li>Những <a class=\"cloze\" href=\"#mce_temp_url#\">đặc tính </a>sản phẩm/thương hiệu thật sự khác biệt và có ý nghĩa giá trị giúp <a class=\"cloze\" href=\"#mce_temp_url#\">phân biệt </a>cung ứng của doanh nghiệp với đối thủ cạnh tranh.</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Tiêu chuẩn</a> cho một khác biệt/dị biệt: Quan trọng,độc đáo,dễ truyền đạt, giá cả phải chăng, lợi nhuận</li>\r\n</ul>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Phát triển tuyên ngôn về <a class=\"cloze\" href=\"#mce_temp_url#\">định vị</a>:</span></em></strong></p>\r\n<ul>\r\n<li>Công ty không những phải<a class=\"cloze\" href=\"#mce_temp_url#\"> xây dựng</a> một <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược định v</a>ị rõ ràng mà còn<a class=\"cloze\" href=\"#mce_temp_url#\"> tuyên truyề</a>n nó một cách hiệu quả</li>\r\n<li>Chất lượng, bao bì, kênh phân phối, khuyến mãi,...phải cùng truyền bá và hỗ trợ hình ảnh cho thương hiệu</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Danh tiếng</a> của nhà sản xuất cũng góp phần nhận thức vào chất lượng.</li>\r\n</ul>\r\n<p>-        <strong><em><span style=\"text-decoration: underline;\">Thực hiện và truyền thông định</span></em></strong> vị</p>\r\n<ul>\r\n<li>Tiến hành những bước tiến mạnh mẽ để truyền thông định vị mon đợi đến  với khách hàng mục tiêu bằng nỗ lực marketing mix</li>\r\n<li>Duy trình định vị truyền thông qua hiệu suất và truyền thông phù hợp</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4417', '1.	Giai đoạn triển khai?', null, null, '5', '0', '657', '463', '1552659384', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4418', null, null, null, '5', '4417', '657', '463', '1552659384', null, null, null, '<ul>\r\n<li>Chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">hớt váng</a> <a class=\"cloze\" href=\"#mce_temp_url#\">chớp nhoáng</a>: tung ra sản phẩm mới với giá cao và xúc tiến mạnh.</li>\r\n<li>Chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">hớt váng</a> <a class=\"cloze\" href=\"#mce_temp_url#\">từ từ</a>: tung ra sản phẩm với mức giá cao và xúc tiến thấp.</li>\r\n<li>Chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">xâm nhập</a> <a class=\"cloze\" href=\"#mce_temp_url#\">chớp nhoáng</a>: tung ra sản phẩm với giá thấp và xúc tiến mạnh</li>\r\n<li>Chiến lược <a class=\"cloze\" href=\"#mce_temp_url#\">xâm nhập</a> <a class=\"cloze\" href=\"#mce_temp_url#\">từ từ</a>: Tung sản phẩm với giá cao và xúc tiến thấp</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4419', '2. Giai đoạn phát triển?', null, null, '5', '0', '657', '463', '1552659498', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4420', null, null, null, '5', '4419', '657', '463', '1552659498', null, null, null, '<p>- Giá cả vẫn giữ nguyên hay có giảm đôi chút vì nhu cầu tăng nhất nhanh. Các công ty vẫn<a class=\"cloze\" href=\"#mce_temp_url#\"> duy trì </a>chi phí khuyến mãi của mình ở mức cũ hoặc có tăng đôi chút để đối phó với sự cạnh tranh. Chiến lược kéo dài mức tăng trưởng của thị trường</p>\r\n<ul>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Nâng cao chất lượng</a> sản phẩm,bổ sung thêm những tính chất mới và cải tiến kiểu dáng</li>\r\n<li>Bổ sung những mẫu mã mới và   những sản phẩm che sườn</li>\r\n<li><a class=\"cloze\" href=\"#mce_temp_url#\">Xâm nhập</a> khúc thị trường mới</li>\r\n<li>Chuyển từ quảng cáo mức độ biết đến sản phẩm sang quảng cáo mức độ ưu thích sản phẩm</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4421', '3. Giai đoạn sung mãn', null, null, '5', '0', '657', '463', '1552659621', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4422', null, null, null, '5', '4421', '657', '463', '1552659621', null, null, null, '<p><strong>- Cải biến<a class=\"cloze\" href=\"#mce_temp_url#\"> thị trường</a></strong>: Tăng số người sử dụng nhãn hiệu</p>\r\n<p>+Thay đổi thái độ của người không sử dụng</p>\r\n<p>+ Xâm nhập khúc thị trường mới: có thể mới về khu vực địa lý, nhân khẩu học..</p>\r\n<p>+Giành khách hàng từ đối thủ cạnh tranh</p>\r\n<p>-  <strong>      Cải tiến <a class=\"cloze\" href=\"#mce_temp_url#\">sản phẩm</a></strong>:</p>\r\n<ul>\r\n<li>Cải biến chất lượng nhằm nâng cao tính năng sản phẩm</li>\r\n<li>Cải tiến tính chất nhằm bổ sung thêm những tính chất mới làm tăng thêm công dụng, mức độ an toàn hay sự thuận tiện của sản phẩm</li>\r\n<li>Cải tiến kiểu dáng sản phẩm</li>\r\n</ul>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4423', '4. Giai đoạn suy thoái:', null, null, '5', '0', '657', '463', '1552659699', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4424', null, null, null, '5', '4423', '657', '463', '1552659699', null, null, null, '<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Phát hiện</a> những sản phẩm yếu kém: thiết lập một hệ thống để phát hiện những sản phẩm yếu kém</p>\r\n<p>-        xác định <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a> marketing: 5 kiểu chiến lược</p>\r\n<ul>\r\n<li>Tăng vốn tư của công ty</li>\r\n<li>Duy trì mức đầu tư cho công ty</li>\r\n<li>Giảm có chọn lọc mức đầu tư</li>\r\n<li>Thu hoạch vốn đầu tư để trang trải nhanh cho các khoản tiền mặt</li>\r\n<li>Giải thể nhanh chóng bằng cách bán tài sản một cách có lợi nhất</li>\r\n</ul>\r\n<p>-      <a class=\"cloze\" href=\"#mce_temp_url#\">  Loại bỏ</a> sản phẩm: có thể bán nó cho một công ty khác hoặc thanh lý nhãn hiệu.</p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4425', 'Trình bày/phân tích các nội trong kế hoạch hóa chiến lược của doanh nghiệp.', null, null, '5', '0', '658', '463', '1552659835', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4426', null, null, null, '5', '4425', '658', '463', '1552659835', null, null, null, '<p><strong><em>1.     </em></strong><strong><em><span style=\"text-decoration: underline;\">Xác định sứ <a class=\"cloze\" href=\"#mce_temp_url#\">mạng kinh doanh</a></span></em></strong></p>\r\n<ul>\r\n<li>Doanh nghiệp cua ta là gì?( xác định lĩnh vực kinh doanh)</li>\r\n<li>Ai là khách hàng của doanh nghiệp</li>\r\n<li>Cái gì có ý nghĩa là quan trọng  với khách hàng đó</li>\r\n<li>Doanh nghiệp của chúng ta sẽ ra sao. doanh nghiệp của chúng ta cả phải làm như thế nào?</li>\r\n</ul>\r\n<p><strong><em>2. Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> của doanh nghiệp:</em></strong></p>\r\n<ul>\r\n<li>Là trạng thái cuối cùng của sứ mạng kinh doanh của doanh nghiệp và là những gì mà doanh nghiệp tìm kiếm trong hoạt động dài hơi và diễn biến marketing của mình.</li>\r\n<li>Sứ mạng kinh doanh được chuyến hóa thành các mục tiêu cụ thể và có khả năng đạt được</li>\r\n<li>Rất ít các doanh nghiệp theo đuổi 1 mục tiêu. Hầu hết các doanh nghiệp đều theo đuổi một số mục tiêu như : Lợi nhuận, tăng thị phần, danh tiếng...</li>\r\n</ul>\r\n<p><strong><em><span style=\"text-decoration: underline;\">3. Xác định <a class=\"cloze\" href=\"#mce_temp_url#\">chiến lược</a></span></em></strong></p>\r\n<ul>\r\n<li>Là việc lựa chọn các hướng đi chủ yếu của doanh nghiệp nhằm theo đuối mục tiêu đã đặt ra</li>\r\n<li>3 nhóm chiến lược:\r\n<ul>\r\n<li>Sản phẩm/thị trường</li>\r\n<li>lợi thế cạnh tranh</li>\r\n<li>giá trị</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>Các nhóm có sơ đồ trên web</p>\r\n<p><strong><em><span style=\"text-decoration: underline;\">4. Phân tích <a class=\"cloze\" href=\"#mce_temp_url#\">hồ sơ kinh doanh</a></span></em></strong>:</p>\r\n<ul>\r\n<li>Sử dụng kĩ thuật BCG, GE để phác họa hồ sơ kinh doanh của đơn vị kinh doanh chiến lược SBUs- xác định cần đầu tư bao nhiêu cho từng SBU</li>\r\n</ul>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4427', null, null, null, '5', '0', '659', '463', '1552659953', null, null, null, null, null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4428', null, null, null, '5', '4427', '659', '463', '1552659953', null, null, null, '<p><strong><em><span style=\"text-decoration: underline;\">1.Phân tích<a class=\"cloze\" href=\"#mce_temp_url#\"> bối cảnh SWOT</a></span></em></strong></p>\r\n<ul>\r\n<li>Marketers phân tích và xác lập vị trí của doanh nghiệp đặc biệt là phòng marketing trong điều kiện quá khứ, hiện tại, tương lại.</li>\r\n<li>Phân tích SWOT là hoạch định: Điểm mạnh, điểm yếu, cơ hội, thách thức.</li>\r\n</ul>\r\n<p><strong><em><span style=\"text-decoration: underline;\">2. <a class=\"cloze\" href=\"#mce_temp_url#\">Thiết lập mục tiêu</a> marketing</span></em></strong></p>\r\n<ul>\r\n<li>Các mục tiêu marketing phổ biến: Lợi nhuận, lượng bán,số lượng trung gian có tham gia tiêu thụ...</li>\r\n</ul>\r\n<p><strong><em><span style=\"text-decoration: underline;\">3. Lựa chọn <a class=\"cloze\" href=\"#mce_temp_url#\">thị trường mục tiêu</a></span></em></strong></p>\r\n<ul>\r\n<li>Thị trường sản phẩm được phân đoạn</li>\r\n<li>NHu cầu cụ thể của từng đoạn thị trường</li>\r\n<li>Quy mô thị trường là bao nhiêu?</li>\r\n<li>Sự tăng trưởng của thị trường ntn?</li>\r\n<li>Các thức để thỏa mãn nhu cầu của từng đoạn thị trường</li>\r\n</ul>\r\n<p>4<strong><em><span style=\"text-decoration: underline;\">. <a class=\"cloze\" href=\"#mce_temp_url#\">Phát triển</a> marketing mix</span></em></strong></p>\r\n<div id=\"eJOY__extension_root\" style=\"all: unset;\"> </div>', null, '62', null, null, null, null, null);
INSERT INTO `question` VALUES ('4431', ' Câu 3: CM DN là một đơn vị sản xuất, một đơn vị phân phối?', null, null, '2', '0', '483', '1', '1552662756', null, null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('4432', ' Câu 4: Chứng minh DN là một hệ thống? Tại sao nói DN là một hệ thống mở, hoạt động của DN phải tính đến tác động của môi trường?', null, null, '2', '0', '483', '1', '1552662756', null, null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('4433', ' Câu 5: Nội dung các quy luật của thị trường tác động đến kinh doanh của DN.', null, null, '2', '0', '483', '1', '1552662756', null, null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('4434', ' Câu 6: Tiêu thụ hàng hóa là gì? MQH giữa các hoạt động của quá trình tiêu thụ hàng hóa? Phân tích vai trò của tiêu thụ hàng hóa đối với DN?', null, null, '2', '0', '483', '1', '1552662756', null, null, null, null, null, '36', null, null, null, null, null);
INSERT INTO `question` VALUES ('4435', 'Quan điểm HCM về tăng cường tính nghiêm minh của pháp luật đi đôi với đẩy mạnh giáo dục đạo đức?', '', '', '1', '0', '660', '51', '1552662978', null, '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;line-height:130%\"><span style=\"font-size: 14.0pt;line-height:130%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font: major-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US\">- Kết hợp đức trị và pháp trị<o:p></o:p></span></p> <p class=\"MsoListParagraphCxSpLast\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;line-height:130%\"><span style=\"font-size: 14.0pt;line-height:130%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font: major-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US\">- Thống nhất hài hòa giữa lý trí và tình cảm<o:p></o:p></span></p><br>', '', 'Quan điểm HCM về tăng cường tính nghiêm minh của pháp luật đi đôi với đẩy mạnh giáo dục đạo đức?', '<p class=\"MsoListParagraphCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;line-height:130%\"><span style=\"font-size: 14.0pt;line-height:130%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font: major-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US\">- HCM kết hợp nhuần nhuyễn đức trị và pháp trị trong quản lý xã hội<o:p></o:p></span></p> <p class=\"MsoListParagraphCxSpLast\" style=\"margin-bottom:0in;margin-bottom:.0001pt; mso-add-space:auto;text-align:justify;line-height:130%\"><span style=\"font-size: 14.0pt;line-height:130%;font-family:&quot;Times New Roman&quot;,&quot;serif&quot;;mso-ascii-theme-font: major-latin;mso-hansi-theme-font:major-latin;mso-bidi-theme-font:major-latin; mso-ansi-language:EN-US\">- Trong việc thực thi quyền hạn và trách nhiệm của mình HCM luôn thống nhất hài hòa giữa lý trí và tình cảm<o:p></o:p></span></p><br>', '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4436', 'Quan điểm HCM về tăng cường tính nghiêm minh của pháp luật đi đôi với đẩy mạnh giáo dục đạo đức?', null, null, '5', '0', '660', '51', '1552663093', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4437', null, null, null, '5', '4436', '660', '51', '1552663093', null, null, null, '<p>-    HCM kết hợp nhuần nhuyễn <a class=\"cloze\" href=\"#mce_temp_url#\">đức trị</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">pháp trị</a> trong <a class=\"cloze\" href=\"#mce_temp_url#\">quản lý xã hội</a></p>\r\n<p>-    Trong việc thực thi <a class=\"cloze\" href=\"#mce_temp_url#\">quyền hạn</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">trách nhiệm</a> của mình HCM luôn thống nhất hài hòa giữa <a class=\"cloze\" href=\"#mce_temp_url#\">lý trí</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tình cảm</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4438', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4439', null, null, null, '5', '4438', '661', '6', '1552663143', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = \\frac{{x.{e^x}}}{{y\\sqrt {1 + {y^2}} }}\\)</span> là<span class=\"math-tex\">\\(\\sqrt {{{(1 + {y^2})}^3}} = \\square.{e^x} - \\square.{e^x} + C\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">3x,3</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4440', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4441', null, null, null, '5', '4440', '661', '6', '1552663143', null, '', null, ' nghiệm tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = 2 + 2y + x + xy\\)</span> là<span class=\"math-tex\">\\(y = - 1;y = C.{e^{\\frac{{{x^2}}}{2} + \\square}} - 1\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">2x</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4442', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4443', null, null, null, '5', '4442', '661', '6', '1552663143', null, '', null, 'Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{dy}}{{d{\\rm{x}}}} = 1 + {y^2}\\)</span> là <span class=\"math-tex\">\\(\\[{\\mathop{\\rm arctany}\\nolimits} = \\square+ C\\]\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4444', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4445', null, null, null, '5', '4444', '661', '6', '1552663143', null, '', null, '<p>Tích phân tổng quát của:<span class=\"math-tex\">\\(y\' = {e^{x + y}} + {e^{x - y}}\\)</span> là <span class=\"math-tex\">\\(\\arctan{\\square^y} = {\\square^x} + C\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">e,e</a></p> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4446', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4447', null, null, null, '5', '4446', '661', '6', '1552663143', null, '', null, ' nghiệm tổng quát của:<span class=\"math-tex\">\\(y\' = 4{{\\rm{x}}^3}y\\)</span> là<span class=\"math-tex\">\\(y = 0;y = C.{e^{\\square}}\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x^4</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4448', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4449', null, null, null, '5', '4448', '661', '6', '1552663143', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\tan y{\\rm{dx}} - x\\ln {\\rm{xd}}y = 0\\)</span> <span class=\"math-tex\">\\(\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\ln {\\rm{x}}) = C\\)</span> là<span class=\"math-tex\">\\(\\frac{1}{{{{\\sin }^2}y}} + \\ln (\\square) = C\\)</span>. <a class=\"clozetip\" title=\"\" href=\"#\">lnx</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4450', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4451', null, null, null, '5', '4450', '661', '6', '1552663143', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{4 + {y^2}}}{{\\sqrt {{x^2} + 4{\\rm{x}} + 13} }} = \\frac{{3y + 2}}{{x + 1}}y\'\\)</span> là<span class=\"math-tex\">\\(\\square\\ln ({y^2} + 4) + \\arctan \\square = \\sqrt {{{(x + 2)}^2} + 9} - \\ln \\left| {x + 2 + \\sqrt {{{(x + 2)}^2} + 9} } \\right| + C\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">3/2,y/2</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4452', 'Điền biểu thức đúng vào chỗ trống bằng cách giải phương trình biến số phân li(điền theo thứ tự ngăn cách nhau bằng dấu phẩy). ', '', null, '5', '0', '661', '6', '1552663143', null, null, null, null, null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4453', null, null, null, '5', '4452', '661', '6', '1552663143', null, '', null, ' Tích phân tổng quát của:<span class=\"math-tex\">\\(\\frac{{x{\\rm{dx}}}}{{{x^2} + 1}} + (y + 1)dy = 0\\)</span> là<span class=\"math-tex\">\\(\\frac{{{y^2}}}{{\\square}} + y = \\frac{{ - 1}}{{\\square}}\\ln ({x^2} + 1) + C\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">2,2</a> ', null, '33', null, null, null, null, null);
INSERT INTO `question` VALUES ('4456', 'Khái niệm văn hóa, vị trí, vai trò của văn hóa trong TTHCM?', null, null, '2', '0', '662', '51', '1552664529', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4457', 'Khái niệm văn hóa trong TTHCM', null, null, '5', '0', '662', '51', '1552664620', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4458', null, null, null, '5', '4457', '662', '51', '1552664620', null, null, null, '<p>“Vì lẽ <a class=\"cloze\" href=\"#mce_temp_url#\">sinh tồn</a> cũng như <a class=\"cloze\" href=\"#mce_temp_url#\">mục đích</a> của cuộc sống, loài người mới sáng tạo và phát minh ra <a class=\"cloze\" href=\"#mce_temp_url#\">ngôn ngữ</a>, chữ viết, <a class=\"cloze\" href=\"#mce_temp_url#\">đạo đức</a>, pháp luận, khoa học, <a class=\"cloze\" href=\"#mce_temp_url#\">tôn giáo</a>, văn học, nghệ thuật, những công cụ cho <a class=\"cloze\" href=\"#mce_temp_url#\">sinh hoạt hằng ngày</a> về mặc, ăn, ở và các <a class=\"cloze\" href=\"#mce_temp_url#\">phương thức sử dụng</a>. Toàn bộ những sáng tạo và phát minh đó tức là <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa</a>.”</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4459', 'Vị trí, vai trò của văn hóa trong TTHCM', null, null, '5', '0', '662', '51', '1552664810', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4460', null, null, null, '5', '4459', '662', '51', '1552664810', null, null, null, '<p>- Văn hóa là <a class=\"cloze\" href=\"#mce_temp_url#\">đời sống tinh thần</a> của xã hội, thuộc kiến thức <a class=\"cloze\" href=\"#mce_temp_url#\">thượng tầng</a></p>\r\n<p>  + Trong quan hệ với <a class=\"cloze\" href=\"#mce_temp_url#\">chính trị - xã hội</a></p>\r\n<ul>\r\n<li>Chính trị - xã hội được <a class=\"cloze\" href=\"#mce_temp_url#\">giải phóng</a> thì <a class=\"cloze\" href=\"#mce_temp_url#\">văn hóa</a> mới được giải phóng</li>\r\n<li>Chính trị giải phóng <a class=\"cloze\" href=\"#mce_temp_url#\">mở đường</a> cho văn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển</a></li>\r\n</ul>\r\n<p>  + Trong quan hệ với <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a>: xây dựng <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a> để tạo điều kiện cho việc <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển</a> văn hóa. Phải đẩy mạnh <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a> trước</p>\r\n<p>- Văn hóa không thể <a class=\"cloze\" href=\"#mce_temp_url#\">đứng ngoài</a> mà phải ở trong <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a> và chính trị, phục vụ <a class=\"cloze\" href=\"#mce_temp_url#\">nhiệm vụ</a> chính trị và thúc đẩy <a class=\"cloze\" href=\"#mce_temp_url#\">sự phát triển</a> kinh tế</p>\r\n<p>  + Văn hóa phải <a class=\"cloze\" href=\"#mce_temp_url#\">tích cực</a>, chủ động tham gia thực hiện những <a class=\"cloze\" href=\"#mce_temp_url#\">nhiệm vụ chính trị</a>, thúc đẩy xây dựng và phát triển <a class=\"cloze\" href=\"#mce_temp_url#\">kinh tế</a></p>\r\n<p>  + Làm kinh tế và chính trị cũng phải có <a class=\"cloze\" href=\"#mce_temp_url#\">tính văn hóa</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4461', 'Tính dân tộc của nền văn hóa mới trong TTHCM', null, null, '5', '0', '663', '51', '1552665529', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4462', null, null, null, '5', '4461', '663', '51', '1552665529', null, null, null, '<p>Tính dân tộc</p>\r\n<p>+ Mang <a class=\"cloze\" href=\"#mce_temp_url#\">đặc trưng</a> của dân tộc</p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Giữ gìn</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">kế thừa</a>, phát huy những <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống</a> dân tộc</p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Phát triển</a> những truyền thống dân tộc cho <a class=\"cloze\" href=\"#mce_temp_url#\">phù hợp</a> với điều kiện <a class=\"cloze\" href=\"#mce_temp_url#\">lịch sử mới</a> của đất nước</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4463', 'Tính khoa học của nền văn hóa mới trong TTHCM', null, null, '5', '0', '663', '51', '1552665688', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4464', null, null, null, '5', '4463', '663', '51', '1552665688', null, null, null, '<p>Tính khoa học</p>\r\n<p>+ Tính <a class=\"cloze\" href=\"#mce_temp_url#\">hiện đại</a>, tiên tiến, thuận với trào lưu <a class=\"cloze\" href=\"#mce_temp_url#\">tiến hóa</a> của thời đại</p>\r\n<p>+ Đấu tranh với những gì <a class=\"cloze\" href=\"#mce_temp_url#\">trái khoa học</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">phản tiến bộ</a></p>\r\n<p>+ <a class=\"cloze\" href=\"#mce_temp_url#\">Gạn đục khơi trong</a>, kế thừa <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống</a> tốt đẹp của dân tộc và tiếp thu <a class=\"cloze\" href=\"#mce_temp_url#\">tinh hoa văn hóa nhân loại</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4465', 'Tính đại chúng của nền văn hóa mới trong TTHCM', null, null, '5', '0', '663', '51', '1552665827', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4466', null, null, null, '5', '4465', '663', '51', '1552665827', null, null, null, '<p>Tính đại chúng</p>\r\n<p>+ Phải <a class=\"cloze\" href=\"#mce_temp_url#\">phục vụ</a> nhân dân</p>\r\n<p>+ Phù hợp với <a class=\"cloze\" href=\"#mce_temp_url#\">nguyện vọng</a> của nhân dân</p>\r\n<p>+ Đậm đà <a class=\"cloze\" href=\"#mce_temp_url#\">tính nhân văn</a></p>\r\n<p>+ Do nhân dân <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> nên</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4467', 'TTHCM về tính chất của nền văn hóa mới?', null, null, '2', '0', '663', '51', '1552665948', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4468', 'TTHCM về chức năng của nền văn hóa mới?', null, null, '2', '0', '664', '51', '1552666705', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4469', 'TTHCM về chức năng của nền văn hóa mới?', null, null, '5', '0', '664', '51', '1552667301', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4470', null, null, null, '5', '4469', '664', '51', '1552667301', null, null, null, '<p>- Bồi dưỡng <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng</a> đúng đắn và những <a class=\"cloze\" href=\"#mce_temp_url#\">tình cảm cao đẹp</a></p>\r\n<p>  + Mục đích: bồi dưỡng, nêu cao <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng đúng đắn</a> và tình cảm cao đẹp cho nhân dân, loại bỏ những <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng sai lầm</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">tình cảm thấp hèn</a> có thể có trong tư tưởng, tình cảm mỗi người</p>\r\n<p>  + Văn hóa phải đặc biệt quan tâm đến những <a class=\"cloze\" href=\"#mce_temp_url#\">tư tưởng</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tình cảm lớn</a>, chi phối <a class=\"cloze\" href=\"#mce_temp_url#\">đời sống tinh thần</a> của mỗi người và cả dân tộc</p>\r\n<p>  + Chức năng này phải được tiến hành <a class=\"cloze\" href=\"#mce_temp_url#\">thường xuyên liên tục</a></p>\r\n<p>- Mở rộng hiểu biết, nâng cao <a class=\"cloze\" href=\"#mce_temp_url#\">dân trí</a></p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Dân trí</a> là trình độ hiểu biết, kiến thức của người dân</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Nội dung</a> của nâng cao dân trí là bắt đầu từ chỗ <a class=\"cloze\" href=\"#mce_temp_url#\">biết đọc</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">biết viết</a>, tiếp đó là sự hiểu biết các lĩnh vực khác nhau của <a class=\"cloze\" href=\"#mce_temp_url#\">đời sống xã hội</a> như kinh tế, chính trị, khoa học, kĩ thuật, thực tiễn VN và thế giới.</p>\r\n<p>  + Nâng cao dân trí nhằm hướng vào <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu chung</a> là <a class=\"cloze\" href=\"#mce_temp_url#\">độc lập dân tộc</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa xã hội</a></p>\r\n<p>- Bồi dưỡng những <a class=\"cloze\" href=\"#mce_temp_url#\">phẩm chất</a>, phong cách và <a class=\"cloze\" href=\"#mce_temp_url#\">lối sống</a> tốt đẹp, hướng <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a> đến chân, thiện, mỹ để <a class=\"cloze\" href=\"#mce_temp_url#\">hoàn thiện</a> bản thân</p>\r\n<p>  + HCM đề ra <a class=\"cloze\" href=\"#mce_temp_url#\">phẩm chất</a>, phong cách cần thiết để mỗi người <a class=\"cloze\" href=\"#mce_temp_url#\">tự tu dưỡng</a>, đặc biệt quan tâm đến phẩm chất <a class=\"cloze\" href=\"#mce_temp_url#\">đạo đức - chính trị</a> của cán bộ, đảng viên.</p>\r\n<p>  + Văn hóa giúp con người làm <a class=\"cloze\" href=\"#mce_temp_url#\">nảy nở</a> cái tốt đẹp, giảm bớt cái <a class=\"cloze\" href=\"#mce_temp_url#\">lạc hậu</a>, xấu xa, <a class=\"cloze\" href=\"#mce_temp_url#\">bảo thủ</a>, vươn tới cái <a class=\"cloze\" href=\"#mce_temp_url#\">chân</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">thiện</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">mỹ</a> để hoàn thiện bản thân</p>\r\n<p>  + Phải làm cho văn hóa <a class=\"cloze\" href=\"#mce_temp_url#\">thấm sâu</a> vào <a class=\"cloze\" href=\"#mce_temp_url#\">tâm lý quốc dân</a>. Văn hóa phải <a class=\"cloze\" href=\"#mce_temp_url#\">soi đường</a> cho quốc dân đi.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4471', 'Nguyên tắc nói đi đôi với làm, nêu gương về đạo đức', null, null, '5', '0', '665', '51', '1552667723', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4472', null, null, null, '5', '4471', '665', '51', '1552667723', null, null, null, '<p>+ “Nói đi đôi với làm”: nguyên tắc quan trọng <a class=\"cloze\" href=\"#mce_temp_url#\">bậc nhất</a> trong xây dựng nền đạo đức mới. Nói đi đôi với làm <a class=\"cloze\" href=\"#mce_temp_url#\">đối lập</a> hoàn toàn với thói <a class=\"cloze\" href=\"#mce_temp_url#\">đạo đức giả</a> của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp bóc lột</a>, nói một đằng làm một nẻo, thậm chí nói mà <a class=\"cloze\" href=\"#mce_temp_url#\">không làm</a>. HCM đã chỉ ra những <a class=\"cloze\" href=\"#mce_temp_url#\">biểu hiện</a> của thói đạo đức giả ở một số cán bộ, “vác mặt làm <a class=\"cloze\" href=\"#mce_temp_url#\">quan cách mạng</a>, nói mà không làm”, <a class=\"cloze\" href=\"#mce_temp_url#\">tẩy sạch quan liêu</a>, coi thường dân chúng của một số cán bộ.</p>\r\n<p>+ “Gương về đạo đức”: nét đẹp của <a class=\"cloze\" href=\"#mce_temp_url#\">truyền thống văn hóa phương Đông</a>. Nói đi đôi với làm phải <a class=\"cloze\" href=\"#mce_temp_url#\">gắn liền</a> với nêu gương đạo đức. HCM đã đào tạo các thế hệ cán bộ cách mạng VN không chỉ bằng <a class=\"cloze\" href=\"#mce_temp_url#\">lý luận cách mạng tiền phong</a> mà còn bằng chính <a class=\"cloze\" href=\"#mce_temp_url#\">tấm gương đạo đức</a> cao cả của mình.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4473', 'Nguyên tắc tu dưỡng đạo đức suốt đời', null, null, '5', '0', '665', '51', '1552667929', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4474', null, null, null, '5', '4473', '665', '51', '1552667929', null, null, null, '<p>+ Một nền đạo đức mới chỉ có thể được <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> trên cơ sở sự <a class=\"cloze\" href=\"#mce_temp_url#\">tự giác tu dưỡng</a> đạo đức của mỗi người.</p>\r\n<p>+ Đạo đức cách mạng đòi hỏi mỗi người phải <a class=\"cloze\" href=\"#mce_temp_url#\">tự giác rèn luyện</a> thông qua các <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động thực tiễn</a>, trong công việc, trong các mối quan hệ của mình, phải <a class=\"cloze\" href=\"#mce_temp_url#\">nhìn thẳng</a> vào mình, không <a class=\"cloze\" href=\"#mce_temp_url#\">tự lừa dối</a>, huyễn hoặc. <a class=\"cloze\" href=\"#mce_temp_url#\">Phát huy</a> cái hay cái tốt, cái thiện và khắc phụ những <a class=\"cloze\" href=\"#mce_temp_url#\">cái xấu cái ác</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4475', 'Nguyên tắc nói đi đôi với làm, nêu gương về đạo đức và nguyên tắc tu dưỡng đạo đức suốt đời trong TTHCM?', null, null, '2', '0', '665', '51', '1552668097', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4476', 'Quan điểm HCM về lực lượng CMGPDT? Lực lượng nào là quyết định và quan trọng nhất?', null, null, '5', '0', '668', '45', '1552668627', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4477', null, null, null, '5', '4476', '668', '45', '1552668627', null, null, null, '<p>* Quan điểm của HCM về lực lượng giải phóng dân tộc:</p>\r\n<p> - Thời cha ông ta cho rằng CM là công cuộc của <a class=\"cloze\" href=\"#\">một hai người</a> dẫn đến những cuộc bạo đông non như: khởi nghĩa Yên Thế, phong trào Cần Vương… Các cuộc khởi nghĩa này nhanh chóng bị đàn áp =&gt; HCM cho rằng <strong><span>cách mạng là công việc của <a class=\"cloze\" href=\"#\">toàn dân tộc</a></span></strong></p>\r\n<p>   + Người đánh giá cao <a class=\"cloze\" href=\"#\">vai trò</a> của quần chúng trong lực lượng đấu tranh vũ trang:</p>\r\n<ul>\r\n<li>Quần chúng là người <a class=\"cloze\" href=\"#\">bị áp bức </a>nên có khả năng tham gia đấu tranh CM</li>\r\n<li>Sức mạnh của quần chúng nhân dân là yếu tố <a class=\"cloze\" href=\"#mce_temp_url#\">then chốt</a> làm nên thắng lợi của CM</li>\r\n</ul>\r\n<p>   + Phê phán hành động lấy <a class=\"cloze\" href=\"#mce_temp_url#\">ám sát cá nhân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">bạo động</a> làm phương thức CM</p>\r\n<p> - <strong><span>Lực lượng cách mạng giải phóng dân tộc bao gồm toàn dân tộc</span></strong>: giai cấp <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân</a> và giai cấp <a class=\"cloze\" href=\"#mce_temp_url#\">nông dân</a> có vai trò quyết định, tầng lớp khác “chỉ là <a class=\"cloze\" href=\"#mce_temp_url#\">bầu bạn</a> cách mệnh với công nông thôi”</p>\r\n<p>   + Đại bộ phận GC công nhân, GC nông dân, tiểu tư sản, tư sản, trí thức, trung nông,… đi vào phe <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp vô sản</a></p>\r\n<p>   + Phú nông, trung, tiểu địa chủ, tư bản An Nam chưa rõ mặt phản CM thì phải <a class=\"cloze\" href=\"#mce_temp_url#\">lợi dụng</a>, ít lâu mới làm cho họ đứng <a class=\"cloze\" href=\"#mce_temp_url#\">trung lập</a></p>\r\n<p>   + Bộ phận nào đã ra mặt <a class=\"cloze\" href=\"#mce_temp_url#\">phản cách mạng</a> thì phải đánh đổ</p>\r\n<p>* Lực lượng GC <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">nông dân</a> là quan trọng nhất vì họ có số lượng <a class=\"cloze\" href=\"#mce_temp_url#\">đông nhất</a> nên có <a class=\"cloze\" href=\"#mce_temp_url#\">sức mạnh lớn nhất</a>. Họ bị <a class=\"cloze\" href=\"#mce_temp_url#\">áp bức bóc lột</a> nặng nề nhất nên “lòng cách mệnh càng bền, chí cách mệnh càng quyết, công nông là tay không chân rồi, nếu thua chỉ mất một cái kiếp khổ, nếu được thì được cả TG, cho nên họ gan góc”</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4478', 'TTHCM về các chuẩn mực đạo đức: Cần, Kiệm, Liêm, Chính, Chí công vô tư?', null, null, '5', '0', '666', '45', '1552668662', '1552669347', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4479', null, null, null, '5', '4478', '666', '45', '1552668662', '1552669347', null, null, '<p>- Cần là <a class=\"cloze\" href=\"#mce_temp_url#\">siêng năng</a>, chăm chỉ, lao động <a class=\"cloze\" href=\"#mce_temp_url#\">có kế hoạch</a>, có hiệu quả, có <a class=\"cloze\" href=\"#mce_temp_url#\">năng suất cao</a> với tinh thần <a class=\"cloze\" href=\"#mce_temp_url#\">tự lực cánh sinh</a>.</p>\r\n<p>- Kiệm là <a class=\"cloze\" href=\"#mce_temp_url#\">tiết kiệm</a> của nước của dân, không <a class=\"cloze\" href=\"#mce_temp_url#\">xa xỉ</a>, hoang phí, bừa bãi; không <a class=\"cloze\" href=\"#mce_temp_url#\">phô trương hình thức</a>, liên hoan, chè chén lu bù.</p>\r\n<p>- Liêm là luôn <a class=\"cloze\" href=\"#mce_temp_url#\">tôn trọng</a> của công và <a class=\"cloze\" href=\"#mce_temp_url#\">của dân</a>. Phải “trong sạch, không tham lam” tiền của, địa vị, danh tiếng.</p>\r\n<p>- Chính là <a class=\"cloze\" href=\"#mce_temp_url#\">thẳng thắn</a>, đứng đắn.</p>\r\n<p>  + Đối với mình: không được <a class=\"cloze\" href=\"#mce_temp_url#\">tự cao</a>, tự đại, tự phụ, phải <a class=\"cloze\" href=\"#mce_temp_url#\">khiêm tốn</a> học hỏi, <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển</a> cái hay, <a class=\"cloze\" href=\"#mce_temp_url#\">sửa chữa</a> cái dở của mình.</p>\r\n<p>  + Đối với người: không <a class=\"cloze\" href=\"#mce_temp_url#\">nịnh</a> người trên, không <a class=\"cloze\" href=\"#mce_temp_url#\">khinh</a> người dưới, <a class=\"cloze\" href=\"#mce_temp_url#\">thật thà</a> không dối trá.</p>\r\n<p>  + Đối với việc: phải để việc <a class=\"cloze\" href=\"#mce_temp_url#\">công</a> lên trên, lên trước, việc <a class=\"cloze\" href=\"#mce_temp_url#\">thiện</a> nhỏ mấy cũng làm, việc <a class=\"cloze\" href=\"#mce_temp_url#\">ác</a> nhỏ mấy cũng tránh.</p>\r\n<p>- Chí công vô tư là <a class=\"cloze\" href=\"#mce_temp_url#\">công bằng</a>, công tâm, không thiên tư, <a class=\"cloze\" href=\"#mce_temp_url#\">thiên vị</a>, làm việc gì cũng không nghĩ đến mình trước, chỉ biết vì <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng</a> vì <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc</a>, “lo trước thiên hạ, vui sau thiên hạ”. Chí công vô tư là nêu cao <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa tập thể</a> trừ bỏ <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cá nhân</a>.</p>\r\n<p>=&gt; Đây là phẩm chất đạo đức gắn liền với <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động hằng ngày</a> của mỗi người, là <a class=\"cloze\" href=\"#mce_temp_url#\">đại cương</a> đạo đức HCM. Cần, kiệm, liêm, chính, chí công vô tư cũng là một <a class=\"cloze\" href=\"#mce_temp_url#\">biểu hiện</a> cụ thể, một nội dung của phẩm chất “<a class=\"cloze\" href=\"#mce_temp_url#\">trung với nước, hiếu với dân</a>”</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4480', 'TTHCM về các chuẩn mực đạo đức: Cần, Kiệm, Liêm, Chính, Chí công vô tư?', null, null, '2', '0', '667', '51', '1552669001', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4481', 'TTHCM về các chuẩn mực đạo đức: Cần, Kiệm, Liêm, Chính, Chí công vô tư', null, null, '5', '0', '667', '51', '1552669315', '1552669381', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4482', null, null, null, '5', '4481', '667', '51', '1552669315', '1552669381', null, null, '<p>- Cần là <a class=\"cloze\" href=\"#mce_temp_url#\">siêng năng</a>, chăm chỉ, lao động <a class=\"cloze\" href=\"#mce_temp_url#\">có kế hoạch</a>, có hiệu quả, có <a class=\"cloze\" href=\"#mce_temp_url#\">năng suất cao</a> với tinh thần <a class=\"cloze\" href=\"#mce_temp_url#\">tự lực cánh sinh</a>.</p>\r\n<p>- Kiệm là <a class=\"cloze\" href=\"#mce_temp_url#\">tiết kiệm</a> của nước của dân, không <a class=\"cloze\" href=\"#mce_temp_url#\">xa xỉ</a>, hoang phí, bừa bãi; không <a class=\"cloze\" href=\"#mce_temp_url#\">phô trương hình thức</a>, liên hoan, chè chén lu bù.</p>\r\n<p>- Liêm là luôn <a class=\"cloze\" href=\"#mce_temp_url#\">tôn trọng</a> của công và <a class=\"cloze\" href=\"#mce_temp_url#\">của dân</a>. Phải “trong sạch, không tham lam” tiền của, địa vị, danh tiếng.</p>\r\n<p>- Chính là <a class=\"cloze\" href=\"#mce_temp_url#\">thẳng thắn</a>, đứng đắn.</p>\r\n<p>  + Đối với mình: không được <a class=\"cloze\" href=\"#mce_temp_url#\">tự cao</a>, tự đại, tự phụ, phải <a class=\"cloze\" href=\"#mce_temp_url#\">khiêm tốn</a> học hỏi, <a class=\"cloze\" href=\"#mce_temp_url#\">phát triển</a> cái hay, <a class=\"cloze\" href=\"#mce_temp_url#\">sửa chữa</a> cái dở của mình.</p>\r\n<p>  + Đối với người: không <a class=\"cloze\" href=\"#mce_temp_url#\">nịnh</a> người trên, không <a class=\"cloze\" href=\"#mce_temp_url#\">khinh</a> người dưới, <a class=\"cloze\" href=\"#mce_temp_url#\">thật thà</a> không dối trá.</p>\r\n<p>  + Đối với việc: phải để việc <a class=\"cloze\" href=\"#mce_temp_url#\">công</a> lên trên, lên trước, việc <a class=\"cloze\" href=\"#mce_temp_url#\">thiện</a> nhỏ mấy cũng làm, việc <a class=\"cloze\" href=\"#mce_temp_url#\">ác</a> nhỏ mấy cũng tránh.</p>\r\n<p>- Chí công vô tư là <a class=\"cloze\" href=\"#mce_temp_url#\">công bằng</a>, công tâm, không thiên tư, <a class=\"cloze\" href=\"#mce_temp_url#\">thiên vị</a>, làm việc gì cũng không nghĩ đến mình trước, chỉ biết vì <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng</a> vì <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc</a>, “lo trước thiên hạ, vui sau thiên hạ”. Chí công vô tư là nêu cao <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa tập thể</a> trừ bỏ <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cá nhân</a>.</p>\r\n<p>=&gt; Đây là phẩm chất đạo đức gắn liền với <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động hằng ngày</a> của mỗi người, là <a class=\"cloze\" href=\"#mce_temp_url#\">đại cương</a> đạo đức HCM. Cần, kiệm, liêm, chính, chí công vô tư cũng là một <a class=\"cloze\" href=\"#mce_temp_url#\">biểu hiện</a> cụ thể, một nội dung của phẩm chất “<a class=\"cloze\" href=\"#mce_temp_url#\">trung với nước, hiếu với dân</a>”</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4483', 'Trong TTHCM con người có vai trò như thế nào?', null, null, '2', '0', '669', '51', '1552670189', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4484', 'Trong TTHCM con người có vai trò như thế nào?', null, null, '5', '0', '669', '51', '1552670461', '1552670506', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4485', null, null, null, '5', '4484', '669', '51', '1552670461', '1552670506', null, null, '<p>- Con người là <a class=\"cloze\" href=\"#mce_temp_url#\">vốn quý</a> nhất, nhân tố <a class=\"cloze\" href=\"#mce_temp_url#\">quyết định</a> thành công của <a class=\"cloze\" href=\"#mce_temp_url#\">sự nghiệp cách mạng</a>.</p>\r\n<p>  + Nhân dân là người <a class=\"cloze\" href=\"#mce_temp_url#\">sáng tạo</a> ra mọi gía trị <a class=\"cloze\" href=\"#mce_temp_url#\">vật chất</a> và <a class=\"cloze\" href=\"#mce_temp_url#\">tinh thần</a>.</p>\r\n<p>  + Dân <a class=\"cloze\" href=\"#mce_temp_url#\">tài năng</a>, trí tuệ sáng tạo, có lòng sốt sắng, hăng hái để thực hiện <a class=\"cloze\" href=\"#mce_temp_url#\">con đường cách mạng</a>.</p>\r\n<p>- Con người vừa là <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a>, vừa là <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> của cách mạng; phải coi trọng, chăm sóc, phát huy <a class=\"cloze\" href=\"#mce_temp_url#\">nhân tố con người</a>.</p>\r\n<p>  + Con người là <a class=\"cloze\" href=\"#mce_temp_url#\">mục tiêu</a> của cách mạng nên mọi chủ trương, <a class=\"cloze\" href=\"#mce_temp_url#\">đường lối</a>, chính sách của Đảng, <a class=\"cloze\" href=\"#mce_temp_url#\">Chính Phủ</a> đều vì <a class=\"cloze\" href=\"#mce_temp_url#\">lợi ích chính đáng</a> của con người.</p>\r\n<p>  + Con người là <a class=\"cloze\" href=\"#mce_temp_url#\">động lực</a> của cách mạng được nhìn nhận trên <a class=\"cloze\" href=\"#mce_temp_url#\">phạm vi</a> cả nước, toàn thể <a class=\"cloze\" href=\"#mce_temp_url#\">đồng bào</a>, song trước hết là ở <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a> và nông dân. Con người là động lực chỉ có thể thực hiện được khi <a class=\"cloze\" href=\"#mce_temp_url#\">hoạt động</a> có tổ chức, có lãnh đạo. Vì vậy, cần có sự lãnh đạo của <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng Cộng sản</a>.</p>\r\n<p>  + Mối quan hệ giữa con người - mục tiêu và con người - động lực: <a class=\"cloze\" href=\"#mce_temp_url#\">Chăm lo</a> cho con người - mục tiêu tốt bao nhiêu thì sẽ tạo thành <a class=\"cloze\" href=\"#mce_temp_url#\">con người</a> - động lực tốt bấy nhiêu và ngược lại.</p>\r\n<p>  + Điều kiện để phát huy con người - mục tiêu và con người - động lực: kiên quyết khắc phục kịp thời các <a class=\"cloze\" href=\"#mce_temp_url#\">phản động lực</a> trong con người và tổ chức. Đó là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cá nhân</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4486', 'Quan điểm của HCM về bản chất giai cấp của Đảng Cộng sản Việt Nam. Bàn về vấn đề “Đảng của ai”, HCM đã có quan điểm như thế nào?', null, null, '5', '0', '671', '51', '1552670651', '1552670841', null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4487', null, null, null, '5', '4486', '671', '51', '1552670651', '1552670841', null, null, '<p>a. Quan điểm của HCM về bản chất giai cấp của Đảng Cộng sản Việt Nam</p>\r\n<p>- ĐCSVN là Đảng của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đội tiên phong</a> của giai cấp công nhân, mang <a class=\"cloze\" href=\"#mce_temp_url#\">bản chất</a> giai cấp công nhân. Cơ sở khẳng định:</p>\r\n<p>  + Từ <a class=\"cloze\" href=\"#mce_temp_url#\">sứ mệnh lịch sử</a> của giai cấp công nhân</p>\r\n<p>  + Nền tảng lý luận và tư tưởng của Đảng là <a class=\"cloze\" href=\"#mce_temp_url#\">Chủ nghĩa Mác Lênin</a>, mục tiêu của Đảng cần đạt tới là <a class=\"cloze\" href=\"#mce_temp_url#\">chủ nghĩa cộng sản</a></p>\r\n<p>  + Đảng tuân thủ một cách nghiêm túc, chặt chẽ những nguyên tắc <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng đảng kiểu mới</a> của giai cấp vô sản</p>\r\n<p>- Đảng không những là Đảng của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a> mà còn là Đảng của <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân lao động</a> và của <a class=\"cloze\" href=\"#mce_temp_url#\">toàn dân tộc</a>. Cơ sở khẳng định:</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Lợi ích cơ bản</a> của giai cấp công nhân, của <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân lao động</a> và của <a class=\"cloze\" href=\"#mce_temp_url#\">toàn dân tộc</a> là một</p>\r\n<p>  + Trong <a class=\"cloze\" href=\"#mce_temp_url#\">thành phần</a> của Đảng, ngoài <a class=\"cloze\" href=\"#mce_temp_url#\">công nhân</a> còn có những <a class=\"cloze\" href=\"#mce_temp_url#\">người ưu tú</a> thuộc giai cấp nông dân, <a class=\"cloze\" href=\"#mce_temp_url#\">tầng lớp trí thức</a> và các thành phần khác</p>\r\n<p>  + <a class=\"cloze\" href=\"#mce_temp_url#\">Sức mạnh của Đảng</a> không chỉ bắt nguồn từ <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a> mà còn bắt nguồn từ các tầng lớp <a class=\"cloze\" href=\"#mce_temp_url#\">nhân dân lao động</a> khác.</p>\r\n<p>b. Quan điểm của HCM khi bàn về vấn đề “Đảng của ai\"</p>\r\n<p>Bàn về vấn đề “Đảng của ai”, HCM đã có quan điểm như sau: Hồ Chí Minh khẳng định: <a class=\"cloze\" href=\"#mce_temp_url#\">Đảng Cộng sản Việt Nam</a> là Đảng của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">đội tiên phong</a> của giai cấp công nhân, mang <a class=\"cloze\" href=\"#mce_temp_url#\">bản chất</a> của <a class=\"cloze\" href=\"#mce_temp_url#\">giai cấp công nhân</a>.</p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4488', 'Quan điểm HCM về nguyên tắc, bước đi và biện pháp cụ thể trong quá trình xây dựng xã hội chủ nghĩa', null, null, '5', '0', '672', '51', '1552671012', null, null, null, null, null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4489', null, null, null, '5', '4488', '672', '51', '1552671012', null, null, null, '<p>-    Nguyên tắc:</p>\r\n<p>     + Quán triệt các nguyên lí chủ nghĩa Mác - Lênin về xây dựng <a class=\"cloze\" href=\"#mce_temp_url#\">chế độ mới</a>, có thể tham khảo, học tập các nước anh em</p>\r\n<p>     + Xác định bước đi và nguyên tắc cụ thể, phải xuất phát từ <a class=\"cloze\" href=\"#mce_temp_url#\">điều kiện thực tế</a>, đặc điểm <a class=\"cloze\" href=\"#mce_temp_url#\">dân tộc</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">nhu cầu</a> và bản năng thực tế của nhân dân</p>\r\n<p>-    Bước đi:</p>\r\n<p>     +  Phương châm: dần dần, <a class=\"cloze\" href=\"#mce_temp_url#\">thận trọng</a> từng bước một, từ thấp đến cao, làm vững chắc từng bước, phù hợp với điều kiện thực tế</p>\r\n<p>     +  Chú trọng vai trò <a class=\"cloze\" href=\"#mce_temp_url#\">công nghiệp hóa</a> CNXH, đó là nhiệm vụ trọng tâm trong thời kỳ quá độ lên CNXH</p>\r\n<p>-    Biện pháp</p>\r\n<p>     + Cải tạo xã hội cũ, xây dựng xã hội mới, trong đó <a class=\"cloze\" href=\"#mce_temp_url#\">xây dựng</a> là quan trọng nhất</p>\r\n<p>     + Kết hợp XD với bảo vệ, đồng thời thực hiện hai <a class=\"cloze\" href=\"#mce_temp_url#\">nhiệm vụ</a> chiến lược ở hai vùng nam bắc</p>\r\n<p>     + XD CNXH phải có <a class=\"cloze\" href=\"#mce_temp_url#\">kế hoạch</a>, <a class=\"cloze\" href=\"#mce_temp_url#\">biện pháp</a>, quyết tâm thực hiện thắng lợi kế hoạch</p>\r\n<p>     + Đem của dân, sức dân, tài dân làm lợi cho <a class=\"cloze\" href=\"#mce_temp_url#\">dân</a></p>', null, '55', null, null, null, null, null);
INSERT INTO `question` VALUES ('4490', 'Tính gần đúng các giá trị sau.làm tròn 3 chữ số sau dấu phẩy', null, null, '5', '0', '286', '450', '1552709326', '1552709358', null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('4491', null, null, null, '5', '4490', '286', '450', '1552709326', '1552709358', null, null, '<p>Kết quả của phép tính: <span class=\"math-tex\">\\(F = \\sqrt {5.{e^{0,02}} + {(2,03)^2}}\\)</span> là <a class=\"clozetip\" title=\"\" href=\"#\">3,036</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('4636', 'Điền đáp án theo các tính toán sau:\r\nCông an tỉnh Thanh Hóa vừa có thông báo chính thức về việc đã ra Quyết định khởi tố vụ án hình sự về tội “Lừa đảo chiếm đoạt tài sản” liên quan đến thửa đất số nhà 38 Tống Duy Tân, phường Lam Sơn, thành phố Thanh Hóa. \r\n>>\"Nữ quái\" được cán bộ tiếp tay trong vụ “1 thửa đất cấp 4 sổ đỏ” tại Thanh Hóa là ai? \r\n>>Vụ 1 thửa đất cấp 4 sổ đỏ: “Điểm mặt” những sai phạm nghiêm trọng cần khởi tố! \r\n>>Trung ương MTTQ Việt Nam đề nghị khởi tố vụ “1 thửa đất cấp 4 sổ đỏ” tại Thanh Hóa!\r\nTheo nguồn tin của PV Dân trí, ngày 12/3/2019, Cơ quan Cảnh sát điều tra (CSĐT), Công an tỉnh Thanh Hóa đã có Thông báo kết quả giải quyết nguồn tin về tội phạm số 1165/PC01-TB.', null, null, '3', '0', '678', '6', '1552711217', null, null, null, null, null, '37', null, null, null, null, null);
INSERT INTO `question` VALUES ('4637', null, null, null, '3', '4636', '678', '6', '1552711217', null, null, null, 'Hàm trên đạt cực trị tại:', null, '37', null, null, null, null, null);
INSERT INTO `question` VALUES ('4638', null, null, null, '3', '4636', '678', '6', '1552711217', null, null, null, 'Giá trị cực trị là:', null, '37', null, null, null, null, null);
INSERT INTO `question` VALUES ('4639', null, null, null, '5', '0', '680', '6', '1552711786', null, null, null, null, null, '37', null, null, null, null, null);
INSERT INTO `question` VALUES ('4640', null, null, null, '5', '4639', '680', '6', '1552711786', null, null, null, '<h3 dir=\"ltr\"><span>Câu 5.2</span></h3>\r\n<p dir=\"ltr\"><span>1. Xuất kho nguyên vật liệu dùng cho sản xuất sản phẩm 20.000, dùng cho quản lý phân xưởng 2.000.</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK <a class=\"cloze\" href=\"#mce_temp_url#\">621</a>: 20.000</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK <a class=\"cloze\" href=\"#mce_temp_url#\">627</a>: 2.000</span></p>\r\n<p dir=\"ltr\"><span>  Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">152</a>: 22.000</span></p>\r\n<p dir=\"ltr\"><span>2. Tiền lương phải trả công nhân sản xuất sản phẩm 35.000, quản lý phân xưởng 5.000</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK <a class=\"cloze\" href=\"#mce_temp_url#\">622</a>: 35.000</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK <a class=\"cloze\" href=\"#mce_temp_url#\">627</a>: 5.000</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 334: 40.000</span></p>\r\n<p dir=\"ltr\"><span>3. Các khoản trích theo lương được trích theo tỷ lệ quy định</span></p>\r\n<p dir=\"ltr\"><span>Theo doanh nghiệp:</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK <a class=\"cloze\" href=\"#mce_temp_url#\">622</a>: 1.680</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3382</a>: 140</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3383</a>: 1.260</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3384</a>: 210</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3386</a>: 70</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK <a class=\"cloze\" href=\"#mce_temp_url#\">627</a>: 240</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3382</a>: 20</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3383</a>: 180</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3384</a>: 30</span></p>\r\n<p dir=\"ltr\"><span>    Có TK <a class=\"cloze\" href=\"#mce_temp_url#\">3386</a>: 10</span></p>\r\n<p><span> </span></p>\r\n<p dir=\"ltr\"><span>Theo người lao động:</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK 334: 840</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 3383: 640</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 3384: 120</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 3386: 80</span></p>\r\n<p dir=\"ltr\"><span>4. Hao mòn TSCĐ dùng cho sản xuất 50.000</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK 627: 50.000</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 214: 50.000</span></p>\r\n<p dir=\"ltr\"><span>5. Chi phí điện nước mua ngoài 2.100 (đã bao gồm 5% thuế GTGT) đã trả cho người bán bằng chuyển khoản</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK 6277: 2.000</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK 1331: 100</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 112: 2.100</span></p>\r\n<p dir=\"ltr\"><span>6. Kết chuyển chi phí</span></p>\r\n<p dir=\"ltr\"><span>- Kết chuyển chi phí sản xuất:</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK 154: 115.920</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 621: 20.000</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 622: 36.680</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 627: 59.240</span></p>\r\n<p dir=\"ltr\"><span>7. Nhập kho hàng hoá, biết rằng giá trị thành phẩm chế dở cuối kỳ là 5.000</span></p>\r\n<p dir=\"ltr\"><span>Giá trị sản phẩm hoàn thành trong kỳ = 0 + 115.920 - 5.000 = 110.920</span></p>\r\n<p dir=\"ltr\"><span>Nợ TK 155: 110.920</span></p>\r\n<p dir=\"ltr\"><span>    Có TK 154: 110.920</span></p>\r\n<p><span> </span></p>', null, '37', null, null, null, null, null);
INSERT INTO `question` VALUES ('4691', ' Bài 1: Bốn nhóm người sản xuất cùng làm ra một loại hàng hoá:\nNhóm 1 hao phí cho một đơn vị hàng hoá là 3 giờ và sản xuất được 100 đơn vị hàng hoá.\nNhóm 2 ……………………………là 5 giờ…………600 đơn vị …\nNhóm 3…………………………… là 6 giờ…………200 đv…….\nNhóm 4…………………………… là 7 giờ…………100 đv…….   \nHãy tính thời gian lao động xã hội cần thiết để sản xuất ra một đơn vị hàng hoá ?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4692', 'Bài 2: Ngày lao động 8 giờ sản xuất được 16 sản phẩm, có tổng giá trị là 80 $. Hỏi giá trị của tổng sản phẩm trong ngày và giá trị của 1 sản phẩm là bao nhiêu nếu:\na. Năng suất lao động tăng lên 2 lần ?\nb. Cường độ lao động tăng lên 1.5 lần ?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4693', ' Bài 3: Để tái sản xuất sức lao động cần có những vật phẩm tiêu dùng sau đây:\na. Sản phẩm ăn uống 7 $/ngày.\nb. Đồ dùng gia đình 75 $/ năm\nc. Quần áo dầy dép 270 $/năm\nd. Những đồ dùng lâu bền 5.700 $/ 10 năm\ne. Đáp ứng nhu cầu văn hoá 15 $/tháng.\nHãy xác định giá trị sức lao động trong một ngày ?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4694', ' Bài 4: Giả định tại một công ty tư bản, trong quá trình sản xuất sản phẩm, chi phí tư bản cố định là 30 triệu EUR và bằng 50% so với chi phí nguyên vật liệu, tổng giá trị sản phẩm là 120 triệu EUR và tỉ suất giá trị thặng dư là 200%.\nHãy xác định chi phí tư bản khả biến trong trường hợp này? Tư bản  khả biến, tư bản bất biến là gì? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4695', ' Bài 5: Trong một chu kỳ kinh doanh của một doanh nghiệp sản xuất kẹo sôcôla ở Bỉ, tư bản đầu tư là 300 triệu EUR, với số lượng kẹo sản xuất được là 2.000.000 thùng. Chi phí tư bản bất biến chiếm 90% trong tư bản đầu tư, tỉ suất giá trị thặng dư là 200%.\nHãy tính giá trị của mỗi thùng kẹo và kết cấu giá trị của sản phẩm đó trong trường hợp này? \n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4696', ' Bài 5B: Trong một xí nghiệp tư bản sử dụng 200 công nhân làm thuê, sản xuất 1 tháng được 30.000 đơn vị sản phẩm với chi phí tư bản bất biến là 300.000 <span class=\"math-tex\">\\(, giá trị sức lao động 1 tháng của mỗi công nhân là 300\\)</span>, tỉ suất giá trị thặng dư là 200%. \nHãy xác định giá trị của mỗi đơn vị sản phẩm và kết cấu của nó? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4697', 'Bài 6: Trong một chu kỳ kinh doanh, tư bản dùng để mua sức lao động chiếm 10% trong tổng tư bản đầu tư. \nHãy xác định giá trị mới tạo ra trong trường hợp này, biết rằng tư bản bất biến là 81 triệu EUR, tỉ suất giá trị thặng dư là 200%? \nGiá trị mới là gì? Trình bày cấu tạo giá trị của một đơn vị hàng hóa?', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4698', ' Bài 6B: Trong một tình huống kinh doanh, một nhà tư bản Hồng Kông đầu tư 50 tỉ HKD, trong đó lượng tư bản khả biến bằng 25% lượng tư bản bất biến, số công nhân làm thuê ở đây là 10.000 người, trình độ bóc lột là 300%.\nHãy tính lượng giá trị mới do 1 công nhân làm thuê tạo ra trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4699', ' Bài 6C: Giả định trong một chu kỳ kinh doanh, tư bản dùng để mua sức lao động chiếm 1/5 trong tổng tư bản đầu tư. Biết rằng tư bản bất biến là 80 triệu EUR, trình độ bóc lột là 250%.\nHãy xác định giá trị mới tạo ra trong trường hợp kinh doanh này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4700', ' Bài 6D: Giả định trong một chu kỳ kinh doanh, chủ công ty chế biến thực phẩm Mitorang đã đầu tư 100 triệu EUR, với cấu tạo hữu cơ của tư bản là 4/1.\nHãy xác định lượng giá trị mới do 1 công nhân làm thuê tạo ra trong trường hợp này? Biết rằng trình độ bóc lột lao động làm thuê là 200% và số công nhân làm việc ở đây là 1500 người. ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4701', ' Bài 7: Giả định trong một công ty con của tập đoàn đa quốc gia Omatachi, lượng giá trị mới tạo trong năm 2010 là 150 tỉ yên, trong đó tiền lương của công nhân (sau khi đã trừ đi các khoản đóng góp) chiếm 1/3. Đến năm 2015, lượng giá trị mới tạo ra tăng 150% và tiền lương của công nhân sau khi đã trừ đi các khoản đóng góp đã tăng 130% so với năm 2010. \nHỏi tỉ suất giá trị thặng dư trong những năm đó thay đổi như thế nào? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4702', ' Bài 8: Trong một nhà máy, cứ 1 giờ 1 công nhân tạo ra lượng giá trị mới là 5 <span class=\"math-tex\">\\(, m’= 300%, giá trị sức lao động mỗi ngày của  một công nhân là 10 \\)</span>. \nHãy xác định độ dài chung của ngày lao động trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4703', ' Bài 9: Năm 1980 tiền lương trung bình của một công nhân chế biến ở một nước TB là 1.238 <span class=\"math-tex\">\\(, còn giá trị thặng dư do 1 công nhân tạo ra là 2.134 \\)</span>. Đến năm 2005 những chỉ tiêu trên tăng lên tương ứng là 1.589<span class=\"math-tex\">\\( và 5.138 \\)</span> .\n      Hãy xác định trong những năm đó thời gian công nhân làm việc cho mình và cho nhà tư bản thay đổi như thế nào, nếu ngày làm việc 8 giờ ?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4704', ' Bài 9B: Giả định trong năm trước, tiền lương trung bình của một công nhân chế biến ở một công ty tư bản là 10.700<span class=\"math-tex\">\\(, và giá trị thặng dư do 1 công nhân tạo ra cũng là 10.700 \\)</span>. Năm sau, những chỉ tiêu trên tăng lên tương ứng là 20.150 <span class=\"math-tex\">\\( và 30.225 \\)</span>.\nHãy xác định trong những năm đó thời gian công nhân làm việc cho mình và cho nhà tư bản thay đổi như thế nào, nếu ngày lao động làm việc 10h? Nhà tư bản đã sử dụng phương pháp sản xuất giá trị thặng dư nào trong trường hợp trên? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4705', ' Bài 9C: Trong 1 doanh nghiệp, năm trước lượng giá trị mới do một công nhân làm thuê tạo ra là 8.000 USD, trong đó tiền lương trung bình của họ bằng với lượng giá trị thặng dư được tạo ra. Năm sau, lượng giá trị mới 1 công nhân tạo ra tăng thêm 1.000 USD, trong đó có lượng giá trị thặng dư gấp đôi so với tiền lương trung bình mà họ được nhận.\nHãy xác định thời gian lao động cần thiết (TGLĐ tất yếu) và thời gian lao động thặng dư thay đổi như thế nào, nếu ngày lao động của người công nhân là 9 giờ? \n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4706', ' Bài 10: Tổng tư bản công nghiệp là 240 tỷ $, thời gian sản xuất trung bình là 2,5 tháng, còn thời gian chu chuyển là 4 tháng.\n     Hãy xác định tổng tư bản hoạt động trong lĩnh vực lưu thông ?', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4707', ' Bài 11: Giả định rằng tại một công ty tư bản, ban đầu người công nhân làm việc 8 giờ/ngày, tiền lương của một giờ lao động là 2 USD. Nhưng sau đó do nạn thất nghiệp, nhà tư bản cắt giảm 20 % tiền lương trả cho công nhân theo giờ. \nHỏi để có thể nhận được mức tiền công trong một ngày như cũ, thì thời gian lao động trong ngày đó của công nhân phải thay đổi như thế nào ? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4708', ' Bài 11B: Giả định trong một trường hợp kinh doanh, nhà tư bản đầu tư 120 triệu USD, trong đó lượng tư bản khả biến bằng 25% so với lượng tư bản bất biến; số công nhân làm thuê là 2.000 người. Sau đó, tư bản đầu tư tăng lên thành 180 triệu USD, cấu tạo hữu cơ của tư bản tăng thành 9/1.\nHỏi: Nếu tiền lương trả cho mỗi công nhân không thay đổi thì nhu cầu sức lao động của cơ sở kinh doanh này thay đổi như thế nào trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4709', ' Bài 12: Trước kia sức lao động bán theo đúng giá trị, sau đó tiền lương danh nghĩa tăng lên 2 lần, giá cả vật phẩm tiêu dùng tăng 60%, còn giá trị sức lao động tăng lên 35%.\n     Hỏi tiền lương thực tế thay đổi như thế nào?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4710', ' Bài 13: Giả sử, trong trường hợp ngày làm việc của người công nhân làm thuê là 8 giờ, tỉ suất giá trị thặng dư là 100%, sau đó nhà tư bản kéo dài ngày làm việc của người công nhân làm thuê lên đến 10 giờ.\nHỏi: Tỉ suất giá trị thặng dư thay đổi như thế nào, nếu giá trị sức lao động không đổi? Nhà tư bản thu được giá trị thặng dư bằng phương pháp sản xuất giá trị thặng dư nào trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4711', ' Bài 13B: Trong một doanh nghiệp dệt ở thành phố Lion (Pháp), thời kỳ đầu ngày làm việc của người công nhân 9h/ngày, trong đó thời gian lao động thặng dư chiếm 1/3.  Sau đó tỉ suất giá trị thặng dư tăng lên gấp ba lần và thời gian làm việc trong ngày của người công nhân tăng lên thành 10h/ngày.\nHãy tính thời gian lao động cần thiết (TGLĐ tất yếu) và thời gian lao động thặng dư trong ngày làm việc của người công nhân ở trường hợp này? \nCơ sở phân chia thời gian lao động cần thiết (tất yếu), thời gian lao động thặng dư? Cần phải làm gì để rút ngắn thời gian lao động cần thiết hoặc tăng thời gian lao động thặng dư?', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4712', ' Bài 13C: Tại một công ty sản xuất giầy da của Đức, trong tháng 6 người công nhân làm việc 8h/ngày với thời gian lao động cần thiết và thời gian lao động thặng dư trong ngày lao động của công nhân là ngang bằng nhau. Sang tháng 7, họ phải làm việc kéo dài thêm 1h/ngày nhưng mức lương nhận được vẫn như tháng trước.\n  Hãy xác định tỉ suất giá trị thặng dư trong 2 tháng trên ở công ty này thay đổi như thế nào? Chủ tư bản đã thực hiện phương pháp sản xuất giá trị thặng dư nào trong trường hợp này? Vì sao?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4713', ' Bài 13D: Ngày làm việc 8 giờ, thời gian lao động thặng dư là 4 giờ. Sau đó do tăng năng suất lao động trong các ngành sản xuất vật phẩm tiêu dùng và dịch vụ, cho nên hàng hoá, dịch vụ ở những ngành này rẻ hơn trước 2 lần (Giả định GT của các yếu tố còn lại của SLĐ là không đáng kể).\nHỏi: Tỷ suất giá trị thặng dư (m’) (Tức trình độ bóc lột) thay đổi như thế nào nếu thời gian ngày lao động không đổi ?\nTrường hợp này nhà TB đã sử dụng phương pháp sản xuất giá trị thặng dư nào ?\nÝ nghĩa của việc sử dụng phương pháp sản xuất giá trị thặng dư này trong giai đoạn hiện nay ?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4714', 'Bài 13E: Trong công ty của một chủ tư bản, người công nhân làm việc 10 giờ/ngày nhưng trong đó thời gian lao động cần thiết (Tất yếu) chỉ chiếm 40%. Sau đó, năng suất lao động trong các ngành sản xuất vật phẩm tiêu dùng của xã hội tăng gấp đôi.\nHỏi: Tỉ suất giá trị thặng dư thay đổi như thế nào nếu số giờ làm việc trong ngày của công nhân không thay đổi? Trong trường hợp này nhà tư bản đã sử dụng phương pháp nào để tăng tỉ suất giá trị thặng dư?\n Ý nghĩa của phương pháp sản xuất giá trị thăng dư này trong giai đoạn hiện nay.\n\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4715', ' Bài 14: Giả sử nền sản xuất xã hội gồm 3 ngành sản xuất, trong đó tư bản ứng trước của:\n- Ngành 1 là 100 tỷ $, cấu tạo HC là 6/4, m’ = 140%\n- Ngành 2 là 200 tỷ $, cấu tạo HC là 7/3, m’ = 130% \n- Ngành 3 là 300 tỷ $, cấu tạo HC là 9/1, m’ = 150%\n Hãy xác định tỷ suất lợi nhuận bình quân ? Các nhà tư bản ở ngành 1 phải bán hàng hóa với giá cả là bao nhiêu để thu được lợi nhuận bình quân ? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4716', ' Bài 14B: Giả sử nền sản xuất xã hội gồm 3 ngành, trong đó tư bản ứng trước (K) của:\n- Ngành 1 là 100 tỷ $, cấu tạo hữu cơ là 7/3, m’ = 130%\n- Ngành 2 là 200 tỷ $, cấu tạo HC là 4/1, m’ = 150% \n- Ngành 3 là 300 tỷ $, cấu tạo HC là 9/1, m’ = 180%\n  Hãy xác định tỷ suất lợi nhuận bình quân và giá cả sản xuất của ngành 1 trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4717', ' Bài 15:  Giả sử trong một chu kỳ kinh doanh của một nhà tư bản, cấu tạo hữu cơ tư bản là 7/3 và giá trị thặng dư tạo ra là 800 triệu USD, tỷ suất giá trị thặng dư là 200%.\nTrong điều kiện tư bản bất biến hao mòn hoàn toàn trong một chu kỳ sản xuất, hãy xác định chi phí sản xuất tư bản chủ nghĩa và giá trị của hàng hóa ? \n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4718', ' Bài 15B: Để sản xuất ra 10 tấn sợi/tháng, nhà tư bản D.Ricardo chuyên sản xuất sợi đã đầu tư mua bông và các nguyên phụ liệu hết 200.000 USD; mua máy móc thiết bị, xây dựng nhà xưởng theo thiết kế kỹ thuật để sử dụng trong 1 năm hết 1.200.000 USD (= Khấu hao 1 năm); giá trị thặng dư thu được 50.000 USD/tháng. Biết rằng tỷ suất giá trị thặng dư là 200%. \nHãy xác định chi phí sản xuất tư bản chủ nghĩa trong việc sản xuất 1 kg sợi trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4719', ' Bài 16: Có 200.000 <span class=\"math-tex\">\\( cho vay trong 3 tháng mang lại thu nhập cho người chủ sở hữu là 4.000 \\)</span>, sau đó thu hồi tiền về, nhà tư bản lại dùng chúng cho vay trong thời gian 8 tháng với tỷ suất lợi tức là 3% một năm, tháng cuối cùng nhà tư bản cho vay còn thu được 2.000 $ lợi tức.\n Hãy xác định tỷ suất lợi tức tiền vay bình quân cả năm?', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4720', ' Bài 17: Giả sử trong một chu kỳ kinh doanh tại một công ty con của tập đoàn Walras, lượng tư bản ứng trước là 15.000.000 USD, tư bản đầu tư vào nhà xưởng chiếm 20% và  tư bản mua sắm máy móc thiết chiếm 25% trong tổng tư bản ứng trước. Biết rằng giá trị nguyên liệu, nhiên liệu và vật liệu phụ gấp 2 lần giá trị sức lao động. \nHãy xác định: Tư bản cố định, Tư bản lưu động; Tư bản bất biến, Tư bản khả biến trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4721', ' Bài 17B: Hãy xác định tư bản lưu động trong trường hợp kinh doanh sau đây: Tư bản đầu tư là 450 triệu Yên, cấu tạo hữu cơ là 4/1. Biết rằng trong tư bản bất biến, đầu tư cho nguyên, nhiên vật liệu và vật liệu phụ bằng 50% đầu tư tư bản cố định. ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4722', ' Bài 17C: Giả định trong một tình huống kinh doanh, tư bản đầu tư là 75 triệu SGD, trong đó tư bản khả biến chiếm 40%. Biết rằng trong tư bản bất biến, đầu tư tư bản cố định gấp đôi đầu tư cho nguyên nhiên vật liệu và vật liệu phụ. \n  Hãy tính tư bản lưu động trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4723', ' Bài 17D: Giả định rằng, trong một chu kỳ kinh doanh của một công ty tư bản, số lượng công nhân làm thuê là 1.000 người, tiền lương của 1 công nhân là 70.000 USD. Biết rằng lượng tư bản khả biến chiếm 25% trong tổng tư bản đầu tư, còn lượng tư bản cố định chiếm 40% trong tư bản bất biến.\nHãy tính chi phí tư bản cố định trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4724', ' Bài 18: Trong một tình huống kinh doanh, tổng tư bản hoạt động trong lĩnh vực sản xuất công nghiệp là 600 triệu USD, trong đó vốn tự có chiếm 60%. Giả định tỷ suất lợi nhuận bình quân là 13% và tỷ suất lợi tức là 8%.\n   Hãy xác định lợi nhuận của các nhà tư bản công nghiệp, lợi tức của các nhà tư bản cho vay trong trường hợp này ? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4725', ' Bài 18 B: Hãy xác định lợi nhuận của các nhà tư bản công nghiệp và tức lợi của các nhà tư bản cho vay, trong trường hợp kinh doanh với tổng tư bản hoạt động trong sản xuất công nghiệp là 60 tỷ Yên, trong đó vốn đi vay chiếm 1/3. Biết rằng  tỷ suất lợi nhuận bình quân là 12% và tỷ suất lợi tức là 3%.', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4726', ' Bài 19: Giả định tư bản đầu tư trong công nghiệp là 80 triệu USD và tư bản đầu tư trong nông nghiệp cũng cùng lượng tư bản như vậy; với cấu tạo hữu cơ của tư bản trong công nghiệp là 7/3 và trong nông nghiệp là 3/2; trình độ bóc lột trong công nghiệp là 150% và trong nông nghiệp là 120%.\nHãy xác định địa tô tuyệt đối trong trường hợp này?  ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4727', ' Bài 19B: Hãy xác định địa tô tuyệt đối trong trường hợp sau: Tư bản đầu tư trong công nghiệp và trong nông nghiệp đều là 20 triệu $, với cấu tạo hữu cơ tư bản trong công nghiệp là 9/1, còn trong nông nghiệp là 4/1, tỉ suất giá trị thặng dư trong công nghiệp là 200%, trong nông nghiệp là 150%? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4728', ' Bài 20: Tổng tư bản công nghiệp và tư bản thương nghiệp là 16 triệu EUR với tỉ suất lợi nhuận bình quân là 20%, lợi nhuận công nghiệp là 3 triệu EUR.\nHỏi: Các nhà tư bản thương nghiệp cần phải mua và bán hàng hóa theo giá bao nhiêu để họ và các nhà tư bản công nghiệp đều thu được lợi nhuận bình quân? Trình bày bản chất của lợi nhuận thương nghiệp?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4729', ' Bài 21: Chi phí trung bình về tư bản bất biến cho một đơn vị hàng hoá trong một ngành là 90 <span class=\"math-tex\">\\(. Chi phí tư bản khả biến là 10 \\)</span> , m’= 200%, một nhà tư bản sản xuất trong 1 năm được 1.000 đơn vị hàng hoá. Sau khi áp dụng kỹ thuật mới, năng suất lao động sống của xí nghiệp đó tăng lên 2 lần, số lượng hàng hoá sản xuất cũng tăng lên tương ứng.\n  Hỏi nhà tư bản trong năm thu được bao nhiêu giá trị thặng dư siêu ngạch?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4730', ' Bài 22: Trong một tình huống kinh doanh, một chủ doanh nghiệp đầu tư một lượng tư bản là 25 triệu USD, với tỉ suất tích lũy là 60%. Biết rằng tiền lương trả cho công nhân chỉ chiếm 25% so với lượng tư bản bất biến, trình độ bóc lột là 300%. \nHãy tính lượng giá trị thặng dư tư bản hóa trong trường hợp này. ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4731', ' Bài 22B: Giả định trong một tình huống kinh doanh, nhà tư bản ứng trước 120 triệu EUR, trong đó lượng tư bản bất biến gấp đôi lượng tư bản khả biến. \nHãy xác định lượng giá trị thặng dư tư bản hóa trong trường hợp này, nếu tỉ suất giá trị thặng dư là 200%, và tỉ suất tích lũy là 40% ?', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4732', ' Bài 22C: Giả định trong một chu kỳ kinh doanh, nhà tư bản đầu tư 10 tỉ USD cho nhà xưởng và mua sắm trang thiết bị, 5 tỉ USD mua nguyên nhiên vật liệu, tiền lương trả cho công nhân làm thuê bằng 20% so với chi phí tư bản bất biến, trình độ bóc lột lao động làm thuê là 150%. Kết thúc chu kỳ kinh doanh này, có 1,5 tỉ USD được tư bản hóa.\n  Hãy tính tỉ suất tích lũy trong trường hợp này? Nếu khối lượng giá trị thặng dư tạo ra không đổi, tỷ suất giá trị thặng dư ảnh hưởng như thế nào đến quy mô tích lũy.\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4733', ' Bài 23: Giả định trong 1 năm, tổng giá cả hàng hoá trong lưu thông là 298 tỷ USD, trong đó tổng giá cả hàng hoá bán chịu 40 tỷ USD, tổng số tiền thanh toán đến kỳ hạn trả là 75 tỷ USD, tổng số tiền khấu trừ trong thanh toán là 45 tỷ USD, trong năm đó trung bình đồng tiền quay 0,5 vòng/tháng. Tổng số tiền nhà nước đưa vào trong lưu thông trong năm đó là 96 tỷ USD.\nHỏi: Giả định rằng các yếu tố tác động khác không thay đổi thì trong trường hợp này nền kinh tế đó đang xảy ra tình trạng gì? Vì sao?\n Nếu trong năm này, nhà nước rút bớt 50% lượng tiền đã đưa vào lưu thông, thì diễn biến trong lưu thông tiền tệ của nền kinh tế đó có sự thay đổi như thế nào? Hãy giải thích? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4734', ' Bài 24: Trong một trường hợp đầu tư, tư bản ứng trước là 60 triệu USD, với cấu tạo hữu cơ là 4/1, số công nhân làm thuê là 1.000 người. Sau đó tư bản tăng lên thành 90 triệu USD, cấu tạo hữu cơ tăng lên là 9/1.\n   Hỏi nhu cầu sức lao động thay đổi như thế nào nếu tiền lương của mỗi công nhân không thay đổi? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4735', ' Bài 24B: Trong một chu kỳ kinh doanh tại một công ty chế biến thực phẩm của Nhật, tư bản đầu tư là 100 triệu Yên với cấu tạo hữu cơ tư bản là 7/3, số công nhân được thuê vào làm việc ở đây là 3000 người và m’=100%. Đến chu kỳ kinh doanh sau, tư bản đầu tư tăng gấp đôi và cấu tạo hữu cơ tư bản tăng thành 9/1, do công ty đã tích cực đổi mới trang thiết bị theo hướng hiện đại hóa. Điều đó khiến cho nhu cầu sức lao động của công ty giảm đi 1/3 so với trước, còn tỉ suất giá trị thặng dư tăng thành 200%.\nBạn có nhận xét gì về sự thay đổi của khối lượng giá trị thặng dư nhà tư bản thu được, về tổng tiền lương trả cho công nhân và tiền lương trung bình của 1 công nhân trong chu kỳ kinh doanh sau? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4736', ' Bài 24C: Giả định trong một trường hợp kinh doanh, nhà tư bản đầu tư 120 triệu USD, trong đó lượng tư bản khả biến bằng 25% so với lượng tư bản bất biến; số công nhân làm thuê là 2.000 người. Sau đó, tư bản đầu tư tăng lên thành 180 triệu USD, cấu tạo hữu cơ của tư bản tăng thành 9/1.\nHỏi: Nếu tiền lương trả cho mỗi công nhân không thay đổi thì nhu cầu sức lao động của cơ sở kinh doanh này thay đổi như thế nào trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4737', ' Bài 25: Trong cơ sở sản xuất công nghiệp của một chủ tư bản, tư bản đầu tư ban đầu là 30 triệu USD, với cấu tạo hữu cơ là 4/1, m’ = 100%. Năm sau, tư bản đầu tư ở đây tăng gấp 3 lần với cấu tạo hữu cơ là 9/1, m’ = 150%.\nHãy tính sự thay đổi của tỉ suất lợi nhuận ? Giải thích tại sao có sự thay đổi đó? Tỷ suất lợi nhuận là gì ? Trình bày các nhân tố ảnh hưởng đến tỷ suất lợi nhuận?\n', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4738', ' Bài 26: Giả định trong một tình huống kinh doanh, nhà tư bản A.Smith mua một dây chuyền máy mới có giá trị quy ra tiền khi mua là 6.000.000 Bảng, dự kiến hao mòn hữu hình trong 30 năm. Nhưng sau 5 năm hoạt động, giá trị của các máy cùng loại được đưa ra bán trên thị trường đã giảm đi 25%. \nHãy tính tổn thất do hao mòn vô hình gây ra cho dây chuyền máy đó trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4739', ' Bài 27: Giả sử trong một trường hợp kinh doanh của một chủ tư bản, tư bản đầu tư là 20.000.000 EUR, trong đó lượng tư bản khả biến bằng 20% so với lượng tư bản bất biến, trình độ bóc lột là 150%.\nHỏi sau bao nhiêu năm trong điều kiện tái sản xuất giản đơn, tư bản đó sẽ biến thành giá trị thặng dư tư bản hóa hoàn toàn? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4740', ' Bài 28: Giả định rằng trong một trường hợp kinh doanh, tổng giá trị hàng hóa tạo ra là 120 triệu USD, trong đó giá trị mới tạo ra chiếm 2/3. Biết rằng trình độ bóc lột là 300%. \nHãy xác định cấu tạo hữu cơ của tư bản trong trường hợp này? ', null, null, '2', '0', '682', '443', '1552713466', null, null, null, null, null, '42', null, null, null, null, null);
