INSERT INTO `question` VALUES ('4941', 'Câu 2.5:Những đặc trưng và ưu thế của sản xuất hàng hóa', null, null, '2', '0', '697', '448', '1552747969', null, null, null, null, null, '42', null, null, null, null, null);
