INSERT INTO `question` VALUES ('4979', 'Biểu hiện và tác động của quy luật giá trị đối với nền kinh tế hàng hóa, ý nghĩa thực tiễn đối với Việt Nam. ', '', null, '5', '0', '698', '448', '1552751751', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4980', null, null, null, '5', '4979', '698', '448', '1552751751', null, '', null, '<p>Khái niệm: Quy luật giá trị là quy luật kinh tế <a class=\"clozetip\" title=\"\" href=\"#\">căn bản</a> của sản xuất và trao đổi hàng hóa, ở đâu có sản xuất và trao đổi hàng hóa thì ở đó có sự <a class=\"clozetip\" title=\"\" href=\"#\">tồn tại</a> và <a class=\"clozetip\" title=\"\" href=\"#\">phát huy</a> của QLGT</p> <p>- Biểu hiện: việc sản xuất lưu thông hàng hóa phải dựa trên cơ sở <a class=\"clozetip\" title=\"\" href=\"#\">hao phí lao động</a> XH cần thiết</p> <p>+ Đối với sản xuất: hao phí lao động cá biệt phải <a class=\"clozetip\" title=\"\" href=\"#\">nhỏ hơn</a> hoặc <a class=\"clozetip\" title=\"\" href=\"#\">bằng</a> hao phí LĐXHCT</p> <p>+ Đối với lưu thông: phải tuân thủ nguyên tắc <a class=\"clozetip\" title=\"\" href=\"#\">ngang giá</a> ( tổng giá trị= tổng giá cả)</p> <p>-Tác động:</p> <p>+ <a class=\"clozetip\" title=\"\" href=\"#\">Điều tiết</a> sản xuất và lưu thông hàng hóa</p> <p>+ Kích thích cải tiến <a class=\"clozetip\" title=\"\" href=\"#\">kĩ thuật</a>, hợp lí hóa sản xuất, tăng <a class=\"clozetip\" title=\"\" href=\"#\">năng suất lao động</a>, thúc đẩy lực lượng xã hội sản xuất phát triển.</p> <p>+ Lựa chọn <a class=\"clozetip\" title=\"\" href=\"#\">tự nhiên</a> và <a class=\"clozetip\" title=\"\" href=\"#\">phân hóa</a> người sản xuất </p> <p>-Ý nghĩa thực tiễn:</p> <p>+ Vận dụng các biện pháp tác động vào sản xuất, lưu thông giá theo hướng kết hợp hài hòa <a class=\"clozetip\" title=\"\" href=\"#\">lợi ích</a> của người sản xuất, doanh nghiệp, người tiêu dùng</p> <p>+ Khuyến khích <a class=\"clozetip\" title=\"\" href=\"#\">nghiên cứu</a> và cải tiến <a class=\"clozetip\" title=\"\" href=\"#\">khoa học</a> để ứng dụng vào sản xuất, giảm giá trị <a class=\"clozetip\" title=\"\" href=\"#\">cá biệt</a> của hàng hóa</p> <p>+ Nhà nước tạo điều kiện <a class=\"clozetip\" title=\"\" href=\"#\">vay vốn lãi thấp</a>, hỗ trợ người nghèo; <a class=\"clozetip\" title=\"\" href=\"#\">xóa đói giảm nghèo</a>; trợ cấp,…</p> ', null, '42', null, null, null, null, null);
