INSERT INTO `question` VALUES ('4997', null, null, null, '5', '4996', '700', '448', '1552752066', null, '', null, '<p>-Khái niệm: là sự chuyển hóa 1 phần <a class=\"clozetip\" title=\"\" href=\"#\">giá trị thặng dư</a> thành <a class=\"clozetip\" title=\"\" href=\"#\">tư bản</a> hay quá trình chuyển hóa GTTD</p> <p>- Các nhân tố:</p> <p>+ Nếu M (khối lượng GTTD) không đổi: phụ thuộc vào sự phân chia <a class=\"clozetip\" title=\"\" href=\"#\">GTTD</a> thành quỹ <a class=\"clozetip\" title=\"\" href=\"#\">tích lũy</a> và quỹ <a class=\"clozetip\" title=\"\" href=\"#\">tiêu dùng</a></p> <p>+ Nếu tỷ lệ phân chia được xác định: phụ thuộc vào Trình độ bóc lột</p> <p> Năng suất<a class=\"clozetip\" title=\"\" href=\"#\"> LĐXH</a></p> <p> Quy mô TB <a class=\"clozetip\" title=\"\" href=\"#\">ứng trước</a></p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4998', 'Câu 1.1. Khái niệm giai cấp công nhân là gì? (Khái niệm giai cấp công nhân? Điều kiện khách quan quy định, nội dung sứ mệnh lịch sử của giai cấp công nhân -> liên hệ Việt Nam)', null, null, '2', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('4999', 'KHÁI NIỆM GIAI CẤP CÔNG NHÂN ', '', null, '5', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5000', null, null, null, '5', '4999', '694', '443', '1552755488', null, '', null, '<p><img title=\"tập đoàn\" src=\"/images/buttons/info.gif\" alt=\"\" align=\"baseline\" border=\"0\" /><img title=\"tập đoàn\" src=\"/images/buttons/info.gif\" alt=\"\" align=\"baseline\" border=\"0\" />Giai cấp công nhân là một <a class=\"clozetip\" title=\"tđxh\" href=\"#\">tập đoàn xã hội</a> ổn định, hình thành và phát triển cùng với <a class=\"clozetip\" title=\"sự pt\" href=\"#\">sự phát triển</a> của công nghiệp hiện đại, với <a class=\"clozetip\" title=\"n.đ.pt\" href=\"#\">nhịp độ phát triển</a> của lực lượng sản xuất có <a class=\"clozetip\" title=\"tc.xh.h\" href=\"#\">tính chất xã hội hóa</a> ngày càng cao; là <a class=\"clozetip\" title=\"ll.cb.tt\" href=\"#\">lực lượng cơ bản tiên tiến</a> trong quy trình công nghệ, dịch vụ công nghiệp <a class=\"clozetip\" title=\"tt/gt\" href=\"#\">trực tiếp hoặc gián tiếp</a> tham gia vào quá trình sản xuất hoặc tái sản xuất ra của cải vật chất và <a class=\"clozetip\" title=\"\" href=\"#\">cải tạo</a> các mối quan hệ xã hội; là <a class=\"clozetip\" title=\"gc.đb\" href=\"#\">giai cấp đại biểu</a> cho lực lượng sản xuất và phương thức sản xuất <a class=\"clozetip\" title=\"\" href=\"#\">tiên tiến</a> trong thời đại hiện nay.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5001', 'Câu 1.2. Những điều kiện khách quan quy định sứ mệnh lịch sử giai cấp?', null, null, '2', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5002', 'NHỮNG ĐIỀU KIỆN KHÁCH QUAN QUY ĐỊNH SỨ MỆNH CỦA GIAI CẤP CÔNG NHÂN ? ( 2 ĐIỀU KIỆN LỚN) ', '', null, '5', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5003', null, null, null, '5', '5002', '694', '443', '1552755488', null, '', null, '<p style=\"text-align: left;\"><span style=\"font-size: 14px;\">- <a class=\"clozetip\" title=\"đv.kt-xh\" href=\"#\"><strong> Địa vị kinh tế- xã hội</strong></a> của giai cấp công nhân trong xã hội <a class=\"clozetip\" title=\"TBCN\" href=\"#\">tư bản chủ nghĩa</a>:</span></p> <p style=\"text-align: left;\"><span style=\"font-size: 14px;\">“<a class=\"clozetip\" title=\"llsx- I\" href=\"#\">Lực lượng sản xuất hàng đầu</a> của toàn nhân loại là công nhân, là người lao động”</span></p> <p style=\"text-align: left;\"><span style=\"font-size: 14px;\">+ thứ nhất, giai cấp công nhân là bộ phận <a class=\"clozetip\" title=\"\" href=\"#\">quan trọng nhất</a>,<a class=\"clozetip\" title=\"\" href=\"#\">cách mạng nhất</a> trong các bộ phận <a class=\"clozetip\" title=\"\" href=\"#\">cấu thành</a> lực lượng sản xuất dưới CNTB: GCCN vừa là <a class=\"clozetip\" title=\"\" href=\"#\">chủ thể</a> trực tiếp, vừa là <a class=\"clozetip\" title=\"sp.cb\" href=\"#\">sản phẩm căn bản</a> nhất của <a class=\"clozetip\" title=\"\" href=\"#\">nền sản xuất</a> đại công nghiệp; giai cấp công nhân hiện đại có xu hướng ngày càng được <a class=\"clozetip\" title=\"\" href=\"#\">tri thức</a> hóa.</span></p> <p style=\"text-align: left;\"><span style=\"font-size: 14px;\">+ thứ hai, về mặt <a class=\"clozetip\" title=\"\" href=\"#\">lợi ích</a> giai cấp công nhân là giai cấp <a class=\"clozetip\" title=\"đk.tt\" href=\"#\">đối kháng trực tiếp</a> với giai cấp <a class=\"clozetip\" title=\"TS\" href=\"#\">tư sản</a>: là giai cấp <a class=\"clozetip\" title=\"cm.tđ\" href=\"#\">cách mạng triệt để</a> nhất chống lại chế độ <a class=\"clozetip\" title=\"\" href=\"#\">áp bức</a>, <a class=\"clozetip\" title=\"\" href=\"#\">bóc lột</a> TBCN; giai cấp CN <a class=\"clozetip\" title=\"\" href=\"#\">giải phóng</a> toàn xã hội khỏi chế độ TBCN, trong cuộc cách mạng này giai cấp <a class=\"clozetip\" title=\"\" href=\"#\">không mất gì</a> ngoài xiềng xích mà lại được cả thế giới.</span></p> <p style=\"text-align: left;\"><span style=\"font-size: 14px;\">+ thứ ba, GCCN có <a class=\"clozetip\" title=\"l.i.cb\" href=\"#\">lợi ích căn bản</a> thống nhất với lợi ích của đại đa số quần chúng nhân dân lao động.</span></p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5004', null, null, null, '5', '5002', '694', '443', '1552755488', null, '', null, '<p>- <a class=\"clozetip\" title=\"đđ.ct-xh\" href=\"#\"><strong>Đặc điểm chính trị- xã hội</strong></a> của giai cấp công nhân: ( 3 đặc điểm) </p> <p>+ Là giai cấp <a class=\"clozetip\" title=\"tp.cm\" href=\"#\">tiên phong cách mạng</a> có tinh thần cách mạng <a class=\"clozetip\" title=\"t.đ\" href=\"#\">triệt để</a> nhất:</p> <p><a class=\"clozetip\" title=\"đ.d\" href=\"#\">Đại diện</a> cho phương thức sản xuất <a class=\"clozetip\" title=\"tt\" href=\"#\">tiên tiến</a>, phương thức sản xuất <a class=\"clozetip\" title=\"CSCN\" href=\"#\">cộng sản chủ nghĩa</a>, gắn liền với nền <a class=\"clozetip\" title=\"KH.CN\" href=\"#\">khoa học công nghiệp</a> hiện đại; có <a class=\"clozetip\" title=\"h.tt\" href=\"#\">hệ tư tưởng</a> tiên phong của thời đại ngày nay là Mác-Lenin mang tính <a class=\"clozetip\" title=\"CM, KH\" href=\"#\">cách mạng và khoa học</a>; trong cuộc cách mạng tư sản, giai cấp tư sản chỉ có <a class=\"clozetip\" title=\"t.th\" href=\"#\">tinh thần</a> cách mạng trong thời kì đấu tranh chống <a class=\"clozetip\" title=\"pk\" href=\"#\">chế độ phong kiến</a> còn khi giai cấp này đã dành được chính quyền thì họ <a class=\"clozetip\" title=\"bóc lột\" href=\"#\">quay trở lại bóc lột</a> giai cấp công nhân, giai cấp nông dân, những giai cấp đã cùng <a class=\"clozetip\" title=\"kề...cánh\" href=\"#\">kề vai sát cánh</a> với các giai cấp này trong cuộc cách mạng dân chủ <a class=\"clozetip\" title=\"TS\" href=\"#\">tư sản</a>; giai cấp công nhân bị giai cấp tư sản <a class=\"clozetip\" title=\"bl\" href=\"#\">bóc lột</a>, điều kiện sống, điều kiện lao động trong chế độ tư bản chủ nghĩa đã chỉ cho họ thấy họ chỉ có thể được <a class=\"clozetip\" title=\"gp\" href=\"#\">giải phóng</a> bằng cách giải phóng toàn xã hội khỏi chế độ tư bản chủ nghĩa. Trong quá trình xây dựng CNXH giai cấp công nhân <a class=\"clozetip\" title=\"không...TH\" href=\"#\">không gắn với tư hữu</a>, do vậy họ cũng <a class=\"clozetip\" title=\"kđ\" href=\"#\">kiên định</a> trong công cuộc <a class=\"clozetip\" title=\"c.t\" href=\"#\">cải tạo</a> XHCN, <a class=\"clozetip\" title=\"kqđt\" href=\"#\">kiên quyết đấu tranh</a>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5005', null, null, null, '5', '5002', '694', '443', '1552755488', null, '', null, '<p><strong>- <a class=\"clozetip\" title=\"\" href=\"#\">Đặc điểm chính trị- xã hội</a></strong> của giai cấp công nhân: ( 3 đặc điểm)</p> <p>+ Là giai cấp có <a class=\"clozetip\" title=\"yt.tc kl\" href=\"#\">ý thức tổ chức kỉ luật</a> cao:</p> <p>Do <a class=\"clozetip\" title=\"yc\" href=\"#\">yêu cầu</a> của cuộc đấu tranh giai cấp <a class=\"clozetip\" title=\"c.l\" href=\"#\">chống lại</a> giai cấp tư sản, giai cấp công nhân phải đấu tranh bằng <a class=\"clozetip\" title=\"pc.kl\" href=\"#\">phẩm chất kỷ luật</a> của mình</p> <p>Giai cấp công nhân trong nền sản xuất đại công nghiệp với <a class=\"clozetip\" title=\"ht.sx\" href=\"#\">hệ thống sản xuất</a> mang tính dây chuyền và <a class=\"clozetip\" title=\"nđ lv\" href=\"#\">nhịp độ làm việc</a> khẩn trương buộc giai cấp này phải <a class=\"clozetip\" title=\"tt.kl.lđ\" href=\"#\">tuân thủ kỉ luật lao động</a> cùng với cuộc sống <a class=\"clozetip\" title=\"đt\" href=\"#\">đô thị</a> đã tạo nên tính tổ chức, kỷ luật chặt chẽ.</p> <p>Ở trong các <a class=\"clozetip\" title=\"tc\" href=\"#\">tổ chức</a>, <a class=\"clozetip\" title=\"cđ\" href=\"#\">công đoàn</a> yêu cầu giai cấp này phải có tính tổ chức kỷ luật chặt chẽ.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5006', null, null, null, '5', '5002', '694', '443', '1552755488', null, '', null, '<p><strong>- <a class=\"clozetip\" title=\"\" href=\"#\">Đặc điểm chính trị- xã hội</a></strong> của giai cấp công nhân: ( 3 đặc điểm)</p> <p>+ Giai cấp công nhân có <a class=\"clozetip\" title=\"bc.qt\" href=\"#\">bản chất quốc tế</a>:</p> <p>Giai cấp <a class=\"clozetip\" title=\"TS\" href=\"#\">tư sản</a> là một lực lượng quốc tế, bóc lột cả giai cấp <a class=\"clozetip\" title=\"cn\" href=\"#\">công nhân</a> ở nước mình và nước thuộc địa.</p> <p>Ngày nay với sự phát triển mạnh mẽ toàn cầu hóa, sản phẩm không chỉ được sản xuất <a class=\"clozetip\" title=\"ở...\" href=\"#\">ở một nước</a> mà là kết quả lao động của <a class=\"clozetip\" title=\"nhiều qg\" href=\"#\">nhiều quốc gia</a>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5007', null, null, null, '5', '5002', '694', '443', '1552755488', null, '', null, '<p>SUY RA: giai cấp công nhân là giai cấp duy nhất có khả năng <a class=\"clozetip\" title=\"lđ\" href=\"#\">lãnh đạo</a> cuộc cách mạng xã hội, <a class=\"clozetip\" title=\"lđ\" href=\"#\">lật đổ</a> chủ nghĩa tư bản, <a class=\"clozetip\" title=\"xd\" href=\"#\">xây dựng</a> xã hội mới XHCN và <a class=\"clozetip\" title=\"\" href=\"#\">CSCN</a>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5008', 'Câu 1.3. Nội dung sứ mệnh lịch sử của giai cấp công nhân?', null, null, '2', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5009', 'NỘI DUNG SỨ MỆNH LỊCH SỬ CỦA GIAI CẤP CÔNG NHÂN ', '', null, '5', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5010', null, null, null, '5', '5009', '694', '443', '1552755488', null, '', null, '<p>+ <a class=\"clozetip\" title=\"xb\" href=\"#\">xóa bỏ</a> chủ nghĩa tư bản, xóa bỏ chế độ người <a class=\"clozetip\" title=\"bl\" href=\"#\">bóc lột</a> người</p> <p>+ <a class=\"clozetip\" title=\"xd\" href=\"#\">xây dựng</a> xã hội mới- xã hội <a class=\"clozetip\" title=\"XHCN\" href=\"#\">xã hội chủ nghĩa</a> và <a class=\"clozetip\" title=\"CSCN\" href=\"#\">cộng sản chủ nghĩa</a></p> <p>+ <a class=\"clozetip\" title=\"gp\" href=\"#\">giải phóng</a> giai cấp công nhân, nhân dân lao động và giải phóng toàn thể nhân loại khỏi mọi sự áp bức, bóc lột, nghèo nàn lạc hậu.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5011', null, null, null, '5', '5009', '694', '443', '1552755488', null, '', null, '<p>Để thực hiện được sứ mệnh trên thì giai cấp phải trải qua 2 bước: ( theo C.Mac & Ph.Ăngghen)</p> <p>B1: “… giai cấp vô sản <a class=\"clozetip\" title=\"b.th\" href=\"#\">biến thành</a> giai cấp thống trị”. Và “giai cấp vô sản <a class=\"clozetip\" title=\"chiếm...cq\" href=\"#\">chiếm lấy chính quyền</a> nhà nước”</p> <p>B2: …giai cấp vô sản dùng <a class=\"clozetip\" title=\"s.th.tr\" href=\"#\">sự thống trị</a> của mình để từng bước đoạt lấy toàn bộ <a class=\"clozetip\" title=\"TB\" href=\"#\">tư bản</a> trong tay giai cấp tư sản để <a class=\"clozetip\" title=\"t.tr\" href=\"#\">tập trung</a> tất cả những <a class=\"clozetip\" title=\"ccsx\" href=\"#\">công cụ sản xuất</a> vào trong tay <a class=\"clozetip\" title=\"nn\" href=\"#\">nhà nước</a> tiến hành tổ chức xây dựng xã hội mới- xã hội chủ nghĩa.</p> <p> </p> <p>Hai bước này có <a class=\"clozetip\" title=\"qh.cc\" href=\"#\">quan hệ chặt chẽ</a> với nhau: không thực hiện được B1 thì không thực hiện được B2, nhưng B2 là bước quan trọng nhất để giai cấp công nhân hoàn thành sứ mệnh của mình.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5012', 'Câu 1.4. Liên hệ Việt Nam ( sứ mệnh lịch sử của giai cấp công nhân)', null, null, '2', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5013', 'LIÊN HỆ SỨ MỆNH LỊCH SỬ CỦA CÔNG NHÂN VỚI VIỆT NAM ', '', null, '5', '0', '694', '443', '1552755488', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5014', null, null, null, '5', '5013', '694', '443', '1552755488', null, '', null, '<p>+ Sứ mệnh lịch sử của giai cấp CN hiện nay: <a class=\"clozetip\" title=\"đ.m\" href=\"#\">đẩy mạnh</a> CNH, HĐH; phát triển đất nước theo <a class=\"clozetip\" title=\"đh\" href=\"#\">định hướng</a> XHCN -> <a class=\"clozetip\" title=\"gp\" href=\"#\">giải phóng</a> người lao động -> <a class=\"clozetip\" title=\"xd\" href=\"#\">xây dựng</a> dân giàu nước mạnh</p> <p>+ Đảng khẳng định: coi kinh tế <a class=\"clozetip\" title=\"tr.th\" href=\"#\">tri thức</a> là yếu tố quan trọng của nền kinh tế -> phát triển giai cấp công nhân -> xây dựng <a class=\"clozetip\" title=\"khối đđk\" href=\"#\">khối đại đoàn kết</a> dân tộc …</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5065', 'Câu 6.1. Cơ sở khách quan của liên minh công- nông trong cách mạng XHCN', null, null, '2', '0', '703', '443', '1552758393', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5066', 'CƠ SỞ KHÁCH QUAN CỦA LIÊN MINH CÔNG- NÔNG TRONG CÁCH MẠNG XÃ HỘI CHỦ NGHĨA ( 3 CS) ', '', null, '5', '0', '703', '443', '1552758393', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5067', null, null, null, '5', '5066', '703', '443', '1552758393', null, '', null, '<p>Thứ nhất, trong xã hội <a class=\"clozetip\" title=\"tbcn\" href=\"#\">tư bản chủ nghĩa</a>, giai cấp công nhân, giai cấp nông dân cũng như nhiều tầng lớp lao động khác đều là những người <a class=\"clozetip\" title=\"lđ\" href=\"#\">lao động</a>, đều bị <a class=\"clozetip\" title=\"ab bl\" href=\"#\">áp bức bóc lột</a>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5068', null, null, null, '5', '5066', '703', '443', '1552758393', null, '', null, '<p>Thứ hai, nền kinh tế quốc dân là một <a class=\"clozetip\" title=\"thể t.n\" href=\"#\">thể thống nhất</a> của nhiều ngành nghề, nhưng trong đó <a class=\"clozetip\" title=\"cn\" href=\"#\">công nghiệp</a> và <a class=\"clozetip\" title=\"nn\" href=\"#\">nông nghiệp</a> là 2 ngành sản xuất chính trong xã hội, vì vậy cần có sự <a class=\"clozetip\" title=\"lm cc\" href=\"#\">liên minh chặt chẽ</a> giữa giai cấp công nhân và nông dân để 2 ngành kinh tế này <a class=\"clozetip\" title=\"pt\" href=\"#\">phát triển</a>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5069', null, null, null, '5', '5066', '703', '443', '1552758393', null, '', null, '<p>Thứ ba, xét về mặt <a class=\"clozetip\" title=\"ctr-xh\" href=\"#\">chính trị- xã hội</a> GCCN, GCND và các tầng lớp lao động khác là <a class=\"clozetip\" title=\"ll.ctr\" href=\"#\">lực lượng chính trị</a> to lớn trong xây dựng, bảo vệ <a class=\"clozetip\" title=\"cq\" href=\"#\">chính quyền</a> nhà nước, trong xây dựng <a class=\"clozetip\" title=\"k đk\" href=\"#\">khối đoàn kết</a> dân tộc.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5070', 'Câu 6.2. Liên hệ Việt Nam ( cơ sở khách quan của liên minh công- nông) ', null, null, '2', '0', '703', '443', '1552758393', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5071', 'Liên minh công – nông có ý nghĩa đặc biệt quan trọng đối với việc củng cố khối đại đoàn kết dân tộc ở Việt Nam trong mọi thời kỳ cách mạng.', null, null, '2', '0', '703', '443', '1552758393', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5072', 'LIÊN HỆ CƠ SỞ KHÁCH QUAN CỦA CUỘC CÁCH MẠNG XÃ HỘI CHỦ NGHĨA ĐẾN VIỆT NAM ', '', null, '5', '0', '703', '443', '1552758393', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5073', null, null, null, '5', '5072', '703', '443', '1552758393', null, '', null, '<p><strong>Liên minh công – nông có ý nghĩa <a class=\"clozetip\" title=\"đb qt\" href=\"#\">đặc biệt quan trọng</a> đối với việc củng cố khối đại đoàn kết dân tộc ở Việt Nam trong mọi thời kỳ cách mạng.</strong></p> <ul> <li>Đại hội X của Đảng khẳng định “<strong>Đại đoàn kết dân tộc trên <a class=\"clozetip\" title=\"nền.t.lm\" href=\"#\">nền tảng liên minh</a> giai cấp công nhân</strong>. <a class=\"clozetip\" title=\"sn\" href=\"#\">Sự nghiệp</a> <strong>công nghiệp hóa, hiện đại hóa</strong> đất nước sẽ không <a class=\"clozetip\" title=\"trở.t.ht\" href=\"#\">trở thành hiện thực</a> nếu không có nền <strong>nông nghiệp phát triển ổn định</strong>, vững chắc làm <a class=\"clozetip\" title=\"cs\" href=\"#\">cơ sở</a> và điều đó <strong>không thể <a class=\"clozetip\" title=\"tách.r.vt\" href=\"#\">tách rời vai trò</a> của giai cấp nông dân</strong>”.</li> <li>Trong <a class=\"clozetip\" title=\"tk đm\" href=\"#\">thời kỳ đổi mới</a>, Nghị quyết Hội nghị TW 7 khóa X chỉ rõ: “<strong>Nông nghiệp, nông dân, nông thôn có <a class=\"clozetip\" title=\"vtr.cl\" href=\"#\">vị trí chiến lược </a></strong>trong sự nghiệp công nghiệp hóa, hiện đại hóa, xây dựng và bảo vệ tổ quốc, là cơ sở và lực lượng <strong><a class=\"clozetip\" title=\"qt\" href=\"#\">quan trọng</a> để phát triển kinh tế - xã hội</strong> bền vững, giữ vững <strong>ổn định <a class=\"clozetip\" title=\"ctr\" href=\"#\">chính trị</a></strong>, bảo đảm <a class=\"clozetip\" title=\"anqp\" href=\"#\"><strong>an</strong><strong> ninh quốc phòng</strong></a>; <strong>giữ gìn, phát huy <a class=\"clozetip\" title=\"bsvh\" href=\"#\">bản sắc văn hóa</a> dân tộc và bảo vệ <a class=\"clozetip\" title=\"mt.sth\" href=\"#\">môi trường sinh thái</a> của đất nước</strong>”.</li> <li><strong>Mọi nguồn lực, mọi <a class=\"clozetip\" title=\"tn.st\" href=\"#\">tiềm năng sáng tạo</a> trong nhân dân</strong>, trong đó có công nhân, nông dân và trí thức cần được <a class=\"clozetip\" title=\"kh.th\" href=\"#\"><strong>khai thác</strong></a> và <a class=\"clozetip\" title=\"p.h\" href=\"#\"><strong>phát huy</strong></a> để <strong>xây dựng</strong> và <strong>bảo vệ đất nước.</strong></li> </ul> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5074', 'Câu 7.1. Nội dung của liên minh công- nông trong cách mạng xã hội chủ nghĩa', null, null, '2', '0', '703', '443', '1552758404', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5075', 'NỘI DUNG CỦA LIÊN MINH CÔNG- NÔNG ( CHÍNH TRỊ; KINH TẾ; TƯ TƯỞNG- VĂN HÓA) ', '', null, '5', '0', '703', '443', '1552758404', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5076', null, null, null, '5', '5075', '703', '443', '1552758404', null, '', null, '<ul> <li>Liên minh về mặt chính trị: <a class=\"clozetip\" title=\"t.c\" href=\"#\">thực chất</a> của liên minh về chính trị là nhằm <a class=\"clozetip\" title=\"t.h\" href=\"#\">tập hợp</a>, <a class=\"clozetip\" title=\"đk\" href=\"#\">đoàn kết</a> rộng rãi được tất cả các giai cấp, các tầng lớp trong xã hội <a class=\"clozetip\" title=\"qt\" href=\"#\">quy tụ</a> dưới ngọn cờ của Đảng nhằm đạt được mục tiêu <a class=\"clozetip\" title=\"đl.dt\" href=\"#\">độc lập dân tộc</a> và CNXH.</li> <li>Liên minh về kinh tế: đảm bảo <a class=\"clozetip\" title=\"k.h\" href=\"#\">kết hợp</a> đúng đắn các lợi ích giữa hai giai cấp, <a class=\"clozetip\" title=\"hđ.kt\" href=\"#\">hoạt động kinh tế</a> vừa phải bảo đảm lợi ích của nhà nước, của xã hội, đồng thời phải <a class=\"clozetip\" title=\"thường.x.q.t\" href=\"#\">thường xuyên quan tâm</a> tới các lợi ích của giai cấp nông dân.</li> <li>Liên minh về tư tưởng- văn hóa:</li> </ul> <p> + xây dựng nên một xã hội với nền công nghiệp <a class=\"clozetip\" title=\"sxhđ\" href=\"#\">sản xuất hiện đại</a>.</p> <p> + xây dựng một xã hội nhân văn, nhân đạo: có tinh thần <a class=\"clozetip\" title=\"h.n\" href=\"#\">hữu nghị</a>, tương trợ, giúp đỡ lẫn nhau.</p> <p> + tạo điều kiện cho quần chúng nhân dân lao động <a class=\"clozetip\" title=\"tgql\" href=\"#\">tham gia quản lý</a> kinh tế, quản lý xã hội, quản lý nhà nước.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5077', 'Câu 7.2. Liên hệ Việt Nam ( nội dung liên minh công- nông)', null, null, '2', '0', '703', '443', '1552758404', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5078', 'LIÊN HỆ VIỆT NAM: NỘI DUNG CỦA LIÊN MINH CÔNG- NÔNG TRONG CÁCH MẠNG XÃ HỘI CHỦ NGHĨA ', '', null, '5', '0', '703', '443', '1552758404', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5079', null, null, null, '5', '5078', '703', '443', '1552758404', null, '', null, '<p>- <a class=\"clozetip\" title=\"kđ\" href=\"#\">Khẳng định</a> vai trò quan trọng của các <a class=\"clozetip\" title=\"bp\" href=\"#\">bộ phận</a> trong sự nghiệp cách mạng dưới sự <a class=\"clozetip\" title=\"lđ\" href=\"#\">lãnh đạo</a> của Đảng. <span>Từ ngày thành lập đến nay, Đảng ta luôn khẳng định vai trò lãnh đạo của GCCN đối với cách mạng Việt Nam. </span></p> <p>Cùng với liên minh công nông là <a class=\"clozetip\" title=\"n.c\" href=\"#\">nòng cốt</a> của cách mạng, Đảng ta luôn coi trọng, đánh giá cao vai trò của <a class=\"clozetip\" title=\"đ.n.t.th\" href=\"#\">đội ngũ trí thức</a> trong sự nghiệp <a class=\"clozetip\" title=\"đ.tr\" href=\"#\">đấu tranh</a> cách mạng cũng như trong quá trình <a class=\"clozetip\" title=\"đ.m\" href=\"#\">đổi mới</a>, xây dựng đất nước. </p> <p>- Từng bước <a class=\"clozetip\" title=\"n.c đ.s\" href=\"#\">nâng cao đời sống</a> vật chất tinh thần của các <a class=\"clozetip\" title=\"bp\" href=\"#\">bộ phận</a> trong liên minh. Việc thực hiện các <a class=\"clozetip\" title=\"c.tr\" href=\"#\">chủ trương</a>, <a class=\"clozetip\" title=\"c.s\" href=\"#\">chính sách</a> phát triển nông nghiệp, khuyến khích và trợ giúp nông dân trong tiến trình <a class=\"clozetip\" title=\"t.h.n.q\" href=\"#\">thực hiện nghị quyết</a> các đại hội đảng đã phát huy tác dụng, <a class=\"clozetip\" title=\"b.m\" href=\"#\">bộ mặt</a> nông thôn có nhiều thay đổi.</p> <p>- Tuy nhiên, nông dân chưa được hưởng lợi <a class=\"clozetip\" title=\"t.xứng\" href=\"#\">tương xứng</a> với đóng góp cho sự phát triển đất nước. Các biện pháp <a class=\"clozetip\" title=\"kt-kt\" href=\"#\">kinh tế - kỹ thuật</a> dù đã phát huy tác dụng, nhưng sự <a class=\"clozetip\" title=\"cb.tduy\" href=\"#\">chuyển biến tư duy</a> của người nông dân vẫn chưa được như kỳ vọng. Tâm lý tiểu nông còn khá nặng nề trong nông dân và trong cả những giai cấp, tầng lớp khác...</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5080', 'Câu 8.1. Nguyên tắc cơ bản của liên minh công- nông trong cách mạng XHCN', null, null, '2', '0', '703', '443', '1552758413', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5081', 'NGUYÊN TẮC CƠ BẢN CỦA LIÊN MINH CÔNG- NÔNG VÀ LIÊN HỆ VIỆT NAM ', '', null, '5', '0', '703', '443', '1552758413', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5082', null, null, null, '5', '5081', '703', '443', '1552758413', null, '', null, '<p>NGUYÊN TẮC CƠ BẢN</p> <p>- Đảm bảo <a class=\"clozetip\" title=\"v.tr lđ\" href=\"#\">vai trò lãnh đạo</a> của giai cấp công nhân</p> <p>- Đảm bảo <a class=\"clozetip\" title=\"ng.t\" href=\"#\">nguyên tắc</a> tự nguyện</p> <p>- <a class=\"clozetip\" title=\"k.h\" href=\"#\">Kết hợp</a> đúng đắn các lợi ích</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5083', null, null, null, '5', '5081', '703', '443', '1552758413', null, '', null, '<p>LIÊN HỆ VIỆT NAM</p> <p>- <strong>Liên minh giai cấp công nhân, nông dân và tầng lớp trí thức là <a class=\"clozetip\" title=\"n.c\" href=\"#\">nòng cốt</a> của khối <a class=\"clozetip\" title=\"đ.đ.k\" href=\"#\">đại đoàn kết</a> dân tộc</strong>, là <a class=\"clozetip\" title=\"c.d\" href=\"#\">chỗ dựa</a> chủ yếu của Đảng và Nhà nước. Muốn cho <a class=\"clozetip\" title=\"khối lm\" href=\"#\"><strong>khối liên minh</strong></a> đó ngày càng phát huy được <strong><a class=\"clozetip\" title=\"t.d\" href=\"#\">tác dụng</a> mạnh mẽ</strong>, thì <strong>Đảng và Nhà nước</strong> phải kịp thời <strong>ban hành</strong> và <strong>thực thi</strong> các <a class=\"clozetip\" title=\"c.s\" href=\"#\"><strong>chính sách</strong></a> nhằm <strong>tăng cường <a class=\"clozetip\" title=\"sự t.n\" href=\"#\">sự thống nhất</a> về lợi ích giữa các <a class=\"clozetip\" title=\"gc\" href=\"#\">giai cấp</a></strong>, đồng thời phải <strong>coi trọng <a class=\"clozetip\" title=\"lãnh.đ.xd\" href=\"#\">lãnh đạo xây dựng</a> các tổ chức của nó</strong>, làm cho các tổ chức đó thực sự là <a class=\"clozetip\" title=\"chỗ.d.t.c\" href=\"#\">chỗ dựa tin cậy</a> của hội viên, đoàn viên, là <strong>chỗ dựa <a class=\"clozetip\" title=\"v.ch\" href=\"#\">vững chắc</a> của Đảng và Nhà nước trong nền <a class=\"clozetip\" title=\"kttt\" href=\"#\">kinh tế thị trường</a> theo định hướng XHCN</strong>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5084', 'Câu 8.2. Liên hệ Việt Nam ( nguyên tắc cơ bản của liên minh công- nông)', null, null, '2', '0', '703', '443', '1552758413', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5085', 'Câu 9.1. Tính tất yếu khách quan của thời kỳ quá độ lên chủ nghĩa xã hội', null, null, '2', '0', '703', '443', '1552758424', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5086', 'Tính tất yếu khách quan của thời kỳ quá độ lên chủ nghĩa xã hội. ', '', null, '5', '0', '703', '443', '1552758424', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5087', null, null, null, '5', '5086', '703', '443', '1552758424', null, '', null, '<p><strong>TÍNH TẤT YẾU KHÁCH QUAN:</strong></p> <p>+ Sự khác nhau về <a class=\"clozetip\" title=\"b.c\" href=\"#\">bản chất</a> giữa CNXH và CNTB</p> <p>+ Phải xây dựng được nền <a class=\"clozetip\" title=\"đại c.n\" href=\"#\">đại công nghiệp</a> có trình độ phát triển cao</p> <p>+ Các <a class=\"clozetip\" title=\"qhxh\" href=\"#\">quan hệ xã hội</a> của CNXH không <a class=\"clozetip\" title=\"tự.p\" href=\"#\">tự phát</a> hình thành trong xã hội tư bản mà phải trong quá trình xây dựng xã hội mới</p> <p>+ Công cuộc xây dựng CNXH là mới mẻ, khó khăn, phức tạp -> cần có <a class=\"clozetip\" title=\"tg\" href=\"#\">thời gian</a></p> <p> </p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5088', 'Câu 9.2. Chứng minh thời kỳ quá độ lên CNXH là thời kỳ cải biến cách mạng toàn diện, triệt để, sâu sắc trên mọi lĩnh vực kinh tế chính trị, tư tưởng, văn hóa…', null, null, '2', '0', '703', '443', '1552758424', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5089', ' Chứng minh thời kỳ quá độ lên chủ nghĩa xã hội là tất yếu đối với các quốc gia lựa chọn con đường đi lên chủ nghĩa xã hội.', '', null, '5', '0', '703', '443', '1552758424', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5090', null, null, null, '5', '5089', '703', '443', '1552758424', null, '', null, '<p><strong>CHỨNG MINH</strong>:</p> <p>+ Thời kỳ <a class=\"clozetip\" title=\"\" href=\"#\">chuyển</a> từ xã hội này sang xã hội khác đều <a class=\"clozetip\" title=\"n.định\" href=\"#\">nhất định</a> phải trải qua <a class=\"clozetip\" title=\"tk.qđ\" href=\"#\">thời kỳ quá độ</a></p> <p>+ Sự ra đời của 1 XH bao giờ cũng có <a class=\"clozetip\" title=\"sự k.t\" href=\"#\">sự kế thừa</a> từ các nhân tố do XH cũ tạo ra</p> <p>+ Các quan hệ sản xuất của CNXH không tự <a class=\"clozetip\" title=\"p.s\" href=\"#\">phát sinh</a>, chúng là kết quả <a class=\"clozetip\" title=\"c.t\" href=\"#\">cải tạo</a> XHCN</p> <p>+ Công cuộc xây dựng CNXH là mới mẻ, <a class=\"clozetip\" title=\"ph.t\" href=\"#\">phức tạp</a></p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5091', 'Câu 9.3. Liên hệ Việt Nam', null, null, '2', '0', '703', '443', '1552758424', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5092', ' Liên hệ Việt Nam.', '', null, '5', '0', '703', '443', '1552758424', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5093', null, null, null, '5', '5092', '703', '443', '1552758424', null, '', null, '<p>LIÊN HỆ VIỆT NAM: </p> <p>+ Nước ta đi lên CNXH từ năm 1975 sau khi <a class=\"clozetip\" title=\"gp\" href=\"#\">giải phóng</a> đất nước (miền Bắc năm 1945), <a class=\"clozetip\" title=\"bỏ q\" href=\"#\">bỏ qua</a> chế độ TBCN -> tránh cho dân những <a class=\"clozetip\" title=\"đ.k\" href=\"#\">đau khổ</a> của chế độ TBCN</p> <p>+ Kết hợp sự <a class=\"clozetip\" title=\"q.l\" href=\"#\">quản lý</a> của nhà nước theo định hướng XHCN trên cơ sở <a class=\"clozetip\" title=\"pt.kt\" href=\"#\">phát triển kinh tế</a></p> <p>->đây không phải là công việc có thể làm 1 sớm 1 chiều mà phải từ từ</p> <p>->nhiệm vụ: CNH, HĐH ,xây dựng quan hệ sản xuất mới, mở rộng hợp tác,…</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5094', 'Câu 10.1. Đặc điểm và thực chất của thời kỳ quá độ lên CNXH', null, null, '2', '0', '703', '443', '1552758434', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5095', 'ĐẶC ĐIỂM VÀ THỰC CHẤT CỦA THỜI KỲ QUÁ ĐỘ LÊN CHỦ NGHĨA XÃ HỘI.  ', '', null, '5', '0', '703', '443', '1552758434', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5096', null, null, null, '5', '5095', '703', '443', '1552758434', null, '', null, '<p>ĐẶC ĐIỂM VÀ THỰC CHẤT CỦA THỜI KỲ QUÁ ĐỘ LÊN CHỦ NGHĨA XÃ HỘI:</p> <p>- Trên lĩnh vực <strong>kinh tế</strong>: còn <strong><a class=\"clozetip\" title=\"t.t\" href=\"#\">tồn tại</a> nền kinh tế <a class=\"clozetip\" title=\"nhiều tp\" href=\"#\">nhiều thành phần</a></strong>, nhiều loại hình <a class=\"clozetip\" title=\"s.h\" href=\"#\">sở hữu</a>, nhiều loại hình <a class=\"clozetip\" title=\"pp\" href=\"#\">phân phối</a>, trong đó <strong>phân phối <a class=\"clozetip\" title=\"theo lđ\" href=\"#\">theo lao động</a> giữ vai trò chủ đạo.</strong></p> <p>- Trên lĩnh vực <strong>chính trị</strong>: <a class=\"clozetip\" title=\"k.c\" href=\"#\"><strong>kết cấu</strong></a> giai cấp trong xã hội <strong>phức tạp</strong> gồm nhiều giai cấp có <strong><a class=\"clozetip\" title=\"thu.n & yt.c.t\" href=\"#\">thu nhập và ý thức chính trị</a> khác nhau</strong>.</p> <p>- Trên lĩnh vực <strong>văn hóa, tư tưởng</strong>: <strong>tồn tại nhiều tư tưởng khác nhau</strong>, thậm chí <strong><a class=\"clozetip\" title=\"đ.l\" href=\"#\">đối lập</a> nhau</strong>. <strong>Tồn tại nền văn hóa cũ mới <a class=\"clozetip\" title=\"đ.x\" href=\"#\">đan xen</a> nhau, <a class=\"clozetip\" title=\"đ.tr\" href=\"#\">đấu tranh</a> với nhau</strong>.</p> <p><strong>Thực chất của thời kỳ quá độ là <a class=\"clozetip\" title=\"cuộc đ.tr\" href=\"#\">cuộc đấu tranh</a> giai cấp giữa giai cấp <a class=\"clozetip\" title=\"ts\" href=\"#\">tư sản</a> với giai cấp <a class=\"clozetip\" title=\"cn\" href=\"#\">công nhân</a>, <a class=\"clozetip\" title=\"nd.lđ\" href=\"#\">nhân dân lao động</a> trong điều kiện mới.</strong></p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5097', 'Câu 10.2. Chứng minh thời kỳ quá độ lên CNXH là thời kỳ cải biến cách mạng toàn diện, triệt để, sâu sắc trên mọi lĩnh vực kinh tế chính trị, tư tưởng, văn hóa… ( 3 ý)', null, null, '2', '0', '703', '443', '1552758434', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5098', 'CHỨNG MINH THỜI KỲ QUÁ ĐỘ LÊN CHỦ NGHĨA XÃ HỘI LÀ THỜI KỲ CẢI BIẾN CÁCH MẠNG TRIỆT ĐỂ, TOÀN DIỆN VÀ SÂU SẮC TRÊN MỌI LĨNH VỰC KINH TẾ, CHÍNH TRỊ, VĂN HÓA, TƯ TƯỞNG...', '', null, '5', '0', '703', '443', '1552758434', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5099', null, null, null, '5', '5098', '703', '443', '1552758434', null, '', null, '<p>CHỨNG MINH:</p> <ul> <li>Thứ nhất, là <strong>xã hội cũ</strong>, tư bản hay phong kiến <strong><a class=\"clozetip\" title=\"chỉ tạo t.đ\" href=\"#\">chỉ tạo ra tiền đề</a>, <a class=\"clozetip\" title=\"đk\" href=\"#\">điều kiện</a> nhất định,</strong> những yếu tố của <strong>xã hội chủ nghĩa</strong> chứ <strong>chưa có những <a class=\"clozetip\" title=\"bc\" href=\"#\">bản chất</a></strong> như <a class=\"clozetip\" title=\"tlsx\" href=\"#\">tư liệu sản xuất</a> hiện đại, <a class=\"clozetip\" title=\"t/c xhh\" href=\"#\">tính chất xã hội hóa</a> cao, xác lập cao độ <a class=\"clozetip\" title=\"c.h\" href=\"#\">công hữu</a> về tư liệu sản xuất. Do đó, cần phải có <a class=\"clozetip\" title=\"tk lâu d\" href=\"#\">thời kỳ lâu dài</a> để <strong>cải tạo nền kinh tế cũ, <a class=\"clozetip\" title=\"xl\" href=\"#\">xác lập</a> những yếu tố <a class=\"clozetip\" title=\"bc\" href=\"#\">bản chất</a> của xã hội mới.</strong></li> <li>Thứ hai, là <strong>giai cấp thống trị cũ mặc dù bị lật đổ nhưng <a class=\"clozetip\" title=\"chưa ht\" href=\"#\">chưa hoàn toàn</a> được cải tạo.</strong></li> <li>Thứ ba, <strong>văn hóa vẫn phải chịu ảnh hưởng của <a class=\"clozetip\" title=\"t.dư\" href=\"#\">tàn dư</a> văn hóa cũ, lạc hậu.</strong></li> </ul> <p> </p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5100', 'Câu 10.3. Liên hệ Việt Nam', null, null, '2', '0', '703', '443', '1552758434', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5101', ' LIÊN HỆ VIỆT NAM. ( Thời kỳ quá độ lên chủ nghĩa xã hội) ', '', null, '5', '0', '703', '443', '1552758434', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5102', null, null, null, '5', '5101', '703', '443', '1552758434', null, '', null, '<p>LIÊN HỆ VIỆT NAM:</p> <p>Ở Việt Nam cuộc cách mạng xã hội chủ nghĩa diễn ra với 2 <a class=\"clozetip\" title=\"gđ\" href=\"#\">giai đoạn</a>.</p> <p>Trước hết nó là cuộc cách mạng <a class=\"clozetip\" title=\"dt.dc.nd\" href=\"#\">dân tộc dân chủ nhân dân</a> vì nó đã làm nhiệm vụ <a class=\"clozetip\" title=\"xb.cđ\" href=\"#\">xóa bỏ chế độ</a> phong kiến đã được thiết lập hàng nghìn năm ở Việt Nam. Giai đoạn thứ 2 từ 1954 ở miền Bắc và từ 1975 cả nước đã <a class=\"clozetip\" title=\"xd.xh\" href=\"#\">xây dựng xã hội</a> theo định hướng <a class=\"clozetip\" title=\"xhcn\" href=\"#\">xã hội chủ nghĩa</a>, đưa cả nước <a class=\"clozetip\" title=\"qđ\" href=\"#\">quá độ</a> lên chủ nghĩa xã hội. Cách mạng xã hội chủ nghĩa là cuộc cách mạng <a class=\"clozetip\" title=\"nhằm t.t\" href=\"#\">nhằm thay thế</a> chế độ cũ,trong cuộc cách mạng đó, <a class=\"clozetip\" title=\"gc.cn\" href=\"#\">giai cấp công nhân</a> là người lãnh đạo và <a class=\"clozetip\" title=\"c.v\" href=\"#\">cùng với</a> quần chúng nhân dân lao động khác xây dựng một xã hội công bằng, dân chủ, văn minh.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5103', 'Câu 11: Công thức chung của tư bản (mâu thuẫn, giải thích vì sao T- H- T’ là công thức chung của tư bản)', null, null, '2', '0', '699', '448', '1552787935', '1552791701', null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5104', 'Công thức chung của tư bản (mâu thuẫn, giải thích vì sao T- H- T’ là công thức chung của tư bản) ', '', null, '5', '0', '699', '448', '1552787935', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5105', null, null, null, '5', '5104', '699', '448', '1552787935', null, '', null, '<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\"> <tbody> <tr> <td> <div> <p align=\"center\"> </p> </div> </td> </tr> </tbody> </table> <p>-Công thức chung của tư bản: <a class=\"clozetip\" title=\"\" href=\"#\">T- H- T’ </a></p> <p>- Mâu thuẫn: Công thức chung của tư bản là T- H- T’ ( T’= T+ T). Vậy T xuất hiện từ đâu?</p> <p> </p> <p>+ Trong lưu thông: Trao đổi ngang giá: Tổng <a class=\"clozetip\" title=\"\" href=\"#\">giá trị</a> và <a class=\"clozetip\" title=\"\" href=\"#\">giá trị cá biệt</a> không thay đổi</p> <p> Trao đổi không ngang giá: <a class=\"clozetip\" title=\"\" href=\"#\">Tổng giá trị</a> không thay đổi</p> <p>+ Ngoài lưu thông: Người trao đổi đứng mình với hàng hóa ->GT <a class=\"clozetip\" title=\"\" href=\"#\">không tăng lên</a></p> <p> Người sx sáng tạo thêm <a class=\"clozetip\" title=\"\" href=\"#\">thặng dư</a> phải bằng với <a class=\"clozetip\" title=\"\" href=\"#\">lao động</a> của mình</p> <p>=>Không lý giải được sự chuyển hóa của tiền thành <a class=\"clozetip\" title=\"\" href=\"#\">tư bản</a></p> <p>+ Vấn đề đặt ra ở đây là: phải xuất phát từ quy luật <a class=\"clozetip\" title=\"\" href=\"#\">nội tại</a> của lưu thông hàng hóa để lý giải sự chuyển hóa của tiền thành <a class=\"clozetip\" title=\"\" href=\"#\">tư bản</a></p> <p>=> Mâu thuẫn trong công thức chung của tư bản: “Tư bản không thể xuất hiện từ <a class=\"clozetip\" title=\"\" href=\"#\">trong lưu thông</a> và cũng không thể xuất hiện bên <a class=\"clozetip\" title=\"\" href=\"#\">ngoài lưu thông</a>. Nó phải <a class=\"clozetip\" title=\"\" href=\"#\">xuất hiện</a> trong lưu thông và đồng thời <a class=\"clozetip\" title=\"\" href=\"#\">không phải</a> trong lưu thông ”</p> <p>-Giải thích:Vì <a class=\"clozetip\" title=\"\" href=\"#\">sự vận động</a> của mọi tư bản đều biểu hiện trong lưu thông dưới dạng khái quát đó, dù đó là TB thương nghiệp, TB công nghiệp hay TB cho vay</p> <p>+ TB thương nghiệp: mua vào để bán ra <a class=\"clozetip\" title=\"\" href=\"#\">đắt hơn</a> -> thích hợp với công thức</p> <p>+ TB công nghiệp: <a class=\"clozetip\" title=\"\" href=\"#\">vận động</a> phức tạp hơn -> không tránh khỏi giai đoạn T- H và H- T’</p> <p>+ TB cho vay: <a class=\"clozetip\" title=\"\" href=\"#\">lấy lãi</a> -> rút ngắn lại T- T’</p> ', null, '42', null, null, null, null, null);
