INSERT INTO `question` VALUES ('5108', 'Câu 18.1: Khái niệm tích tụ và tập trung tư bản?', null, null, '2', '0', '700', '448', '1552792314', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5109', 'Câu 18.2: So sánh tích tụ tư bản và tập trung tư bản', null, null, '2', '0', '700', '448', '1552792314', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5110', 'Khái niệm tích tụ và tập trung tư bản? So sánh tích tụ và tập trung tư bản, mối quan hệ giữa tích lũy với tích tụ và tập trung tư bản, ví dụ minh họa. ', '', null, '5', '0', '700', '448', '1552792314', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5111', null, null, null, '5', '5110', '700', '448', '1552792314', null, '', null, '<p><img title=\"tltb\" src=\"/images/buttons/info.gif\" alt=\"\" align=\"baseline\" border=\"0\" /><img title=\"tttb\" src=\"/images/buttons/info.gif\" alt=\"\" align=\"baseline\" border=\"0\" />v Tích tụ tư bản: Là sự tăng thêm quy mô <a class=\"clozetip\" title=\"\" href=\"#\">tư bản cá biệt</a> bằng cách <a class=\"clozetip\" title=\"\" href=\"#\">tư bản hóa</a> giá trị thặng dư.</p> <p>v Tập trung tư bản: Là sự <a class=\"clozetip\" title=\"\" href=\"#\">hợp nhất</a> một số tư bản nhỏ thành một <a class=\"clozetip\" title=\"\" href=\"#\">tư bản cá biệt lớn.</a></p> <p>v So sánh:</p> <ul> <li>Điểm giống: Cả hai đều làm <a class=\"clozetip\" title=\"\" href=\"#\">tăng quy mô</a> tư bản cá biệt</li> <li>Điểm khác:</li> </ul> <table width=\"642\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\"> <tbody> <tr> <td width=\"321\"> <p align=\"center\">Tích tụ tư bản</p> </td> <td width=\"321\"> <p align=\"center\">Tập trung tư bản</p> </td> </tr> <tr> <td valign=\"top\" width=\"321\"> <p>- Nguồn: là <a class=\"clozetip\" title=\"\" href=\"#\">thời gian</a> lao động thặng dư (m) tư bản hóa.</p> <p>- Làm tư bản cá biệt và tư bản xã hội <a class=\"clozetip\" title=\"\" href=\"#\">tăng</a></p> <p> </p> <p>- Biểu hiện mối quan hệ giữa <a class=\"clozetip\" title=\"\" href=\"#\">tư bản</a> và <a class=\"clozetip\" title=\"\" href=\"#\">lao động</a></p> </td> <td valign=\"top\" width=\"321\"> <p>- Nguồn: là các nhà tư bản đã hình thành trong <a class=\"clozetip\" title=\"\" href=\"#\">xã hội</a></p> <p>- Là sự <a class=\"clozetip\" title=\"\" href=\"#\">bố trí lại</a> bộ phận tư bản xã hội đã có, quy mô tư bản xã hội không đổi.</p> <p>- Biểu hiện mối quan hệ giữa các <a class=\"clozetip\" title=\"\" href=\"#\">nhà tư bản</a> với nhau.</p> </td> </tr> </tbody> </table> <p> </p> <p>v Mối quan hệ giữa tích lũy với tích tụ và tâp trung tư bản:</p> <p>Tích tụ tư bản làm tăng thêm <a class=\"clozetip\" title=\"\" href=\"#\">quy mô</a> và <a class=\"clozetip\" title=\"\" href=\"#\">sức mạnh</a> của tư bản cá biệt. Ngược lại, tập trung tư bản tạo điều kiện thuận lợi để tăng <a class=\"clozetip\" title=\"\" href=\"#\">cường độ bóc lột</a> giá trị thặng dư, nên đẩy nhanh <a class=\"clozetip\" title=\"\" href=\"#\">tích tụ tư bản</a>. Ảnh hưởng qua lại của tích tụ và tập trung tư bản làm cho <a class=\"clozetip\" title=\"\" href=\"#\">tích lũy tư bản</a> ngày càng mạnh. Như vậy, quá trình tích lũy tư bản gắn liền với quá trình tích tụ và tập trung tư bản ngày càng tăng, làm cho <a class=\"clozetip\" title=\"\" href=\"#\">mâu thuẫn</a> kinh tế <a class=\"clozetip\" title=\"\" href=\"#\">cơ bản</a> của chủ nghĩa tư bản ngày càng <a class=\"clozetip\" title=\"\" href=\"#\">sâu sắc</a>.</p> ', null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5112', ' Câu 19: Cấu tạo hữu cơ của tư bản? Lưu ý công thức cấu tạo hữu cơ của tư bản', null, null, '2', '0', '700', '448', '1552792329', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5113', 'Cấu tạo hữu cơ của tư bản? Lưu ý công thức cấu tạo hữu cơ của tư bản ', '', null, '5', '0', '700', '448', '1552792329', null, null, null, null, null, '42', null, null, null, null, null);
INSERT INTO `question` VALUES ('5114', null, null, null, '5', '5113', '700', '448', '1552792329', null, '', null, '<p><strong>-</strong>Khái niệm: là <a class=\"clozetip\" title=\"\" href=\"#\">cấu tạo giá trị</a> của TB, do <a class=\"clozetip\" title=\"\" href=\"#\">cấu tạo kỹ thuật</a> của TB quyết định, phản ánh <a class=\"clozetip\" title=\"\" href=\"#\">sự thay đổi</a> của cấu tạo kỹ thuật của TB</p> <p>- Phản ánh mối quan hệ của <a class=\"clozetip\" title=\"\" href=\"#\">cấu tạo kỹ thuật</a> và <a class=\"clozetip\" title=\"\" href=\"#\">cấu tạo giá trị</a> của TB</p> <p>- Công thức: <strong>Cấu tạo hữu cơ của TB = <a class=\"clozetip\" title=\"\" href=\"#\">c/v</a></strong></p> ', null, '42', null, null, null, null, null);
