INSERT INTO `question` VALUES ('5116', 'Câu 20.2: Các biện pháp làm tăng tốc độ chu chuyển của tư bản', null, null, '2', '0', '700', '448', '1552792350', null, null, null, null, null, '42', null, null, null, null, null);
