INSERT INTO `question` VALUES ('5701', null, null, null, '5', '5700', '814', '450', '1552976754', null, '', null, 'Câu1. 1.	Tính gần đúng giá trị sau(lấy 5 chữ số sau dâu phẩy)<span class=\"math-tex\">\\(A = \\ln \\left( {e + \\sqrt {4,01}  - \\sqrt[3]{{7,98}}} \\right),e \\approx 2,7273\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">1,00153</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5702', null, null, null, '5', '5700', '814', '450', '1552976754', null, '', null, 'Câu1. 2.Xét tính hội tụ  phân kì của tích phân sau(nếu là hội tụ hãy viết giá trị hội tụ, VD hội tụ đến 1)<span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}} \\)</span><a class=\"clozetip\" title=\"\" href=\"#\">hội tụ đến 2/3</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5703', null, null, null, '5', '5700', '814', '450', '1552976754', null, '', null, 'Câu2. 1.Giải phương trình vi phân <span class=\"math-tex\">\\(\\left( {{y^2} - 2} \\right)d{\\rm{x}} = \\left( {y + {x^2}y} \\right)dy \\)</span>      Phương trình vi phân có tích phân tổng quát là <span class=\"math-tex\">\\({\\mathop{\\rm arctan\\square}\\nolimits}  = \\frac{1}{2}\\ln \\left| {\\square} \\right| + C\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x,y^2-2</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5704', null, null, null, '5', '5700', '814', '450', '1552976754', null, '', null, 'Câu2. 2.2.	Giải phương trình sai phân <span class=\"math-tex\">\\(y\\left( {n + 2} \\right) + 2y\\left( {n + 1} \\right) + 4y\\left( n \\right) = 3n - 4\\)</span>      Phương trình sai phân có nghiệm là <span class=\"math-tex\">\\(y\\left( n \\right) = \\overline y \\left( n \\right) = {\\square^n}\\left( {{C_1}\\cos \\frac{{2n\\pi }}{3} + {C_2}i\\sin \\frac{{2n\\pi }}{3}} \\right) + \\frac{\\square- \\frac{{40}}{{49}}\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">2,3n/7</a>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5705', 'Điền biểu thức đúng vào chỗ trống(điền theo thứ tự ngăn cách nhau bằng dấu phẩy, sử dụng các kí hiệu / nếu chứa phân số hoặc ^ nếu chứa mũ).', null, null, '5', '0', '814', '450', '1552976982', '1552977035', null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5706', null, null, null, '5', '5705', '814', '450', '1552976982', '1552977035', null, null, '<p>Câu1. 1. Tính gần đúng giá trị sau(lấy 5 chữ số sau dâu phẩy)<span class=\"math-tex\">\\(A = \\ln \\left( {e + \\sqrt {4,01} - \\sqrt[3]{{7,98}}} \\right),e \\approx 2,7273\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">1,00153</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5707', null, null, null, '5', '5705', '814', '450', '1552976982', '1552977035', null, null, '<p>Câu1. 2.Xét tính hội tụ phân kì của tích phân sau(nếu là hội tụ hãy viết giá trị hội tụ, VD hội tụ đến 1)<span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{{x^3}}}{{\\sqrt {1 - {x^2}} }}} \\)</span><a class=\"clozetip\" title=\"\" href=\"#\">hội tụ đến 2/3</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5708', null, null, null, '5', '5705', '814', '450', '1552976982', '1552977035', null, null, '<p>Câu2. 1.Giải phương trình vi phân <span class=\"math-tex\">\\(\\left( {{y^2} - 2} \\right)d{\\rm{x}} = \\left( {y + {x^2}y} \\right)dy \\)</span> Phương trình vi phân có tích phân tổng quát là <span class=\"math-tex\">\\({\\mathop{\\rm arctan\\square}\\nolimits} = \\frac{1}{2}\\ln \\left| {\\square} \\right| + C\\)</span><a class=\"clozetip\" title=\"\" href=\"#\">x,y^2-2</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5709', null, null, null, '5', '5705', '814', '450', '1552976982', '1552977035', null, null, '<p>Câu2. 2. Giải phương trình sai phân <span class=\"math-tex\">\\(y\\left( {n + 2} \\right) + 2y\\left( {n + 1} \\right) + 4y\\left( n \\right) = 3n - 4\\)</span> Phương trình sai phân có nghiệm là<span class=\"math-tex\">\\(y\\left( n \\right) = \\overline y \\left( n \\right) = {\\square^n}\\left( {{C_1}\\cos \\frac{{2n\\pi }}{3} + {C_2}i\\sin \\frac{{2n\\pi }}{3}} \\right) + \\square - \\frac{{40}}{{49}}\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">2,3n/7</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5710', 'Điền biểu thức đúng vào chỗ trống(điền theo thứ tự ngăn cách nhau bằng dấu phẩy, sử dụng các kí hiệu / nếu chứa phân số hoặc ^ nếu chứa mũ).', null, null, '5', '0', '815', '450', '1552978064', '1552978095', null, null, null, null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5711', null, null, null, '5', '5710', '815', '450', '1552978064', '1552978095', null, null, '<p>Câu1. 1 Tìm cực trị có điều kiện của hàm hai biến sau <span class=\"math-tex\">\\({\\rm{z}} = f\\left( {x;y} \\right) = {x^3} + {y^3} - 3{\\rm{x}}y\\)</span> và <span class=\"math-tex\">\\(g\\left( {x;y} \\right) = x + y + 2 = 0\\)</span> Hàm số đạtc cực đại tại điểm ___ và giá trị cực đại là ___(viết điểm dưới dạng (a,b) ) <a class=\"clozetip\" title=\"\" href=\"#\">(-1,-1),-5</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5712', null, null, null, '5', '5710', '815', '450', '1552978064', '1552978095', null, null, '<p>Câu1. 2. Xét tính hội tụ phân kì của tích phân sau <span class=\"math-tex\">\\(I = \\int\\limits_0^1 {\\frac{{1 + 3{{\\ln }^2}x}}{x}d{\\rm{x}}} \\)</span><a class=\"clozetip\" title=\"\" href=\"#\">phân kỳ</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5713', null, null, null, '5', '5710', '815', '450', '1552978064', '1552978095', null, null, '<p>Câu2. 1. Giải phương trình vi phân <span class=\"math-tex\">\\(y\' - 2{\\rm{x}}y = 2{{\\rm{x}}^3}{\\mathop{\\rm sinx}\\nolimits} \\)</span> Phương trình vi phân có nghiệm tổng quát là <span class=\"math-tex\">\\( \\Rightarrow y = {e^{\\square}}.\\int {\\frac{{{x^3}\\sin x}}{{{e^{{x^2}}}}}d{\\rm{x}}} \\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x^2</a></p>', null, '51', null, null, null, null, null);
INSERT INTO `question` VALUES ('5714', null, null, null, '5', '5710', '815', '450', '1552978064', '1552978095', null, null, '<p>Câu2.2. Giải phương trình sai phân <span class=\"math-tex\">\\(y\\left( {n + 2} \\right) - 4y\\left( {n + 1} \\right) + 3y\\left( n \\right) = {2^n}\\left( {n + 3} \\right)\\)</span> Phương trình sai phân có nghiệm là<span class=\"math-tex\">\\(y = {C_1}{e^\\square} + {C_2}{e^{3{\\rm{x}}}} - {2^n}\\left( {\\square} \\right)\\)</span> <a class=\"clozetip\" title=\"\" href=\"#\">x,n+3</a></p>', null, '51', null, null, null, null, null);
